import { useState, useRef, useEffect } from 'react';

export const TimelineItem = ({
  item,
  isSelected,
  isDragged,
  isCopied,
  getTrackColor,
  getTimelineItemStyle,
  onSelect,
  onDragStart,
  onDragEnd,
  onResizeStart,
  onVolumeChange,
  showVolumeControls = true,
  hasAudio = false,
}) => {
  const [showVolumeSlider, setShowVolumeSlider] = useState(false);
  const [volume, setVolume] = useState(item.volume || 100);
  const volumeControlRef = useRef(null);
  const isDraggingVolume = useRef(false);

  const handleVolumeChange = (e) => {
    const newVolume = parseInt(e.target.value);
    setVolume(newVolume);
    onVolumeChange?.(item.id, newVolume);
  };

  const toggleVolumeSlider = (e) => {
    e.stopPropagation();
    setShowVolumeSlider(!showVolumeSlider);
  };

  // Ð‘Ð»Ð¾ÐºÐ¸Ñ€ÑƒÐµÐ¼ drag Ñ‚Ð¾Ð»ÑŒÐºÐ¾ ÐºÐ¾Ð³Ð´Ð° Ð°ÐºÑ‚Ð¸Ð²Ð½Ð¾ Ð²Ð·Ð°Ð¸Ð¼Ð¾Ð´ÐµÐ¹ÑÑ‚Ð²ÑƒÐµÐ¼ Ñ volume slider
  const handleVolumeMouseDown = (e) => {
    e.stopPropagation();
    isDraggingVolume.current = true;
  };

  const handleVolumeMouseUp = () => {
    isDraggingVolume.current = false;
  };

  // Ð—Ð°ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ slider Ð¿Ñ€Ð¸ ÐºÐ»Ð¸ÐºÐµ Ð²Ð½Ðµ ÐµÐ³Ð¾
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (showVolumeSlider && volumeControlRef.current && !volumeControlRef.current.contains(e.target)) {
        setShowVolumeSlider(false);
      }
    };

    if (showVolumeSlider) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [showVolumeSlider]);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° drag start - Ð±Ð»Ð¾ÐºÐ¸Ñ€ÑƒÐµÐ¼ ÐµÑÐ»Ð¸ Ð²Ð·Ð°Ð¸Ð¼Ð¾Ð´ÐµÐ¹ÑÑ‚Ð²ÑƒÐµÐ¼ Ñ volume
  const handleDragStart = (e) => {
    if (isDraggingVolume.current) {
      e.preventDefault();
      return false;
    }
    onDragStart(e, item);
  };

  const getVolumeIcon = () => {
    if (volume === 0) return "ðŸ”‡";
    if (volume < 30) return "ðŸ”ˆ";
    if (volume < 70) return "ðŸ”‰";
    return "ðŸ”Š";
  };

  return (
    <div
      className={`absolute h-10 rounded-lg border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md group ${getTrackColor(
        item.trackType
      )} ${isSelected ? "ring-2 ring-blue-300" : ""} ${
        isDragged ? "opacity-50 z-30" : ""
      } ${isCopied ? "ring-2 ring-green-300 ring-dashed" : ""}`}
      style={getTimelineItemStyle(item)}
      onClick={(e) => {
        e.stopPropagation();
        onSelect(item);
      }}
    >
      {/* Left resize handle */}
      <div
        className="absolute left-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-l-lg z-10 flex items-center justify-center"
        onMouseDown={(e) => onResizeStart(e, item, "left")}
        title="Trim start - Drag to adjust start time"
        style={{
          background:
            "linear-gradient(to right, rgba(255,255,255,0.8), transparent)",
        }}
      >
        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
      </div>

      {/* Main draggable content */}
      <div
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={onDragEnd}
        className="p-2 text-white text-xs truncate font-medium flex items-center h-full mx-3 cursor-grab active:cursor-grabbing"
        title="Drag to move item on timeline - Ripple mode: pushes other blocks"
      >
        <span className="flex-1 truncate">
          {item.name}
          {isCopied && <span className="ml-2 text-green-200">ðŸ“‹</span>}
        </span>

        {/* Volume controls */}
        {hasAudio && showVolumeControls && (
          <div 
            ref={volumeControlRef}
            className="relative ml-2"
            onClick={(e) => e.stopPropagation()}
          >
            {showVolumeSlider ? (
              // Expanded volume control
              <div 
                className="flex items-center gap-1 bg-black/30 rounded-md px-2 py-1 min-w-[80px]"
                onMouseDown={handleVolumeMouseDown}
                onMouseUp={handleVolumeMouseUp}
                onMouseLeave={handleVolumeMouseUp}
              >
                <button
                  className="text-white/80 hover:text-white transition-colors"
                  onClick={toggleVolumeSlider}
                  style={{ fontSize: '8px', cursor: 'pointer' }}
                >
                  {getVolumeIcon()}
                </button>
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={volume}
                  onChange={handleVolumeChange}
                  className="flex-1 h-1 bg-white/30 rounded appearance-none cursor-pointer min-w-[40px]"
                  style={{
                    background: `linear-gradient(to right, #ffffff 0%, #ffffff ${volume}%, rgba(255,255,255,0.3) ${volume}%, rgba(255,255,255,0.3) 100%)`
                  }}
                  onMouseDown={(e) => e.stopPropagation()}
                />
                <span className="text-white text-xs min-w-[24px] text-center" style={{ fontSize: '9px' }}>
                  {volume}
                </span>
              </div>
            ) : (
              // Collapsed volume icon
              <button
                className="text-white/80 hover:text-white transition-colors p-1 rounded"
                onClick={toggleVolumeSlider}
                title={`Volume: ${volume}%`}
                style={{ 
                  fontSize: '10px',
                  lineHeight: 1,
                  minWidth: '16px',
                  height: '16px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer'
                }}
              >
                {getVolumeIcon()}
              </button>
            )}
          </div>
        )}
      </div>

      {/* Right resize handle */}
      <div
        className="absolute right-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-r-lg z-10 flex items-center justify-center"
        onMouseDown={(e) => onResizeStart(e, item, "right")}
        title="Trim end - Drag to adjust duration"
        style={{
          background:
            "linear-gradient(to left, rgba(255,255,255,0.8), transparent)",
        }}
      >
        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
      </div>

      <style jsx>{`
        input[type="range"]::-webkit-slider-thumb {
          appearance: none;
          height: 12px;
          width: 12px;
          border-radius: 50%;
          background: #ffffff;
          cursor: pointer;
          border: 1px solid rgba(0,0,0,0.2);
          box-shadow: 0 1px 2px rgba(0,0,0,0.3);
        }
        
        input[type="range"]::-moz-range-thumb {
          height: 12px;
          width: 12px;
          border-radius: 50%;
          background: #ffffff;
          cursor: pointer;
          border: 1px solid rgba(0,0,0,0.2);
          box-shadow: 0 1px 2px rgba(0,0,0,0.3);
        }
      `}</style>
    </div>
  );
};
