import { useState, useRef, useEffect, useCallback } from "react";
import { MediaLibrary } from "../MediaLibrary";
import { Timeline } from "../Timeline";

import {
  Play,
  Pause,
  Volume2,
  SkipBack,
  SkipForward,
  Download,
  VolumeX,
  Film,
  X,
  Settings,
  Check,
  AlertCircle,
  Loader2,
  Zap,
  HardDrive,
  Cpu,
} from "lucide-react";

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç —ç–∫—Å–ø–æ—Ä—Ç–∞ –≤–∏–¥–µ–æ
const VideoExporter = ({ 
  isOpen, 
  onClose, 
  timelineItems, 
  videoDuration, 
  tracks 
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: '1920x1080',
    fps: 30,
    quality: 'high',
    format: 'mp4',
    bitrate: 8000,
    audioQuality: 192
  });
  
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState('');
  const [exportedBlob, setExportedBlob] = useState(null);
  const [exportError, setExportError] = useState(null);
  const [estimatedSize, setEstimatedSize] = useState(0);
  const [exportMethod, setExportMethod] = useState('canvas'); // 'webcodecs' –∏–ª–∏ 'canvas'

  const canvasRef = useRef(null);
  const videoRef = useRef(null);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–¥–¥–µ—Ä–∂–∫–∏ WebCodecs
  const supportsWebCodecs = typeof VideoEncoder !== 'undefined' && typeof VideoDecoder !== 'undefined';

  // –†–∞—Å—á–µ—Ç –ø—Ä–∏–º–µ—Ä–Ω–æ–≥–æ —Ä–∞–∑–º–µ—Ä–∞ —Ñ–∞–π–ª–∞
  const calculateEstimatedSize = useCallback(() => {
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    const duration = videoDuration;
    const bitrate = exportSettings.bitrate * 1000; // kbps to bps
    
    // –ü—Ä–∏–º–µ—Ä–Ω—ã–π —Ä–∞—Å—á–µ—Ç —Ä–∞–∑–º–µ—Ä–∞ –≤ MB
    const sizeBytes = (bitrate * duration) / 8;
    const sizeMB = sizeBytes / (1024 * 1024);
    
    setEstimatedSize(Math.round(sizeMB * 10) / 10);
  }, [exportSettings, videoDuration]);

  React.useEffect(() => {
    calculateEstimatedSize();
  }, [calculateEstimatedSize]);

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–Ω—Ç–∞ –≤—Ä–µ–º–µ–Ω–Ω–æ–π —à–∫–∞–ª—ã –¥–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏
  const getTimelineContentAtTime = useCallback((time) => {
    const currentItems = timelineItems.filter(
      item => time >= item.startTime && time < item.startTime + item.duration
    );

    return {
      mainVideo: currentItems.find(item => item.trackType === 'main' && item.type.includes('video')),
      overlays: currentItems.filter(item => item.trackType === 'overlay'),
      audio: currentItems.filter(item => item.trackType === 'audio'),
      images: currentItems.filter(item => item.type.includes('image'))
    };
  }, [timelineItems]);

  // –°–æ–∑–¥–∞–Ω–∏–µ canvas —Å –∫–æ–º–ø–æ–∑–∏—Ü–∏–µ–π –∫–∞–¥—Ä–∞
  const renderFrameToCanvas = useCallback(async (time, canvas, ctx) => {
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    canvas.width = width;
    canvas.height = height;
    
    // –û—á–∏—Å—Ç–∫–∞ canvas
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    const content = getTimelineContentAtTime(time);
    
    try {
      // –û—Ç—Ä–∏—Å–æ–≤–∫–∞ –æ—Å–Ω–æ–≤–Ω–æ–≥–æ –≤–∏–¥–µ–æ
      if (content.mainVideo) {
        const video = document.createElement('video');
        video.src = content.mainVideo.url;
        video.currentTime = time - content.mainVideo.startTime;
        video.crossOrigin = 'anonymous';
        
        await new Promise((resolve) => {
          const handleCanPlay = () => {
            video.removeEventListener('canplay', handleCanPlay);
            resolve();
          };
          video.addEventListener('canplay', handleCanPlay);
          if (video.readyState >= 3) {
            resolve();
          }
        });
        
        ctx.drawImage(video, 0, 0, width, height);
      }

      // –û—Ç—Ä–∏—Å–æ–≤–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –∫–∞–∫ –æ—Å–Ω–æ–≤–Ω–æ–≥–æ –∫–æ–Ω—Ç–µ–Ω—Ç–∞
      if (!content.mainVideo && content.images.length > 0) {
        const image = new Image();
        image.crossOrigin = 'anonymous';
        image.src = content.images[0].url;
        
        await new Promise((resolve, reject) => {
          image.onload = resolve;
          image.onerror = reject;
        });
        
        // –ü–æ–¥–≥–æ–Ω–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –ø–æ–¥ —Ä–∞–∑–º–µ—Ä canvas
        const imgAspect = image.width / image.height;
        const canvasAspect = width / height;
        
        let drawWidth, drawHeight, drawX, drawY;
        
        if (imgAspect > canvasAspect) {
          drawWidth = width;
          drawHeight = width / imgAspect;
          drawX = 0;
          drawY = (height - drawHeight) / 2;
        } else {
          drawWidth = height * imgAspect;
          drawHeight = height;
          drawX = (width - drawWidth) / 2;
          drawY = 0;
        }
        
        ctx.drawImage(image, drawX, drawY, drawWidth, drawHeight);
      }

      // –û—Ç—Ä–∏—Å–æ–≤–∫–∞ –æ–≤–µ—Ä–ª–µ–µ–≤
      for (const overlay of content.overlays) {
        if (overlay.type.includes('image')) {
          const img = new Image();
          img.crossOrigin = 'anonymous';
          img.src = overlay.url;
          
          await new Promise((resolve, reject) => {
            img.onload = resolve;
            img.onerror = () => resolve(); // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –æ—à–∏–±–∫–∏ –∑–∞–≥—Ä—É–∑–∫–∏
          });

          // –ü–æ–∑–∏—Ü–∏—è –æ–≤–µ—Ä–ª–µ—è (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è - –≤ —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ —É—á–∏—Ç—ã–≤–∞–π—Ç–µ —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏–∏)
          const overlayWidth = 200;
          const overlayHeight = 150;
          const x = 100;
          const y = 50;
          
          ctx.globalAlpha = overlay.opacity || 1;
          ctx.drawImage(img, x, y, overlayWidth, overlayHeight);
          ctx.globalAlpha = 1;
        }
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç—Ä–∏—Å–æ–≤–∫–∏ –∫–∞–¥—Ä–∞:', error);
    }
  }, [exportSettings, getTimelineContentAtTime]);

  // –≠–∫—Å–ø–æ—Ä—Ç —á–µ—Ä–µ–∑ Canvas + MediaRecorder
  const exportWithCanvas = useCallback(async () => {
    setExportStatus('–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∑–∞–ø–∏—Å–∏...');
    
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    
    canvas.width = width;
    canvas.height = height;

    const stream = canvas.captureStream(exportSettings.fps);
    
    // –ü–æ–ø—Ä–æ–±—É–µ–º —Ä–∞–∑–Ω—ã–µ —Ñ–æ—Ä–º–∞—Ç—ã
    let mimeType = 'video/webm;codecs=vp9';
    if (!MediaRecorder.isTypeSupported(mimeType)) {
      mimeType = 'video/webm;codecs=vp8';
      if (!MediaRecorder.isTypeSupported(mimeType)) {
        mimeType = 'video/webm';
      }
    }

    const mediaRecorder = new MediaRecorder(stream, {
      mimeType,
      videoBitsPerSecond: exportSettings.bitrate * 1000
    });

    const chunks = [];
    
    mediaRecorder.ondataavailable = (event) => {
      if (event.data.size > 0) {
        chunks.push(event.data);
      }
    };

    return new Promise((resolve, reject) => {
      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: mimeType });
        resolve(blob);
      };

      mediaRecorder.onerror = reject;

      mediaRecorder.start();
      
      let currentFrame = 0;
      const totalFrames = Math.ceil(videoDuration * exportSettings.fps);
      
      const renderFrame = async () => {
        if (currentFrame >= totalFrames) {
          mediaRecorder.stop();
          return;
        }

        const time = currentFrame / exportSettings.fps;
        await renderFrameToCanvas(time, canvas, ctx);
        
        currentFrame++;
        const progress = (currentFrame / totalFrames) * 100;
        setExportProgress(progress);
        setExportStatus(`–û–±—Ä–∞–±–æ—Ç–∫–∞ –∫–∞–¥—Ä–∞ ${currentFrame} –∏–∑ ${totalFrames}`);

        // –ù–µ–±–æ–ª—å—à–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è UI
        setTimeout(renderFrame, 1000 / exportSettings.fps);
      };

      renderFrame();
    });
  }, [exportSettings, videoDuration, renderFrameToCanvas]);

  // –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è —ç–∫—Å–ø–æ—Ä—Ç–∞
  const handleExport = useCallback(async () => {
    setIsExporting(true);
    setExportProgress(0);
    setExportError(null);
    setExportedBlob(null);

    try {
      const blob = await exportWithCanvas();
      setExportedBlob(blob);
      setExportStatus('–≠–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω!');
      setExportProgress(100);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞:', error);
      setExportError(error.message || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞');
      setExportStatus('–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞');
    } finally {
