import React, { useState } from "react";
import { X, Sparkles, Image, ArrowRight, Upload, Type, Palette, Wand2 } from "lucide-react";

const ThumbnailCreator = ({ isOpen, onClose, exportSettings }) => {
  const [activeTab, setActiveTab] = useState("styles"); // "styles" –∏–ª–∏ "custom"
  const [selectedStyle, setSelectedStyle] = useState(null);
  const [customData, setCustomData] = useState({
    text: "",
    description: "",
    referenceImage: null,
    userImages: []
  });
  
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedThumbnails, setGeneratedThumbnails] = useState([]);
  const [selectedThumbnail, setSelectedThumbnail] = useState(null);

  // –ì–æ—Ç–æ–≤—ã–µ —Å—Ç–∏–ª–∏ –æ–±–ª–æ–∂–µ–∫
  const thumbnailStyles = [
    {
      id: "youtube-gaming",
      name: "Gaming YouTube",
      preview: "üéÆ",
      prompt: "Epic gaming thumbnail with vibrant colors, explosive effects, and dramatic lighting"
    },
    {
      id: "youtube-tech",
      name: "Tech Review",
      preview: "‚ö°",
      prompt: "Clean tech review thumbnail with modern gadgets, blue and white color scheme"
    },
    {
      id: "youtube-tutorial",
      name: "Tutorial",
      preview: "üìö",
      prompt: "Educational tutorial thumbnail with clear text, arrows, and professional layout"
    },
    {
      id: "youtube-vlog",
      name: "Vlog Style",
      preview: "‚ú®",
      prompt: "Personal vlog thumbnail with warm colors, friendly atmosphere, and engaging expressions"
    },
    {
      id: "youtube-music",
      name: "Music Video",
      preview: "üéµ",
      prompt: "Music video thumbnail with vibrant colors, artistic design, and musical elements"
    },
    {
      id: "youtube-fitness",
      name: "Fitness",
      preview: "üí™",
      prompt: "Fitness thumbnail with energetic colors, dynamic poses, and motivational design"
    },
    {
      id: "youtube-food",
      name: "Food & Cooking",
      preview: "üç≥",
      prompt: "Food thumbnail with appetizing colors, delicious presentation, and kitchen vibes"
    },
    {
      id: "youtube-business",
      name: "Business",
      preview: "üíº",
      prompt: "Professional business thumbnail with sophisticated colors, charts, and corporate style"
    }
  ];

  // –§—É–Ω–∫—Ü–∏—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —á–µ—Ä–µ–∑ DALL-E (–º–æ–∫–æ–≤–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è)
  const generateWithDALLE = async () => {
    setIsGenerating(true);
    setGeneratedThumbnails([]);

    try {
      let prompt = "";
      
      if (activeTab === "styles" && selectedStyle) {
        const style = thumbnailStyles.find(s => s.id === selectedStyle);
        prompt = `${style.prompt}. Text overlay: "${customData.text || 'Amazing Video'}". YouTube thumbnail style, 1280x720 resolution, high quality, eye-catching design.`;
      } else if (activeTab === "custom") {
        prompt = `Create a YouTube thumbnail with the following requirements: ${customData.description}. ${customData.text ? `Include text: "${customData.text}"` : ""}. 1280x720 resolution, professional quality, eye-catching and clickable design.`;
      }

      console.log("DALL-E Prompt:", prompt);

      // –ú–æ–∫–æ–≤–∞—è –≥–µ–Ω–µ—Ä–∞—Ü–∏—è 5 –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ (–∑–¥–µ—Å—å –±—É–¥–µ—Ç —Ä–µ–∞–ª—å–Ω—ã–π API –≤—ã–∑–æ–≤)
      const mockThumbnails = [];
      for (let i = 0; i < 5; i++) {
        // –°–æ–∑–¥–∞–µ–º –º–æ–∫–æ–≤—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è —Å —Ä–∞–∑–Ω—ã–º–∏ –≥—Ä–∞–¥–∏–µ–Ω—Ç–∞–º–∏
        const canvas = document.createElement('canvas');
        canvas.width = 1280;
        canvas.height = 720;
        const ctx = canvas.getContext('2d');
        
        // –†–∞–∑–Ω—ã–µ –≥—Ä–∞–¥–∏–µ–Ω—Ç—ã –¥–ª—è –∫–∞–∂–¥–æ–≥–æ –≤–∞—Ä–∏–∞–Ω—Ç–∞
        const gradients = [
          ['#FF6B6B', '#4ECDC4', '#45B7D1'],
          ['#8E44AD', '#3498DB', '#2ECC71'],
          ['#E74C3C', '#F39C12', '#F1C40F'],
          ['#9B59B6', '#E67E22', '#E74C3C'],
          ['#1ABC9C', '#16A085', '#27AE60']
        ];
        
        const colors = gradients[i];
        const gradient = ctx.createLinearGradient(0, 0, 1280, 720);
        gradient.addColorStop(0, colors[0]);
        gradient.addColorStop(0.5, colors[1]);
        gradient.addColorStop(1, colors[2]);
        
        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 1280, 720);
        
        // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—Å—Ç
        const text = customData.text || selectedStyle || `–í–∞—Ä–∏–∞–Ω—Ç ${i + 1}`;
        ctx.fillStyle = '#FFFFFF';
        ctx.font = 'bold 80px Arial';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.strokeStyle = '#000000';
        ctx.lineWidth = 4;
        ctx.strokeText(text, 640, 360);
        ctx.fillText(text, 640, 360);
        
        // –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–º–µ—Ä –≤–∞—Ä–∏–∞–Ω—Ç–∞
        ctx.font = 'bold 40px Arial';
        ctx.fillStyle = '#FFD700';
        ctx.strokeStyle = '#000000';
        ctx.lineWidth = 2;
        ctx.strokeText(`#${i + 1}`, 100, 100);
        ctx.fillText(`#${i + 1}`, 100, 100);
        
        const blob = await new Promise(resolve => canvas.toBlob(resolve, 'image/png'));
        const url = URL.createObjectURL(blob);
        
        mockThumbnails.push({
          id: i + 1,
          url: url,
          blob: blob
        });
      }

      // –°–∏–º—É–ª–∏—Ä—É–µ–º –∑–∞–¥–µ—Ä–∂–∫—É API
      await new Promise(resolve => setTimeout(resolve, 3000));
      
      setGeneratedThumbnails(mockThumbnails);
      
      // –ó–¥–µ—Å—å –±—É–¥–µ—Ç —Ä–µ–∞–ª—å–Ω—ã–π –≤—ã–∑–æ–≤ DALL-E API:
      /*
      const response = await fetch('/api/dalle-generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          prompt,
          n: 5,
          size: "1792x1024", // –ë–ª–∏–∂–∞–π—à–∏–π –∫ 1280x720
          quality: "hd"
        })
      });
      
      const data = await response.json();
      setGeneratedThumbnails(data.images);
      */
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ DALL-E:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
  const handleImageUpload = (type, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (type === 'reference') {
          setCustomData(prev => ({ ...prev, referenceImage: e.target.result }));
        } else if (type === 'user') {
          setCustomData(prev => ({ 
            ...prev, 
            userImages: [...prev.userImages, { id: Date.now(), url: e.target.result }]
          }));
        }
      };
      reader.readAsDataURL(file);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ thumbnail
  const downloadThumbnail = () => {
    if (selectedThumbnail) {
      const a = document.createElement('a');
      a.href = selectedThumbnail.url;
      a.download = `${exportSettings?.filename || 'video'}_thumbnail_v${selectedThumbnail.id}.png`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  const navigateToApp = () => {
    window.location.href = 'http://localhost:3000/app';
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999] p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-6xl w-full max-h-[95vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Wand2 size={24} className="mr-3 text-purple-600" />
            AI Thumbnail Generator
          </h2>
          <button
            onClick={() => { onClose(); navigateToApp(); }}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          {/* Tabs */}
          <div className="flex bg-gray-100 p-1 rounded-xl">
            <button
              onClick={() => setActiveTab("styles")}
              className={`flex-1 flex items-center justify-center py-3 px-4 rounded-lg font-medium transition-all ${
                activeTab === "styles" 
                  ? "bg-white text-purple-600 shadow-sm" 
                  : "text-gray-600 hover:text-gray-800"
              }`}
            >
              <Palette size={18} className="mr-2" />
              Ready Styles
            </button>
            <button
              onClick={() => setActiveTab("custom")}
              className={`flex-1 flex items-center justify-center py-3 px-4 rounded-lg font-medium transition-all ${
                activeTab === "custom" 
                  ? "bg-white text-purple-600 shadow-sm" 
                  : "text-gray-600 hover:text-gray-800"
              }`}
            >
              <Sparkles size={18} className="mr-2" />
              Custom Design
            </button>
          </div>

          {/* Content */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Left Panel - Controls */}
            <div className="space-y-6">
              {activeTab === "styles" ? (
                <div className="bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl p-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Choose Your Style</h3>
                  
                  <div className="grid grid-cols-2 gap-3 mb-6">
                    {thumbnailStyles.map((style) => (
                      <button
                        key={style.id}
                        onClick={() => setSelectedStyle(style.id)}
                        className={`p-4 rounded-lg border-2 transition-all text-left ${
                          selectedStyle === style.id
                            ? "border-purple-500 bg-purple-50"
                            : "border-gray-200 hover:border-purple-300 bg-white"
                        }`}
                      >
                        <div className="text-2xl mb-2">{style.preview}</div>
                        <div className="font-medium text-gray-800 text-sm">{style.name}</div>
                      </button>
                    ))}
                  </div>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Type size={16} className="inline mr-1" />
                        Thumbnail Text
                      </label>
                      <input
                        type="text"
                        value={customData.text}
                        onChange={(e) => setCustomData(prev => ({ ...prev, text: e.target.value }))}
                        placeholder="Enter your video title..."
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                      />
                    </div>
                  </div>
                </div>
              ) : (
                <div className="bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl p-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Custom Thumbnail</h3>
                  
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Type size={16} className="inline mr-1" />
                        Thumbnail Text
                      </label>
                      <input
                        type="text"
                        value={customData.text}
                        onChange={(e) => setCustomData(prev => ({ ...prev, text: e.target.value }))}
                        placeholder="Enter text for your thumbnail..."
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Sparkles size={16} className="inline mr-1" />
                        Describe Your Thumbnail
                      </label>
                      <textarea
                        value={customData.description}
                        onChange={(e) => setCustomData(prev => ({ ...prev, description: e.target.value }))}
                        placeholder="Describe what you want to see on your thumbnail (colors, style, objects, mood, etc.)"
                        rows={3}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Image size={16} className="inline mr-1" />
                        Reference Image (Optional)
                      </label>
                      <div className="flex items-center space-x-3">
                        <label className="cursor-pointer bg-white border-2 border-dashed border-gray-300 hover:border-purple-400 rounded-lg p-4 flex items-center justify-center flex-1 transition-colors">
                          <Upload size={20} className="mr-2 text-gray-400" />
                          <span className="text-sm text-gray-600">Upload reference image</span>
                          <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => handleImageUpload('reference', e)}
                            className="hidden"
                          />
                        </label>
                      </div>
                      {customData.referenceImage && (
                        <div className="mt-2">
                          <img 
                            src={customData.referenceImage} 
                            alt="Reference" 
                            className="w-full h-24 object-cover rounded-lg"
                          />
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {/* Generate Button */}
              <button
                onClick={generateWithDALLE}
                disabled={isGenerating || (activeTab === "styles" && !selectedStyle)}
                className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl disabled:cursor-not-allowed flex items-center justify-center"
              >
                {isGenerating ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    Generating with DALL-E...
                  </>
                ) : (
                  <>
                    <Wand2 size={20} className="mr-3" />
                    Generate 5 Thumbnails
                  </>
                )}
              </button>
            </div>

            {/* Right Panel - Preview/Results */}
            <div className="space-y-6">
              <div className="bg-gray-50 rounded-xl p-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                  <Image size={20} className="mr-2 text-blue-600" />
                  Generated Thumbnails
                </h3>
                
                {generatedThumbnails.length === 0 ? (
                  <div className="bg-white rounded-lg p-8 text-center border-2 border-dashed border-gray-300">
                    <div className="text-4xl mb-4">üé®</div>
                    <div className="text-lg text-gray-600 mb-2">Ready to create</div>
                    <div className="text-sm text-gray-400">
                      {activeTab === "styles" 
                        ? "Choose a style and add text, then generate!" 
                        : "Describe your thumbnail and generate 5 AI variants!"}
                    </div>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 gap-4">
                    {generatedThumbnails.map((thumbnail) => (
                      <div 
                        key={thumbnail.id}
                        className={`relative cursor-pointer rounded-lg overflow-hidden border-3 transition-all ${
                          selectedThumbnail?.id === thumbnail.id 
                            ? "border-purple-500 ring-2 ring-purple-200" 
                            : "border-gray-200 hover:border-purple-300"
                        }`}
                        onClick={() => setSelectedThumbnail(thumbnail)}
                      >
                        <img 
                          src={thumbnail.url} 
                          alt={`Thumbnail ${thumbnail.id}`}
                          className="w-full h-32 object-cover"
                        />
                        <div className="absolute top-2 left-2 bg-black bg-opacity-70 text-white text-xs px-2 py-1 rounded">
                          –í–∞—Ä–∏–∞–Ω—Ç {thumbnail.id}
                        </div>
                        {selectedThumbnail?.id === thumbnail.id && (
                          <div className="absolute inset-0 bg-purple-500 bg-opacity-20 flex items-center justify-center">
                            <div className="bg-white rounded-full p-2">
                              <ArrowRight size={16} className="text-purple-600" />
                            </div>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Quick Tips */}
              <div className="bg-blue-50 border border-blue-200 rounded-xl p-6">
                <h4 className="font-semibold text-blue-800 mb-3 flex items-center">
                  <Sparkles size={18} className="mr-2" />
                  Pro Tips
                </h4>
                <ul className="text-sm text-blue-700 space-y-2">
                  <li>‚Ä¢ Use action words: "AMAZING", "SHOCKING", "MUST SEE"</li>
                  <li>‚Ä¢ Mention specific colors: "bright red background"</li>
                  <li>‚Ä¢ Include emotions: "excited face", "surprised expression"</li>
                  <li>‚Ä¢ Be specific: "person pointing at screen with arrows"</li>
                  <li>‚Ä¢ Reference popular styles: "MrBeast style", "PewDiePie aesthetic"</li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-sm text-gray-500">
            {selectedThumbnail 
              ? `Thumbnail #${selectedThumbnail.id} selected and ready to download` 
              : generatedThumbnails.length > 0
                ? 'Choose a thumbnail to download'
                : 'Generate thumbnails to get started'
            }
          </div>
          <div className="flex space-x-3">
            <button
              onClick={() => { onClose(); navigateToApp(); }}
              className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              Skip & Continue
            </button>
            {selectedThumbnail && (
              <button
                onClick={() => {
                  downloadThumbnail();
                  onClose();
                  navigateToApp();
                }}
                className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 font-medium transition-colors flex items-center"
              >
                Download & Continue
                <ArrowRight size={16} className="ml-2" />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThumbnailCreator;
