// src/components/routing/AppRouter.js
import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

// Public components
import ScriptifyLanding from '../pages/ScriptifyLanding';
import AuthPage from '../Auth/AuthPage';

// Protected components
import ProtectedRoute from '../Auth/ProtectedRoute';
import Dashboard from '../pages/Dashboard';
import VideoMaker from '../pages/VideoMaker';
import ScriptGenerator from '../pages/ScriptGenerator';
import Teleprompter from '../pages/Teleprompter';
// import MyVideos from '../pages/MyVideos';
// import Profile from '../pages/Profile';
// import Settings from '../pages/Settings';

// Loading component
import LoadingScreen from '../common/LoadingScreen';

const AppRouter = () => {
  const { loading } = useAuth();

  // Показываем загрузочный экран пока проверяется авторизация
  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <Routes>
      {/* Public routes */}
      <Route path="/" element={<ScriptifyLanding />} />
      <Route path="/auth" element={<AuthPage />} />
      
      {/* Protected routes */}
      <Route path="/app" element={
        <ProtectedRoute>
          <Dashboard />
        </ProtectedRoute>
      } />
      
      <Route path="/app/video-maker" element={
        <ProtectedRoute>
          <VideoMaker />
        </ProtectedRoute>
      } />
      
      <Route path="/app/script-generator" element={
        <ProtectedRoute>
          <ScriptGenerator />
        </ProtectedRoute>
      } />
      
      <Route path="/app/teleprompter" element={
        <ProtectedRoute>
          <Teleprompter />
        </ProtectedRoute>
      } />
      
      <Route path="/app/audio-recorder" element={
        <ProtectedRoute>
          <AudioRecorder />
        </ProtectedRoute>
      } />
      
      {/* <Route path="/app/my-videos" element={
        <ProtectedRoute>
          <MyVideos />
        </ProtectedRoute>
      } /> */}
      
      {/* <Route path="/app/profile" element={
        <ProtectedRoute>
          <Profile />
        </ProtectedRoute>
      } />
       */}
      {/* <Route path="/app/settings" element={
        <ProtectedRoute>
          <Settings />
        </ProtectedRoute>
      } /> */}
      
      {/* Fallback route */}
      <Route path="*" element={<ScriptifyLanding />} />
    </Routes>
  );
};

export default AppRouter;
