import { useState, useRef, useEffect, useCallback } from "react";
import {
  Upload,
  Play,
  Pause,
  Volume2,
  X,
  Video,
  Music,
  SkipBack,
  SkipForward,
  VolumeX,
  Copy,
  Trash2,
  Split,
  RotateCw,
  Crop,
  Filter,
  Type,
  Image,
  Film,
} from "lucide-react";

const VideoEditor = () => {
  const [mediaLibrary, setMediaLibrary] = useState({
    videos: [],
    audios: [],
    images: [],
  });
  const [timelineItems, setTimelineItems] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(60);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(null);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [activeTab, setActiveTab] = useState("videos");
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentVideoSource, setCurrentVideoSource] = useState(null);
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedTimelineItem, setDraggedTimelineItem] = useState(null);
  const [dropPreview, setDropPreview] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isTimelineDragging, setIsTimelineDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);

  const videoRef = useRef(null);
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  const timelineRef = useRef(null);
  const intervalRef = useRef(null);

  const getCurrentTimelineContent = useCallback(() => {
    const currentItems = timelineItems.filter(
      (item) =>
        currentTime >= item.startTime &&
        currentTime < item.startTime + item.duration
    );

    return {
      video: currentItems.find(
        (item) => item.type === "video" && item.track === 0
      ),
      audio: currentItems.filter(
        (item) => item.type === "audio" && item.track === 1
      ),
      images: currentItems.filter(
        (item) => item.type === "image" && item.track === 2
      ),
    };
  }, [currentTime, timelineItems]);

  // Check for collision with other timeline items
  const checkCollision = useCallback((newItem, excludeId = null) => {
    return timelineItems.some(item => {
      if (item.id === excludeId) return false;
      if (item.track !== newItem.track) return false;
      
      const itemStart = item.startTime;
      const itemEnd = item.startTime + item.duration;
      const newStart = newItem.startTime;
      const newEnd = newItem.startTime + newItem.duration;
      
      return (newStart < itemEnd && newEnd > itemStart);
    });
  }, [timelineItems]);

  // Find the next available position on a track
  const findNextAvailablePosition = useCallback((track, duration, preferredStart = 0) => {
    const trackItems = timelineItems
      .filter(item => item.track === track)
      .sort((a, b) => a.startTime - b.startTime);
    
    // Try preferred position first
    if (preferredStart >= 0) {
      const testItem = { track, startTime: preferredStart, duration };
      if (!checkCollision(testItem)) {
        return preferredStart;
      }
    }
    
    // Find gap between items
    for (let i = 0; i < trackItems.length; i++) {
      const currentItem = trackItems[i];
      const nextItem = trackItems[i + 1];
      
      // Check gap before first item
      if (i === 0 && currentItem.startTime >= duration) {
        return 0;
      }
      
      // Check gap between items
      if (nextItem) {
        const gapStart = currentItem.startTime + currentItem.duration;
        const gapEnd = nextItem.startTime;
        const gapSize = gapEnd - gapStart;
        
        if (gapSize >= duration) {
          return gapStart;
        }
      }
    }
    
    // Place at the end
    if (trackItems.length === 0) return 0;
    const lastItem = trackItems[trackItems.length - 1];
    return lastItem.startTime + lastItem.duration;
  }, [timelineItems, checkCollision]);

  useEffect(() => {
    const content = getCurrentTimelineContent();

    if (content.video && content.video.url !== currentVideoSource) {
      setCurrentVideoSource(content.video.url);
      if (videoRef.current) {
        const relativeTime = currentTime - content.video.startTime;
        videoRef.current.currentTime = relativeTime;
      }
    } else if (!content.video && content.images.length > 0) {
      setCurrentVideoSource(content.images[0].url);
    } else if (!content.video && !content.images.length) {
      setCurrentVideoSource(null);
    }
  }, [currentTime, timelineItems, getCurrentTimelineContent]);

  useEffect(() => {
    if (isPlaying) {
      intervalRef.current = setInterval(() => {
        setCurrentTime((prev) => {
          const newTime = prev + 0.1;
          if (newTime >= videoDuration) {
            setIsPlaying(false);
            return videoDuration;
          }
          return newTime;
        });
      }, 100);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isPlaying, videoDuration]);

  useEffect(() => {
    if (videoRef.current && currentVideoSource) {
      const content = getCurrentTimelineContent();
      if (content.video) {
        const relativeTime = currentTime - content.video.startTime;
        const timeDiff = Math.abs(videoRef.current.currentTime - relativeTime);

        if (timeDiff > 0.5) {
          videoRef.current.currentTime = relativeTime;
        }

        if (isPlaying && videoRef.current.paused) {
          videoRef.current.play().catch(console.error);
        } else if (!isPlaying && !videoRef.current.paused) {
          videoRef.current.pause();
        }
      }
    }
  }, [currentTime, currentVideoSource, getCurrentTimelineContent, isPlaying]);

  const handleVideoUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newVideo = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 10,
        type: "video",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        videos: [...prev.videos, newVideo],
      }));
    });
  };

  const handleAudioUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newAudio = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 10,
        type: "audio",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        audios: [...prev.audios, newAudio],
      }));
    });
  };

  const handleImageUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newImage = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        type: "image",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        images: [...prev.images, newImage],
      }));
    });
  };

  const addToTimeline = (mediaItem, startTime = null) => {
    const track = mediaItem.type === "video" ? 0 : mediaItem.type === "audio" ? 1 : 2;
    const duration = mediaItem.duration || (mediaItem.type === "image" ? 5 : 10);
    
    const finalStartTime = startTime !== null ? startTime : 
      findNextAvailablePosition(track, duration);

    const newTimelineItem = {
      id: Date.now() + Math.random(),
      mediaId: mediaItem.id,
      type: mediaItem.type,
      name: mediaItem.name,
      url: mediaItem.url,
      startTime: finalStartTime,
      duration: duration,
      track: track,
      volume: 1,
      effects: [],
    };

    // Check for collision before adding
    if (!checkCollision(newTimelineItem)) {
      setTimelineItems((prev) => [...prev, newTimelineItem]);

      const newEndTime = newTimelineItem.startTime + newTimelineItem.duration;
      if (newEndTime > videoDuration) {
        setVideoDuration(newEndTime);
      }
    }
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const seekTo = (time) => {
    setCurrentTime(Math.max(0, Math.min(videoDuration, time)));
  };

  const removeFromLibrary = (itemId, type) => {
    setMediaLibrary((prev) => ({
      ...prev,
      [type]: prev[type].filter((item) => item.id !== itemId),
    }));
  };

  const removeFromTimeline = (itemId) => {
    setTimelineItems((prev) => prev.filter((item) => item.id !== itemId));
    setSelectedTimelineItem(null);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  const handleTimelineClick = (e) => {
    if (isDragging || isTimelineDragging) return;
    
    const rect = timelineRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const timePerPixel = videoDuration / (rect.width * timelineZoom);
    const clickTime = clickX * timePerPixel;
    seekTo(clickTime);
  };

  // Media library drag handlers
  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setDropPreview(null);
    setIsDragging(false);
  };

  // Timeline item drag handlers
  const handleTimelineItemDragStart = (e, timelineItem) => {
    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);
    
    const rect = timelineRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(e.clientX - itemRect.left);
    
    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleTimelineItemDragEnd = () => {
    setDraggedTimelineItem(null);
    setDropPreview(null);
    setIsTimelineDragging(false);
    setDragOffset(0);
  };

  const handleDragOver = (e) => {
    if (!draggedItem && !draggedTimelineItem) return;
    
    e.preventDefault();
    e.dataTransfer.dropEffect = draggedItem ? "copy" : "move";
    
    const rect = timelineRef.current.getBoundingClientRect();
    const dragX = e.clientX - rect.left - (draggedTimelineItem ? dragOffset : 0);
    const timePerPixel = videoDuration / (rect.width * timelineZoom);
    const dropTime = Math.max(0, dragX * timePerPixel);
    
    if (draggedItem) {
      // Dragging from media library
      const track = draggedItem.type === "video" ? 0 : draggedItem.type === "audio" ? 1 : 2;
      const duration = draggedItem.duration || (draggedItem.type === "image" ? 5 : 10);
      const testItem = { track, startTime: dropTime, duration };
      
      setDropPreview({
        time: dropTime,
        duration: duration,
        track: track,
        isValid: !checkCollision(testItem)
      });
    } else if (draggedTimelineItem) {
      // Dragging existing timeline item
      const testItem = { 
        ...draggedTimelineItem, 
        startTime: dropTime 
      };
      
      setDropPreview({
        time: dropTime,
        duration: draggedTimelineItem.duration,
        track: draggedTimelineItem.track,
        isValid: !checkCollision(testItem, draggedTimelineItem.id)
      });
    }
  };

  const handleDrop = (e) => {
    if ((!draggedItem && !draggedTimelineItem) || !dropPreview) return;
    
    e.preventDefault();
    
    if (dropPreview.isValid) {
      if (draggedItem) {
        // Adding new item from media library
        addToTimeline(draggedItem, dropPreview.time);
      } else if (draggedTimelineItem) {
        // Moving existing timeline item
        setTimelineItems(prev => prev.map(item => 
          item.id === draggedTimelineItem.id 
            ? { ...item, startTime: dropPreview.time }
            : item
        ));
        
        // Update selected item if it's the one being moved
        if (selectedTimelineItem?.id === draggedTimelineItem.id) {
          setSelectedTimelineItem(prev => ({ ...prev, startTime: dropPreview.time }));
        }
      }
    }
    
    setDropPreview(null);
    setDraggedItem(null);
    setDraggedTimelineItem(null);
    setIsDragging(false);
    setIsTimelineDragging(false);
  };

  const handleDragLeave = (e) => {
    if (!timelineRef.current?.contains(e.relatedTarget)) {
      setDropPreview(null);
    }
  };

  const getTimelineItemStyle = (item) => {
    return {
      left: `${
        (item.startTime / videoDuration) *
        videoDuration *
        20 *
        timelineZoom
      }px`,
      width: `${
        (item.duration / videoDuration) *
        videoDuration *
        20 *
        timelineZoom
      }px`,
      top: "4px",
    };
  };

  const getDropPreviewStyle = () => {
    if (!dropPreview) return {};
    
    return {
      left: `${
        (dropPreview.time / videoDuration) *
        videoDuration *
        20 *
        timelineZoom
      }px`,
      width: `${
        (dropPreview.duration / videoDuration) *
        videoDuration *
        20 *
        timelineZoom
      }px`,
      top: "4px",
    };
  };

  return (
    <div className="flex flex-1 h-screen gap-4 p-4 bg-gray-100">
      <div className="w-80 bg-white border border-gray-200 flex flex-col rounded-2xl shadow-sm overflow-hidden">
        <div className="p-4 pb-0">
          <div className="flex bg-gray-100 rounded-xl p-1">
            {[
              { id: "videos", name: "Videos", icon: Video },
              { id: "audios", name: "Audio", icon: Music },
              { id: "images", name: "Images", icon: Image },
            ].map((tab) => {
              const IconComponent = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex-1 p-3 text-sm font-medium transition-all duration-200 rounded-lg ${
                    activeTab === tab.id
                      ? "bg-white text-blue-600 shadow-sm"
                      : "text-gray-600 hover:text-gray-800"
                  }`}
                >
                  <IconComponent size={16} className="mr-2 inline" />
                  {tab.name}
                </button>
              );
            })}
          </div>
        </div>

        <div className="p-4">
          <button
            onClick={() => {
              if (activeTab === "videos") fileInputRef.current?.click();
              else if (activeTab === "audios") audioInputRef.current?.click();
              else imageInputRef.current?.click();
            }}
            className="w-full p-6 border-2 border-dashed border-gray-300 rounded-2xl hover:border-blue-400 hover:bg-blue-50/50 transition-all duration-200 text-center group"
          >
            <div className="w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-3 group-hover:bg-blue-100 transition-colors">
              <Upload
                size={20}
                className="text-gray-500 group-hover:text-blue-500"
              />
            </div>
            <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">
              Upload{" "}
              {activeTab === "videos"
                ? "Video"
                : activeTab === "audios"
                ? "Audio"
                : "Images"}
            </span>
            <p className="text-xs text-gray-500 mt-1">
              {activeTab === "videos"
                ? "MP4, AVI, MOV up to 2GB"
                : activeTab === "audios"
                ? "MP3, WAV, M4A up to 100MB"
                : "JPG, PNG, GIF up to 10MB"}
            </p>
          </button>
        </div>

        <input
          ref={fileInputRef}
          type="file"
          accept="video/*"
          multiple
          onChange={handleVideoUpload}
          className="hidden"
        />
        <input
          ref={audioInputRef}
          type="file"
          accept="audio/*"
          multiple
          onChange={handleAudioUpload}
          className="hidden"
        />
        <input
          ref={imageInputRef}
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          className="hidden"
        />

        <div className="flex-1 overflow-y-auto px-4 pb-4">
          <div className="space-y-2">
            {mediaLibrary[activeTab].map((item) => (
              <div
                key={item.id}
                draggable="true"
                onDragStart={(e) => handleDragStart(e, item)}
                onDragEnd={handleDragEnd}
                className={`flex items-center p-3 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-sm transition-all duration-200 cursor-grab active:cursor-grabbing group ${
                  draggedItem?.id === item.id ? "opacity-50" : ""
                }`}
                onClick={() => addToTimeline(item)}
              >
                <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center mr-3 shadow-sm group-hover:shadow">
                  {activeTab === "videos" && (
                    <Video size={20} className="text-blue-500" />
                  )}
                  {activeTab === "audios" && (
                    <Music size={20} className="text-green-500" />
                  )}
                  {activeTab === "images" && (
                    <Image size={20} className="text-purple-500" />
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 truncate">
                    {item.name}
                  </p>
                  <p className="text-xs text-gray-500">
                    {activeTab === "images"
                      ? "Image"
                      : formatTime(item.duration || 0)}
                  </p>
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromLibrary(item.id, activeTab);
                  }}
                  className="opacity-0 group-hover:opacity-100 p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
                >
                  <Trash2 size={14} />
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="flex-1 flex flex-col gap-4">
        <div className="flex-1 bg-black rounded-2xl relative overflow-hidden shadow-sm">
          {currentVideoSource ? (
            <>
              {getCurrentTimelineContent().video ? (
                <video
                  ref={videoRef}
                  src={currentVideoSource}
                  className="w-full h-full object-contain rounded-2xl"
                  muted={isMuted}
                />
              ) : (
                <img
                  src={currentVideoSource}
                  alt="Timeline content"
                  className="w-full h-full object-contain rounded-2xl"
                />
              )}
            </>
          ) : (
            <div className="w-full h-full flex items-center justify-center text-white">
              <div className="text-center">
                <div className="w-20 h-20 bg-gray-800 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <Film size={40} className="opacity-50" />
                </div>
                <p className="text-xl opacity-75 font-medium">
                  No content at current time
                </p>
                <p className="text-sm opacity-50">
                  Drag media to timeline
                </p>
              </div>
            </div>
          )}

          <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 bg-black/60 backdrop-blur-md rounded-2xl p-4">
            <div className="flex items-center space-x-6">
              <button
                onClick={() => seekTo(Math.max(0, currentTime - 10))}
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
              >
                <SkipBack size={20} />
              </button>
              <button
                onClick={handlePlayPause}
                className="w-12 h-12 bg-white/20 hover:bg-white/30 rounded-2xl flex items-center justify-center text-white transition-all hover:scale-105"
              >
                {isPlaying ? <Pause size={24} /> : <Play size={24} />}
              </button>
              <button
                onClick={() =>
                  seekTo(Math.min(videoDuration, currentTime + 10))
                }
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
              >
                <SkipForward size={20} />
              </button>
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setIsMuted(!isMuted)}
                  className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
                >
                  {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
                </button>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={volume}
                  onChange={(e) => setVolume(parseFloat(e.target.value))}
                  className="w-24 accent-blue-500"
                />
              </div>
              <span className="text-white text-sm font-medium min-w-0">
                {formatTime(currentTime)} / {formatTime(videoDuration)}
              </span>
            </div>
          </div>
        </div>

        <div 
          className={`h-96 bg-white rounded-2xl shadow-sm border-2 transition-all duration-200 overflow-hidden ${
            isDragging || isTimelineDragging 
              ? "border-blue-400 bg-blue-50/20" 
              : "border-gray-200"
          }`}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onDragLeave={handleDragLeave}
        >
          <div className="bg-gray-50 p-4 border-b border-gray-200">
            <div className="flex items-center justify-between">
              <h4 className="text-gray-800 font-semibold">
                Timeline {(isDragging || isTimelineDragging) && 
                  dropPreview && (
                    <span className={dropPreview.isValid ? "text-green-600" : "text-red-600"}>
                      - {dropPreview.isValid ? "Drop to place" : "Position blocked"}
                    </span>
                  )
                }
              </h4>
              <div className="flex items-center space-x-6">
                <div className="flex items-center space-x-3">
                  <span className="text-gray-600 text-sm font-medium">
                    Zoom:
                  </span>
                  <div className="bg-white rounded-xl p-2 border border-gray-200">
                    <input
                      type="range"
                      min="0.5"
                      max="3"
                      step="0.1"
                      value={timelineZoom}
                      onChange={(e) =>
                        setTimelineZoom(parseFloat(e.target.value))
                      }
                      className="w-24 accent-blue-500"
                    />
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <button className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-xl transition-all duration-200">
                    <Split size={16} />
                  </button>
                  <button className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-xl transition-all duration-200">
                    <Copy size={16} />
                  </button>
                  <button className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-xl transition-all duration-200">
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div
            className="flex-1 relative overflow-x-auto"
            ref={timelineRef}
            onClick={handleTimelineClick}
          >
            <div className="h-12 bg-gray-50 border-b border-gray-200 relative">
              {Array.from(
                { length: Math.ceil(videoDuration / 5) + 1 },
                (_, i) => (
                  <div
                    key={i}
                    className="absolute top-0 h-full border-l border-gray-300"
                    style={{ left: `${i * 100 * timelineZoom}px` }}
                  >
                    <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-medium">
                      {formatTime(i * 5)}
                    </span>
                  </div>
                )
              )}
              <div
                className="absolute top-0 w-0.5 h-full bg-red-500 z-10 shadow-sm"
                style={{
                  left: `${
                    (currentTime / videoDuration) *
                    videoDuration *
                    20 *
                    timelineZoom
                  }px`,
                }}
              />
            </div>

            {/* Video Track */}
            <div className="h-24 bg-white border-b border-gray-100 relative">
              <div className="absolute left-4 top-3 text-gray-700 text-sm font-semibold">
                Video
              </div>
              {timelineItems
                .filter((item) => item.track === 0)
                .map((item) => (
                  <div
                    key={item.id}
                    draggable="true"
                    onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                    onDragEnd={handleTimelineItemDragEnd}
                    className={`absolute h-16 bg-blue-500 rounded-xl border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md ${
                      selectedTimelineItem?.id === item.id
                        ? "border-blue-700 shadow-lg"
                        : "border-blue-400"
                    } ${
                      draggedTimelineItem?.id === item.id ? "opacity-50 z-20" : ""
                    }`}
                    style={getTimelineItemStyle(item)}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedTimelineItem(item);
                    }}
                  >
                    <div className="p-2 text-white text-xs truncate font-medium">
                      {item.name}
                    </div>
                  </div>
                ))}
              
              {/* Drop preview for video track */}
              {dropPreview && dropPreview.track === 0 && (
                <div
                  className={`absolute h-16 rounded-xl border-2 border-dashed opacity-75 ${
                    dropPreview.isValid 
                      ? "bg-blue-300 border-blue-500" 
                      : "bg-red-300 border-red-500"
                  }`}
                  style={getDropPreviewStyle()}
                />
              )}
            </div>

            {/* Audio Track */}
            <div className="h-24 bg-white border-b border-gray-100 relative">
              <div className="absolute left-4 top-3 text-gray-700 text-sm font-semibold">
                Audio
              </div>
              {timelineItems
                .filter((item) => item.track === 1)
                .map((item) => (
                  <div
                    key={item.id}
                    draggable="true"
                    onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                    onDragEnd={handleTimelineItemDragEnd}
                    className={`absolute h-16 bg-green-500 rounded-xl border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md ${
                      selectedTimelineItem?.id === item.id
                        ? "border-green-700 shadow-lg"
                        : "border-green-400"
                    } ${
                      draggedTimelineItem?.id === item.id ? "opacity-50 z-20" : ""
                    }`}
                    style={getTimelineItemStyle(item)}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedTimelineItem(item);
                    }}
                  >
                    <div className="p-2 text-white text-xs truncate font-medium">
                      {item.name}
                    </div>
                  </div>
                ))}
              
              {/* Drop preview for audio track */}
              {dropPreview && dropPreview.track === 1 && (
                <div
                  className={`absolute h-16 rounded-xl border-2 border-dashed opacity-75 ${
                    dropPreview.isValid 
                      ? "bg-green-300 border-green-500" 
                      : "bg-red-300 border-red-500"
                  }`}
                  style={getDropPreviewStyle()}
                />
              )}
            </div>

            {/* Effects Track */}
            <div className="h-24 bg-white border-b border-gray-100 relative">
              <div className="absolute left-4 top-3 text-gray-700 text-sm font-semibold">
                Effects
              </div>
              {timelineItems
                .filter((item) => item.track === 2)
                .map((item) => (
                  <div
                    key={item.id}
                    draggable="true"
                    onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                    onDragEnd={handleTimelineItemDragEnd}
                    className={`absolute h-16 bg-purple-500 rounded-xl border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md ${
                      selectedTimelineItem?.id === item.id
                        ? "border-purple-700 shadow-lg"
                        : "border-purple-400"
                    } ${
                      draggedTimelineItem?.id === item.id ? "opacity-50 z-20" : ""
                    }`}
                    style={getTimelineItemStyle(item)}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedTimelineItem(item);
                    }}
                  >
                    <div className="p-2 text-white text-xs truncate font-medium">
                      {item.name}
                    </div>
                  </div>
                ))}
              
              {/* Drop preview for effects track */}
              {dropPreview && dropPreview.track === 2 && (
                <div
                  className={`absolute h-16 rounded-xl border-2 border-dashed opacity-75 ${
                    dropPreview.isValid 
                      ? "bg-purple-300 border-purple-500" 
                      : "bg-red-300 border-red-500"
                  }`}
                  style={getDropPreviewStyle()}
                />
              )}
            </div>
          </div>
        </div>
      </div>

      {selectedTimelineItem && (
        <div className="w-80 bg-white border border-gray-200 rounded-2xl p-6 shadow-sm">
          <div className="space-y-6">
            <div className="flex items-center justify-between">
              <h4 className="font-semibold text-gray-800 text-lg">
                Properties
              </h4>
              <button
                onClick={() => setSelectedTimelineItem(null)}
                className="text-gray-400 hover:text-gray-600 p-2 hover:bg-gray-100 rounded-xl transition-all duration-200"
              >
                <X size={16} />
              </button>
            </div>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Name
                </label>
                <input
                  type="text"
                  value={selectedTimelineItem.name}
                  className="w-full p-3 border border-gray-200 rounded-xl text-sm bg-gray-50 font-medium"
                  readOnly
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Start Time (seconds)
                </label>
                <input
                  type="number"
                  value={selectedTimelineItem.startTime}
                  onChange={(e) => {
                    const newStartTime = parseFloat(e.target.value);
                    const testItem = { ...selectedTimelineItem, startTime: newStartTime };
                    
                    if (!checkCollision(testItem, selectedTimelineItem.id)) {
                      setTimelineItems((prev) =>
                        prev.map((item) =>
                          item.id === selectedTimelineItem.id
                            ? { ...item, startTime: newStartTime }
                            : item
                        )
                      );
                      setSelectedTimelineItem((prev) =>
                        prev
                          ? {
                              ...prev,
                              startTime: newStartTime,
                            }
                          : null
                      );
                    }
                  }}
                  className="w-full p-3 border border-gray-200 rounded-xl text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
                  step="0.1"
                  min="0"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Duration (seconds)
                </label>
                <input
                  type="number"
                  value={selectedTimelineItem.duration}
                  onChange={(e) => {
                    const newDuration = parseFloat(e.target.value);
                    const testItem = { ...selectedTimelineItem, duration: newDuration };
                    
                    if (!checkCollision(testItem, selectedTimelineItem.id)) {
                      setTimelineItems((prev) =>
                        prev.map((item) =>
                          item.id === selectedTimelineItem.id
                            ? { ...item, duration: newDuration }
                            : item
                        )
                      );
                      setSelectedTimelineItem((prev) =>
                        prev
                          ? {
                              ...prev,
                              duration: newDuration,
                            }
                          : null
                      );
                    }
                  }}
                  className="w-full p-3 border border-gray-200 rounded-xl text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
                  step="0.1"
                  min="0.1"
                />
              </div>

              {selectedTimelineItem.type === "audio" && (
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Volume
                  </label>
                  <div className="bg-gray-50 rounded-xl p-4">
                    <input
                      type="range"
                      min="0"
                      max="2"
                      step="0.1"
                      value={selectedTimelineItem.volume}
                      onChange={(e) => {
                        const newVolume = parseFloat(e.target.value);
                        setTimelineItems((prev) =>
                          prev.map((item) =>
                            item.id === selectedTimelineItem.id
                              ? { ...item, volume: newVolume }
                              : item
                          )
                        );
                        setSelectedTimelineItem((prev) =>
                          prev
                            ? {
                                ...prev,
                                volume: newVolume,
                              }
                            : null
                        );
                      }}
                      className="w-full accent-blue-500"
                    />
                    <div className="text-sm text-gray-600 mt-2 text-center font-medium">
                      {Math.round(selectedTimelineItem.volume * 100)}%
                    </div>
                  </div>
                </div>
              )}

              <div className="space-y-3">
                <label className="block text-sm font-semibold text-gray-700">
                  Effects
                </label>
                <div className="grid grid-cols-2 gap-3">
                  <button className="p-4 border border-gray-200 rounded-xl hover:bg-gray-50 hover:border-gray-300 transition-all duration-200 text-center group">
                    <Filter
                      size={20}
                      className="mx-auto mb-2 text-gray-500 group-hover:text-gray-700"
                    />
                    <span className="text-sm font-medium text-gray-600 group-hover:text-gray-800">
                      Filter
                    </span>
                  </button>
                  <button className="p-4 border border-gray-200 rounded-xl hover:bg-gray-50 hover:border-gray-300 transition-all duration-200 text-center group">
                    <RotateCw
                      size={20}
                      className="mx-auto mb-2 text-gray-500 group-hover:text-gray-700"
                    />
                    <span className="text-sm font-medium text-gray-600 group-hover:text-gray-800">
                      Rotate
                    </span>
                  </button>
                  <button className="p-4 border border-gray-200 rounded-xl hover:bg-gray-50 hover:border-gray-300 transition-all duration-200 text-center group">
                    <Crop
                      size={20}
                      className="mx-auto mb-2 text-gray-500 group-hover:text-gray-700"
                    />
                    <span className="text-sm font-medium text-gray-600 group-hover:text-gray-800">
                      Crop
                    </span>
                  </button>
                  <button className="p-4 border border-gray-200 rounded-xl hover:bg-gray-50 hover:border-gray-300 transition-all duration-200 text-center group">
                    <Type
                      size={20}
                      className="mx-auto mb-2 text-gray-500 group-hover:text-gray-700"
                    />
                    <span className="text-sm font-medium text-gray-600 group-hover:text-gray-800">
                      Text
                    </span>
                  </button>
                </div>
              </div>

              <button
                onClick={() => removeFromTimeline(selectedTimelineItem.id)}
                className="w-full p-3 bg-red-500 hover:bg-red-600 text-white rounded-xl text-sm font-semibold transition-all duration-200 hover:shadow-md"
              >
                <Trash2 size={16} className="inline mr-2" />
                Remove from Timeline
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoEditor;
