                            setContentType(type.id);
                            setIsContentTypeDropdownOpen(false);
                          }}
                          className={`w-full p-4 text-left transition-all duration-200 flex items-center space-x-4 hover:bg-gray-50 ${
                            contentType === type.id
                              ? `${
                                  type.color === "slate"
                                    ? "bg-slate-50 border-l-4 border-slate-500"
                                    : type.color === "blue"
                                    ? "bg-blue-50 border-l-4 border-blue-500"
                                    : "bg-indigo-50 border-l-4 border-indigo-500"
                                }`
                              : "hover:bg-gray-50"
                          }`}
                        >
                          <div
                            className={`w-12 h-12 rounded-xl flex items-center justify-center transition-all duration-300 ${
                              contentType === type.id
                                ? type.color === "slate"
                                  ? "bg-slate-100 shadow-lg shadow-slate-500/20"
                                  : type.color === "blue"
                                  ? "bg-blue-100 shadow-lg shadow-blue-500/20"
                                  : "bg-indigo-100 shadow-lg shadow-indigo-500/20"
                                : "bg-gray-50"
                            }`}
                          >
                            <span className="text-xl">{type.emoji}</span>
                          </div>

                          <div className="flex-1">
                            <div
                              className={`font-semibold transition-colors ${
                                contentType === type.id
                                  ? type.color === "slate"
                                    ? "text-slate-700"
                                    : type.color === "blue"
                                    ? "text-blue-700"
                                    : "text-indigo-700"
                                  : "text-gray-700"
                              }`}
                            >
                              {type.name}
                            </div>
                            <div className="text-sm text-gray-500">
                              {type.id === "lifestyle" &&
                                "Personal growth & wellness"}
                              {type.id === "gaming" &&
                                "Gaming content & reviews"}
                              {type.id === "tech" && "Technology & innovations"}
                              {type.id === "education" &&
                                "Learning & tutorials"}
                              {type.id === "entertainment" &&
                                "Fun & entertainment"}
                            </div>
                          </div>

                          {contentType === type.id && (
                            <div className="ml-auto">
                              <svg
                                className={`w-6 h-6 ${
                                  type.color === "slate"
                                    ? "text-slate-500"
                                    : type.color === "blue"
                                    ? "text-blue-500"
                                    : "text-indigo-500"
                                }`}
                                fill="currentColor"
                                viewBox="0 0 24 24"
                              >
                                <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                              </svg>
                            </div>
                          )}
                        </button>
                      );
                    })}
                  </div>
                )}
              </div>
            </div>
      
            {/* Topic Input */}
            <div className="space-y-3">
              <label className="block text-gray-700 font-semibold">
                What is the video about?
              </label>
              <div className="relative group">
                <input
                  type="text"
                  value={topic}
                  onChange={(e) => setTopic(e.target.value)}
                  placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                  className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
                />
                <div className="absolute right-4 top-4">
                  <Brain size={20} className="text-slate-400" />
                </div>
              </div>
            </div>
            {/* Key Points */}
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <label className="block text-gray-700 font-semibold text-lg">
                    Key points
                  </label>
                  <p className="text-sm text-gray-500 font-medium">
                    What will engage viewers?
                  </p>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={generateKeyPoints}
                    disabled={
                      !topic.trim() ||
                      isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript ||
                      !isAuthenticated
                    }
                    className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                  >
                    {isGeneratingKeyPoints ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
