import React, { useState, useCallback } from "react";
import { Volume2, VolumeX, Headphones, Music, Film, Image } from "lucide-react";

export const CapCutPropertiesPanel = ({
  selectedTimelineItem,
  timelineItems,
  setTimelineItems,
}) => {
  const [activeTab, setActiveTab] = useState("audio");

  // Обновляем свойства выбранного элемента
  const updateItemProperty = useCallback(
    (property, value) => {
      if (!selectedTimelineItem) return;

      setTimelineItems((prev) =>
        prev.map((item) =>
          item.id === selectedTimelineItem.id
            ? { ...item, [property]: value }
            : item
        )
      );
    },
    [selectedTimelineItem, setTimelineItems]
  );

  // Проверяем, поддерживает ли элемент аудио
  const hasAudio =
    selectedTimelineItem &&
    (selectedTimelineItem.type === "video" ||
      selectedTimelineItem.type === "audio");

  // Проверяем, поддерживает ли элемент прозрачность
  const hasOpacity =
    selectedTimelineItem && selectedTimelineItem.type === "image";

  if (!selectedTimelineItem) {
    return (
      <div className="w-80 bg-white border-l border-gray-200 flex flex-col">
        <div className="p-6 text-center text-gray-500">
          <div className="mb-4">
            <Film className="w-12 h-12 mx-auto text-gray-300" />
          </div>
          <p className="text-sm">Выберите элемент для редактирования</p>
        </div>
      </div>
    );
  }

  const getItemIcon = () => {
    switch (selectedTimelineItem.type) {
      case "video":
        return <Film className="w-5 h-5" />;
      case "audio":
        return <Music className="w-5 h-5" />;
      case "image":
        return <Image className="w-5 h-5" />;
      default:
        return <Film className="w-5 h-5" />;
    }
  };

  return (
    <div className="w-80 bg-white border-l border-gray-200 flex flex-col">
      <style>
        {`
          /* CapCut Style Slider */
          .capcut-slider {
            -webkit-appearance: none;
            width: 100%;
            height: 6px;
            border-radius: 3px;
            background: #e5e7eb;
            outline: none;
            transition: all 0.2s ease;
          }
          
          .capcut-slider::-webkit-slider-thumb {
            -webkit-appearance: none;
            width: 18px;
            height: 18px;
            border-radius: 50%;
            background: #3b82f6;
            cursor: pointer;
            box-shadow: 0 2px 4px rgba(59, 130, 246, 0.3);
            transition: all 0.2s ease;
            border: 2px solid white;
          }
          
          .capcut-slider::-webkit-slider-thumb:hover {
            background: #2563eb;
            transform: scale(1.1);
            box-shadow: 0 4px 8px rgba(59, 130, 246, 0.4);
          }
          
          .capcut-slider::-webkit-slider-thumb:active {
            transform: scale(1.2);
            background: #1d4ed8;
          }
          
          .capcut-slider::-moz-range-thumb {
            width: 18px;
            height: 18px;
            border-radius: 50%;
            background: #3b82f6;
            cursor: pointer;
            border: 2px solid white;
            box-shadow: 0 2px 4px rgba(59, 130, 246, 0.3);
          }
          
          .capcut-slider:disabled {
            opacity: 0.5;
            cursor: not-allowed;
          }
          
          .capcut-slider.muted {
            background: #fca5a5;
          }
          
          .capcut-slider.muted::-webkit-slider-thumb {
            background: #ef4444;
          }
        `}
      </style>

      {/* Заголовок панели */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center space-x-3">
          <div className="p-2 bg-blue-100 rounded-lg">{getItemIcon()}</div>
          <div className="flex-1 min-w-0">
            <h3 className="font-medium text-gray-900 truncate">
              {selectedTimelineItem.name || "Unnamed Item"}
            </h3>
            <p className="text-sm text-gray-500 capitalize">
              {selectedTimelineItem.type}
            </p>
          </div>
        </div>
      </div>

      {/* Табы */}
      <div className="flex border-b border-gray-200">
        {hasAudio && (
          <button
            onClick={() => setActiveTab("audio")}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-colors border-b-2 ${
              activeTab === "audio"
                ? "text-blue-600 border-blue-600 bg-blue-50"
                : "text-gray-600 hover:text-gray-900 border-transparent"
            }`}
          >
            <Headphones className="w-4 h-4 mx-auto mb-1" />
            Аудио
          </button>
        )}

        {hasOpacity && (
          <button
            onClick={() => setActiveTab("visual")}
            className={`flex-1 px-4 py-3 text-sm font-medium transition-colors border-b-2 ${
              activeTab === "visual"
                ? "text-blue-600 border-blue-600 bg-blue-50"
                : "text-gray-600 hover:text-gray-900 border-transparent"
            }`}
          >
            <Image className="w-4 h-4 mx-auto mb-1" />
            Изображение
          </button>
        )}
      </div>

      {/* Содержимое */}
      <div className="flex-1 overflow-y-auto">
        {/* Аудио контролы */}
        {hasAudio && activeTab === "audio" && (
          <div className="p-4 space-y-6">
            {/* Громкость */}
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <label className="text-sm font-medium text-gray-700">
                  Громкость
                </label>
                <span className="text-sm text-gray-500">
                  {selectedTimelineItem.volume || 100}%
                </span>
              </div>

              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <button
                    onClick={() =>
                      updateItemProperty("muted", !selectedTimelineItem.muted)
                    }
                    className={`p-2 rounded-lg transition-colors ${
                      selectedTimelineItem.muted
                        ? "bg-red-100 text-red-600 hover:bg-red-200"
                        : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                    }`}
                  >
                    {selectedTimelineItem.muted ? (
                      <VolumeX className="w-4 h-4" />
                    ) : (
                      <Volume2 className="w-4 h-4" />
                    )}
                  </button>

                  <div className="flex-1">
                    <input
                      type="range"
                      min="0"
                      max="200"
                      value={
                        selectedTimelineItem.muted
                          ? 0
                          : selectedTimelineItem.volume || 100
                      }
                      onChange={(e) => {
                        const value = parseInt(e.target.value);
                        updateItemProperty("volume", value);
                        if (value > 0 && selectedTimelineItem.muted) {
                          updateItemProperty("muted", false);
                        }
                      }}
                      disabled={selectedTimelineItem.muted}
                      className={`capcut-slider ${
                        selectedTimelineItem.muted ? "muted" : ""
                      }`}
                      style={{
                        background: selectedTimelineItem.muted
                          ? "#fca5a5"
                          : `linear-gradient(to right, #3b82f6 0%, #3b82f6 ${
                              (selectedTimelineItem.volume || 100) / 2
                            }%, #e5e7eb ${
                              (selectedTimelineItem.volume || 100) / 2
                            }%, #e5e7eb 100%)`,
                      }}
                    />
                  </div>
                </div>

                {/* Кнопки быстрого доступа */}
                <div className="flex space-x-2">
                  <button
                    onClick={() => {
                      updateItemProperty("volume", 0);
                      updateItemProperty("muted", true);
                    }}
                    className="px-3 py-1.5 text-xs font-medium bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition-colors active:scale-95"
                  >
                    0%
                  </button>
                  <button
                    onClick={() => {
                      updateItemProperty("volume", 50);
                      updateItemProperty("muted", false);
                    }}
                    className="px-3 py-1.5 text-xs font-medium bg-gray-100 text-gray-600 rounded-lg hover:bg-gray-200 transition-colors active:scale-95"
                  >
                    50%
                  </button>
                  <button
                    onClick={() => {
                      updateItemProperty("volume", 100);
                      updateItemProperty("muted", false);
                    }}
                    className={`px-3 py-1.5 text-xs font-medium rounded-lg transition-colors active:scale-95 ${
                      (selectedTimelineItem.volume || 100) === 100
                        ? "bg-blue-100 text-blue-700 hover:bg-blue-200"
                        : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                    }`}
                  >
                    100%
                  </button>
                  <button
                    onClick={() => {
                      updateItemProperty("volume", 150);
                      updateItemProperty("muted", false);
                    }}
                    className={`px-3 py-1.5 text-xs font-medium rounded-lg transition-colors active:scale-95 ${
                      (selectedTimelineItem.volume || 100) === 150
                        ? "bg-blue-100 text-blue-700 hover:bg-blue-200"
                        : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                    }`}
                  >
                    150%
                  </button>
                </div>
              </div>
            </div>

            {/* Информация */}
            <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
              <h4 className="text-xs font-medium text-gray-500 uppercase tracking-wide mb-3">
                Информация
              </h4>
              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Длительность:</span>
                  <span className="text-sm font-medium text-gray-900">
                    {selectedTimelineItem.duration}s
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Статус:</span>
                  <span
                    className={`text-sm font-medium ${
                      selectedTimelineItem.muted
                        ? "text-red-600"
                        : "text-green-600"
                    }`}
                  >
                    {selectedTimelineItem.muted ? "Отключен" : "Включен"}
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Уровень:</span>
                  <span className="text-sm font-medium text-gray-900">
                    {selectedTimelineItem.volume || 100}%
                  </span>
                </div>
              </div>
            </div>

            {/* Дополнительные контролы */}
            <div className="space-y-4">
              <div className="pt-4 border-t border-gray-100">
                <h4 className="text-sm font-medium text-gray-700 mb-3">
                  Быстрые действия
                </h4>
                <div className="space-y-2">
                  <button
                    onClick={() => {
                      // Нормализация громкости - устанавливаем оптимальный уровень
                      updateItemProperty("volume", 85);
                      updateItemProperty("muted", false);
                    }}
                    className="w-full px-3 py-2 text-sm bg-blue-50 text-blue-700 rounded-lg hover:bg-blue-100 transition-colors"
                  >
                    🎯 Нормализовать громкость
                  </button>
                  <button
                    onClick={() => {
                      // Сброс к дефолтным настройкам
                      updateItemProperty("volume", 100);
                      updateItemProperty("muted", false);
                    }}
                    className="w-full px-3 py-2 text-sm bg-gray-50 text-gray-700 rounded-lg hover:bg-gray-100 transition-colors"
                  >
                    ↻ Сбросить настройки
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Контролы для изображений */}
        {hasOpacity && activeTab === "visual" && (
          <div className="p-4 space-y-6">
            {/* Прозрачность */}
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <label className="text-sm font-medium text-gray-700">
                  Прозрачность
                </label>
                <span className="text-sm text-gray-500">
                  {selectedTimelineItem.opacity || 100}%
                </span>
              </div>

              <div className="space-y-3">
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={selectedTimelineItem.opacity || 100}
                  onChange={(e) =>
                    updateItemProperty("opacity", parseInt(e.target.value))
                  }
                  className="capcut-slider"
                  style={{
                    background: `linear-gradient(to right, #3b82f6 0%, #3b82f6 ${
                      selectedTimelineItem.opacity || 100
                    }%, #e5e7eb ${
                      selectedTimelineItem.opacity || 100
                    }%, #e5e7eb 100%)`,
                  }}
                />

                {/* Кнопки быстрого доступа */}
                <div className="flex space-x-2">
                  <button
                    onClick={() => updateItemProperty("opacity", 0)}
                    className="px-3 py-1.5 text-xs font-medium bg-red-100 text-red-700 rounded-lg hover:bg-red-200 transition-colors"
                  >
                    0%
                  </button>
                  <button
                    onClick={() => updateItemProperty("opacity", 25)}
                    className="px-3 py-1.5 text-xs font-medium bg-gray-100 text-gray-600 rounded-lg hover:bg-gray-200 transition-colors"
                  >
                    25%
                  </button>
                  <button
                    onClick={() => updateItemProperty("opacity", 50)}
                    className="px-3 py-1.5 text-xs font-medium bg-gray-100 text-gray-600 rounded-lg hover:bg-gray-200 transition-colors"
                  >
                    50%
                  </button>
                  <button
                    onClick={() => updateItemProperty("opacity", 100)}
                    className={`px-3 py-1.5 text-xs font-medium rounded-lg transition-colors ${
                      (selectedTimelineItem.opacity || 100) === 100
                        ? "bg-blue-100 text-blue-700"
                        : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                    }`}
                  >
                    100%
                  </button>
                </div>
              </div>
            </div>

            {/* Информация */}
            <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
              <h4 className="text-xs font-medium text-gray-500 uppercase tracking-wide mb-3">
                Информация
              </h4>
              <div className="space-y-2">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Длительность:</span>
                  <span className="text-sm font-medium text-gray-900">
                    {selectedTimelineItem.duration}s
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">Прозрачность:</span>
                  <span className="text-sm font-medium text-gray-900">
                    {selectedTimelineItem.opacity || 100}%
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-600">
                    Режим наложения:
                  </span>
                  <span className="text-sm font-medium text-gray-900">
                    Нормальный
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
