import { useState, useEffect } from "react";
import { X, Download, Check, AlertCircle } from "lucide-react";

export const VideoExportModal = ({
  isOpen,
  onClose,
  timelineItems,
  tracks,
  videoDuration,
  onExport,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1920x1080",
    fps: 30,
    bitrate: 5000,
    format: "mp4",
    quality: "high",
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState("idle");
  const [estimatedTime, setEstimatedTime] = useState(null);

  // üîß –ò–°–ü–†–ê–í–õ–ï–ù–û: –í—ã–Ω–µ—Å–ª–∏ –æ–ø—Ü–∏–∏ –≤ –∫–æ–Ω—Å—Ç–∞–Ω—Ç—ã, —á—Ç–æ–±—ã –æ–Ω–∏ –Ω–µ –ø–µ—Ä–µ—Å–æ–∑–¥–∞–≤–∞–ª–∏—Å—å –∫–∞–∂–¥—ã–π —Ä–µ–Ω–¥–µ—Ä
  const resolutionOptions = [
    { value: "3840x2160", label: "4K (3840√ó2160)", size: "2160p" },
    { value: "1920x1080", label: "Full HD (1920√ó1080)", size: "1080p" },
    { value: "1280x720", label: "HD (1280√ó720)", size: "720p" },
    { value: "854x480", label: "SD (854√ó480)", size: "480p" },
  ];

  const fpsOptions = [24, 30, 60];
  
  const qualityOptions = [
    { value: "low", label: "–ù–∏–∑–∫–æ–µ –∫–∞—á–µ—Å—Ç–≤–æ", bitrate: 1000 },
    { value: "medium", label: "–°—Ä–µ–¥–Ω–µ–µ –∫–∞—á–µ—Å—Ç–≤–æ", bitrate: 3000 },
    { value: "high", label: "–í—ã—Å–æ–∫–æ–µ –∫–∞—á–µ—Å—Ç–≤–æ", bitrate: 5000 },
    { value: "ultra", label: "–£–ª—å—Ç—Ä–∞ –∫–∞—á–µ—Å—Ç–≤–æ", bitrate: 10000 },
  ];

  const formatOptions = [
    { value: "mp4", label: "MP4 (H.264)", extension: ".mp4" },
    { value: "webm", label: "WebM (VP9)", extension: ".webm" },
    { value: "mov", label: "QuickTime (MOV)", extension: ".mov" },
  ];

  // üîß –ò–°–ü–†–ê–í–õ–ï–ù–û: –î–æ–±–∞–≤–∏–ª–∏ –ø—Ä–∞–≤–∏–ª—å–Ω—ã–µ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –∏ —É–±—Ä–∞–ª–∏ qualityOptions –∏–∑ deps
  useEffect(() => {
    if (exportSettings.quality) {
      const quality = qualityOptions.find(
        (q) => q.value === exportSettings.quality
      );
      if (quality && quality.bitrate !== exportSettings.bitrate) {
        setExportSettings((prev) => ({ ...prev, bitrate: quality.bitrate }));
      }
    }
  }, [exportSettings.quality]); // –£–±—Ä–∞–ª–∏ qualityOptions –∏–∑ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–µ–π

  // üîß –ò–°–ü–†–ê–í–õ–ï–ù–û: –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–ª–∏ —Ä–∞—Å—á–µ—Ç –≤—Ä–µ–º–µ–Ω–∏
  useEffect(() => {
    if (videoDuration && exportSettings.fps && timelineItems) {
      const complexity = timelineItems.filter(
        (item) => item.trackType === "overlay"
      ).length;
      const multiplier = 2 + complexity * 0.5;
      const newEstimatedTime = Math.ceil(videoDuration * multiplier);
      
      // –û–±–Ω–æ–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ –∏–∑–º–µ–Ω–∏–ª–æ—Å—å
      if (newEstimatedTime !== estimatedTime) {
        setEstimatedTime(newEstimatedTime);
      }
    }
  }, [videoDuration, exportSettings.fps, timelineItems.length, estimatedTime]); // –ò–∑–º–µ–Ω–∏–ª–∏ timelineItems –Ω–∞ timelineItems.length

  const getFileSize = () => {
    if (!videoDuration) return "~";

    const sizeInMB =
      (exportSettings.bitrate * videoDuration * 1000) / (8 * 1024 * 1024);

    if (sizeInMB < 1024) {
      return `~${sizeInMB.toFixed(0)} –ú–ë`;
    } else {
      return `~${(sizeInMB / 1024).toFixed(1)} –ì–ë`;
    }
  };

  const handleExport = async () => {
    if (!timelineItems || timelineItems.length === 0) {
      alert('–ù–µ—Ç —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞!');
      return;
    }

    setIsExporting(true);
    setExportStatus("processing");
    setExportProgress(0);

    try {
      await simulateVideoExport();
      setExportStatus("success");
    } catch (error) {
      console.error("Export failed:", error);
      setExportStatus("error");
    } finally {
      setIsExporting(false);
    }
  };

  const simulateVideoExport = async () => {
    const totalSteps = 100;
    const stepDuration = (estimatedTime * 1000) / totalSteps;

    for (let i = 0; i <= totalSteps; i++) {
      setExportProgress(i);

      const delay = stepDuration * (1 + Math.sin(i / 20) * 0.3);
      await new Promise((resolve) => setTimeout(resolve, delay));

      if (i === totalSteps) {
        await actualVideoExport();
      }
    }
  };

  const actualVideoExport = async () => {
    try {
      const videoExporter = new VideoExporter({
        timelineItems,
        tracks,
        videoDuration,
        settings: exportSettings,
      });

      const exportedVideoBlob = await videoExporter.render();
      downloadVideo(exportedVideoBlob);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ —ç–∫—Å–ø–æ—Ä—Ç–µ:', error);
      throw error;
    }
  };

  const downloadVideo = (blob) => {
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    
    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –ø—Ä–∞–≤–∏–ª—å–Ω–æ–µ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ
    let extension = exportSettings.format;
    if (blob.type.includes('webm')) {
      extension = 'webm';
    } else if (blob.type.includes('mp4')) {
      extension = 'mp4';
    }
    
    a.download = `edited_video_${Date.now()}.${extension}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // üîß –ò–°–ü–†–ê–í–õ–ï–ù–û: –°–±—Ä–æ—Å —Å–æ—Å—Ç–æ—è–Ω–∏—è –ø—Ä–∏ –∑–∞–∫—Ä—ã—Ç–∏–∏ –º–æ–¥–∞–ª–∫–∏
  const handleClose = () => {
    if (!isExporting) {
      setExportStatus("idle");
      setExportProgress(0);
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-2xl w-full max-h-[90vh] overflow-hidden">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Download size={24} className="mr-2 text-blue-600" />
            –≠–∫—Å–ø–æ—Ä—Ç –≤–∏–¥–µ–æ
          </h2>
          <button
            onClick={handleClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(90vh-120px)] overflow-y-auto">
          {exportStatus !== "idle" && (
            <div
              className={`p-4 rounded-xl ${
                exportStatus === "processing"
                  ? "bg-blue-50 border border-blue-200"
                  : exportStatus === "success"
                  ? "bg-green-50 border border-green-200"
                  : "bg-red-50 border border-red-200"
              }`}
            >
              <div className="flex items-center">
                {exportStatus === "processing" && (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600 mr-3"></div>
                    <div className="flex-1">
                      <div className="flex justify-between text-sm text-blue-700 mb-2">
                        <span>–≠–∫—Å–ø–æ—Ä—Ç –≤–∏–¥–µ–æ...</span>
                        <span>{exportProgress}%</span>
                      </div>
                      <div className="w-full bg-blue-200 rounded-full h-2">
                        <div
                          className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${exportProgress}%` }}
                        ></div>
                      </div>
                      {estimatedTime && (
                        <div className="text-xs text-blue-600 mt-2">
                          –û—Å—Ç–∞–ª–æ—Å—å –ø—Ä–∏–º–µ—Ä–Ω–æ:{" "}
                          {Math.max(
                            0,
                            Math.ceil(
                              (estimatedTime * (100 - exportProgress)) / 100
                            )
                          )}{" "}
                          —Å–µ–∫
                        </div>
                      )}
                    </div>
                  </>
                )}

                {exportStatus === "success" && (
                  <>
                    <Check size={20} className="text-green-600 mr-3" />
                    <span className="text-green-700 font-medium">
                      –í–∏–¥–µ–æ —É—Å–ø–µ—à–Ω–æ —ç–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–æ!
                    </span>
                  </>
                )}

                {exportStatus === "error" && (
                  <>
                    <AlertCircle size={20} className="text-red-600 mr-3" />
                    <span className="text-red-700 font-medium">
                      –û—à–∏–±–∫–∞ –ø—Ä–∏ —ç–∫—Å–ø–æ—Ä—Ç–µ –≤–∏–¥–µ–æ
                    </span>
                  </>
                )}
              </div>
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ
              </label>
              <select
                value={exportSettings.resolution}
                onChange={(e) =>
                  setExportSettings((prev) => ({
                    ...prev,
                    resolution: e.target.value,
                  }))
                }
                className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {resolutionOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                –ß–∞—Å—Ç–æ—Ç–∞ –∫–∞–¥—Ä–æ–≤ (FPS)
              </label>
              <select
                value={exportSettings.fps}
                onChange={(e) =>
                  setExportSettings((prev) => ({
                    ...prev,
                    fps: parseInt(e.target.value),
                  }))
                }
                className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {fpsOptions.map((fps) => (
                  <option key={fps} value={fps}>
                    {fps} FPS
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                –ö–∞—á–µ—Å—Ç–≤–æ
              </label>
              <select
                value={exportSettings.quality}
                onChange={(e) =>
                  setExportSettings((prev) => ({
                    ...prev,
                    quality: e.target.value,
                  }))
                }
                className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {qualityOptions.map((quality) => (
                  <option key={quality.value} value={quality.value}>
                    {quality.label} ({quality.bitrate} kbps)
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                –§–æ—Ä–º–∞—Ç
              </label>
              <select
                value={exportSettings.format}
                onChange={(e) =>
                  setExportSettings((prev) => ({
                    ...prev,
                    format: e.target.value,
                  }))
                }
                className="w-full p-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {formatOptions.map((format) => (
                  <option key={format.value} value={format.value}>
                    {format.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="bg-gray-50 rounded-xl p-4">
            <h4 className="font-medium text-gray-800 mb-3">
              –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ–± —ç–∫—Å–ø–æ—Ä—Ç–µ
            </h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-600">–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å:</span>
                <span className="ml-2 font-medium">
                  {Math.round(videoDuration)}—Å
                </span>
              </div>
              <div>
                <span className="text-gray-600">–†–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞:</span>
                <span className="ml-2 font-medium">{getFileSize()}</span>
              </div>
              <div>
                <span className="text-gray-600">–ë–∏—Ç—Ä–µ–π—Ç:</span>
                <span className="ml-2 font-medium">
                  {exportSettings.bitrate} kbps
                </span>
              </div>
              <div>
                <span className="text-gray-600">–í—Ä–µ–º—è —ç–∫—Å–ø–æ—Ä—Ç–∞:</span>
                <span className="ml-2 font-medium">~{estimatedTime}—Å</span>
              </div>
            </div>
          </div>

          <div className="bg-gray-50 rounded-xl p-4">
            <h4 className="font-medium text-gray-800 mb-3">
              –≠–ª–µ–º–µ–Ω—Ç—ã –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞ ({timelineItems?.length || 0})
            </h4>
            <div className="space-y-2 max-h-32 overflow-y-auto">
              {timelineItems && timelineItems.length > 0 ? (
                timelineItems.map((item) => (
                  <div
                    key={item.id}
                    className="flex items-center justify-between text-sm"
                  >
                    <span className="text-gray-700 truncate">{item.name}</span>
                    <span
                      className={`px-2 py-1 rounded text-xs flex-shrink-0 ml-2 ${
                        item.trackType === "main"
                          ? "bg-blue-100 text-blue-700"
                          : item.trackType === "audio"
                          ? "bg-green-100 text-green-700"
                          : "bg-purple-100 text-purple-700"
                      }`}
                    >
                      {item.trackType}
                    </span>
                  </div>
                ))
              ) : (
                <div className="text-gray-500 text-sm text-center py-4">
                  –ù–µ—Ç —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex justify-end space-x-3 p-6 border-t border-gray-200">
          <button
            onClick={handleClose}
            className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            disabled={isExporting}
          >
            –û—Ç–º–µ–Ω–∞
          </button>
          <button
            onClick={handleExport}
            disabled={isExporting || !timelineItems || timelineItems.length === 0}
            className={`px-6 py-3 rounded-xl font-medium transition-all flex items-center ${
              isExporting || !timelineItems || timelineItems.length === 0
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700 hover:shadow-lg"
            }`}
          >
            {isExporting ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                –≠–∫—Å–ø–æ—Ä—Ç...
              </>
            ) : (
              <>
                <Download size={16} className="mr-2" />
                –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// üîß –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô VideoExporter –∫–ª–∞—Å—Å
class VideoExporter {
  constructor({ timelineItems, tracks, videoDuration, settings }) {
    this.timelineItems = timelineItems;
    this.tracks = tracks;
    this.videoDuration = videoDuration;
    this.settings = settings;

    this.canvas = document.createElement("canvas");
    this.ctx = this.canvas.getContext("2d");

    const [width, height] = settings.resolution.split("x").map(Number);
    this.canvas.width = width;
    this.canvas.height = height;
  }

  async render() {
    try {
      const stream = this.canvas.captureStream(this.settings.fps);
      
      const options = {
        mimeType: this.getMimeType(),
        videoBitsPerSecond: this.settings.bitrate * 1000,
      };

      const mediaRecorder = new MediaRecorder(stream, options);
      const chunks = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      return new Promise((resolve, reject) => {
        mediaRecorder.onstop = () => {
          const blob = new Blob(chunks, {
            type: `video/${this.settings.format}`,
          });
          resolve(blob);
        };

        mediaRecorder.onerror = reject;

        mediaRecorder.start(100); // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫–∞–∂–¥—ã–µ 100–º—Å

        this.renderFrames()
          .then(() => {
            setTimeout(() => {
              mediaRecorder.stop();
            }, 100);
          })
          .catch(reject);
      });
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –∑–∞–ø–∏—Å–∏:', error);
      throw error;
    }
  }

  getMimeType() {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ–¥–¥–µ—Ä–∂–∫—É —Ä–∞–∑–Ω—ã—Ö —Ñ–æ—Ä–º–∞—Ç–æ–≤
    const testTypes = [
      'video/webm;codecs=vp9',
      'video/webm;codecs=vp8',
      'video/mp4;codecs=h264',
      'video/webm'
    ];
    
    for (const type of testTypes) {
      if (MediaRecorder.isTypeSupported(type)) {
        return type;
      }
    }
    
    return 'video/webm'; // fallback
  }

  async renderFrames() {
    const frameCount = Math.ceil(this.videoDuration * this.settings.fps);
    const frameDuration = 1000 / this.settings.fps; // –≤ –º–∏–ª–ª–∏—Å–µ–∫—É–Ω–¥–∞—Ö

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame / this.settings.fps;
      await this.renderFrame(currentTime);

      // –ñ–¥–µ–º –º–µ–∂–¥—É –∫–∞–¥—Ä–∞–º–∏ –¥–ª—è –ø–ª–∞–≤–Ω–æ—Å—Ç–∏
      await new Promise((resolve) => setTimeout(resolve, frameDuration / 10));
    }
  }

  async renderFrame(currentTime) {
    // –ß–µ—Ä–Ω—ã–π —Ñ–æ–Ω
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã –ø–æ —Å–ª–æ—è–º
    const sortedItems = [...this.timelineItems].sort((a, b) => {
      const order = { main: 1, audio: 2, overlay: 3 };
      return order[a.trackType] - order[b.trackType];
    });

    // –†–µ–Ω–¥–µ—Ä–∏–º –∞–∫—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
    for (const item of sortedItems) {
      if (this.isItemActive(item, currentTime)) {
        await this.renderItem(item, currentTime - item.startTime);
      }
    }
  }

  isItemActive(item, currentTime) {
    return currentTime >= item.startTime && 
           currentTime < item.startTime + item.duration;
  }

  async renderItem(item, relativeTime) {
    try {
      this.ctx.save();
      
      switch (item.type) {
        case "video":
          await this.renderVideo(item, relativeTime);
          break;
        case "image":
          await this.renderImage(item);
          break;
        case "text":
          this.renderText(item.text || item.name || 'Text');
          break;
      }
    } catch (error) {
      console.warn('–û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ —ç–ª–µ–º–µ–Ω—Ç–∞:', error);
      // –†–µ–Ω–¥–µ—Ä–∏–º placeholder
      this.renderPlaceholder(item.name || '–≠–ª–µ–º–µ–Ω—Ç');
    } finally {
      this.ctx.restore();
    }
  }

  async renderVideo(item, relativeTime) {
    if (!item.videoElement) return;

    try {
      item.videoElement.currentTime = relativeTime;
      await this.waitForVideoFrame(item.videoElement);
      this.drawScaled(item.videoElement);
    } catch (error) {
      console.warn('–û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –≤–∏–¥–µ–æ:', error);
    }
  }

  async renderImage(item) {
    if (!item.imageElement) return;

    try {
      await this.waitForImageLoad(item.imageElement);
      this.drawScaled(item.imageElement);
    } catch (error) {
      console.warn('–û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è:', error);
    }
  }

  renderText(text) {
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = "48px Arial";
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    
    const x = this.canvas.width / 2;
    const y = this.canvas.height / 2;
    
    // –û–±–≤–æ–¥–∫–∞
    this.ctx.strokeStyle = "#000000";
    this.ctx.lineWidth = 4;
    this.ctx.strokeText(text, x, y);
    
    // –¢–µ–∫—Å—Ç
    this.ctx.fillText(text, x, y);
  }

  renderPlaceholder(text) {
    this.ctx.fillStyle = "#333333";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = "24px Arial";
    this.ctx.textAlign = "center";
    this.ctx.fillText(text, this.canvas.width / 2, this.canvas.height / 2);
  }

  drawScaled(element) {
    const canvasRatio = this.canvas.width / this.canvas.height;
    const elementWidth = element.videoWidth || element.naturalWidth || element.width;
    const elementHeight = element.videoHeight || element.naturalHeight || element.height;
    
    if (!elementWidth || !elementHeight) return;
    
    const elementRatio = elementWidth / elementHeight;

    let drawWidth, drawHeight, drawX, drawY;

    if (elementRatio > canvasRatio) {
      drawWidth = this.canvas.width;
      drawHeight = this.canvas.width / elementRatio;
      drawX = 0;
      drawY = (this.canvas.height - drawHeight) / 2;
    } else {
      drawHeight = this.canvas.height;
      drawWidth = this.canvas.height * elementRatio;
      drawX = (this.canvas.width - drawWidth) / 2;
      drawY = 0;
    }

    this.ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
  }

  async waitForVideoFrame(videoElement) {
    return new Promise((resolve) => {
      const checkReady = () => {
        if (videoElement.readyState >= 2) {
          resolve();
        } else {
          setTimeout(checkReady, 10);
        }
      };
      checkReady();
    });
  }

  async waitForImageLoad(imageElement) {
    return new Promise((resolve, reject) => {
      if (imageElement.complete && imageElement.naturalWidth > 0) {
        resolve();
      } else {
        const onLoad = () => {
          imageElement.removeEventListener('load', onLoad);
          imageElement.removeEventListener('error', onError);
          resolve();
        };
        
        const onError = () => {
          imageElement.removeEventListener('load', onLoad);
          imageElement.removeEventListener('error', onError);
          reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ'));
        };
        
        imageElement.addEventListener('load', onLoad);
        imageElement.addEventListener('error', onError);
      }
    });
  }
}

// üîß –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô —Ö—É–∫ –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è —ç–∫—Å–ø–æ—Ä—Ç–∞
export const useVideoExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};
