import { useState, useRef, useEffect } from "react";
import {
  Upload,
  Play,
  Pause,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  Sparkles,
  ChevronLeft,
  ChevronRight,
  Zap,
  Brain,
  Mic,
  Users,
  Eye,
  Heart,
  Share2,
  Clock,
  Star,
  Target,
  Trash2,
  Download,
  SkipBack,
  SkipForward,
  Split,
  Copy,
  Move,
  Layers,
  Music,
  Video,
  Image,
  Folder,
  Grid,
  List
} from "lucide-react";

const VideoEditorApp = () => {
  const [videoFile, setVideoFile] = useState(null);
  const [videoUrl, setVideoUrl] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [script, setScript] = useState("");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [videoDuration, setVideoDuration] = useState("~10 минут (1000 слов)");
  const [audioUrl, setAudioUrl] = useState("");
  const [currentStep, setCurrentStep] = useState(1);
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [selectedText, setSelectedText] = useState("");
  const [aiPrompt, setAiPrompt] = useState("");
  const [showAiEditor, setShowAiEditor] = useState(false);
  const [isGeneratingText, setIsGeneratingText] = useState(false);
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [isGeneratingKeyPoints, setIsGeneratingKeyPoints] = useState(false);
  const [audioMethod, setAudioMethod] = useState("ai");
  const [isRecording, setIsRecording] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [showAudioEditor, setShowAudioEditor] = useState(false);
  const [assessment, setAssessment] = useState({});
  const [contentType, setContentType] = useState("lifestyle");
  const [assessmentLoading, setAssessmentLoading] = useState(false);
  const [isExtendingScript, setIsExtendingScript] = useState(false);
  const [showTeleprompter, setShowTeleprompter] = useState(false);

  // New video editing states
  const [clips, setClips] = useState([]);
  const [selectedClip, setSelectedClip] = useState(null);
  const [playheadPosition, setPlayheadPosition] = useState(0);
  const [zoom, setZoom] = useState(1);
  const [mediaLibrary, setMediaLibrary] = useState([]);
  const [showMediaLibrary, setShowMediaLibrary] = useState(false);
  const [timelineMode, setTimelineMode] = useState('both'); // 'video', 'audio', 'both'
  const [selectedTool, setSelectedTool] = useState('select'); // 'select', 'cut', 'split'
  const [cutStart, setCutStart] = useState(null);
  const [cutEnd, setCutEnd] = useState(null);
  const [isCutting, setIsCutting] = useState(false);

  const videoRef = useRef(null);
  const fileInputRef = useRef(null);
  const scriptRef = useRef(null);
  const timelineRef = useRef(null);

  useEffect(() => {
    if (videoRef.current && videoUrl) {
      const video = videoRef.current;
      
      const handleLoadedMetadata = () => {
        setDuration(video.duration);
        setVideoLoaded(true);
        console.log('Video metadata loaded, duration:', video.duration);
      };

      const handleTimeUpdate = () => {
        setCurrentTime(video.currentTime);
        setPlayheadPosition(video.currentTime);
      };

      const handlePlay = () => setIsPlaying(true);
      const handlePause = () => setIsPlaying(false);
      
      // Reset states
      setVideoLoaded(false);
      setDuration(0);
      setCurrentTime(0);
      setIsPlaying(false);
      
      video.addEventListener('loadedmetadata', handleLoadedMetadata);
      video.addEventListener('timeupdate', handleTimeUpdate);
      video.addEventListener('play', handlePlay);
      video.addEventListener('pause', handlePause);
      
      return () => {
        video.removeEventListener('loadedmetadata', handleLoadedMetadata);
        video.removeEventListener('timeupdate', handleTimeUpdate);
        video.removeEventListener('play', handlePlay);
        video.removeEventListener('pause', handlePause);
      };
    }
  }, [videoUrl]);

  // Initialize clips when video is loaded
  useEffect(() => {
    if (videoFile && videoUrl && videoLoaded && duration > 0) {
      const initialClip = {
        id: Date.now(),
        name: videoFile.name,
        type: 'video',
        url: videoUrl,
        startTime: 0,
        endTime: duration,
        duration: duration,
        trackIndex: 0
      };
      setClips([initialClip]);
      console.log('Created initial clip:', initialClip);
    }
  }, [videoFile, videoUrl, videoLoaded, duration]);

  const addKeyPoint = () => {
    if (keyPoints.length < 10) {
      setKeyPoints([...keyPoints, ""]);
    }
  };

  const updateKeyPoint = (index, value) => {
    const newKeyPoints = [...keyPoints];
    newKeyPoints[index] = value;
    setKeyPoints(newKeyPoints);
  };

  const removeKeyPoint = (index) => {
    const newKeyPoints = keyPoints.filter((_, i) => i !== index);
    setKeyPoints(newKeyPoints.length > 0 ? newKeyPoints : [""]);
  };

  const generateScript = async () => {
    setIsGeneratingScript(true);
    // Simulate API call
    setTimeout(() => {
      setScript("This is a generated script about " + topic + ". It includes detailed analysis and professional content structure. The script covers all key points and provides engaging narrative for the audience.");
      setCurrentStep(2);
      setIsGeneratingScript(false);
    }, 2000);
  };

  const handleVideoUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Clear previous video
      if (videoUrl) {
        URL.revokeObjectURL(videoUrl);
      }
      
      setVideoFile(file);
      const url = URL.createObjectURL(file);
      setVideoUrl(url);
      setVideoLoaded(false);
      setClips([]);
      
      // Add to media library
      const mediaItem = {
        id: Date.now(),
        name: file.name,
        type: file.type.startsWith('video/') ? 'video' : 'audio',
        url: url,
        duration: 0,
        size: file.size
      };
      
      setMediaLibrary(prev => {
        const exists = prev.find(item => item.name === file.name);
        if (!exists) {
          return [...prev, mediaItem];
        }
        return prev;
      });
      
      // Advance step
      if (script) {
        setCurrentStep(4);
      } else {
        setCurrentStep(2);
      }
    }
  };

  const handleMediaUpload = (event) => {
    const files = Array.from(event.target.files);
    files.forEach(file => {
      const url = URL.createObjectURL(file);
      const mediaItem = {
        id: Date.now() + Math.random(),
        name: file.name,
        type: file.type.startsWith('video/') ? 'video' : file.type.startsWith('audio/') ? 'audio' : 'image',
        url: url,
        duration: 0,
        size: file.size
      };
      
      setMediaLibrary(prev => {
        const exists = prev.find(item => item.name === file.name);
        if (!exists) {
          return [...prev, mediaItem];
        }
        return prev;
      });
    });
  };

  const handlePlayPause = async () => {
    if (videoRef.current && videoLoaded) {
      try {
        if (isPlaying) {
          videoRef.current.pause();
        } else {
          const playPromise = videoRef.current.play();
          if (playPromise !== undefined) {
            await playPromise;
          }
        }
      } catch (error) {
        console.error('Playback error:', error);
      }
    }
  };

  const seekTo = (time) => {
    if (videoRef.current && videoLoaded && duration > 0) {
      const clampedTime = Math.max(0, Math.min(time, duration));
      videoRef.current.currentTime = clampedTime;
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Video editing functions
  const startCut = (time) => {
    setCutStart(Math.max(0, Math.min(time, duration)));
    setIsCutting(true);
    console.log('Cut started at:', time);
  };

  const endCut = (time) => {
    if (cutStart !== null) {
      const endTime = Math.max(0, Math.min(time, duration));
      setCutEnd(endTime);
      setIsCutting(false);
      console.log('Cut ended at:', endTime);
    }
  };

  const applyCut = () => {
    if (cutStart !== null && cutEnd !== null && selectedClip) {
      const start = Math.min(cutStart, cutEnd);
      const end = Math.max(cutStart, cutEnd);
      
      const newClips = clips.filter(clip => {
        if (clip.id === selectedClip.id) {
          // Remove the cut section - create clips before and after
          const beforeClip = {
            ...clip,
            id: Date.now(),
            endTime: start,
            duration: start - clip.startTime
          };
          
          const afterClip = {
            ...clip,
            id: Date.now() + 1,
            startTime: end,
            duration: clip.endTime - end
          };
          
          const newClips = [];
          if (beforeClip.duration > 0.1) newClips.push(beforeClip);
          if (afterClip.duration > 0.1) newClips.push(afterClip);
          
          return newClips;
        }
        return [clip];
      }).flat();
      
      setClips(newClips);
      setCutStart(null);
      setCutEnd(null);
      setSelectedClip(null);
      console.log('Cut applied, new clips:', newClips);
    }
  };

  const splitClip = (time) => {
    if (selectedClip && time > selectedClip.startTime && time < selectedClip.endTime) {
      const newClips = clips.map(clip => {
        if (clip.id === selectedClip.id) {
          const firstClip = {
            ...clip,
            id: Date.now(),
            endTime: time,
            duration: time - clip.startTime,
            name: clip.name + ' (part 1)'
          };
          
          const secondClip = {
            ...clip,
            id: Date.now() + 1,
            startTime: time,
            duration: clip.endTime - time,
            name: clip.name + ' (part 2)'
          };
          
          return [firstClip, secondClip];
        }
        return clip;
      }).flat();
      
      setClips(newClips);
      setSelectedClip(null);
      console.log('Clip split at:', time);
    }
  };

  const deleteClip = (clipId) => {
    setClips(clips.filter(clip => clip.id !== clipId));
    if (selectedClip && selectedClip.id === clipId) {
      setSelectedClip(null);
    }
  };

  const duplicateClip = (clip) => {
    const newClip = {
      ...clip,
      id: Date.now(),
      name: clip.name + ' (copy)'
    };
    setClips([...clips, newClip]);
  };

  const TimelineTrack = ({ track, trackIndex, trackType }) => {
    const trackClips = clips.filter(clip => 
      clip.trackIndex === trackIndex && 
      (trackType === 'both' || clip.type === trackType)
    );

    return (
      <div className="relative h-16 bg-slate-100 border border-slate-200 rounded-lg mb-2">
        <div className="absolute left-2 top-2 text-xs font-medium text-slate-600">
          {trackType === 'video' ? 'Video' : trackType === 'audio' ? 'Audio' : `Track ${trackIndex + 1}`}
        </div>
        {trackClips.map(clip => (
          <div
            key={clip.id}
            className={`absolute top-6 h-8 rounded-md border-2 transition-all duration-200 cursor-pointer hover:shadow-md ${
              selectedClip?.id === clip.id
                ? 'border-blue-500 bg-blue-200 shadow-lg'
                : clip.type === 'video'
                ? 'border-indigo-300 bg-indigo-100 hover:bg-indigo-200'
                : 'border-green-300 bg-green-100 hover:bg-green-200'
            }`}
            style={{
              left: duration > 0 ? `${(clip.startTime / duration) * 100}%` : '0%',
              width: duration > 0 ? `${(clip.duration / duration) * 100}%` : '100%'
            }}
            onClick={(e) => {
              e.stopPropagation();
              setSelectedClip(clip);
              seekTo(clip.startTime);
            }}
          >
            <div className="px-2 py-1 text-xs font-medium truncate text-slate-700">
              {clip.name}
            </div>
            {clip.duration > 0 && (
              <div className="absolute bottom-0 left-2 text-xs text-slate-500">
                {formatTime(clip.duration)}
              </div>
            )}
          </div>
        ))}
      </div>
    );
  };

  const MediaLibraryItem = ({ item }) => (
    <div className="bg-white border border-slate-200 rounded-lg p-3 hover:shadow-md transition-all duration-200">
      <div className="flex items-center space-x-3">
        <div className={`p-2 rounded-lg ${
          item.type === 'video' ? 'bg-indigo-100' : 
          item.type === 'audio' ? 'bg-green-100' : 'bg-slate-100'
        }`}>
          {item.type === 'video' ? (
            <Video size={16} className="text-indigo-600" />
          ) : item.type === 'audio' ? (
            <Music size={16} className="text-green-600" />
          ) : (
            <Image size={16} className="text-slate-600" />
          )}
        </div>
        <div className="flex-1 min-w-0">
          <p className="text-sm font-medium text-slate-900 truncate">{item.name}</p>
          <p className="text-xs text-slate-500">
            {item.duration > 0 ? formatTime(item.duration) : 'Loading...'} • {(item.size / 1024 / 1024).toFixed(1)}MB
          </p>
        </div>
        <div className="flex space-x-1">
          {item.type === 'video' && (
            <button
              onClick={() => {
                // Set as main video
                setVideoFile({ name: item.name, size: item.size });
                setVideoUrl(item.url);
                setVideoLoaded(false);
              }}
              className="p-1 text-slate-400 hover:text-indigo-500 transition-colors"
              title="Set as main video"
            >
              <Play size={14} />
            </button>
          )}
          <button
            onClick={() => {
              // Add to timeline
              const newClip = {
                id: Date.now(),
                name: item.name,
                type: item.type,
                url: item.url,
                startTime: currentTime,
                endTime: currentTime + (item.duration || 10),
                duration: item.duration || 10,
                trackIndex: item.type === 'video' ? 0 : 1
              };
              setClips([...clips, newClip]);
            }}
            className="p-1 text-slate-400 hover:text-blue-500 transition-colors"
            title="Add to timeline"
          >
            <Plus size={14} />
          </button>
        </div>
      </div>
    </div>
  );

  const steps = [
    {
      num: 1,
      title: "Create Script",
      active: currentStep >= 1,
      completed: currentStep > 1,
      icon: FileText,
      desc: "AI will create professional content",
    },
    {
      num: 2,
      title: "Upload Video",
      active: currentStep >= 2,
      completed: currentStep > 2,
      icon: Upload,
      desc: "Add your footage",
    },
    {
      num: 3,
      title: "AI Voiceover",
      active: currentStep >= 3,
      completed: currentStep > 3,
      icon: Mic,
      desc: "High-quality voiceover",
    },
    {
      num: 4,
      title: "Editing",
      active: currentStep >= 4,
      completed: currentStep > 4,
      icon: Scissors,
      desc: "Professional video editing",
    },
  ];

  const contentTypes = [
    { id: "lifestyle", name: "Lifestyle", icon: Heart, color: "slate" },
    { id: "gaming", name: "Gaming", icon: Target, color: "blue" },
    { id: "tech", name: "Technology", icon: Zap, color: "indigo" },
    { id: "education", name: "Education", icon: Brain, color: "blue" },
    { id: "entertainment", name: "Entertainment", icon: Star, color: "slate" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 text-gray-900 flex flex-col">
      {/* Progress Steps */}
      <div className="bg-gradient-to-r from-slate-100/80 to-blue-100/80 backdrop-blur-lg border-b border-slate-200/50 px-6 py-8">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
              Professional Video Editor
            </h2>
            <p className="text-gray-600 font-medium">
              From idea to finished video with advanced editing tools
            </p>
          </div>
          <div className="flex items-center justify-between">
            {steps.map((step, index) => {
              const IconComponent = step.icon;
              return (
                <div key={step.num} className="flex items-center">
                  <div className="flex flex-col items-center text-center max-w-xs">
                    <div
                      className={`w-10 h-10 rounded-2xl flex items-center justify-center text-sm font-semibold transition-all duration-500 shadow-lg relative overflow-hidden ${
                        step.completed
                          ? "bg-gradient-to-br from-green-500 to-emerald-600 text-white shadow-green-500/30"
                          : step.active
                          ? "bg-gradient-to-br from-slate-600 to-blue-600 text-white shadow-slate-500/30"
                          : "bg-white/80 text-gray-400 shadow-gray-200/50"
                      }`}
                    >
                      {step.completed ? (
                        <div className="text-xl">✓</div>
                      ) : (
                        <IconComponent size={18} />
                      )}
                    </div>
                    <span
                      className={`mt-4 text-lg font-semibold transition-colors duration-300 ${
                        step.active ? "text-slate-700" : "text-gray-500"
                      }`}
                    >
                      {step.title}
                    </span>
                    <span className="text-xs text-gray-500 mt-1 font-medium">
                      {step.desc}
                    </span>
                  </div>
                  {index < steps.length - 1 && (
                    <div
                      className={`w-32 h-2 mx-8 rounded-full transition-all duration-500 ${
                        currentStep > step.num
                          ? "bg-gradient-to-r from-green-500 to-emerald-600"
                          : "bg-gray-200"
                      }`}
                    />
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <div className="flex flex-1 relative">
        {/* Left Panel */}
        <div
          className={`w-[${
            isLeftPanelCollapsed ? "100px" : "400px"
          }] min-h-full bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10`}
        >
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-slate-200/50 from-slate-50 to-blue-50">
            {!isLeftPanelCollapsed && (
              <div>
                <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                  Content Studio
                </h2>
                <p className="text-sm text-gray-600 font-medium">
                  Professional content creation tools
                </p>
              </div>
            )}
            <button
              onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
              className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300 group"
            >
              {isLeftPanelCollapsed ? (
                <ChevronRight
                  size={20}
                  className="text-gray-600 group-hover:text-slate-700"
                />
              ) : (
                <ChevronLeft
                  size={20}
                  className="text-gray-600 group-hover:text-slate-700"
                />
              )}
            </button>
          </div>

          {!isLeftPanelCollapsed && (
            <div className="p-6 overflow-y-auto flex-1 space-y-6">
              {/* Content Type Selection */}
              <div className="space-y-4">
                <label className="block text-gray-700 font-semibold text-lg">
                  Content type
                </label>
                <div className="grid grid-cols-2 gap-3">
                  {contentTypes.map((type) => {
                    const IconComponent = type.icon;
                    return (
                      <button
                        key={type.id}
                        onClick={() => setContentType(type.id)}
                        className={`p-4 rounded-xl border-2 transition-all duration-300 ${
                          contentType === type.id
                            ? type.color === "slate"
                              ? "border-slate-500 bg-slate-50 shadow-lg shadow-slate-500/30"
                              : type.color === "blue"
                              ? "border-blue-500 bg-blue-50 shadow-lg shadow-blue-500/30"
                              : "border-indigo-500 bg-indigo-50 shadow-lg shadow-indigo-500/30"
                            : "border-gray-200 bg-white hover:border-gray-300"
                        }`}
                      >
                        <IconComponent
                          size={24}
                          className={`mx-auto mb-2 ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "text-slate-600"
                                : type.color === "blue"
                                ? "text-blue-600"
                                : "text-indigo-600"
                              : "text-gray-400"
                          }`}
                        />
                        <span
                          className={`text-sm font-bold ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "text-slate-700"
                                : type.color === "blue"
                                ? "text-blue-700"
                                : "text-indigo-700"
                              : "text-gray-600"
                          }`}
                        >
                          {type.name}
                        </span>
                      </button>
                    );
                  })}
                </div>
              </div>

              {/* Topic Input */}
              <div className="space-y-3">
                <label className="block text-gray-700 font-semibold">
                  What is the video about?
                </label>
                <div className="relative group">
                  <input
                    type="text"
                    value={topic}
                    onChange={(e) => setTopic(e.target.value)}
                    placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                    className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
                  />
                  <div className="absolute right-4 top-4">
                    <Brain size={20} className="text-slate-400" />
                  </div>
                </div>
              </div>

              {/* Key Points */}
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <label className="block text-gray-700 font-semibold text-lg">
                      Key points
                    </label>
                    <p className="text-sm text-gray-500 font-medium">
                      What will engage viewers?
                    </p>
                  </div>
                  <button
                    onClick={addKeyPoint}
                    disabled={keyPoints.length >= 10}
                    className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-slate-500 to-blue-500 hover:from-slate-600 hover:to-blue-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                  >
                    <Plus size={16} className="mr-2" />
                    Add
                  </button>
                </div>
                <div className="space-y-3 max-h-80 overflow-y-auto">
                  {keyPoints.map((point, index) => (
                    <div
                      key={index}
                      className="flex items-center space-x-3 group"
                    >
                      <div className="w-8 h-8 bg-gradient-to-br from-slate-400 to-blue-400 rounded-lg flex items-center justify-center text-white font-semibold text-sm">
                        {index + 1}
                      </div>
                      <input
                        type="text"
                        value={point}
                        onChange={(e) => updateKeyPoint(index, e.target.value)}
                        placeholder="Enter key point..."
                        className="flex-1 p-3 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 hover:shadow-md font-medium"
                      />
                      {keyPoints.length > 1 && (
                        <button
                          onClick={() => removeKeyPoint(index)}
                          className="p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-xl transition-all duration-300 opacity-0 group-hover:opacity-100"
                        >
                          <X size={16} />
                        </button>
                      )}
                    </div>
                  ))}
                </div>
              </div>

              {/* Generate Button */}
              <button
                onClick={generateScript}
                disabled={!topic || isGeneratingScript}
                className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center shadow-lg hover:shadow-xl hover:scale-105 group overflow-hidden relative"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-slate-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                <span className="relative flex items-center">
                  {isGeneratingScript ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                      Generating script...
                    </>
                  ) : (
                    <>
                      <Wand2 className="mr-3" size={20} />
                      Generate Script
                    </>
                  )}
                </span>
              </button>

              {/* Script Editor */}
              {script && (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <label className="block text-gray-700 font-semibold text-lg">
                        Your script is ready ✨
                      </label>
                      <p className="text-sm text-gray-500">
                        Professional content for your video
                      </p>
                    </div>
                  </div>
                  <textarea
                    ref={scriptRef}
                    value={script}
                    onChange={(e) => setScript(e.target.value)}
                    rows={8}
                    className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 leading-relaxed transition-all duration-300 shadow-lg hover:shadow-xl resize-none font-medium"
                  />
                </div>
              )}
            </div>
          )}
        </div>

        <div className="w-2 bg-gradient-to-b from-transparent via-blue-400 to-transparent transition-all duration-300 relative group">
          <div className="absolute inset-y-0 -left-2 -right-2 bg-blue-400/10 transition-colors duration-300"></div>
        </div>

        {/* Right Panel - Video Editor */}
        <div className="flex-1 bg-gradient-to-br from-white/70 to-slate-50/70 backdrop-blur-xl flex flex-col">
          {/* Video Editor Header */}
          <div className="border-b border-slate-200/50 p-6">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                  Professional Video Editor
                </h2>
                <p className="text-sm text-gray-600 font-medium">
                  Cut, trim and arrange your video with precision
                </p>
              </div>
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setShowMediaLibrary(!showMediaLibrary)}
                  className={`flex items-center px-4 py-2 rounded-xl transition-all duration-300 font-semibold ${
                    showMediaLibrary
                      ? 'bg-blue-500 text-white shadow-lg'
                      : 'bg-white/80 text-slate-700 border border-slate-200 hover:bg-slate-50'
                  }`}
                >
                  <Folder size={16} className="mr-2" />
                  Media Library
                </button>
                <button className="flex items-center px-4 py-2 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl font-semibold">
                  <Download size={16} className="mr-2" />
                  Export
                </button>
              </div>
            </div>
          </div>

          <div className="flex flex-1">
            {/* Media Library Sidebar */}
            {showMediaLibrary && (
              <div className="w-80 bg-white/90 border-r border-slate-200/50 p-4">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="font-semibold text-slate-800">Media Library</h3>
                  <div className="flex space-x-2">
                    <input
                      type="file"
                      id="media-upload"
                      accept="video/*,audio/*,image/*"
                      onChange={handleMediaUpload}
                      className="hidden"
                      multiple
                    />
                    <button
                      onClick={() => document.getElementById('media-upload').click()}
                      className="p-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                      title="Add media files"
                    >
                      <Plus size={16} />
                    </button>
                  </div>
                </div>
                <div className="space-y-2 max-h-96 overflow-y-auto">
                  {mediaLibrary.map((item) => (
                    <MediaLibraryItem key={item.id} item={item} />
                  ))}
                  {mediaLibrary.length === 0 && (
                    <div className="text-center py-8 text-slate-500">
                      <Upload size={48} className="mx-auto mb-3 text-slate-300" />
                      <p className="text-sm">No media files yet</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Main Video Area */}
            <div className="flex-1 flex flex-col p-6">
              {/* Video Preview */}
              <div className="flex-1 mb-6">
                {!videoFile ? (
                  <div
                    className="h-full border-2 border-dashed border-slate-300/70 rounded-2xl flex flex-col items-center justify-center cursor-pointer hover:border-blue-400 transition-all duration-300 bg-white/60 backdrop-blur-sm hover:bg-white/80 group relative overflow-hidden"
                    onClick={() => fileInputRef.current?.click()}
                  >
                    <div className="absolute inset-0 bg-gradient-to-br from-slate-100/20 to-blue-100/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    <div className="relative z-10 text-center">
                      <div className="w-20 h-20 bg-gradient-to-br from-slate-200 to-blue-200 rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300 mx-auto shadow-lg">
                        <Upload size={25} className="text-slate-600" />
                      </div>
                      <h3 className="text-xl font-bold text-gray-700 mb-2">
                        Upload your video
                      </h3>
                      <p className="text-gray-500 mb-2 font-medium">
                        Drag file or click to select
                      </p>
                      <p className="text-sm text-gray-400">
                        MP4, AVI, MOV up to 2GB
                      </p>
                    </div>
                  </div>
                ) : (
                  <div className="h-full flex flex-col bg-white/70 backdrop-blur-sm rounded-2xl p-4 shadow-xl border border-slate-200/50">
                    <video
                      ref={videoRef}
                      src={videoUrl}
                      className="flex-1 w-full rounded-xl bg-black shadow-lg"
                      controls={false}
                      preload="metadata"
                      playsInline
                      muted={false}
                    />
                    
                    {/* Video Controls */}
                    <div className="flex items-center justify-center space-x-4 mt-4 p-4 bg-slate-50 rounded-xl">
                      <button
                        onClick={() => seekTo(Math.max(0, currentTime - 10))}
                        disabled={!videoLoaded}
                        className="p-2 bg-white rounded-lg hover:bg-slate-100 disabled:opacity-50 disabled:cursor-not-allowed transition-colors shadow-md"
                      >
                        <SkipBack size={16} />
                      </button>
                      <button
                        onClick={handlePlayPause}
                        disabled={!videoLoaded}
                        className="flex items-center px-6 py-3 bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl font-semibold"
                      >
                        {isPlaying ? <Pause size={18} /> : <Play size={18} />}
                        <span className="ml-2">{isPlaying ? "Pause" : "Play"}</span>
                      </button>
                      <button
                        onClick={() => seekTo(Math.min(duration, currentTime + 10))}
                        disabled={!videoLoaded}
                        className="p-2 bg-white rounded-lg hover:bg-slate-100 disabled:opacity-50 disabled:cursor-not-allowed transition-colors shadow-md"
                      >
                        <SkipForward size={16} />
                      </button>
                      <div className="text-sm font-mono text-slate-600">
                        {formatTime(currentTime)} / {formatTime(duration)}
                      </div>
                      <div className="text-xs text-slate-500 bg-white px-2 py-1 rounded">
                        {videoLoaded ? '✅ Ready' : '⏳ Loading...'}
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Video Editing Tools - Show when video is loaded */}
              {videoFile && (
                <div className="bg-white/90 backdrop-blur-xl rounded-2xl border border-slate-200/50 shadow-xl p-4">
                  {/* Editing Toolbar */}
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => setSelectedTool('select')}
                        className={`flex items-center px-3 py-2 rounded-lg transition-all duration-200 ${
                          selectedTool === 'select'
                            ? 'bg-blue-500 text-white shadow-lg'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Move size={16} className="mr-1" />
                        Select
                      </button>
                      <button
                        onClick={() => setSelectedTool('cut')}
                        className={`flex items-center px-3 py-2 rounded-lg transition-all duration-200 ${
                          selectedTool === 'cut'
                            ? 'bg-blue-500 text-white shadow-lg'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Scissors size={16} className="mr-1" />
                        Cut
                      </button>
                      <button
                        onClick={() => setSelectedTool('split')}
                        className={`flex items-center px-3 py-2 rounded-lg transition-all duration-200 ${
                          selectedTool === 'split'
                            ? 'bg-blue-500 text-white shadow-lg'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Split size={16} className="mr-1" />
                        Split
                      </button>
                    </div>

                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => setTimelineMode('video')}
                        className={`p-2 rounded-lg transition-all duration-200 ${
                          timelineMode === 'video'
                            ? 'bg-indigo-500 text-white'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Video size={16} />
                      </button>
                      <button
                        onClick={() => setTimelineMode('audio')}
                        className={`p-2 rounded-lg transition-all duration-200 ${
                          timelineMode === 'audio'
                            ? 'bg-green-500 text-white'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Music size={16} />
                      </button>
                      <button
                        onClick={() => setTimelineMode('both')}
                        className={`p-2 rounded-lg transition-all duration-200 ${
                          timelineMode === 'both'
                            ? 'bg-slate-500 text-white'
                            : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                        }`}
                      >
                        <Layers size={16} />
                      </button>
                    </div>
                  </div>

                  {/* Timeline */}
                  <div className="space-y-2">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-medium text-slate-700">Timeline</span>
                      <div className="flex items-center space-x-2">
                        <span className="text-xs text-slate-500">Zoom:</span>
                        <input
                          type="range"
                          min="0.5"
                          max="3"
                          step="0.1"
                          value={zoom}
                          onChange={(e) => setZoom(parseFloat(e.target.value))}
                          className="w-20"
                        />
                      </div>
                    </div>

                    {/* Timeline Container */}
                    <div 
                      ref={timelineRef}
                      className="relative bg-slate-50 border border-slate-200 rounded-lg p-2 min-h-32 cursor-pointer"
                      style={{ transform: `scaleX(${zoom})`, transformOrigin: 'left' }}
                      onClick={(e) => {
                        if (duration > 0) {
                          const rect = e.currentTarget.getBoundingClientRect();
                          const x = e.clientX - rect.left - 8;
                          const percentage = x / (rect.width - 16);
                          const time = Math.max(0, Math.min(percentage * duration, duration));
                          
                          if (selectedTool === 'cut' && !isCutting) {
                            startCut(time);
                          } else if (selectedTool === 'cut' && isCutting) {
                            endCut(time);
                          } else if (selectedTool === 'split' && selectedClip) {
                            splitClip(time);
                          } else {
                            seekTo(time);
                          }
                        }
                      }}
                    >
                      {/* Time Ruler */}
                      <div className="absolute top-0 left-2 right-2 h-6 border-b border-slate-300">
                        {Array.from({ length: Math.ceil(duration / 10) }, (_, i) => (
                          <div
                            key={i}
                            className="absolute text-xs text-slate-500"
                            style={{ left: `${(i * 10 / duration) * 100}%` }}
                          >
                            {formatTime(i * 10)}
                          </div>
                        ))}
                      </div>

                      {/* Playhead */}
                      <div
                        className="absolute top-6 bottom-2 w-0.5 bg-red-500 z-20 pointer-events-none"
                        style={{ left: `${(currentTime / duration) * 100}%` }}
                      >
                        <div className="absolute -top-2 -left-1 w-3 h-3 bg-red-500 rounded-full"></div>
                      </div>

                      {/* Timeline Tracks */}
                      <div className="mt-8 space-y-2">
                        {timelineMode === 'video' || timelineMode === 'both' ? (
                          <TimelineTrack track="video" trackIndex={0} trackType="video" />
                        ) : null}
                        {timelineMode === 'audio' || timelineMode === 'both' ? (
                          <TimelineTrack track="audio" trackIndex={1} trackType="audio" />
                        ) : null}
                      </div>

                      {/* Cut Range Indicator */}
                      {isCutting && cutStart !== null && (
                        <div
                          className="absolute top-6 bottom-2 bg-yellow-400/30 border-l-2 border-r-2 border-yellow-500 z-10"
                          style={{
                            left: `${(cutStart / duration) * 100}%`,
                            width: `${((currentTime - cutStart) / duration) * 100}%`
                          }}
                        />
                      )}
                    </div>

                    {/* Timeline Actions */}
                    {selectedClip && (
                      <div className="flex items-center justify-between p-3 bg-blue-50 border border-blue-200 rounded-lg">
                        <div className="flex-1">
                          <p className="font-medium text-blue-800">{selectedClip.name}</p>
                          <p className="text-sm text-blue-600">
                            {formatTime(selectedClip.startTime)} - {formatTime(selectedClip.endTime)}
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => duplicateClip(selectedClip)}
                            className="p-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
                            title="Duplicate"
                          >
                            <Copy size={14} />
                          </button>
                          <button
                            onClick={() => deleteClip(selectedClip.id)}
                            className="p-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
                            title="Delete"
                          >
                            <Trash2 size={14} />
                          </button>
                        </div>
                      </div>
                    )}

                    {/* Cut Controls */}
                    {isCutting && (
                      <div className="flex items-center justify-between p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                        <div>
                          <p className="font-medium text-yellow-800">Cutting Mode Active</p>
                          <p className="text-sm text-yellow-600">
                            Start: {cutStart ? formatTime(cutStart) : 'Not set'} | 
                            Current: {formatTime(currentTime)}
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => endCut(currentTime)}
                            disabled={cutStart === null}
                            className="px-3 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                          >
                            End Cut
                          </button>
                          <button
                            onClick={applyCut}
                            disabled={cutStart === null || cutEnd === null}
                            className="px-3 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                          >
                            Apply
                          </button>
                          <button
                            onClick={() => {
                              setIsCutting(false);
                              setCutStart(null);
                              setCutEnd(null);
                            }}
                            className="px-3 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    )}

                    {selectedTool === 'split' && selectedClip && (
                      <div className="text-center p-4 bg-purple-50 border border-purple-200 rounded-lg">
                        <p className="text-sm text-purple-600 mb-2">
                          Click on the timeline to split "{selectedClip.name}" at {formatTime(currentTime)}
                        </p>
                        <button
                          onClick={() => splitClip(currentTime)}
                          className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition-colors"
                        >
                          Split at {formatTime(currentTime)}
                        </button>
                      </div>
                    )}

                    {selectedTool === 'cut' && !isCutting && (
                      <div className="text-center p-4 bg-slate-50 border border-slate-200 rounded-lg">
                        <p className="text-sm text-slate-600 mb-2">Click on the timeline to start cutting</p>
                        <button
                          onClick={() => startCut(currentTime)}
                          className="px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 transition-colors"
                        >
                          Start Cut at {formatTime(currentTime)}
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept="video/*,audio/*,image/*"
        onChange={handleVideoUpload}
        className="hidden"
      />
    </div>
  );
};

export default VideoEditorApp;
