import React from 'react';
import { 
  TrendingUp, 
  Clock, 
  Users, 
  BookOpen, 
  Heart, 
  Eye, 
  Search, 
  Music, 
  RotateCcw,
  Target,
  AlertCircle,
  CheckCircle,
  Award
} from 'lucide-react';

const DetailedAnalytics = ({ assessmentLoading, assessment }) => {
  // Функция для определения цвета на основе балла
  const getScoreColor = (score) => {
    if (score >= 8) return 'text-green-600 bg-green-100';
    if (score >= 6) return 'text-yellow-600 bg-yellow-100';
    return 'text-red-600 bg-red-100';
  };

  // Функция для определения цвета прогресс-бара
  const getProgressColor = (score) => {
    if (score >= 8) return 'from-green-500 to-green-600';
    if (score >= 6) return 'from-yellow-500 to-yellow-600';
    return 'from-red-500 to-red-600';
  };

  // Компонент метрики
  const MetricItem = ({ icon: Icon, label, score, description }) => (
    <div className="bg-white rounded-lg p-4 border border-gray-200 hover:shadow-md transition-shadow">
      <div className="flex items-center justify-between mb-3">
        <div className="flex items-center space-x-2">
          <Icon size={16} className="text-gray-600" />
          <span className="text-sm font-medium text-gray-700">{label}</span>
        </div>
        <div className={`px-2 py-1 rounded-full text-xs font-bold ${getScoreColor(score)}`}>
          {score}/10
        </div>
      </div>
      
      {/* Прогресс бар */}
      <div className="w-full bg-gray-200 rounded-full h-2 mb-2">
        <div 
          className={`h-2 rounded-full bg-gradient-to-r ${getProgressColor(score)} transition-all duration-500`}
          style={{ width: `${score * 10}%` }}
        />
      </div>
      
      {description && (
        <p className="text-xs text-gray-500 mt-1">{description}</p>
      )}
    </div>
  );

  // Компонент общего балла
  const OverallScore = ({ score }) => {
    const percentage = Math.round(score * 10);
    const circumference = 2 * Math.PI * 45;
    const strokeDasharray = circumference;
    const strokeDashoffset = circumference - (percentage / 100) * circumference;
    
    return (
      <div className="bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl p-6 text-white relative overflow-hidden">
        <div className="absolute top-0 right-0 w-32 h-32 bg-white/10 rounded-full -mr-16 -mt-16" />
        
        <div className="flex items-center justify-between relative z-10">
          <div>
            <h3 className="text-lg font-bold mb-1">Overall Score</h3>
            <p className="text-indigo-200 text-sm">Content Quality Rating</p>
          </div>
          
          <div className="relative">
            <svg className="w-20 h-20 transform -rotate-90" viewBox="0 0 100 100">
              <circle
                cx="50"
                cy="50"
                r="45"
                stroke="rgba(255, 255, 255, 0.2)"
                strokeWidth="8"
                fill="none"
              />
              <circle
                cx="50"
                cy="50"
                r="45"
                stroke="white"
                strokeWidth="8"
                fill="none"
                strokeLinecap="round"
                strokeDasharray={strokeDasharray}
                strokeDashoffset={strokeDashoffset}
                className="transition-all duration-1000 ease-out"
              />
            </svg>
            <div className="absolute inset-0 flex items-center justify-center">
              <span className="text-2xl font-bold">{score}</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Компонент рекомендаций
  const RecommendationsSection = ({ recommendations }) => (
    <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-xl p-4">
      <div className="flex items-start space-x-3 mb-3">
        <AlertCircle size={20} className="text-blue-600 mt-0.5" />
        <h4 className="font-semibold text-gray-800">Recommendations</h4>
      </div>
      
      <div className="space-y-3">
        {recommendations?.map((recommendation, index) => (
          <div key={index} className="flex items-start space-x-3 bg-white rounded-lg p-3 border border-blue-100">
            <div className="flex-shrink-0 w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center mt-0.5">
              <span className="text-xs font-bold text-blue-600">{index + 1}</span>
            </div>
            <p className="text-sm text-gray-700 leading-relaxed">{recommendation}</p>
          </div>
        )) || (
          <p className="text-sm text-gray-500 italic">No recommendations available</p>
        )}
      </div>
    </div>
  );

  if (assessmentLoading) {
    return (
      <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border border-indigo-200 p-6 rounded-xl">
        <div className="flex items-center space-x-3 mb-6">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-indigo-600"></div>
          <h3 className="text-lg font-bold text-gray-800">Analyzing Content...</h3>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
          {[...Array(9)].map((_, index) => (
            <div key={index} className="bg-white rounded-lg p-4 border border-gray-200">
              <div className="flex items-center justify-between mb-3">
                <div className="h-4 bg-gray-200 rounded w-24 animate-pulse"></div>
                <div className="h-6 bg-gray-200 rounded-full w-12 animate-pulse"></div>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2 animate-pulse"></div>
            </div>
          ))}
        </div>
        
        <div className="h-32 bg-gray-200 rounded-xl animate-pulse"></div>
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="bg-gray-50 border border-gray-200 p-6 rounded-xl text-center">
        <AlertCircle size={24} className="text-gray-400 mx-auto mb-2" />
        <p className="text-gray-500">No analysis data available</p>
      </div>
    );
  }

  const metrics = [
    {
      icon: Target,
      label: "Structure",
      score: assessment.structure || 0,
      description: "Organization and flow of content"
    },
    {
      icon: Clock,
      label: "Duration",
      score: assessment.video_duration || 0,
      description: "Optimal video length for audience"
    },
    {
      icon: TrendingUp,
      label: "Engagement",
      score: assessment.engagement || 0,
      description: "Ability to capture audience attention"
    },
    {
      icon: BookOpen,
      label: "Readability",
      score: assessment.readability || 0,
      description: "Ease of understanding content"
    },
    {
      icon: Heart,
      label: "Emotional Tone",
      score: assessment.emotional_tone || 0,
      description: "Emotional connection with audience"
    },
    {
      icon: Eye,
      label: "Clarity",
      score: assessment.clarity || 0,
      description: "Clear and concise messaging"
    },
    {
      icon: Search,
      label: "SEO Optimization",
      score: assessment.seo_optimization || 0,
      description: "Search engine discoverability"
    },
    {
      icon: Music,
      label: "Rhythm",
      score: assessment.rhythm || 0,
      description: "Flow and pacing of content"
    },
    {
      icon: RotateCcw,
      label: "Repetition",
      score: assessment.repetition || 0,
      description: "Appropriate use of repetition"
    }
  ];

  return (
    <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border border-indigo-200 p-6 rounded-xl">
      {/* Header */}
      <div className="flex items-center space-x-3 mb-6">
        <div className="w-10 h-10 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-lg flex items-center justify-center">
          <Award className="text-white" size={20} />
        </div>
        <div>
          <h3 className="text-lg font-bold text-gray-800">Content Analysis</h3>
          <p className="text-sm text-gray-600">Detailed performance metrics and insights</p>
        </div>
      </div>

      {/* Overall Score */}
      <div className="mb-6">
        <OverallScore score={assessment.total_score || 0} />
      </div>

      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
        {metrics.map((metric, index) => (
          <MetricItem
            key={index}
            icon={metric.icon}
            label={metric.label}
            score={metric.score}
            description={metric.description}
          />
        ))}
      </div>

      {/* Performance Summary */}
      <div className="bg-white rounded-xl p-4 border border-gray-200 mb-6">
        <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
          <CheckCircle size={16} className="text-green-600 mr-2" />
          Performance Summary
        </h4>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-green-600">
              {metrics.filter(m => m.score >= 8).length}
            </div>
            <div className="text-xs text-gray-500">Excellent Areas</div>
          </div>
          
          <div className="text-center">
            <div className="text-2xl font-bold text-yellow-600">
              {metrics.filter(m => m.score >= 6 && m.score < 8).length}
            </div>
            <div className="text-xs text-gray-500">Good Areas</div>
          </div>
          
          <div className="text-center">
            <div className="text-2xl font-bold text-red-600">
              {metrics.filter(m => m.score < 6).length}
            </div>
            <div className="text-xs text-gray-500">Needs Improvement</div>
          </div>
        </div>
      </div>

      {/* Recommendations */}
      {assessment.recommendations && assessment.recommendations.length > 0 && (
        <RecommendationsSection recommendations={assessment.recommendations} />
      )}

      {/* Additional Insights */}
      <div className="mt-6 bg-white rounded-xl p-4 border border-gray-200">
        <h4 className="font-semibold text-gray-800 mb-3">Key Insights</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            <span className="text-gray-600">
              Strongest aspect: {metrics.reduce((max, m) => m.score > max.score ? m : max, metrics[0])?.label}
            </span>
          </div>
          
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-red-500 rounded-full"></div>
            <span className="text-gray-600">
              Needs attention: {metrics.reduce((min, m) => m.score < min.score ? m : min, metrics[0])?.label}
            </span>
          </div>
          
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
            <span className="text-gray-600">
              Average score: {(metrics.reduce((sum, m) => sum + m.score, 0) / metrics.length).toFixed(1)}/10
            </span>
          </div>
          
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-purple-500 rounded-full"></div>
            <span className="text-gray-600">
              Content grade: {assessment.total_score >= 8 ? 'A' : assessment.total_score >= 6 ? 'B' : 'C'}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailedAnalytics;
