import { useState, useRef, useEffect } from "react";
import { X, Download, Check, AlertCircle, Settings, Play, Film } from "lucide-react";

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  tracks,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720", // –£–º–µ–Ω—å—à–∞–µ–º —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
    fps: 15, // –£–º–µ–Ω—å—à–∞–µ–º FPS –¥–ª—è —É—Å–∫–æ—Ä–µ–Ω–∏—è
    bitrate: 2000, // –£–º–µ–Ω—å—à–∞–µ–º –±–∏—Ç—Ä–µ–π—Ç
    format: "mp4",
    quality: "medium",
    filename: `video_${Date.now()}`
  });

  const [ffmpeg, setFfmpeg] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [previewFrame, setPreviewFrame] = useState(null);
  const [error, setError] = useState(null);
  
  const canvasRef = useRef(null);
  const previewCanvasRef = useRef(null);

  // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω–∞—è –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg
  useEffect(() => {
    const initFFmpeg = async () => {
      if (ffmpeg || !isOpen) return;

      setIsLoading(true);
      setExportStage("loading");
      setError(null);
      
      try {
        console.log('üîÑ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg...');
        
        const ffmpegModule = await import('@ffmpeg/ffmpeg');
        const utilModule = await import('@ffmpeg/util');
        
        const FFmpegClass = ffmpegModule.FFmpeg;
        const fetchFile = utilModule.fetchFile;
        
        const ffmpegInstance = new FFmpegClass();
        
        await ffmpegInstance.load({
          coreURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js',
          wasmURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm',
        });

        ffmpegInstance.on('progress', ({ progress }) => {
          if (isExporting) {
            const adjustedProgress = 70 + Math.round(progress * 25);
            setExportProgress(Math.min(adjustedProgress, 95));
          }
        });

        window.fetchFile = fetchFile;
        setFfmpeg(ffmpegInstance);
        setExportStage("ready");
        console.log('‚úÖ FFmpeg –≥–æ—Ç–æ–≤');
        
      } catch (error) {
        console.error('‚ùå –û—à–∏–±–∫–∞ FFmpeg:', error);
        setError(error.message);
        setExportStage("error");
      } finally {
        setIsLoading(false);
      }
    };

    if (isOpen) {
      initFFmpeg();
    }
  }, [isOpen]);

  // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω–∞—è –ª–æ–≥–∏–∫–∞ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è –∞–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤
  const getActiveItemsAtTime = (currentTime) => {
    return timelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const endTime = startTime + (item.duration || 0);
      return currentTime >= startTime && currentTime < endTime;
    });
  };

  // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤
  const renderFrameAtTime = async (ctx, currentTime, width, height) => {
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    const activeItems = getActiveItemsAtTime(currentTime);

    if (activeItems.length === 0) {
      // –†–µ–Ω–¥–µ—Ä–∏–º —á–µ—Ä–Ω—ã–π —ç–∫—Ä–∞–Ω
      return;
    }

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Å–ª–æ—è–º
    const sortedItems = activeItems.sort((a, b) => {
      const layerOrder = { main: 1, overlay: 2, audio: 0 };
      return (layerOrder[a.trackType] || 1) - (layerOrder[b.trackType] || 1);
    });

    // –†–µ–Ω–¥–µ—Ä–∏–º –∫–∞–∂–¥—ã–π —ç–ª–µ–º–µ–Ω—Ç
    for (const item of sortedItems) {
      if (item.trackType === 'audio') continue;

      try {
        const element = await loadMediaElement(item);
        if (element) {
          const relativeTime = currentTime - (item.startTime || 0);
          await drawElementOnCanvas(ctx, element, item, relativeTime, width, height);
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ ${item.name}:`, error);
      }
    }
  };

  const loadMediaElement = async (item) => {
    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error(`Timeout loading ${item.name}`));
      }, 5000); // –£–º–µ–Ω—å—à–∞–µ–º timeout

      if (item.type === 'videos' || item.trackType === 'main') {
        const video = document.createElement('video');
        video.crossOrigin = 'anonymous';
        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          resolve(video);
        };
        
        video.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load video: ${item.name}`));
        };
        
        video.src = item.url;
        
      } else if (item.type === 'images' || item.trackType === 'overlay') {
        const img = new Image();
        img.crossOrigin = 'anonymous';
        
        img.onload = () => {
          clearTimeout(timeout);
          resolve(img);
        };
        
        img.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load image: ${item.name}`));
        };
        
        img.src = item.url;
        
      } else {
        clearTimeout(timeout);
        resolve(null);
      }
    });
  };

  const drawElementOnCanvas = async (ctx, element, item, relativeTime, canvasWidth, canvasHeight) => {
    try {
      // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤—Ä–µ–º—è –¥–ª—è –≤–∏–¥–µ–æ
      if (element.tagName === 'VIDEO') {
        const targetTime = Math.max(0, Math.min(relativeTime, element.duration || 0));
        if (Math.abs(element.currentTime - targetTime) > 0.5) {
          element.currentTime = targetTime;
          await new Promise(resolve => setTimeout(resolve, 50)); // –£–º–µ–Ω—å—à–∞–µ–º –ø–∞—É–∑—É
        }
      }

      const elementWidth = element.videoWidth || element.naturalWidth || element.width;
      const elementHeight = element.videoHeight || element.naturalHeight || element.height;

      if (!elementWidth || !elementHeight) {
        console.warn(`‚ö†Ô∏è –≠–ª–µ–º–µ–Ω—Ç ${item.name} –Ω–µ –∏–º–µ–µ—Ç —Ä–∞–∑–º–µ—Ä–æ–≤`);
        return;
      }

      let drawWidth, drawHeight, drawX, drawY;

      if (item.trackType === 'main') {
        // –û—Å–Ω–æ–≤–Ω–æ–µ –≤–∏–¥–µ–æ - –∑–∞–ø–æ–ª–Ω—è–µ—Ç –≤–µ—Å—å —ç–∫—Ä–∞–Ω
        const canvasRatio = canvasWidth / canvasHeight;
        const elementRatio = elementWidth / elementHeight;

        if (elementRatio > canvasRatio) {
          drawWidth = canvasWidth;
          drawHeight = canvasWidth / elementRatio;
          drawX = 0;
          drawY = (canvasHeight - drawHeight) / 2;
        } else {
          drawHeight = canvasHeight;
          drawWidth = canvasHeight * elementRatio;
          drawX = (canvasWidth - drawWidth) / 2;
          drawY = 0;
        }
      } else {
        // Overlay —ç–ª–µ–º–µ–Ω—Ç—ã
        if (item.x !== undefined && item.y !== undefined) {
          drawX = item.x;
          drawY = item.y;
          drawWidth = item.width || 200;
          drawHeight = item.height || 150;
        } else {
          const overlaySize = Math.min(canvasWidth, canvasHeight) * 0.2;
          const elementRatio = elementWidth / elementHeight;
          
          if (elementRatio > 1) {
            drawWidth = overlaySize;
            drawHeight = overlaySize / elementRatio;
          } else {
            drawHeight = overlaySize;
            drawWidth = overlaySize * elementRatio;
          }
          
          drawX = canvasWidth - drawWidth - 20;
          drawY = 20;
        }
      }

      const opacity = item.opacity !== undefined ? item.opacity : 1;
      ctx.globalAlpha = opacity;
      ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
      ctx.globalAlpha = 1;
      
    } catch (error) {
      console.error(`‚ùå –û—à–∏–±–∫–∞ –æ—Ç—Ä–∏—Å–æ–≤–∫–∏ ${item.name}:`, error);
    }
  };

  // –°–æ–∑–¥–∞–Ω–∏–µ –ø—Ä–µ–≤—å—é
  useEffect(() => {
    if (isOpen && timelineItems.length > 0 && exportStage === "ready") {
      generatePreviewFrame();
    }
  }, [isOpen, timelineItems, exportSettings.resolution, exportStage]);

  const generatePreviewFrame = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const previewTime = videoDuration / 2;
      await renderFrameAtTime(ctx, previewTime, width, height);
      
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setPreviewFrame(url);
        }
      });
    } catch (error) {
      console.warn('‚ö†Ô∏è –û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è –ø—Ä–µ–≤—å—é:', error);
    }
  };

  // –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô —ç–∫—Å–ø–æ—Ä—Ç
  const startExport = async () => {
    if (!ffmpeg || isExporting || timelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("preparing");
    setError(null);

    try {
      console.log('üé¨ –ù–∞—á–∏–Ω–∞–µ–º –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç');
      
      // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∑–∞–≤–∏—Å–∞–Ω–∏—è
      const maxDuration = Math.min(videoDuration, 120); // –ú–∞–∫—Å–∏–º—É–º 2 –º–∏–Ω—É—Ç—ã
      const actualDuration = maxDuration;
      
      console.log(`üìä –î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: ${actualDuration}s`);
      console.log(`üìä –≠–ª–µ–º–µ–Ω—Ç–æ–≤ timeline: ${timelineItems.length}`);

      setExportStage("loading_media");
      const mediaElements = await prepareMediaElements();
      setExportProgress(15);

      setExportStage("rendering_frames");
      const frames = await renderAllFrames(mediaElements, actualDuration);
      setExportProgress(50);

      setExportStage("processing_audio");
      const audioFiles = await prepareAudioFiles();
      setExportProgress(60);

      setExportStage("encoding");
      const videoBlob = await encodeWithFFmpeg(frames, audioFiles, actualDuration);
      setExportProgress(95);

      setExportStage("downloading");
      await downloadVideo(videoBlob);
      setExportProgress(100);

      setExportStage("completed");
      setTimeout(() => {
        setExportStage("ready");
        setExportProgress(0);
      }, 3000);

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞:', error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  const prepareMediaElements = async () => {
    const elements = new Map();
    const videoElements = timelineItems.filter(item => item.trackType !== 'audio');
    
    console.log(`üì¶ –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º ${videoElements.length} –º–µ–¥–∏–∞ —ç–ª–µ–º–µ–Ω—Ç–æ–≤...`);
    
    for (const item of videoElements) {
      try {
        const element = await loadMediaElement(item);
        if (element) {
          elements.set(item.id, element);
          console.log(`‚úÖ –ó–∞–≥—Ä—É–∂–µ–Ω: ${item.name}`);
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è –ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å ${item.name}:`, error);
      }
    }

    return elements;
  };

  // –ö–†–ò–¢–ò–ß–ï–°–ö–ò –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤
  const renderAllFrames = async (mediaElements, duration) => {
    const canvas = canvasRef.current;
    if (!canvas) throw new Error('Canvas –Ω–µ –Ω–∞–π–¥–µ–Ω');

    const ctx = canvas.getContext('2d');
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    canvas.width = width;
    canvas.height = height;

    // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ü—Ä–∞–≤–∏–ª—å–Ω—ã–π —Ä–∞—Å—á–µ—Ç –∫–∞–¥—Ä–æ–≤
    const totalFrames = Math.ceil(duration * exportSettings.fps);
    const frames = [];

    console.log(`üéûÔ∏è –†–µ–Ω–¥–µ—Ä–∏–º ${totalFrames} –∫–∞–¥—Ä–æ–≤ (${exportSettings.fps} FPS –¥–ª—è ${duration}s)`);

    // –í–ê–ñ–ù–û: –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞–¥—Ä–æ–≤ –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∑–∞–≤–∏—Å–∞–Ω–∏—è
    const maxFrames = Math.min(totalFrames, 1200); // –ú–∞–∫—Å–∏–º—É–º 1200 –∫–∞–¥—Ä–æ–≤
    
    for (let frame = 0; frame < maxFrames; frame++) {
      const currentTime = (frame / exportSettings.fps);
      
      // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º—Å—è –µ—Å–ª–∏ –ø—Ä–µ–≤—ã—à–∞–µ–º –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å
      if (currentTime >= duration) break;
      
      try {
        await renderFrameAtTime(ctx, currentTime, width, height);

        // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –£–º–µ–Ω—å—à–∞–µ–º –∫–∞—á–µ—Å—Ç–≤–æ –¥–ª—è —É—Å–∫–æ—Ä–µ–Ω–∏—è
        const blob = await new Promise(resolve => {
          canvas.toBlob(resolve, 'image/jpeg', 0.7); // –°–Ω–∏–∂–∞–µ–º –∫–∞—á–µ—Å—Ç–≤–æ
        });
        
        if (blob) {
          frames.push(blob);
        }

        const progress = 15 + Math.round((frame / maxFrames) * 35);
        setExportProgress(progress);

        // –£–º–µ–Ω—å—à–∞–µ–º —á–∞—Å—Ç–æ—Ç—É –ø–∞—É–∑
        if (frame % 100 === 0) {
          await new Promise(resolve => setTimeout(resolve, 1));
        }
        
      } catch (error) {
        console.error(`‚ùå –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∫–∞–¥—Ä–∞ ${frame}:`, error);
        break; // –ü—Ä–µ—Ä—ã–≤–∞–µ–º –ø—Ä–∏ –æ—à–∏–±–∫–µ
      }
    }

    console.log(`‚úÖ –û—Ç—Ä–µ–Ω–¥–µ—Ä–µ–Ω–æ ${frames.length} –∫–∞–¥—Ä–æ–≤`);
    return frames;
  };

  const prepareAudioFiles = async () => {
    const audioItems = timelineItems.filter(item => item.trackType === 'audio');
    const audioFiles = [];

    console.log(`üîä –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º ${audioItems.length} –∞—É–¥–∏–æ —Ñ–∞–π–ª–æ–≤...`);

    for (let i = 0; i < Math.min(audioItems.length, 3); i++) { // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –¥–æ 3 –∞—É–¥–∏–æ —Ñ–∞–π–ª–æ–≤
      const item = audioItems[i];
      try {
        console.log(`üéµ –ó–∞–≥—Ä—É–∂–∞–µ–º –∞—É–¥–∏–æ: ${item.name}`);
        
        const response = await fetch(item.url);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        
        const blob = await response.blob();
        audioFiles.push({
          blob,
          startTime: item.startTime || 0,
          duration: item.duration || videoDuration,
          name: item.name,
          index: i
        });
        
        console.log(`‚úÖ –ê—É–¥–∏–æ –≥–æ—Ç–æ–≤–æ: ${item.name}`);
        
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—É–¥–∏–æ ${item.name}:`, error);
      }
    }

    return audioFiles;
  };

  // –ö–†–ò–¢–ò–ß–ï–°–ö–ò –ò–°–ü–†–ê–í–õ–ï–ù–ù–û–ï –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ
  const encodeWithFFmpeg = async (frames, audioFiles, duration) => {
    if (!window.fetchFile || frames.length === 0) {
      throw new Error('–ù–µ—Ç –∫–∞–¥—Ä–æ–≤ –¥–ª—è –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è');
    }

    console.log(`üì¶ –ö–æ–¥–∏—Ä—É–µ–º ${frames.length} –∫–∞–¥—Ä–æ–≤ –∏ ${audioFiles.length} –∞—É–¥–∏–æ`);
    
    try {
      // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–µ —Ñ–∞–π–ª—ã
      await cleanupTempFiles(2000, 5);

      // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∫–∞–¥—Ä—ã
      for (let i = 0; i < frames.length; i++) {
        const filename = `frame_${i.toString().padStart(6, '0')}.jpg`;
        await ffmpeg.writeFile(filename, await window.fetchFile(frames[i]));
        
        if (i % 50 === 0) {
          const progress = 60 + Math.round((i / frames.length) * 10);
          setExportProgress(progress);
        }
      }

      // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∞—É–¥–∏–æ —Ñ–∞–π–ª—ã
      for (let i = 0; i < audioFiles.length; i++) {
        const audioFile = audioFiles[i];
        const audioFileName = `audio_${i}.mp3`;
        await ffmpeg.writeFile(audioFileName, await window.fetchFile(audioFile.blob));
        console.log(`üìÅ –ó–∞–ø–∏—Å–∞–Ω –∞—É–¥–∏–æ: ${audioFileName}`);
      }

      setExportProgress(70);

      // –ò–°–ü–†–ê–í–õ–ï–ù–ù–ê–Ø –∫–æ–º–∞–Ω–¥–∞ FFmpeg
      const args = [
        '-framerate', exportSettings.fps.toString(),
        '-i', 'frame_%06d.jpg',
        '-t', Math.min(duration, frames.length / exportSettings.fps).toString(), // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –¢–æ—á–Ω–∞—è –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å
      ];

      // –î–æ–±–∞–≤–ª—è–µ–º –∞—É–¥–∏–æ –µ—Å–ª–∏ –µ—Å—Ç—å
      if (audioFiles.length > 0) {
        args.push('-i', 'audio_0.mp3');
        
        if (audioFiles.length > 1) {
          for (let i = 1; i < audioFiles.length; i++) {
            args.push('-i', `audio_${i}.mp3`);
          }
          
          const filterComplex = audioFiles.map((_, i) => `[${i + 1}:a]`).join('') + 
            `amix=inputs=${audioFiles.length}:duration=shortest:dropout_transition=0.5[aout]`;
          
          args.push('-filter_complex', filterComplex);
          args.push('-map', '0:v');
          args.push('-map', '[aout]');
        } else {
          args.push('-c:a', 'aac');
          args.push('-b:a', '96k'); // –£–º–µ–Ω—å—à–∞–µ–º –±–∏—Ç—Ä–µ–π—Ç –∞—É–¥–∏–æ
        }
        
        args.push('-shortest');
      }

      // –û–ü–¢–ò–ú–ò–ó–ò–†–û–í–ê–ù–ù–´–ï –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –≤–∏–¥–µ–æ
      args.push(
        '-c:v', 'libx264',
        '-preset', 'ultrafast', // –°–∞–º—ã–π –±—ã—Å—Ç—Ä—ã–π –ø—Ä–µ—Å–µ—Ç
        '-crf', '28', // –ò—Å–ø–æ–ª—å–∑—É–µ–º CRF –≤–º–µ—Å—Ç–æ –±–∏—Ç—Ä–µ–π—Ç–∞ –¥–ª—è —Å–∫–æ—Ä–æ—Å—Ç–∏
        '-pix_fmt', 'yuv420p',
        '-r', exportSettings.fps.toString(),
        '-movflags', '+faststart',
        '-threads', '4', // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –ø–æ—Ç–æ–∫–∏
        '-g', (exportSettings.fps * 2).toString(), // Keyframe interval
        'output.mp4'
      );

      console.log('üîß –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–∞—è FFmpeg –∫–æ–º–∞–Ω–¥–∞:', args.join(' '));

      // –î–æ–±–∞–≤–ª—è–µ–º —Ç–∞–π–º–∞—É—Ç –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∑–∞–≤–∏—Å–∞–Ω–∏—è
      const timeoutPromise = new Promise((_, reject) => {
        setTimeout(() => reject(new Error('FFmpeg timeout –ø–æ—Å–ª–µ 300 —Å–µ–∫—É–Ω–¥')), 300000);
      });

      const execPromise = ffmpeg.exec(args);
      
      await Promise.race([execPromise, timeoutPromise]);
      
      setExportProgress(90);

      const data = await ffmpeg.readFile('output.mp4');
      const videoBlob = new Blob([data.buffer], { type: 'video/mp4' });

      console.log(`üéâ –í–∏–¥–µ–æ —Å–æ–∑–¥–∞–Ω–æ: ${(videoBlob.size / 1024 / 1024).toFixed(2)} –ú–ë`);

      // –û—á–∏—â–∞–µ–º —Ñ–∞–π–ª—ã
      await cleanupTempFiles(frames.length, audioFiles.length);
      return videoBlob;

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è:', error);
      
      // –ü—ã—Ç–∞–µ–º—Å—è –æ—á–∏—Å—Ç–∏—Ç—å —Ñ–∞–π–ª—ã –ø—Ä–∏ –æ—à–∏–±–∫–µ
      try {
        await cleanupTempFiles(frames.length, audioFiles.length);
      } catch (e) {}
      
      throw new Error(`–û—à–∏–±–∫–∞ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è: ${error.message}`);
    }
  };

  const cleanupTempFiles = async (frameCount, audioCount) => {
    console.log('üßπ –û—á–∏—Å—Ç–∫–∞ —Ñ–∞–π–ª–æ–≤...');
    
    for (let i = 0; i < frameCount; i++) {
      try {
        await ffmpeg.deleteFile(`frame_${i.toString().padStart(6, '0')}.jpg`);
      } catch (e) {}
    }

    for (let i = 0; i < audioCount; i++) {
      try {
        await ffmpeg.deleteFile(`audio_${i}.mp3`);
      } catch (e) {}
    }

    try {
      await ffmpeg.deleteFile('output.mp4');
    } catch (e) {}
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    
    console.log(`üíæ –§–∞–π–ª —Å–æ—Ö—Ä–∞–Ω–µ–Ω: ${filename}`);
  };

  const getStageText = () => {
    switch (exportStage) {
      case "loading": return "–ó–∞–≥—Ä—É–∑–∫–∞ FFmpeg...";
      case "preparing": return "–ê–Ω–∞–ª–∏–∑ timeline...";
      case "loading_media": return "–ó–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞ —Ñ–∞–π–ª–æ–≤...";
      case "rendering_frames": return `–†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤... (–º–∞–∫—Å. 1200 –∫–∞–¥—Ä–æ–≤)`;
      case "processing_audio": return "–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤...";
      case "encoding": return "–ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ (ultrafast preset)...";
      case "downloading": return "–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ñ–∞–π–ª–∞...";
      case "completed": return "–≠–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω —É—Å–ø–µ—à–Ω–æ!";
      case "error": return "–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞";
      case "ready": return `–ì–æ—Ç–æ–≤ –∫ —ç–∫—Å–ø–æ—Ä—Ç—É (${timelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤)`;
      default: return "–û–∂–∏–¥–∞–Ω–∏–µ...";
    }
  };

  const getStageIcon = () => {
    switch (exportStage) {
      case "completed": return <Check size={20} className="text-green-600" />;
      case "error": return <AlertCircle size={20} className="text-red-600" />;
      case "ready": return <Play size={20} className="text-blue-600" />;
      case "loading": return <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-orange-600"></div>;
      default: return <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>;
    }
  };

  const canExport = ffmpeg && !isExporting && !isLoading && timelineItems.length > 0 && exportStage === "ready";

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-4xl w-full max-h-[95vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Film size={24} className="mr-3 text-blue-600" />
            –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç (–∏—Å–ø—Ä–∞–≤–ª–µ–Ω)
          </h2>
          <button
            onClick={onClose}
