import { Routes, Route } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import ScriptifyLanding from "../pages/ScriptifyLanding";
import AuthPage from "../Auth/AuthPage";
import HelpCenter from "../pages/HelpCenter";
import AboutUs from "../pages/AboutUs";
import ProtectedRoute from "../Auth/ProtectedRoute";
import Dashboard from "../pages/Dashboard";
import VideoEditorAppPage from "../pages/VideoEditorAppPage";
import LoadingScreen from "../common/LoadingScreen";
import { ROUTES } from "../constants/routes";

const AppRouter = () => {
  const { loading } = useAuth();

  if (loading) {
    return <LoadingScreen />;
  }

  return (
    <Routes>
      <Route path={ROUTES.HOME} element={<ScriptifyLanding />} />
      <Route path={ROUTES.AUTH} element={<AuthPage />} />
      <Route path={ROUTES.HELP_CENTER} element={<HelpCenter />} />
      <Route path={ROUTES.ABOUT_US} element={<AboutUs />} />

      <Route
        path={ROUTES.APP}
        element={
          <ProtectedRoute>
            <Dashboard />
          </ProtectedRoute>
        }
      />

      <Route
        path={ROUTES.VIDEO_MAKER}
        element={
          <ProtectedRoute>
            <VideoEditorAppPage />
          </ProtectedRoute>
        }
      />

      <Route
        path={ROUTES.PROFILE}
        element={
          <ProtectedRoute>
            <div>Profile Page - Coming Soon</div>
          </ProtectedRoute>
        }
      />

      <Route
        path={ROUTES.SETTINGS}
        element={
          <ProtectedRoute>
            <div>Settings Page - Coming Soon</div>
          </ProtectedRoute>
        }
      />

      <Route
        path={ROUTES.UPGRADE}
        element={
          <ProtectedRoute>
            <div>Upgrade Page - Coming Soon</div>
          </ProtectedRoute>
        }
      />

      <Route
        path={ROUTES.FEATURES}
        element={<div>Features Page - Coming Soon</div>}
      />
      <Route
        path={ROUTES.PRICING}
        element={<div>Pricing Page - Coming Soon</div>}
      />
      <Route
        path={ROUTES.TERMS_OF_USE}
        element={<div>Terms of Use - Coming Soon</div>}
      />
      <Route
        path={ROUTES.PRIVACY_POLICY}
        element={<div>Privacy Policy - Coming Soon</div>}
      />

      <Route path={ROUTES.NOT_FOUND} element={<ScriptifyLanding />} />
    </Routes>
  );
};

export default AppRouter;
