import { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const apiCall = async (endpoint, options = {}) => {
    const url = `${API_BASE_URL}${endpoint}`;
    const token = localStorage.getItem("authToken");

    const config = {
      headers: {
        "Content-Type": "application/json",
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };

    try {
      const response = await fetch(url, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Something went wrong");
      }

      return data;
    } catch (error) {
      console.error("API call error:", error);
      throw error;
    }
  };

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem("authToken");

      if (!token) {
        setLoading(false);
        return;
      }

      try {
        const response = await apiCall("/api/auth/me");

        if (response.success) {
          setUser(response.data.user);
          setIsAuthenticated(true);
          // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ userData
          localStorage.setItem("userData", JSON.stringify(response.data.user));
        } else {
          localStorage.removeItem("authToken");
          localStorage.removeItem("userData"); // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
        }
      } catch (error) {
        console.error("Auth check failed:", error);
        localStorage.removeItem("authToken");
        localStorage.removeItem("userData"); // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  const register = async (name, email, password) => {
    try {
      setLoading(true);

      const response = await apiCall("/api/auth/register", {
        method: "POST",
        body: JSON.stringify({ name, email, password }),
      });

      if (response.success) {
        const { user: userData, token } = response.data;

        localStorage.setItem("authToken", token); // âœ… Ð˜Ð·Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ
        localStorage.setItem("userData", JSON.stringify(userData)); // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
        setUser(userData);
        setIsAuthenticated(true);

        return { success: true, user: userData };
      }

      return { success: false, error: response.error };
    } catch (error) {
      console.error("Registration error:", error);
      return {
        success: false,
        error: error.message || "Registration failed. Please try again.",
      };
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      setLoading(true);

      const response = await apiCall("/api/auth/login", {
        method: "POST",
        body: JSON.stringify({ email, password }),
      });

      if (response.success) {
        const { user: userData, token } = response.data;

        localStorage.setItem("authToken", token); // âœ… Ð˜Ð·Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ
        localStorage.setItem("userData", JSON.stringify(userData)); // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
        setUser(userData);
        setIsAuthenticated(true);

        return { success: true, user: userData };
      }

      return { success: false, error: response.error };
    } catch (error) {
      console.error("Login error:", error);
      return {
        success: false,
        error: error.message || "Login failed. Please try again.",
      };
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      await apiCall("/api/auth/logout", { method: "POST" });
    } catch (error) {
      console.error("Logout error:", error);
    } finally {
      localStorage.removeItem("authToken"); // âœ… Ð˜Ð·Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ
      localStorage.removeItem("userData"); // âœ… Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ
      setUser(null);
      setIsAuthenticated(false);
    }
  };
  const generateScript = async (
    topic,
    duration = "medium",
    keyPoints = [],
    contentType = "Ð›Ð°Ð¹Ñ„ÑÑ‚Ð°Ð¹Ð»"
  ) => {
    try {
      const response = await apiCall("/api/script/generate", {
        method: "POST",
        body: JSON.stringify({ topic, duration, keyPoints, contentType }),
      });

      if (response.success) {
        return { success: true, script: response.data.script };
      }

      return { success: false, error: response.error };
    } catch (error) {
      console.error("Script generation error:", error);
      return {
        success: false,
        error: error.message || "Script generation failed. Please try again.",
      };
    }
  };

  const generateKeyPoints = async (topic, contentType = "Ð›Ð°Ð¹Ñ„ÑÑ‚Ð°Ð¹Ð»") => {
    try {
      const response = await apiCall("/api/script/key-points", {
        method: "POST",
        body: JSON.stringify({ topic, contentType }),
      });

