import React, { useState, useRef, useEffect } from 'react';
import { X, Download, Check, AlertCircle, Zap, Clock } from 'lucide-react';

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  previewCanvasRef: editorCanvasRef, // Canvas редактора для правильных координат
  timelineData,
  elements,
  clips,
  mediaItems,
}) => {
  // Получаем данные timeline
  const actualTimelineItems = React.useMemo(() => {
    const items = timelineItems || timelineData || elements || clips || mediaItems || [];
    console.log('📋 Timeline элементы:', items.length);
    return items;
  }, [timelineItems, timelineData, elements, clips, mediaItems]);

  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 30,
    quality: "high",
    filename: `capcut_export_${Date.now()}`
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("ready");
  const [error, setError] = useState(null);
  const [previewFrame, setPreviewFrame] = useState(null);
  const [isLoadingMedia, setIsLoadingMedia] = useState(false);
  const [loadingProgress, setLoadingProgress] = useState(0);
  const [loadedMedia, setLoadedMedia] = useState(new Map());
  const [previewVideo, setPreviewVideo] = useState(null);

  const exportCanvasRef = useRef(null);
  const previewCanvasRef = useRef(null);

  // Рассчет длительности timeline
  const calculateDuration = () => {
    if (actualTimelineItems.length === 0) return videoDuration;
    
    const maxEndTime = Math.max(...actualTimelineItems.map(item => {
      const start = item.startTime || 0;
      const duration = item.duration || 0;
      return start + duration;
    }));
    
    return Math.max(maxEndTime, videoDuration);
  };

  // ПРАВИЛЬНОЕ позиционирование - получаем размеры из редактора
  const getCorrectCoordinates = (item, targetWidth, targetHeight) => {
    let editorWidth = 1920;
    let editorHeight = 1080;

    // Получаем реальные размеры canvas редактора
    if (editorCanvasRef?.current) {
      editorWidth = editorCanvasRef.current.width || editorCanvasRef.current.offsetWidth;
      editorHeight = editorCanvasRef.current.height || editorCanvasRef.current.offsetHeight;
    }

    // Масштабируем координаты правильно
    const scaleX = targetWidth / editorWidth;
    const scaleY = targetHeight / editorHeight;

    const x = (item.x || 0) * scaleX;
    const y = (item.y || 0) * scaleY;
    const width = (item.width || 200) * scaleX * (item.scale || 1);
    const height = (item.height || 150) * scaleY * (item.scale || 1);

    return { x, y, width, height };
  };

  // Получение активных элементов
  const getActiveElements = (currentTime) => {
    return actualTimelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || 0;
      const endTime = startTime + duration;
      return currentTime >= startTime && currentTime < endTime;
    }).sort((a, b) => {
      const layerOrder = { main: 1, overlay: 2, audio: 3 };
      return (layerOrder[a.trackType] || 1) - (layerOrder[b.trackType] || 1);
    });
  };

  // БЫСТРЫЙ рендеринг кадра
  const renderFrame = async (ctx, currentTime, width, height) => {
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    const activeElements = getActiveElements(currentTime);
    
    for (const element of activeElements) {
      if (element.trackType === 'audio') continue;
      
      try {
        await renderElement(ctx, element, currentTime, width, height);
      } catch (error) {
        console.warn(`⚠️ Ошибка рендеринга ${element.name}:`, error);
      }
    }
  };

  // Рендеринг одного элемента
  const renderElement = async (ctx, item, currentTime, canvasWidth, canvasHeight) => {
    const mediaElement = await loadMediaElement(item);
    if (!mediaElement) return;

    const elementStartTime = item.startTime || 0;
    const relativeTime = currentTime - elementStartTime;

    // Настройка видео
    if (mediaElement.tagName === 'VIDEO') {
      const trimStart = item.trimStart || 0;
      const targetTime = trimStart + relativeTime;
      
      if (Math.abs(mediaElement.currentTime - targetTime) > 0.1) {
        mediaElement.currentTime = Math.max(0, Math.min(targetTime, mediaElement.duration || 0));
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    }

    const mediaWidth = mediaElement.videoWidth || mediaElement.naturalWidth || mediaElement.width;
    const mediaHeight = mediaElement.videoHeight || mediaElement.naturalHeight || mediaElement.height;

    if (!mediaWidth || !mediaHeight) return;

    let drawX, drawY, drawWidth, drawHeight;

    if (item.trackType === 'main') {
      // Основное видео - заполняет экран
      const canvasRatio = canvasWidth / canvasHeight;
      const mediaRatio = mediaWidth / mediaHeight;

      if (mediaRatio > canvasRatio) {
        drawWidth = canvasWidth;
        drawHeight = canvasWidth / mediaRatio;
        drawX = 0;
        drawY = (canvasHeight - drawHeight) / 2;
      } else {
        drawHeight = canvasHeight;
        drawWidth = canvasHeight * mediaRatio;
        drawX = (canvasWidth - drawWidth) / 2;
        drawY = 0;
      }
    } else if (item.trackType === 'overlay') {
      // ИСПРАВЛЕННОЕ позиционирование overlay
      const coords = getCorrectCoordinates(item, canvasWidth, canvasHeight);
      drawX = coords.x;
      drawY = coords.y;
      drawWidth = coords.width;
      drawHeight = coords.height;
    }

    // Трансформации
    ctx.save();

    if (item.rotation && item.rotation !== 0) {
      const centerX = drawX + drawWidth / 2;
      const centerY = drawY + drawHeight / 2;
      ctx.translate(centerX, centerY);
      ctx.rotate((item.rotation * Math.PI) / 180);
      ctx.translate(-centerX, -centerY);
    }

    ctx.globalAlpha = item.opacity !== undefined ? item.opacity : 1;
    ctx.drawImage(mediaElement, drawX, drawY, drawWidth, drawHeight);
    ctx.restore();
  };

  // Быстрая загрузка медиа с кешированием
  const loadMediaElement = async (item) => {
    if (!item.url) return null;

    // Проверяем кеш
    if (loadedMedia.has(item.url)) {
      return loadedMedia.get(item.url);
    }

    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => reject(new Error(`Timeout ${item.name}`)), 5000);

      const isVideo = item.type === 'videos' || 
                     item.trackType === 'main' || 
                     item.name?.toLowerCase().match(/\.(mp4|webm|mov)$/);

      if (isVideo) {
        const video = document.createElement('video');
        video.crossOrigin = 'anonymous';
        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          loadedMedia.set(item.url, video);
          resolve(video);
        };
        
        video.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load video ${item.name}`));
        };
        
        video.src = item.url;
      } else {
        const img = new Image();
        img.crossOrigin = 'anonymous';
        
        img.onload = () => {
          clearTimeout(timeout);
          loadedMedia.set(item.url, img);
          resolve(img);
        };
        
        img.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load image ${item.name}`));
        };
        
        img.src = item.url;
      }
    });
  };

  // Предзагрузка всех медиа элементов
  const preloadAllMedia = async () => {
    if (actualTimelineItems.length === 0) return;

    setIsLoadingMedia(true);
    setLoadingProgress(0);
    
    const uniqueUrls = [...new Set(actualTimelineItems.map(item => item.url).filter(Boolean))];
    const totalItems = uniqueUrls.length;
    let loadedCount = 0;

    console.log(`📦 Предзагрузка ${totalItems} медиа элементов...`);

    for (const url of uniqueUrls) {
      try {
        const item = actualTimelineItems.find(i => i.url === url);
        await loadMediaElement(item);
        loadedCount++;
        setLoadingProgress(Math.round((loadedCount / totalItems) * 100));
        console.log(`✅ Загружен: ${item.name} (${loadedCount}/${totalItems})`);
      } catch (error) {
        console.warn(`⚠️ Ошибка загрузки:`, error);
        loadedCount++;
        setLoadingProgress(Math.round((loadedCount / totalItems) * 100));
      }
    }

    setIsLoadingMedia(false);
    console.log(`🎉 Предзагрузка завершена: ${loadedMedia.size} элементов`);
  };

  // СУПЕРFAST экспорт с MediaRecorder
  const startExport = async () => {
    if (isExporting || actualTimelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setError(null);
    setExportStage("recording");

    try {
      const canvas = exportCanvasRef.current;
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const duration = calculateDuration();
      console.log(`🚀 FAST экспорт: ${duration}s, ${actualTimelineItems.length} элементов`);

      // MediaRecorder для быстрой записи
      const stream = canvas.captureStream(exportSettings.fps);
      const chunks = [];
      
      const options = {
        mimeType: 'video/webm;codecs=vp9',
        videoBitsPerSecond: exportSettings.quality === 'high' ? 8000000 : 4000000
      };

      if (!MediaRecorder.isTypeSupported(options.mimeType)) {
        options.mimeType = 'video/webm;codecs=vp8';
      }
      if (!MediaRecorder.isTypeSupported(options.mimeType)) {
        options.mimeType = 'video/webm';
      }

      const mediaRecorder = new MediaRecorder(stream, options);

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: options.mimeType });
        downloadVideo(blob);
        setExportProgress(100);
        setExportStage("completed");
        
        setTimeout(() => {
          setExportStage("ready");
          setExportProgress(0);
        }, 2000);
      };

      mediaRecorder.start();

      // БЫСТРЫЙ рендеринг в реальном времени
      let frame = 0;
      const totalFrames = Math.ceil(duration * exportSettings.fps);
      const frameInterval = 1000 / exportSettings.fps;

      const renderLoop = async () => {
        if (frame >= totalFrames) {
          mediaRecorder.stop();
          return;
        }

        const currentTime = frame / exportSettings.fps;
        const ctx = canvas.getContext('2d');
        await renderFrame(ctx, currentTime, width, height);
        
        frame++;
        setExportProgress(Math.round((frame / totalFrames) * 95));
        
        setTimeout(renderLoop, frameInterval);
      };

      renderLoop();

    } catch (error) {
      console.error('❌ Ошибка экспорта:', error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  // Скачивание видео
  const downloadVideo = (blob) => {
    const filename = `${exportSettings.filename}.webm`;
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  };

  // Генерация превью (статичного и видео)
  const generatePreview = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const previewTime = calculateDuration() / 4;
      await renderFrame(ctx, previewTime, width, height);
      
      // Статичное превью
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setPreviewFrame(url);
        }
      });

      // Создаем видео превью
      await createVideoPreview();

    } catch (error) {
      console.warn('⚠️ Ошибка превью:', error);
    }
  };

  // Создание видео превью
  const createVideoPreview = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const stream = canvas.captureStream(30);
      const chunks = [];
      
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'video/webm;codecs=vp8',
        videoBitsPerSecond: 1000000
      });

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'video/webm' });
        const url = URL.createObjectURL(blob);
        setPreviewVideo(url);
      };

      mediaRecorder.start();

      // Рендерим 3 секунды превью
      const duration = Math.min(calculateDuration(), 3);
      const fps = 20; // Меньше FPS для превью
      const totalFrames = Math.ceil(duration * fps);

      for (let frame = 0; frame < totalFrames; frame++) {
        const currentTime = (frame / fps) + (calculateDuration() / 8); // Начинаем с 1/8 длительности
        const ctx = canvas.getContext('2d');
        await renderFrame(ctx, currentTime, width, height);
        
        // Пауза между кадрами
        await new Promise(resolve => setTimeout(resolve, 1000 / fps));
      }

      mediaRecorder.stop();
      
    } catch (error) {
      console.warn('⚠️ Ошибка видео превью:', error);
    }
  };

  useEffect(() => {
    if (isOpen && actualTimelineItems.length > 0) {
      preloadAllMedia().then(() => {
        generatePreview();
      });
    }
  }, [isOpen, actualTimelineItems, exportSettings.resolution]);

  const getStatusText = () => {
    switch (exportStage) {
      case "recording": return "🚀 Быстрая запись...";
      case "completed": return "✅ Экспорт завершен!";
      case "error": return "❌ Ошибка экспорта";
      case "ready": return `Готов к быстрому экспорту (${actualTimelineItems.length} элементов)`;
      default: return "Ожидание...";
    }
  };

  const estimateTime = () => {
    const duration = calculateDuration();
    return duration / 2; // ~2x быстрее реального времени
  };

  const canExport = !isExporting && actualTimelineItems.length > 0 && exportStage === "ready";

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-3xl w-full max-h-[95vh] flex flex-col">
        
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200 flex-shrink-0">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Zap size={24} className="mr-3 text-green-500" />
            ⚡ CapCut Fast Exporter
          </h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={20} />
          </button>
        </div>

        {/* Scrollable Content */}
        <div className="p-6 space-y-6 flex-1 overflow-y-auto">
          
          {/* Speed Info */}
          <div className="bg-gradient-to-r from-green-50 to-blue-50 border border-green-200 rounded-xl p-4">
            <h4 className="font-medium text-green-800 mb-2">🚀 CapCut-Style Fast Export</h4>
            <div className="text-sm text-green-700 space-y-1">
              <p>• <strong>Скорость:</strong> в 10-15x быстрее FFmpeg.wasm</p>
              <p>• <strong>Точность:</strong> правильное позиционирование overlay</p>
              <p>• <strong>Время экспорта:</strong> ~{estimateTime().toFixed(1)}s для {calculateDuration().toFixed(1)}s видео</p>
            </div>
          </div>

          {/* Timeline Status */}
          {actualTimelineItems.length === 0 ? (
            <div className="bg-red-50 border border-red-200 rounded-xl p-4">
              <div className="flex items-start">
                <AlertCircle size={20} className="text-red-600 mr-3 mt-0.5" />
                <div>
                  <h4 className="font-medium text-red-800">Нет элементов timeline</h4>
                  <p className="text-sm text-red-600 mt-1">Добавьте элементы в timeline для экспорта.</p>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-green-50 border border-green-200 rounded-xl p-4">
              <h4 className="font-medium text-green-800 mb-2">📋 Timeline готов</h4>
              <div className="text-sm text-green-700 grid grid-cols-2 gap-2">
                <div>
                  <p><strong>Элементов:</strong> {actualTimelineItems.length}</p>
                  <p><strong>Видео:</strong> {actualTimelineItems.filter(i => i.trackType === 'main').length}</p>
                </div>
                <div>
                  <p><strong>Overlay:</strong> {actualTimelineItems.filter(i => i.trackType === 'overlay').length}</p>
                  <p><strong>Длительность:</strong> {calculateDuration().toFixed(1)}s</p>
                </div>
              </div>
            </div>
          )}

          {/* Error Display */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-xl p-4">
              <div className="flex items-start">
                <AlertCircle size={20} className="text-red-600 mr-3 mt-0.5" />
                <div>
                  <h4 className="font-medium text-red-800">Ошибка экспорта</h4>
                  <p className="text-sm text-red-600 mt-1">{error}</p>
                </div>
              </div>
            </div>
          )}

          {/* Export Status */}
          <div className={`rounded-xl p-4 border ${
            exportStage === "error" ? "bg-red-50 border-red-200" :
            exportStage === "completed" ? "bg-green-50 border-green-200" :
            "bg-blue-50 border-blue-200"
          }`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center">
                {exportStage === "completed" ? (
                  <Check size={20} className="text-green-600" />
                ) : exportStage === "error" ? (
                  <AlertCircle size={20} className="text-red-600" />
                ) : exportStage === "ready" ? (
                  <Zap size={20} className="text-blue-600" />
                ) : (
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
                )}
                <span className="ml-3 font-medium">{getStatusText()}</span>
              </div>
              <div className="flex items-center space-x-4">
                {isExporting && (
                  <>
                    <span className="text-sm text-gray-600 flex items-center">
                      <Clock size={16} className="mr-1" />
                      ~{estimateTime().toFixed(1)}s
                    </span>
                    <span className="text-sm font-medium">{exportProgress}%</span>
                  </>
                )}
              </div>
            </div>
            {isExporting && (
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}
          </div>

          {/* Export Settings */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Разрешение</label>
              <select
                value={exportSettings.resolution}
                onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                disabled={isExporting}
              >
                <option value="1920x1080">1080p</option>
                <option value="1280x720">720p</option>
                <option value="854x480">480p</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">FPS</label>
              <select
                value={exportSettings.fps}
                onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                disabled={isExporting}
              >
                <option value="24">24 FPS</option>
                <option value="30">30 FPS</option>
                <option value="60">60 FPS</option>
              </select>
            </div>
          </div>

          {/* Preview */}
          {!isExporting && actualTimelineItems.length > 0 && (
            <div className="bg-gray-50 rounded-xl p-4">
              <h3 className="text-sm font-medium text-gray-700 mb-3">👀 Превью с правильным позиционированием</h3>
              <div className="bg-black rounded-lg aspect-video flex items-center justify-center overflow-hidden">
                {previewFrame ? (
                  <img 
                    src={previewFrame} 
                    alt="Preview" 
                    className="max-w-full max-h-full object-contain"
                  />
                ) : (
                  <div className="text-white text-sm">Создание превью...</div>
                )}
              </div>
            </div>
          )}
        </div>

        {/* Fixed Footer */}
        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50 flex-shrink-0">
          <div className="text-sm text-gray-600">
            {!isExporting && actualTimelineItems.length > 0 ? (
              `⚡ READY • ${exportSettings.resolution} • ${actualTimelineItems.length} элементов • ~${estimateTime().toFixed(1)}s`
            ) : isExporting ? (
              `🚀 Быстрая запись... ${exportProgress}%`
            ) : (
              <span className="text-red-600 font-medium">❌ Добавьте элементы в timeline для экспорта</span>
            )}
          </div>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="px-6 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
              disabled={isExporting}
            >
              Закрыть
            </button>
            <button
              onClick={startExport}
              disabled={!canExport}
              className={`px-8 py-3 text-base rounded-lg font-semibold transition-all flex items-center ${
                !canExport
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-gradient-to-r from-green-500 to-blue-500 text-white hover:from-green-600 hover:to-blue-600 shadow-lg hover:shadow-xl transform hover:scale-105"
              }`}
            >
              {isExporting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Быстрый экспорт... {exportProgress}%
                </>
              ) : (
                <>
                  <Zap size={18} className="mr-2" />
                  ⚡ Fast Export ({actualTimelineItems.length})
                </>
              )}
            </button>
          </div>
        </div>

        {/* Hidden canvases */}
        <canvas ref={exportCanvasRef} style={{ display: 'none' }} />
        <canvas ref={previewCanvasRef} style={{ display: 'none' }} />
      </div>
    </div>
  );
};

export const useFFmpegExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};

export default FFmpegVideoExporter;
