import { useState, useRef, useEffect } from "react";

// ÐšÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Ñ‚ÑƒÐ»Ñ‚Ð¸Ð¿Ð°
export const Tooltip = ({ children, content, delay = 500 }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const timeoutRef = useRef(null);

  const handleMouseEnter = (e) => {
    setMousePos({ x: e.clientX, y: e.clientY });
    timeoutRef.current = setTimeout(() => {
      setIsVisible(true);
    }, delay);
  };

  const handleMouseLeave = () => {
    setIsVisible(false);
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  const handleMouseMove = (e) => {
    setMousePos({ x: e.clientX, y: e.clientY });
  };

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return (
    <div 
      className="relative inline-block"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onMouseMove={handleMouseMove}
    >
      {children}
      {isVisible && (
        <div
          className="fixed z-50 px-3 py-2 text-sm font-medium text-white bg-gray-900 rounded-lg shadow-lg pointer-events-none max-w-xs"
          style={{
            left: `${mousePos.x + 10}px`,
            top: `${mousePos.y - 40}px`,
            transform: 'translateX(-50%)',
          }}
        >
          <div className="whitespace-pre-line">{content}</div>
          <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 translate-y-full">
            <div className="w-2 h-2 bg-gray-900 rotate-45"></div>
          </div>
        </div>
      )}
    </div>
  );
};

const Timeline = () => {
  // Ð”ÐµÐ¼Ð¾ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ
  const [timelineItems, setTimelineItems] = useState([
    {
      id: 1,
      name: "Main Video.mp4",
      startTime: 2,
      duration: 8,
      trackId: "main-video",
      trackType: "main",
      type: "video"
    },
    {
      id: 2,
      name: "Overlay.png",
      startTime: 5,
      duration: 3,
      trackId: "overlay-1",
      trackType: "overlay",
      type: "image"
    },
    {
      id: 3,
      name: "Background Music.mp3",
      startTime: 0,
      duration: 15,
      trackId: "audio-1",
      trackType: "audio",
      type: "audio"
    }
  ]);
  const [currentTime, setCurrentTime] = useState(3);
  const [videoDuration, setVideoDuration] = useState(20);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(timelineItems[0]);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [tracks, setTracks] = useState({
    overlays: [
      { id: 'overlay-1', name: 'Overlay 1', type: 'overlay' },
      { id: 'overlay-2', name: 'Overlay 2', type: 'overlay' }
    ],
    main: [
      { id: 'main-video', name: 'Main Video', type: 'main' }
    ],
    audio: [
      { id: 'audio-1', name: 'Audio 1', type: 'audio' },
      { id: 'audio-2', name: 'Audio 2', type: 'audio' }
    ]
  });
  const [copiedItem, setCopiedItem] = useState(null);

  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  // Ð”ÐµÐ¼Ð¾ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸
  const seekTo = (time) => setCurrentTime(time);
  const splitTimelineItem = () => {
    if (selectedTimelineItem) {
      console.log("Split item:", selectedTimelineItem.name);
    }
  };
  const copyTimelineItem = () => {
    if (selectedTimelineItem) {
      setCopiedItem(selectedTimelineItem);
      console.log("Copied:", selectedTimelineItem.name);
    }
  };
  const pasteTimelineItem = () => {
    if (copiedItem) {
      console.log("Paste item:", copiedItem.name);
    }
  };
  const deleteSelectedItem = () => {
    if (selectedTimelineItem) {
      setTimelineItems(prev => prev.filter(item => item.id !== selectedTimelineItem.id));
      setSelectedTimelineItem(null);
      console.log("Deleted item");
    }
  };

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð·ÑƒÐ¼Ð°
  const zoomIn = () => {
    setTimelineZoom((prev) => Math.min(20, prev * 1.5));
  };

  const zoomOut = () => {
    setTimelineZoom((prev) => Math.max(0.1, prev / 1.5));
  };

  const resetZoom = () => {
    setTimelineZoom(1);
  };

  // Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ñ€ÐµÐºÐ¾Ð²
  const addOverlayTrack = () => {
    const newTrackId = `overlay-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      overlays: [
        ...prev.overlays,
        {
          id: newTrackId,
          name: `Overlay ${prev.overlays.length + 1}`,
          type: "overlay",
        },
      ],
    }));
  };

  const addAudioTrack = () => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      audio: [
        ...prev.audio,
        {
          id: newTrackId,
          name: `Audio ${prev.audio.length + 1}`,
          type: "audio",
        },
      ],
    }));
  };

  // Ð¡Ð¸Ð½Ñ…Ñ€Ð¾Ð½Ð¸Ð·Ð°Ñ†Ð¸Ñ ÑÐºÑ€Ð¾Ð»Ð»Ð°
  const handleTrackAreaScroll = (e) => {
    if (trackLabelsRef.current) {
      trackLabelsRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const handleTrackLabelsScroll = (e) => {
    if (timelineScrollRef.current) {
      timelineScrollRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const getTimelineWidth = () => {
    const baseWidth = videoDuration * 50 * timelineZoom;
    return Math.max(baseWidth, 2000);
  };

  const getPixelsPerSecond = () => {
    return 50 * timelineZoom;
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸ÐºÐ¸ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ð¹
  const handleTimelineClick = (e) => {
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const pixelsPerSecond = getPixelsPerSecond();
    const clickTime = clickX / pixelsPerSecond;
    seekTo(clickTime);
  };

  const getTimelineItemStyle = (item) => {
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(
      (track) => track.id === item.trackId
    );
    const pixelsPerSecond = getPixelsPerSecond();

    return {
      left: `${item.startTime * pixelsPerSecond}px`,
      width: `${item.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getTrackColor = (trackType) => {
    switch (trackType) {
      case "overlay":
        return "bg-purple-500 border-purple-400";
      case "main":
        return "bg-blue-500 border-blue-400";
      case "audio":
        return "bg-green-500 border-green-400";
      default:
        return "bg-gray-500 border-gray-400";
    }
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <div className="h-80 bg-white rounded-2xl shadow-sm border-2 border-gray-200 overflow-hidden flex flex-col flex-shrink-0">
        <div className="bg-gray-50 p-4 border-b border-gray-200 space-y-3">
          <div className="flex items-center justify-between">
            <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
              <span>ðŸŽ¬</span>
              <span>Timeline Pro</span>
              {copiedItem && (
                <span className="text-blue-600 text-sm">
                  - "{copiedItem.name}" copied (Ctrl+V to paste)
                </span>
              )}
            </h4>

            <div className="flex items-center space-x-3">
              <Tooltip content="Keyboard shortcuts:\n\nSpacebar: Play/Pause\nArrows: Navigate frame by frame\nShift+Arrow: Navigate by seconds\nCtrl+Arrow: Navigate by 5 seconds\n\nCtrl+C: Copy selected item\nCtrl+V: Paste item\nCtrl+B: Split item at current time\nDel/Backspace: Delete selected\n\n+/-: Zoom in/out\n0: Reset zoom\nCtrl+Wheel: Zoom at mouse\n\nDrag edges: Trim clips\nDrag center: Move with ripple">
                <button className="p-2 text-gray-500 hover:text-blue-600 transition-colors rounded-lg hover:bg-gray-100 text-lg">
                  â“
                </button>
              </Tooltip>
              <div className="flex items-center space-x-2 bg-white rounded-xl p-2 border border-gray-200">
                <Tooltip content="Zoom out (-)">
                  <button
                    onClick={zoomOut}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                  >
                    ðŸ”âž–
                  </button>
                </Tooltip>
                <span className="text-xs text-gray-500 min-w-12 text-center">
                  {(timelineZoom * 100).toFixed(0)}%
                </span>
                <Tooltip content="Zoom in (+)\nOr Ctrl+Wheel over timeline">
                  <button
                    onClick={zoomIn}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                  >
                    ðŸ”âž•
                  </button>
                </Tooltip>
                <Tooltip content="Reset zoom (0)">
                  <button
                    onClick={resetZoom}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                  >
                    ðŸ”„
                  </button>
                </Tooltip>
              </div>
            </div>
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-2">
