      this.renderPlaceholder(`–û—à–∏–±–∫–∞: ${item.name || '–≠–ª–µ–º–µ–Ω—Ç'}`);
    } finally {
      this.ctx.restore();
    }
  }
  async renderVideo(item, relativeTime) {
    if (!item.videoElement) return;
    try {
      const video = item.videoElement;

      // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤—Ä–µ–º—è —Å —É—á–µ—Ç–æ–º –æ–±—Ä–µ–∑–∫–∏
      const targetTime = Math.max(0, relativeTime);
      if (video.duration && targetTime <= video.duration) {
        if (Math.abs(video.currentTime - targetTime) > 0.1) {
          video.currentTime = targetTime;

          // –ñ–¥–µ–º, –ø–æ–∫–∞ –≤–∏–¥–µ–æ –ø–µ—Ä–µ–π–¥–µ—Ç –Ω–∞ –Ω—É–∂–Ω—ã–π –∫–∞–¥—Ä
