import { useState, useRef, useEffect, useCallback } from "react";
import { ZoomIn, ZoomOut, RotateCcw, Film, Play, Pause, HelpCircle } from "lucide-react";

export const Timeline = () => {
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [currentTime, setCurrentTime] = useState(5);
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoDuration] = useState(30);
  const [showTooltip, setShowTooltip] = useState(false);
  const timelineRef = useRef(null);

  // Демо данные
  const timelineItems = [
    { id: 1, name: "Intro Video", startTime: 0, duration: 8, color: "bg-blue-500" },
    { id: 2, name: "Main Content", startTime: 8, duration: 15, color: "bg-green-500" },
    { id: 3, name: "Outro", startTime: 23, duration: 7, color: "bg-purple-500" },
    { id: 4, name: "Background Music", startTime: 0, duration: 30, color: "bg-orange-500", track: "audio" },
  ];

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  // Главная функция зума как в CapCut
  const zoomToCenter = useCallback((newZoom, centerTime = null) => {
    if (!timelineRef.current) return;
    
    const pixelsPerSecondOld = 50 * timelineZoom;
    const pixelsPerSecondNew = 50 * newZoom;
    
    // Используем currentTime как центр если centerTime не передан
    const focusTime = centerTime !== null ? centerTime : currentTime;
    
    // Вычисляем текущую позицию фокуса в пикселях
    const currentFocusPixel = focusTime * pixelsPerSecondOld;
    const currentScrollLeft = timelineRef.current.scrollLeft;
    const viewportWidth = timelineRef.current.clientWidth;
    
    // Вычисляем относительную позицию фокуса в viewport
    const focusRelativeToViewport = currentFocusPixel - currentScrollLeft;
    const focusRatio = focusRelativeToViewport / viewportWidth;
    
    // Обновляем зум
    setTimelineZoom(newZoom);
    
    // Корректируем скролл после зума
    requestAnimationFrame(() => {
      if (timelineRef.current) {
        const newFocusPixel = focusTime * pixelsPerSecondNew;
        const newScrollLeft = newFocusPixel - (focusRatio * viewportWidth);
        timelineRef.current.scrollLeft = Math.max(0, newScrollLeft);
      }
    });
  }, [timelineZoom, currentTime]);

  const zoomIn = () => {
    const newZoom = Math.min(10, timelineZoom * 1.4);
    zoomToCenter(newZoom);
  };

  const zoomOut = () => {
    const newZoom = Math.max(0.2, timelineZoom / 1.4);
    zoomToCenter(newZoom);
  };

  const resetZoom = () => {
    zoomToCenter(1);
  };

  const fitToTimeline = () => {
    if (!timelineRef.current) return;
    const viewportWidth = timelineRef.current.clientWidth - 40;
    const optimalZoom = Math.max(0.2, Math.min(3, viewportWidth / (videoDuration * 50)));
    zoomToCenter(optimalZoom, videoDuration / 2);
  };

  // Зум колесиком мыши с Ctrl (как в CapCut)
  const handleWheel = useCallback((e) => {
    if (!e.ctrlKey && !e.metaKey) return;
    
    e.preventDefault();
    
    const rect = timelineRef.current.getBoundingClientRect();
    const mouseX = e.clientX - rect.left + timelineRef.current.scrollLeft;
    const pixelsPerSecond = 50 * timelineZoom;
    const mouseTime = mouseX / pixelsPerSecond;
    
    const zoomFactor = e.deltaY > 0 ? 0.9 : 1.1;
    const newZoom = Math.max(0.2, Math.min(10, timelineZoom * zoomFactor));
    
    zoomToCenter(newZoom, mouseTime);
  }, [timelineZoom, zoomToCenter]);

  // Горячие клавиши
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.target.tagName === 'INPUT') return;
      
      switch (e.key) {
        case '=':
        case '+':
          e.preventDefault();
          zoomIn();
          break;
        case '-':
          e.preventDefault();
          zoomOut();
          break;
        case '0':
          e.preventDefault();
          resetZoom();
          break;
        case '9':
          e.preventDefault();
          fitToTimeline();
          break;
        case ' ':
          e.preventDefault();
          setIsPlaying(!isPlaying);
          break;
        case 'Home':
          e.preventDefault();
          setCurrentTime(0);
          break;
        case 'End':
          e.preventDefault();
          setCurrentTime(videoDuration);
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isPlaying, videoDuration]);

  // Клик по timeline для перемещения плейхеда
  const handleTimelineClick = (e) => {
    const rect = timelineRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineRef.current.scrollLeft;
    const pixelsPerSecond = 50 * timelineZoom;
    const newTime = Math.max(0, Math.min(videoDuration, clickX / pixelsPerSecond));
    setCurrentTime(newTime);
  };

  // Подключение обработчика wheel
  useEffect(() => {
    const timelineElement = timelineRef.current;
    if (timelineElement) {
      timelineElement.addEventListener('wheel', handleWheel, { passive: false });
      return () => timelineElement.removeEventListener('wheel', handleWheel);
    }
  }, [handleWheel]);

  const getTimelineWidth = () => {
    return Math.max(videoDuration * 50 * timelineZoom, 800);
  };

  const getPixelsPerSecond = () => {
    return 50 * timelineZoom;
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          {/* Header */}
          <div className="bg-gray-900 text-white p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Film size={24} />
                <h1 className="text-xl font-bold">CapCut Style Timeline</h1>
              </div>
              
              <div className="flex items-center space-x-4">
                <div className="relative">
                  <button
                    onMouseEnter={() => setShowTooltip(true)}
                    onMouseLeave={() => setShowTooltip(false)}
                    className="p-2 text-gray-400 hover:text-white transition-colors"
                    title="Keyboard shortcuts"
                  >
                    <HelpCircle size={20} />
                  </button>
                  
                  {showTooltip && (
                    <div className="absolute right-0 top-12 w-80 bg-gray-800 text-white text-xs rounded-lg shadow-xl border border-gray-600 z-50 p-4">
                      <div className="grid grid-cols-1 gap-3">
                        <div>
                          <div className="font-semibold text-blue-300 mb-2">🎮 Zoom Controls:</div>
                          <div className="space-y-1 text-gray-300">
                            <div className="flex justify-between">
                              <span>Ctrl + Wheel</span>
                              <span>Zoom at cursor</span>
                            </div>
                            <div className="flex justify-between">
                              <span>+ / -</span>
                              <span>Zoom in/out</span>
                            </div>
                            <div className="flex justify-between">
                              <span>0</span>
                              <span>Reset zoom</span>
                            </div>
                            <div className="flex justify-between">
                              <span>9</span>
                              <span>Fit timeline</span>
                            </div>
                          </div>
                        </div>
                        
                        <div className="border-t border-gray-600 pt-2">
                          <div className="font-semibold text-green-300 mb-2">🎯 Navigation:</div>
                          <div className="space-y-1 text-gray-300">
                            <div className="flex justify-between">
                              <span>Space</span>
                              <span>Play/Pause</span>
                            </div>
                            <div className="flex justify-between">
                              <span>Home / End</span>
                              <span>Go to start/end</span>
                            </div>
                            <div className="flex justify-between">
                              <span>Click timeline</span>
                              <span>Seek to position</span>
                            </div>
                          </div>
                        </div>
                        
                        <div className="border-t border-gray-600 pt-2">
                          <div className="text-blue-200 text-center">
                            💡 Zoom centers on mouse cursor with Ctrl+Wheel
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
                
                <button
                  onClick={() => setIsPlaying(!isPlaying)}
                  className="flex items-center space-x-2 bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-lg transition-colors"
                >
                  {isPlaying ? <Pause size={16} /> : <Play size={16} />}
                  <span>{isPlaying ? 'Pause' : 'Play'}</span>
                </button>
                
                <div className="text-sm">
                  {formatTime(currentTime)} / {formatTime(videoDuration)}
                </div>
              </div>
            </div>
          </div>

          {/* Controls */}
          <div className="bg-gray-100 p-4 border-b">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <span className="text-sm font-medium text-gray-700">Zoom:</span>
                <div className="flex items-center space-x-2 bg-white rounded-lg p-2 border">
                  <button
                    onClick={zoomOut}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                    title="Zoom Out (-)"
                  >
                    <ZoomOut size={16} />
                  </button>
                  <span className="text-xs text-gray-500 min-w-12 text-center font-mono">
                    {(timelineZoom * 100).toFixed(0)}%
                  </span>
                  <button
                    onClick={zoomIn}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                    title="Zoom In (+)"
                  >
                    <ZoomIn size={16} />
                  </button>
                  <button
                    onClick={resetZoom}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors ml-2"
                    title="Reset Zoom (0)"
                  >
                    <RotateCcw size={16} />
                  </button>
                  <button
                    onClick={fitToTimeline}
                    className="px-2 py-1 text-xs text-gray-600 hover:text-blue-600 transition-colors border-l border-gray-200 ml-2 pl-2"
                    title="Fit to Screen (9)"
                  >
                    Fit
                  </button>
                </div>
              </div>

              <div className="text-xs text-gray-600 space-y-1">
                <div>🎯 Click timeline to seek • Current zoom: {(timelineZoom * 100).toFixed(0)}%</div>
              </div>
            </div>
          </div>

          {/* Timeline */}
          <div className="h-64 relative overflow-hidden">
            {/* Time ruler */}
            <div className="h-12 bg-gray-200 border-b relative">
              <div
                ref={timelineRef}
                className="h-full overflow-x-auto overflow-y-hidden"
                style={{ scrollbarHeight: '8px' }}
              >
                <div
                  style={{ width: `${getTimelineWidth()}px` }}
                  className="h-full relative cursor-pointer"
                  onClick={handleTimelineClick}
                >
                  {/* Time markers */}
                  {Array.from({ length: Math.ceil(videoDuration * timelineZoom / 2) + 1 }, (_, i) => {
                    const timeValue = (i * 2) / timelineZoom;
                    if (timeValue > videoDuration) return null;
                    
                    return (
                      <div
                        key={i}
                        className="absolute top-0 h-full border-l border-gray-400 flex items-center"
                        style={{ left: `${timeValue * getPixelsPerSecond()}px` }}
                      >
                        <span className="text-xs text-gray-700 ml-2 font-mono">
                          {formatTime(timeValue)}
                        </span>
                      </div>
                    );
                  })}

                  {/* Sub-markers for detailed zoom */}
                  {timelineZoom > 2 && Array.from({ length: videoDuration * timelineZoom + 1 }, (_, i) => {
                    const timeValue = i / timelineZoom;
                    if (timeValue > videoDuration) return null;
                    
                    return (
                      <div
                        key={`sub-${i}`}
                        className="absolute top-6 w-0 h-6 border-l border-gray-300"
                        style={{ left: `${timeValue * getPixelsPerSecond()}px` }}
                      />
                    );
                  })}

                  {/* Playhead */}
                  <div
                    className="absolute top-0 w-0.5 h-full bg-red-500 z-20 shadow-md transition-all duration-100"
                    style={{ left: `${currentTime * getPixelsPerSecond()}px` }}
                  >
                    <div className="absolute -top-1 -left-2 w-4 h-4 bg-red-500 rounded-full shadow-md"></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Video track */}
            <div className="h-16 bg-gray-800 border-b relative">
              <div className="absolute left-0 top-0 h-full w-24 bg-gray-700 border-r flex items-center justify-center text-white text-sm font-medium">
                Video
              </div>
              <div className="ml-24 h-full relative overflow-hidden">
                <div style={{ width: `${getTimelineWidth()}px` }} className="h-full relative">
                  {timelineItems.filter(item => !item.track).map((item) => (
                    <div
                      key={item.id}
                      className={`absolute h-12 top-2 ${item.color} rounded border-2 border-white shadow-md cursor-pointer hover:shadow-lg transition-all`}
                      style={{
                        left: `${item.startTime * getPixelsPerSecond()}px`,
                        width: `${item.duration * getPixelsPerSecond()}px`,
                      }}
                      title={item.name}
                    >
                      <div className="p-2 text-white text-xs font-medium truncate">
                        {item.name}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Audio track */}
            <div className="h-16 bg-gray-700 relative">
              <div className="absolute left-0 top-0 h-full w-24 bg-gray-600 border-r flex items-center justify-center text-white text-sm font-medium">
                Audio
              </div>
              <div className="ml-24 h-full relative overflow-hidden">
                <div style={{ width: `${getTimelineWidth()}px` }} className="h-full relative">
                  {timelineItems.filter(item => item.track === 'audio').map((item) => (
                    <div
                      key={item.id}
                      className={`absolute h-12 top-2 ${item.color} rounded border-2 border-white shadow-md cursor-pointer hover:shadow-lg transition-all`}
                      style={{
                        left: `${item.startTime * getPixelsPerSecond()}px`,
                        width: `${item.duration * getPixelsPerSecond()}px`,
                      }}
                      title={item.name}
                    >
                      <div className="p-2 text-white text-xs font-medium truncate">
                        {item.name}
                      </div>
                      {/* Audio waveform simulation */}
                      <div className="absolute bottom-1 left-2 right-2 h-1 bg-white/30 rounded">
                        <div className="h-full bg-white/60 rounded" style={{ width: '60%' }}></div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Status bar */}
          <div className="bg-gray-100 p-3 text-xs text-gray-600 border-t">
            <div className="flex justify-between items-center">
              <div>
                Zoom: {(timelineZoom * 100).toFixed(0)}% • 
                Pixels per second: {getPixelsPerSecond().toFixed(1)} • 
                Timeline width: {getTimelineWidth()}px
              </div>
              <div>
                Ready • {timelineItems.length} clips loaded
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
