import { useRef, useState } from "react";

import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  ChevronLeft,
  ChevronRight,
  Brain,
  Mic,
  Users,
  Eye,
  Clock,
} from "lucide-react";

const ContentStudio = ({
  isLeftPanelCollapsed,
  setIsLeftPanelCollapsed,
  contentType,
  setContentType,
  topic,
  setTopic,
  keyPoints,
  setKeyPoints,
  duration,
  setDuration,
  script,
  setScript,
  isGeneratingKeyPoints,
  isGeneratingScript,
  isExtendingScript,
  isAuthenticated,
  audioMethod,
  setAudioMethod,
  recordedAudio,
  setShowTeleprompter,
  setShowAudioEditor,
  audioUrl,
  assessment,
  assessmentLoading,
  handleTextSelection,
  generateKeyPoints,
  generateScript,
  extendScript,
  generateAudio,
  addKeyPoint,
  updateKeyPoint,
  removeKeyPoint,
  contentTypes,
}) => {
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false);
  const [isContentTypeDropdownOpen, setIsContentTypeDropdownOpen] =
    useState(false);

  const languages = [
    { id: "english", name: "English", flag: "🇺🇸" },
    { id: "russian", name: "Русский", flag: "🇷🇺" },
    { id: "spanish", name: "Español", flag: "🇪🇸" },
    { id: "french", name: "Français", flag: "🇫🇷" },
    { id: "german", name: "Deutsch", flag: "🇩🇪" },
    { id: "chinese", name: "中文", flag: "🇨🇳" },
  ];

  const [selectedLanguage, setSelectedLanguage] = useState("english");

  const scriptRef = useRef(null);

  // Script loading skeleton component
  const ScriptEditorSkeleton = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="h-6 bg-gray-200 rounded w-48 animate-pulse"></div>
        <div className="h-8 bg-gray-200 rounded w-32 animate-pulse"></div>
      </div>
      <div className="space-y-3">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="h-4 bg-gray-200 rounded animate-pulse"
            style={{ width: `${85 + Math.random() * 15}%` }}
          ></div>
        ))}
      </div>
    </div>
  );

  // Detailed analytics component
  const DetailedAnalytics = ({ assessmentLoading, assessment }) => (
    <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border border-indigo-200 p-4 rounded-xl">
      <div className="flex items-start space-x-3">
        <div className="text-xl">📊</div>
        <div>
          <h4 className="font-semibold text-gray-800 mb-2">Content Analysis</h4>
          {assessmentLoading ? (
            <div className="space-y-2">
              <div className="h-3 bg-gray-200 rounded w-32 animate-pulse"></div>
              <div className="h-3 bg-gray-200 rounded w-24 animate-pulse"></div>
            </div>
          ) : (
            <div className="text-sm text-gray-700 space-y-1">
              <p>
                📈 Engagement Score:{" "}
                <span className="font-medium text-indigo-600">
                  {assessment?.engagement_score || 8.5}/10
                </span>
              </p>
              <p>
                🎯 Target Audience:{" "}
                <span className="font-medium text-purple-600">
                  {assessment?.target_audience || "General"}
                </span>
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    // TODO - поработать над стилями
    <div
      className={`${
        isLeftPanelCollapsed ? "w-24" : "w-[450px]"
      } h-[142vh] overflow-hidden bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10 flex-shrink-0`}
    >
      {/* Global styles for custom scrollbar */}
      <style>
        {`
          .custom-scrollbar::-webkit-scrollbar {
            width: 8px;
          }
          
          .custom-scrollbar::-webkit-scrollbar-track {
            background: #f1f5f9;
            border-radius: 10px;
            margin: 8px 0;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb {
            background: #cbd5e1;
            border-radius: 10px;
            transition: all 0.2s ease;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb:hover {
            background: #94a3b8;
          }
          
          .custom-scrollbar {
            scrollbar-width: thin;
            scrollbar-color: #cbd5e1 #f1f5f9;
          }
        `}
      </style>

      <div className="flex items-center justify-between p-6 border-b border-slate-200/50 from-slate-50 to-blue-50 flex-shrink-0">
        {/* {!isLeftPanelCollapsed && (
          <div className="flex flex-col items-center space-y-4 h-full justify-start pt-4">
            <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
              Content Studio
            </h2>
            <p className="text-sm text-gray-600 font-medium">
