// src/components/common/Header.js
import { Link, useLocation, useNavigate } from "react-router-dom";
import {
  Zap,
  Brain,
  User,
  LogOut,
  Settings,
  Crown,
  VideoIcon,
  FileText,
  Mic,
} from "lucide-react";
import { useState, useRef, useEffect } from "react";
import { useAuth } from "../../contexts/AuthContext";

const Header = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, logout, isAuthenticated } = useAuth();
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  const isLandingPage = location.pathname === "/";
  const isAuthPage = location.pathname === "/auth";
  const isAppPage = location.pathname.startsWith("/app");

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleLogout = () => {
    logout();
    navigate("/");
    setShowDropdown(false);
  };

  const handleAuthClick = () => {
    if (isLandingPage) {
      navigate("/auth");
    } else {
      navigate("/app");
    }
  };

  const getUserInitials = (name) => {
    return (
      name
        ?.split(" ")
        .map((n) => n[0])
        .join("")
        .toUpperCase() || "?"
    );
  };

  const getPlanBadge = (plan) => {
    switch (plan) {
      case "creator":
        return { text: "Creator", color: "from-blue-500 to-purple-500" };
      case "agency":
        return { text: "Agency", color: "from-purple-500 to-pink-500" };
      default:
        return { text: "Free", color: "from-gray-400 to-gray-500" };
    }
  };

  return (
    <div className="bg-white/90 backdrop-blur-xl border-b border-slate-400/50 px-6 py-4 shadow-xl">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-gradient-to-br from-slate-600 to-blue-600 rounded-2xl flex items-center justify-center shadow-xl shadow-slate-500/30">
              <Link
                to={isAuthenticated ? "/app" : "/"}
                className="hover:text-slate-400 transition-colors"
              >
                <Brain className="text-white" size={24} />
              </Link>
            </div>
            <div>
              <div className="text-2xl font-black bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                Scriptify
              </div>
              <div className="text-sm font-bold text-slate-600">
                CONTENT CREATION STUDIO
              </div>
            </div>
          </div>
        </div>

        {/* Navigation for authenticated users in app */}
        {isAuthenticated && isAppPage && (
          <nav className="hidden lg:flex items-center space-x-6">
            <Link
              to="/app/video-maker"
              className={`flex items-center space-x-2 px-3 py-2 rounded-lg transition-colors ${
                location.pathname === "/app/video-maker"
                  ? "bg-blue-100 text-blue-600"
                  : "text-gray-600 hover:text-blue-600 hover:bg-blue-50"
              }`}
            >
              <VideoIcon size={16} />
              <span className="font-medium">Video Maker</span>
            </Link>

            <Link
              to="/app/script-generator"
              className={`flex items-center space-x-2 px-3 py-2 rounded-lg transition-colors ${
                location.pathname === "/app/script-generator"
                  ? "bg-blue-100 text-blue-600"
                  : "text-gray-600 hover:text-blue-600 hover:bg-blue-50"
              }`}
            >
              <FileText size={16} />
              <span className="font-medium">Scripts</span>
            </Link>

            <Link
              to="/app/teleprompter"
              className={`flex items-center space-x-2 px-3 py-2 rounded-lg transition-colors ${
                location.pathname === "/app/teleprompter"
                  ? "bg-blue-100 text-blue-600"
                  : "text-gray-600 hover:text-blue-600 hover:bg-blue-50"
              }`}
            >
              <Mic size={16} />
              <span className="font-medium">Teleprompter</span>
            </Link>
          </nav>
        )}

        {/* Landing page navigation */}
        {isLandingPage && !isAuthenticated ? (
          <nav className="hidden md:flex items-center space-x-8">
            <a
              href="#features"
              className="text-gray-600 hover:text-blue-600 transition-colors font-medium"
            >
              Features
            </a>
            <a
              href="#pricing"
              className="text-gray-600 hover:text-blue-600 transition-colors font-medium"
            >
              Pricing
            </a>
            <a
              href="#testimonials"
              className="text-gray-600 hover:text-blue-600 transition-colors font-medium"
            >
              Reviews
            </a>
            <button
              onClick={handleAuthClick}
              className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white px-6 py-2 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl"
            >
              Try Free
            </button>
          </nav>
        ) : isAuthenticated ? (
          <div className="flex items-center space-x-4">
            {/* Plan Badge */}
            {user?.plan && user.plan !== "free" && (
              <div
                className={`bg-gradient-to-r ${
                  getPlanBadge(user.plan).color
                } text-white px-3 py-1 rounded-full text-sm font-semibold flex items-center space-x-1`}
              >
                <Crown size={14} />
                <span>{getPlanBadge(user.plan).text}</span>
              </div>
            )}

            {/* User Dropdown */}
            <div className="relative" ref={dropdownRef}>
              <button
                onClick={() => setShowDropdown(!showDropdown)}
                className="flex items-center space-x-3 bg-white/80 backdrop-blur-sm border border-slate-200 rounded-xl px-4 py-2 hover:bg-white hover:shadow-lg transition-all duration-300"
              >
                <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-500 rounded-lg flex items-center justify-center text-white font-semibold text-sm">
                  {getUserInitials(user?.name)}
                </div>
                <div className="hidden md:block text-left">
                  <div className="text-sm font-semibold text-gray-800">
                    {user?.name}
                  </div>
                  <div className="text-xs text-gray-500 capitalize">
                    {user?.plan} Plan
                  </div>
                </div>
              </button>

              {showDropdown && (
                <div className="absolute right-0 top-full mt-2 w-64 bg-white/95 backdrop-blur-xl border border-slate-200/50 rounded-2xl shadow-2xl py-2 z-50">
                  <div className="px-4 py-3 border-b border-gray-100">
                    <div className="font-semibold text-gray-800">
                      {user?.name}
                    </div>
                    <div className="text-sm text-gray-500">{user?.email}</div>
                    <div className="text-xs text-blue-600 mt-1">
                      {user?.videosCreated || 0} videos created
                    </div>
                  </div>

                  <div className="py-2">
                    <Link
                      to="/app/profile"
                      onClick={() => setShowDropdown(false)}
                      className="w-full px-4 py-2 text-left hover:bg-blue-50 transition-colors flex items-center space-x-3"
                    >
                      <User size={16} className="text-gray-500" />
                      <span className="text-gray-700">Profile Settings</span>
                    </Link>

                    <Link
                      to="/app/settings"
                      onClick={() => setShowDropdown(false)}
                      className="w-full px-4 py-2 text-left hover:bg-blue-50 transition-colors flex items-center space-x-3"
                    >
                      <Settings size={16} className="text-gray-500" />
                      <span className="text-gray-700">Preferences</span>
                    </Link>

                    {user?.plan === "free" && (
                      <button
                        onClick={() => {
                          setShowDropdown(false);
                          // navigate('/app/upgrade');
                        }}
                        className="w-full px-4 py-2 text-left hover:bg-purple-50 transition-colors flex items-center space-x-3"
                      >
                        <Crown size={16} className="text-purple-500" />
                        <span className="text-purple-600 font-semibold">
                          Upgrade Plan
                        </span>
                      </button>
                    )}
                  </div>

                  <div className="border-t border-gray-100 pt-2">
                    <button
                      onClick={handleLogout}
                      className="w-full px-4 py-2 text-left hover:bg-red-50 transition-colors flex items-center space-x-3"
                    >
                      <LogOut size={16} className="text-red-500" />
                      <span className="text-red-600">Sign out</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        ) : !isAuthPage ? (
          <button
            onClick={handleAuthClick}
            className="group relative bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white px-5 py-2 rounded-xl font-semibold text-lg transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 overflow-hidden"
          >
            <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-slate-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            <span className="relative flex items-center space-x-3">
              <User size={15} />
              <span>Sign In</span>
            </span>
          </button>
        ) : null}
      </div>
    </div>
  );
};

export default Header;
