import { useEffect } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { AuthProvider } from "./contexts/AuthContext";
import MainLayout from "./components/layouts/MainLayout";
import AppRouter from "./components/routing/AppRouter";
import ErrorBoundary from "./components/common/ErrorBoundary";
import NotificationSystem from "./components/NotificationSystem";
function App() {
  useEffect(() => {
    checkPaymentSuccess();
  }, []);

  const checkPaymentSuccess = async () => {
    try {
      const urlParams = new URLSearchParams(window.location.search);
      const sessionId = urlParams.get("session_id");
      const success = urlParams.get("success");

      if (sessionId && success === "true") {
        const token = localStorage.getItem("token");
        if (!token) return;

        const apiUrl = process.env.REACT_APP_API_URL || "http://localhost:5000";

        const response = await fetch(
          `${apiUrl}/api/payments/session/${sessionId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const data = await response.json();

        if (data.success && data.data.user) {
          // ‚úÖ –ó–î–ï–°–¨ –ú–û–ñ–ï–¢–ï –û–ë–ù–û–í–ò–¢–¨ –ì–õ–û–ë–ê–õ–¨–ù–û–ï –°–û–°–¢–û–Ø–ù–ò–ï –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–Ø
          // –ù–∞–ø—Ä–∏–º–µ—Ä, –µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç–µ Context:
          // setUser(data.data.user);

          // –ò–ª–∏ –µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç–µ Redux:
          // dispatch(updateUser(data.data.user));

          // –í—Ä–µ–º–µ–Ω–Ω–æ–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ:
          alert(`üéâ Successfully upgraded to ${data.data.planId} plan!`);
        }

        window.history.replaceState(
          {},
          document.title,
          window.location.pathname
        );
      }
    } catch (error) {
      console.error("‚ùå Payment verification error:", error);
    }
  };
  return (
    <ErrorBoundary>
      <AuthProvider>
        <Router>
          <MainLayout>
            <AppRouter />
          </MainLayout>
          <NotificationSystem />
        </Router>
      </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;
