      this.renderTimestamp(currentTime);
      return;
    }

    // Выводим список активных элементов
    activeItems.forEach((item, index) => {
      console.log(`  ${index + 1}. ${item.name} (${item.type || item.trackType}) - ${item.startTime}с-${(item.startTime || 0) + (item.duration || 0)}с`);
    });

    // Сортируем элементы по слоям (z-index)
