    // Показываем время при отсутствии элементов
    this.ctx.fillStyle = "#333333";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(24, this.canvas.width / 40)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(`Время: ${currentTime.toFixed(1)}с`, this.canvas.width / 2, this.canvas.height / 2);
  }
  renderErrorFrame(currentTime, errorMessage) {
    // Красный фон для ошибки
    this.ctx.fillStyle = "#ff0000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    // Белый текст с ошибкой
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(32, this.canvas.width / 30)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    const x = this.canvas.width / 2;
    const y = this.canvas.height / 2;
    this.ctx.fillText(`ОШИБКА КАДРА`, x, y - 40);
    this.ctx.fillText(`Время: ${currentTime.toFixed(1)}с`, x, y);
    this.ctx.fillText(errorMessage.substring(0, 50), x, y + 40);
  }
