import { useState, useEffect } from "react";
import { X, Download, Check, AlertCircle, FolderOpen, ChevronDown, ChevronUp } from "lucide-react";

export const VideoExportModal = ({
  isOpen,
  onClose,
  timelineItems,
  tracks,
  videoDuration,
  onExport,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1920x1080",
    fps: 30,
    bitrate: 5000,
    format: "mp4",
    quality: "high",
    saveDirectory: null,
    filename: `video_${Date.now()}`
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState("idle");
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [showAllElements, setShowAllElements] = useState(false); // Для кнопки "Еще"

  // Предустановки для быстрого выбора
  const presets = [
    { name: "YouTube 1080p", resolution: "1920x1080", fps: 30, quality: "high", format: "mp4" },
    { name: "Instagram", resolution: "1080x1080", fps: 30, quality: "medium", format: "mp4" },
    { name: "TikTok", resolution: "1080x1920", fps: 30, quality: "medium", format: "mp4" },
    { name: "Web", resolution: "1280x720", fps: 30, quality: "medium", format: "webm" },
  ];

  const qualityOptions = [
    { value: "low", label: "Низкое", bitrate: 1000 },
    { value: "medium", label: "Среднее", bitrate: 3000 },
    { value: "high", label: "Высокое", bitrate: 5000 },
    { value: "ultra", label: "Ультра", bitrate: 10000 },
  ];

  const formatOptions = [
    { value: "mp4", label: "MP4" },
    { value: "webm", label: "WebM" },
    { value: "mov", label: "MOV" },
  ];

  // Быстрый расчет времени экспорта
  useEffect(() => {
    if (videoDuration && timelineItems) {
      const complexity = timelineItems.filter(item => item.trackType === "overlay").length;
      const baseTime = Math.max(2, videoDuration * 0.1);
      const complexityMultiplier = 1 + (complexity * 0.1);
      setEstimatedTime(Math.ceil(baseTime * complexityMultiplier));
    }
  }, [videoDuration, timelineItems]);

  // Синхронизация битрейта с качеством
  useEffect(() => {
    const quality = qualityOptions.find(q => q.value === exportSettings.quality);
    if (quality && quality.bitrate !== exportSettings.bitrate) {
      setExportSettings(prev => ({ ...prev, bitrate: quality.bitrate }));
    }
  }, [exportSettings.quality]);

  // Выбор папки для сохранения
  const handleSelectDirectory = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const directoryHandle = await window.showDirectoryPicker();
        setExportSettings(prev => ({ 
          ...prev, 
          saveDirectory: directoryHandle 
        }));
      } else {
        alert('Ваш браузер не поддерживает выбор папки. Файл будет сохранен в папку загрузок.');
      }
    } catch (error) {
      if (error.name !== 'AbortError') {
        console.error('Ошибка выбора папки:', error);
      }
    }
  };

  // Применение пресета
  const applyPreset = (preset) => {
    setExportSettings(prev => ({
      ...prev,
      ...preset
    }));
  };

  const getFileSize = () => {
    if (!videoDuration) return "~";
    const sizeInMB = (exportSettings.bitrate * videoDuration * 1000) / (8 * 1024 * 1024);
    return sizeInMB < 1024 ? `~${sizeInMB.toFixed(0)} МБ` : `~${(sizeInMB / 1024).toFixed(1)} ГБ`;
  };

  const handleExport = async () => {
    if (!timelineItems || timelineItems.length === 0) {
      alert('Нет элементов для экспорта!');
      return;
    }

    console.log('=== ДИАГНОСТИКА ПЕРЕД ЭКСПОРТОМ ===');
    console.log('TimelineItems:', timelineItems);
    console.log('Длительность видео:', videoDuration);
    console.log('Настройки экспорта:', exportSettings);
    
    // Детальная диагностика каждого элемента
    timelineItems.forEach((item, index) => {
      console.log(`Элемент ${index}:`, {
        id: item.id,
        name: item.name,
        type: item.type,
        trackType: item.trackType,
        startTime: item.startTime,
        duration: item.duration,
        hasFile: !!item.file,
        hasSrc: !!item.src,
        hasUrl: !!item.url,
        fileType: item.file?.type,
        allProps: Object.keys(item)
      });
    });
    console.log('=====================================');

    setIsExporting(true);
    setExportStatus("processing");
    setExportProgress(0);

    try {
      await actualVideoExport();
      setExportStatus("success");
    } catch (error) {
      console.error("❌ КРИТИЧЕСКАЯ ОШИБКА ЭКСПОРТА:", error);
      alert(`Ошибка экспорта: ${error.message}`);
      setExportStatus("error");
    } finally {
      setIsExporting(false);
    }
  };

  const actualVideoExport = async () => {
    try {
      const videoExporter = new VideoExporter({
        timelineItems,
        tracks,
        videoDuration,
        settings: exportSettings,
        onProgress: setExportProgress
      });

      const exportedVideoBlob = await videoExporter.render();
      await downloadVideo(exportedVideoBlob);
    } catch (error) {
      console.error('Ошибка при экспорте:', error);
      throw error;
    }
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    
    try {
      if (exportSettings.saveDirectory && 'showDirectoryPicker' in window) {
        const fileHandle = await exportSettings.saveDirectory.getFileHandle(filename, {
          create: true,
        });
        const writable = await fileHandle.createWritable();
        await writable.write(blob);
        await writable.close();
      } else {
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Ошибка сохранения:', error);
      throw error;
    }
  };

  const handleClose = () => {
    if (!isExporting) {
      setExportStatus("idle");
      setExportProgress(0);
      onClose();
    }
  };

  if (!isOpen) return null;

  // Логика отображения элементов
  const elementsToShow = showAllElements ? timelineItems : timelineItems?.slice(0, 3);
  const hasMoreElements = timelineItems && timelineItems.length > 3;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-hidden">
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-800 flex items-center">
            <Download size={20} className="mr-2 text-blue-600" />
            Экспорт видео
          </h2>
          <button
            onClick={handleClose}
            className="p-1 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={18} />
          </button>
        </div>

        <div className="p-4 space-y-4 max-h-[calc(90vh-120px)] overflow-y-auto">
          {/* Статус экспорта */}
          {exportStatus !== "idle" && (
            <div className={`p-3 rounded-lg ${
              exportStatus === "processing" ? "bg-blue-50 border border-blue-200" :
              exportStatus === "success" ? "bg-green-50 border border-green-200" :
              "bg-red-50 border border-red-200"
            }`}>
              <div className="flex items-center">
                {exportStatus === "processing" && (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                    <div className="flex-1">
                      <div className="flex justify-between text-sm text-blue-700 mb-1">
                        <span>Экспорт...</span>
                        <span>{exportProgress}%</span>
                      </div>
                      <div className="w-full bg-blue-200 rounded-full h-1.5">
                        <div
                          className="bg-blue-600 h-1.5 rounded-full transition-all duration-300"
                          style={{ width: `${exportProgress}%` }}
                        />
                      </div>
                    </div>
                  </>
                )}
                {exportStatus === "success" && (
                  <>
                    <Check size={16} className="text-green-600 mr-2" />
                    <span className="text-green-700 text-sm font-medium">Готово!</span>
                  </>
                )}
                {exportStatus === "error" && (
                  <>
                    <AlertCircle size={16} className="text-red-600 mr-2" />
                    <span className="text-red-700 text-sm font-medium">Ошибка экспорта</span>
                  </>
                )}
              </div>
            </div>
          )}

          {/* Быстрые пресеты */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Быстрые настройки
            </label>
            <div className="grid grid-cols-2 gap-2">
              {presets.map((preset) => (
                <button
                  key={preset.name}
                  onClick={() => applyPreset(preset)}
                  className="p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                  disabled={isExporting}
                >
                  {preset.name}
                </button>
              ))}
            </div>
          </div>

          {/* Настройки экспорта */}
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Разрешение
              </label>
              <select
                value={exportSettings.resolution}
                onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="3840x2160">4K</option>
                <option value="1920x1080">1080p</option>
                <option value="1280x720">720p</option>
                <option value="854x480">480p</option>
                <option value="1080x1920">9:16 (TikTok)</option>
                <option value="1080x1080">1:1 (Instagram)</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                FPS
              </label>
              <select
                value={exportSettings.fps}
                onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="24">24</option>
                <option value="30">30</option>
                <option value="60">60</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Качество
              </label>
              <select
                value={exportSettings.quality}
                onChange={(e) => setExportSettings(prev => ({ ...prev, quality: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {qualityOptions.map((quality) => (
                  <option key={quality.value} value={quality.value}>
                    {quality.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Формат
              </label>
              <select
                value={exportSettings.format}
                onChange={(e) => setExportSettings(prev => ({ ...prev, format: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {formatOptions.map((format) => (
                  <option key={format.value} value={format.value}>
                    {format.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Имя файла и папка сохранения */}
          <div className="space-y-2">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Имя файла
              </label>
              <input
                type="text"
                value={exportSettings.filename}
                onChange={(e) => setExportSettings(prev => ({ ...prev, filename: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
                placeholder="Имя файла"
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Папка для сохранения
              </label>
              <button
                onClick={handleSelectDirectory}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors flex items-center justify-between"
                disabled={isExporting}
              >
                <span className="text-gray-600">
                  {exportSettings.saveDirectory ? exportSettings.saveDirectory.name : 'Выберите папку'}
                </span>
                <FolderOpen size={16} />
              </button>
            </div>
          </div>

          {/* Информация */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="grid grid-cols-2 gap-2 text-xs">
              <div>
                <span className="text-gray-600">Длительность:</span>
                <span className="ml-1 font-medium">{Math.round(videoDuration)}с</span>
              </div>
              <div>
                <span className="text-gray-600">Размер:</span>
                <span className="ml-1 font-medium">{getFileSize()}</span>
              </div>
              <div>
                <span className="text-gray-600">Битрейт:</span>
                <span className="ml-1 font-medium">{exportSettings.bitrate} kbps</span>
              </div>
              <div>
                <span className="text-gray-600">Время:</span>
                <span className="ml-1 font-medium">~{estimatedTime}с</span>
              </div>
            </div>
          </div>

          {/* Элементы для экспорта - ИСПРАВЛЕНО */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <span className="text-xs font-medium text-gray-800">Элементы</span>
              <span className="text-xs text-gray-600">{timelineItems?.length || 0}</span>
            </div>
            {timelineItems && timelineItems.length > 0 ? (
              <div className="space-y-1">
                {elementsToShow?.map((item) => (
                  <div key={item.id} className="flex items-center justify-between text-xs">
                    <span className="text-gray-700 truncate">{item.name}</span>
                    <span className={`px-1 py-0.5 rounded text-xs ${
                      item.trackType === "main" ? "bg-blue-100 text-blue-700" :
                      item.trackType === "audio" ? "bg-green-100 text-green-700" :
                      "bg-purple-100 text-purple-700"
                    }`}>
                      {item.trackType}
                    </span>
                  </div>
                ))}
                {hasMoreElements && (
                  <button
                    onClick={() => setShowAllElements(!showAllElements)}
                    className="w-full text-xs text-blue-600 hover:text-blue-800 flex items-center justify-center py-1 transition-colors"
                  >
                    {showAllElements ? (
                      <>
                        Скрыть <ChevronUp size={12} className="ml-1" />
                      </>
                    ) : (
                      <>
                        +{timelineItems.length - 3} ещё <ChevronDown size={12} className="ml-1" />
                      </>
                    )}
                  </button>
                )}
              </div>
            ) : (
              <div className="text-xs text-gray-500 text-center py-2">
                Нет элементов
              </div>
            )}
          </div>
        </div>

        {/* Кнопки */}
        <div className="flex justify-end space-x-3 p-4 border-t border-gray-200">
          <button
            onClick={handleClose}
            className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
            disabled={isExporting}
          >
            Отмена
          </button>
          <button
            onClick={handleExport}
            disabled={isExporting || !timelineItems || timelineItems.length === 0}
            className={`px-4 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
              isExporting || !timelineItems || timelineItems.length === 0
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700"
            }`}
          >
            {isExporting ? (
              <>
                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-2"></div>
                Экспорт...
              </>
            ) : (
              <>
                <Download size={14} className="mr-2" />
                Экспортировать
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// Полностью переписанный VideoExporter класс для правильного экспорта
class VideoExporter {
  constructor({ timelineItems, tracks, videoDuration, settings, onProgress }) {
    this.timelineItems = timelineItems;
    this.tracks = tracks;
    this.videoDuration = videoDuration;
    this.settings = settings;
    this.onProgress = onProgress;

    // Создаем основной canvas для композиции
    this.canvas = document.createElement("canvas");
    this.ctx = this.canvas.getContext("2d");

    const [width, height] = settings.resolution.split("x").map(Number);
    this.canvas.width = width;
    this.canvas.height = height;

    // Создаем аудио контекст для работы со звуком
    this.audioContext = null;
    this.audioSources = [];
    this.audioBuffers = new Map();
    this.audioDestination = null;
    
    // Подготовленные элементы
    this.preparedElements = new Map();
  }

  async render() {
    try {
      console.log('🎬 СТАРТ ЭКСПОРТА ВИДЕО');
      console.log('📊 Параметры:', {
        элементов: this.timelineItems.length,
        длительность: this.videoDuration,
        разрешение: this.settings.resolution,
        fps: this.settings.fps
      });

      // Проверяем базовые требования
      if (!this.timelineItems || this.timelineItems.length === 0) {
        throw new Error('Нет элементов для экспорта');
      }

      if (!this.videoDuration || this.videoDuration <= 0) {
        throw new Error('Неверная длительность видео');
      }

      // Подготавливаем все элементы
      await this.prepareAllElements();

      // Настраиваем аудио (опционально)
      await this.setupAudio();

      // Создаем видео поток
      const stream = this.canvas.captureStream(this.settings.fps);
      console.log('🎥 Canvas stream создан:', stream);
      
      // Добавляем аудио к потоку (если есть)
      if (this.audioContext && this.audioDestination) {
        const audioTracks = this.audioDestination.stream.getAudioTracks();
        audioTracks.forEach(track => {
          stream.addTrack(track);
        });
        console.log(`🔊 Добавлено ${audioTracks.length} аудио треков`);
      }

      // Проверяем поддерживаемые форматы
      const mimeType = this.getMimeType();
      console.log('🎞️ Используемый MIME тип:', mimeType);

      // Настраиваем запись
      const options = {
        mimeType: mimeType,
        videoBitsPerSecond: this.settings.bitrate * 1000,
      };

      // Добавляем аудио битрейт только если есть аудио
      if (this.audioDestination) {
        options.audioBitsPerSecond = 128000;
      }

      console.log('⚙️ Опции MediaRecorder:', options);

      // Проверяем поддержку MediaRecorder
      if (!MediaRecorder.isTypeSupported(mimeType)) {
        console.warn('⚠️ MIME тип не поддерживается, используем базовый');
        options.mimeType = 'video/webm';
      }

      const mediaRecorder = new MediaRecorder(stream, options);
      const chunks = [];
      let recordedSize = 0;

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
          recordedSize += event.data.size;
          console.log(`📦 Получен чанк: ${event.data.size} байт (всего: ${recordedSize})`);
        }
      };

      return new Promise((resolve, reject) => {
        mediaRecorder.onstop = () => {
          console.log(`✅ Запись завершена. Чанков: ${chunks.length}, размер: ${recordedSize} байт`);
          
          if (chunks.length === 0 || recordedSize === 0) {
            reject(new Error('Не удалось записать видео - нет данных'));
            return;
          }

          const blob = new Blob(chunks, {
            type: `video/${this.settings.format}`,
          });
          
          console.log(`🎉 Финальный файл: ${(blob.size / 1024 / 1024).toFixed(2)} МБ`);
          
          // Очистка ресурсов
          this.cleanup();
          
          resolve(blob);
        };

        mediaRecorder.onerror = (error) => {
          console.error('❌ Ошибка MediaRecorder:', error);
          this.cleanup();
          reject(new Error(`Ошибка записи: ${error.message || error}`));
        };

        // Начинаем запись
        mediaRecorder.start(1000);
        console.log('🔴 MediaRecorder запущен');

        // Запускаем рендеринг кадров
        this.renderFrames()
          .then(() => {
            console.log('🎯 Рендеринг завершен, останавливаем запись...');
            setTimeout(() => {
              if (mediaRecorder.state === 'recording') {
                mediaRecorder.stop();
              }
            }, 1000); // Даем время на финализацию
          })
          .catch((error) => {
            console.error('💥 Ошибка рендеринга:', error);
            if (mediaRecorder.state === 'recording') {
              mediaRecorder.stop();
            }
            reject(error);
          });
      });
    } catch (error) {
      console.error('💥 КРИТИЧЕСКАЯ ОШИБКА render():', error);
      this.cleanup();
      throw error;
    }
  }

  async prepareAllElements() {
    console.log('📦 Подготовка элементов...');
    this.onProgress?.(10);

    const preparationPromises = this.timelineItems.map(async (item, index) => {
      try {
        console.log(`📄 Подготавливаем [${index}]: ${item.name} (${item.type || item.trackType})`);
        
        if (this.isVideoElement(item)) {
          await this.prepareVideoElement(item);
        } else if (this.isImageElement(item)) {
          await this.prepareImageElement(item);
        } else if (this.isAudioElement(item)) {
          await this.prepareAudioElement(item);
        }
        
        this.preparedElements.set(item.id, item);
        console.log(`✅ Элемент подготовлен: ${item.name}`);
      } catch (error) {
        console.warn(`⚠️ Ошибка подготовки ${item.name}:`, error);
      }
    });

    await Promise.all(preparationPromises);
    this.onProgress?.(30);
    console.log('✅ Все элементы подготовлены');
  }

  isVideoElement(item) {
    return (
      item.type === "video" || 
      item.trackType === "main" ||
      (item.file && item.file.type?.startsWith('video/')) ||
      (item.src && /\.(mp4|webm|mov|avi)$/i.test(item.src)) ||
      (item.url && /\.(mp4|webm|mov|avi)$/i.test(item.url))
    );
  }

  isImageElement(item) {
    return (
      item.type === "image" ||
      (item.file && item.file.type?.startsWith('image/')) ||
      (item.src && /\.(jpg|jpeg|png|gif|webp)$/i.test(item.src)) ||
      (item.url && /\.(jpg|jpeg|png|gif|webp)$/i.test(item.url))
    );
  }

  isAudioElement(item) {
    return (
      item.type === "audio" || 
      item.trackType === "audio" ||
      (item.file && item.file.type?.startsWith('audio/')) ||
      (item.src && /\.(mp3|wav|ogg|m4a)$/i.test(item.src)) ||
      (item.url && /\.(mp3|wav|ogg|m4a)$/i.test(item.url))
    );
  }

  async prepareVideoElement(item) {
    if (item.videoElement) return;

    const video = document.createElement("video");
    video.crossOrigin = "anonymous";
    video.muted = true;
    video.preload = "metadata";
    
    // Получаем URL
    let videoUrl = this.getElementUrl(item);
    if (!videoUrl) {
      console.warn(`❌ Не найден URL для видео: ${item.name}`);
      return;
    }

    video.src = videoUrl;
    item.videoElement = video;
    
    await new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        console.warn(`⏰ Таймаут загрузки видео: ${item.name}`);
        resolve();
      }, 10000);

      video.onloadedmetadata = () => {
        clearTimeout(timeout);
        console.log(`🎥 Видео загружено: ${item.name} (${video.duration.toFixed(1)}с)`);
        resolve();
      };
      
      video.onerror = (e) => {
        clearTimeout(timeout);
        console.error(`❌ Ошибка загрузки видео: ${item.name}`, e);
        resolve(); // Не блокируем весь процесс
      };
    });
  }

  async prepareImageElement(item) {
    if (item.imageElement) return;

    const img = new Image();
    img.crossOrigin = "anonymous";
    
    let imageUrl = this.getElementUrl(item);
    if (!imageUrl) {
      console.warn(`❌ Не найден URL для изображения: ${item.name}`);
      return;
    }

    img.src = imageUrl;
    item.imageElement = img;
    
    await new Promise((resolve) => {
      const timeout = setTimeout(() => {
        console.warn(`⏰ Таймаут загрузки изображения: ${item.name}`);
        resolve();
      }, 5000);

      img.onload = () => {
        clearTimeout(timeout);
        console.log(`🖼️ Изображение загружено: ${item.name} (${img.naturalWidth}x${img.naturalHeight})`);
        resolve();
      };
      
      img.onerror = (e) => {
        clearTimeout(timeout);
        console.error(`❌ Ошибка загрузки изображения: ${item.name}`, e);
        resolve();
      };
    });
  }

  async prepareAudioElement(item) {
    if (!this.audioContext) {
      this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
    }

    let audioUrl = this.getElementUrl(item);
    if (!audioUrl) {
      console.warn(`❌ Не найден URL для аудио: ${item.name}`);
      return;
    }

    try {
      console.log(`🎵 Загружаем аудио: ${item.name}`);
      
      let arrayBuffer;
      if (item.file && item.file instanceof File) {
        arrayBuffer = await item.file.arrayBuffer();
      } else {
        const response = await fetch(audioUrl);
        arrayBuffer = await response.arrayBuffer();
      }

      const audioBuffer = await this.audioContext.decodeAudioData(arrayBuffer);
      this.audioBuffers.set(item.id, audioBuffer);
      
      console.log(`🎵 Аудио подготовлено: ${item.name} (${audioBuffer.duration.toFixed(1)}с)`);
    } catch (error) {
      console.error(`❌ Ошибка подготовки аудио ${item.name}:`, error);
    }
  }

  getElementUrl(item) {
    if (item.file && item.file instanceof File) {
      return URL.createObjectURL(item.file);
    }
    return item.src || item.url || item.data;
  }

  async setupAudio() {
    if (!this.audioContext || this.audioBuffers.size === 0) return;

    console.log('🔊 Настраиваем аудио...');
    
    // Создаем destination для микширования аудио
    this.audioDestination = this.audioContext.createMediaStreamDestination();
    
    // Создаем источники для каждого аудио элемента
    for (const [itemId, audioBuffer] of this.audioBuffers) {
      const item = this.timelineItems.find(i => i.id === itemId);
      if (!item) continue;

      // Создаем источник и подключаем к destination
      const source = this.audioContext.createBufferSource();
      source.buffer = audioBuffer;
      source.connect(this.audioDestination);
      
      // Добавляем в список источников
      this.audioSources.push({
        source,
        item,
        startTime: item.startTime || 0,
        duration: item.duration || audioBuffer.duration
      });
    }

    console.log(`🔊 Подготовлено ${this.audioSources.length} аудио источников`);
  }

  async renderFrames() {
    const frameCount = Math.ceil(this.videoDuration * this.settings.fps);
    const frameDuration = 1000 / this.settings.fps;
    
    console.log(`🎞️ Начинаем рендеринг ${frameCount} кадров (${this.settings.fps} FPS)`);
    console.log(`⏱️ Длительность кадра: ${frameDuration.toFixed(2)}мс`);

    // Запускаем аудио источники
    this.startAudioSources();

    // Рендерим тестовый кадр для проверки
    console.log('🧪 Рендерим тестовый кадр...');
    await this.renderFrame(0);
    console.log('✅ Тестовый кадр отрендерен');

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame / this.settings.fps;
      
      try {
        await this.renderFrame(currentTime);
        
        // Обновляем прогресс
        const progress = 30 + Math.round((frame / frameCount) * 60);
        this.onProgress?.(progress);
        
        // Логируем прогресс каждые 30 кадров
        if (frame % 30 === 0) {
          console.log(`🎬 Кадр ${frame}/${frameCount} (${currentTime.toFixed(1)}с) - ${progress}%`);
          await new Promise(resolve => setTimeout(resolve, 1));
        }
      } catch (error) {
        console.error(`❌ Ошибка рендеринга кадра ${frame} (время ${currentTime.toFixed(2)}с):`, error);
        // Рендерим заглушку вместо ошибочного кадра
        this.renderErrorFrame(currentTime, error.message);
      }
    }

    this.onProgress?.(90);
    console.log('🎉 Все кадры отрендерены успешно!');
  }

  startAudioSources() {
    if (!this.audioContext || this.audioSources.length === 0) return;

    console.log('▶️ Запускаем аудио источники...');
    
    // Возобновляем аудио контекст если он приостановлен
    if (this.audioContext.state === 'suspended') {
      this.audioContext.resume();
    }
    
    this.audioSources.forEach(({ source, item, startTime, duration }) => {
      try {
        // Планируем воспроизведение аудио согласно таймлайну
        const when = this.audioContext.currentTime + startTime;
        const offset = 0;
        const sourceDuration = Math.min(duration, source.buffer.duration);
        
        source.start(when, offset, sourceDuration);
        console.log(`🎵 Запущено аудио: ${item.name} в ${startTime}с на ${sourceDuration}с`);
      } catch (error) {
        console.warn(`⚠️ Ошибка запуска аудио ${item.name}:`, error);
      }
    });
  }

  async renderFrame(currentTime) {
    console.log(`🖼️ Рендерим кадр на времени ${currentTime.toFixed(2)}с`);
    
    // Очищаем canvas черным фоном
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    // Получаем активные элементы для текущего времени
    const activeItems = this.getActiveItems(currentTime);
    console.log(`📋 Активных элементов на ${currentTime.toFixed(2)}с: ${activeItems.length}`);

    if (activeItems.length === 0) {
      console.log('⚠️ Нет активных элементов, рендерим временную метку');
      this.renderTimestamp(currentTime);
      return;
    }

    // Выводим список активных элементов
    activeItems.forEach((item, index) => {
      console.log(`  ${index + 1}. ${item.name} (${item.type || item.trackType}) - ${item.startTime}с-${(item.startTime || 0) + (item.duration || 0)}с`);
    });

    // Сортируем элементы по слоям (z-index)
    const sortedItems = this.sortItemsByLayer(activeItems);

    // Рендерим каждый активный элемент
    for (let i = 0; i < sortedItems.length; i++) {
      const item = sortedItems[i];
      const relativeTime = currentTime - (item.startTime || 0);
      
      console.log(`🎨 Рендерим элемент ${i + 1}/${sortedItems.length}: ${item.name} (относительное время: ${relativeTime.toFixed(2)}с)`);
      
      try {
        await this.renderItem(item, relativeTime, currentTime);
        console.log(`✅ Элемент ${item.name} отрендерен`);
      } catch (error) {
        console.error(`❌ Ошибка рендеринга элемента ${item.name}:`, error);
        this.renderPlaceholderItem(`Ошибка: ${item.name}`);
      }
    }
    
    console.log(`✅ Кадр ${currentTime.toFixed(2)}с завершен`);
  }

  getActiveItems(currentTime) {
    return this.timelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || this.videoDuration;
      const endTime = startTime + duration;
      
      return currentTime >= startTime && currentTime < endTime;
    });
  }

  sortItemsByLayer(items) {
    const layerOrder = {
      background: 1,
      main: 2,
      video: 2, 
      image: 3,
      overlay: 4,
      text: 5,
      title: 5,
      audio: 0 // Аудио не рендерим визуально
    };

    return [...items].sort((a, b) => {
      const orderA = layerOrder[a.trackType] || layerOrder[a.type] || 3;
      const orderB = layerOrder[b.trackType] || layerOrder[b.type] || 3;
      return orderA - orderB;
    });
  }

  async renderItem(item, relativeTime, currentTime) {
    try {
      this.ctx.save();

      // Пропускаем аудио элементы
      if (this.isAudioElement(item)) {
        return;
      }

      if (item.videoElement && this.isVideoElement(item)) {
        await this.renderVideoItem(item, relativeTime);
      } else if (item.imageElement && this.isImageElement(item)) {
        this.renderImageItem(item);
      } else if (item.text || item.content || item.title) {
        this.renderTextItem(item);
      } else {
        this.renderPlaceholderItem(item.name || 'Элемент');
      }
    } catch (error) {
      console.warn(`⚠️ Ошибка рендеринга элемента ${item.name}:`, error);
      this.renderPlaceholderItem(`Ошибка: ${item.name}`);
    } finally {
      this.ctx.restore();
    }
  }

  async renderVideoItem(item, relativeTime) {
    const video = item.videoElement;
    if (!video || video.readyState < 2) {
      this.renderPlaceholderItem(`Загрузка: ${item.name}`);
      return;
    }

    try {
      // Устанавливаем время видео
      const targetTime = Math.max(0, Math.min(relativeTime, video.duration));
      
      if (Math.abs(video.currentTime - targetTime) > 0.1) {
        video.currentTime = targetTime;
        
        // Ждем готовности кадра
        let attempts = 0;
        while (video.readyState < 2 && attempts < 20) {
          await new Promise(resolve => setTimeout(resolve, 5));
          attempts++;
        }
      }

      // Рендерим видео кадр
      this.drawScaledElement(video);
    } catch (error) {
      console.warn(`⚠️ Ошибка рендеринга видео ${item.name}:`, error);
      this.renderPlaceholderItem(`Видео: ${item.name}`);
    }
  }

  renderImageItem(item) {
    const img = item.imageElement;
    if (!img || !img.complete || img.naturalWidth === 0) {
      this.renderPlaceholderItem(`Изображение: ${item.name}`);
      return;
    }

    try {
      this.drawScaledElement(img);
    } catch (error) {
      console.warn(`⚠️ Ошибка рендеринга изображения ${item.name}:`, error);
      this.renderPlaceholderItem(`Изображение: ${item.name}`);
    }
  }

  renderTextItem(item) {
    const text = item.text || item.content || item.title || item.name;
    
    // Настройка текста
    this.ctx.fillStyle = item.color || "#ffffff";
    this.ctx.font = `${item.fontSize || Math.min(48, this.canvas.width / 20)}px ${item.fontFamily || 'Arial'}`;
    this.ctx.textAlign = item.textAlign || "center";
    this.ctx.textBaseline = "middle";
    
    // Позиция текста
    const x = item.x !== undefined ? item.x : this.canvas.width / 2;
    const y = item.y !== undefined ? item.y : this.canvas.height / 2;
    
    // Обводка текста для лучшей читаемости
    this.ctx.strokeStyle = "#000000";
    this.ctx.lineWidth = 2;
    this.ctx.strokeText(text, x, y);
    this.ctx.fillText(text, x, y);
  }

  renderPlaceholderItem(text) {
    // Полупрозрачный фон
    this.ctx.fillStyle = "rgba(64, 64, 64, 0.8)";
    this.ctx.fillRect(10, 10, this.canvas.width - 20, 40);
    
    // Текст
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(16, this.canvas.width / 60)}px Arial`;
    this.ctx.textAlign = "left";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(text, 20, 30);
  }

  renderTimestamp(currentTime) {
    // Показываем время при отсутствии элементов
    this.ctx.fillStyle = "#333333";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(24, this.canvas.width / 40)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(
      `Время: ${currentTime.toFixed(1)}с`, 
      this.canvas.width / 2, 
      this.canvas.height / 2
    );
  }

  renderErrorFrame(currentTime, errorMessage) {
    // Красный фон для ошибки
    this.ctx.fillStyle = "#ff0000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    // Белый текст с ошибкой
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(32, this.canvas.width / 30)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    
    const x = this.canvas.width / 2;
    const y = this.canvas.height / 2;
    
    this.ctx.fillText(`ОШИБКА КАДРА`, x, y - 40);
    this.ctx.fillText(`Время: ${currentTime.toFixed(1)}с`, x, y);
    this.ctx.fillText(errorMessage.substring(0, 50), x, y + 40);
  }

  drawScaledElement(element) {
    const elementWidth = element.videoWidth || element.naturalWidth || element.width;
    const elementHeight = element.videoHeight || element.naturalHeight || element.height;
    
    if (!elementWidth || !elementHeight) {
      console.warn('⚠️ Элемент не имеет размеров:', element);
      return;
    }
    
    const canvasRatio = this.canvas.width / this.canvas.height;
    const elementRatio = elementWidth / elementHeight;
    
    let drawWidth, drawHeight, drawX, drawY;

    // Масштабируем с сохранением пропорций (cover)
    if (elementRatio > canvasRatio) {
      // Элемент шире canvas
      drawHeight = this.canvas.height;
      drawWidth = this.canvas.height * elementRatio;
      drawX = (this.canvas.width - drawWidth) / 2;
      drawY = 0;
    } else {
      // Элемент выше canvas
      drawWidth = this.canvas.width;
      drawHeight = this.canvas.width / elementRatio;
      drawX = 0;
      drawY = (this.canvas.height - drawHeight) / 2;
    }

    try {
      this.ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
    } catch (error) {
      console.error('💥 Критическая ошибка отрисовки:', error);
      this.renderPlaceholderItem('Ошибка отрисовки');
    }
  }

  getMimeType() {
    // Проверяем поддерживаемые форматы
    const formats = [
      'video/webm;codecs=vp9,opus',
      'video/webm;codecs=vp8,opus', 
      'video/mp4;codecs=h264,aac',
      'video/webm;codecs=vp9',
      'video/webm;codecs=vp8',
      'video/webm'
    ];
    
    for (const format of formats) {
      if (MediaRecorder.isTypeSupported(format)) {
        console.log(`🎬 Используем формат: ${format}`);
        return format;
      }
    }
    
    console.warn('⚠️ Используем базовый формат: video/webm');
    return 'video/webm';
  }

  cleanup() {
    console.log('🧹 Очистка ресурсов...');
    
    // Останавливаем аудио источники
    this.audioSources.forEach(({ source }) => {
      try {
        if (source.buffer) {
          source.stop();
        }
      } catch (error) {
        // Источник уже остановлен
      }
    });
    
    // Очищаем URL объекты
    this.timelineItems.forEach(item => {
      if (item.videoElement?.src.startsWith('blob:')) {
        URL.revokeObjectURL(item.videoElement.src);
      }
      if (item.imageElement?.src.startsWith('blob:')) {
        URL.revokeObjectURL(item.imageElement.src);
      }
    });

    // Закрываем аудио контекст
    if (this.audioContext && this.audioContext.state !== 'closed') {
      this.audioContext.close();
    }

    // Очищаем массивы
    this.audioSources = [];
    this.audioBuffers.clear();
    this.preparedElements.clear();
    this.audioDestination = null;
  }
}

export const useVideoExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};
