import { useRef, useEffect, useState } from "react";
import { Upload, Video, Music, Trash2, Image, AlertTriangle, Loader2 } from "lucide-react";
import MediaStorage from "../components/MediaStorage"; // Импортируем MediaStorage

export const MediaLibrary = ({
  mediaLibrary,
  setMediaLibrary,
  activeTab,
  setActiveTab,
  draggedItem,
  setDraggedItem,
  setIsDragging,
  addToTimeline,
  formatTime,
}) => {
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  
  // Состояния для обработки загрузки и ошибок
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [storageInfo, setStorageInfo] = useState(null);

  // Инициализация MediaStorage при монтировании компонента
  useEffect(() => {
    const initializeStorage = async () => {
      try {
        setIsLoading(true);
        await MediaStorage.init();
        await loadFilesFromStorage();
        await updateStorageInfo();
        
        // Подписываемся на события обновления хранилища
        const handleStorageUpdate = (event) => {
          setStorageInfo(event.detail);
        };
        window.addEventListener('storageUpdated', handleStorageUpdate);
        
        return () => {
          window.removeEventListener('storageUpdated', handleStorageUpdate);
        };
      } catch (error) {
        console.error('Failed to initialize MediaStorage:', error);
        setError('Failed to initialize storage');
      } finally {
        setIsLoading(false);
      }
    };

    initializeStorage();
  }, []);

  // Загрузка файлов из MediaStorage
  const loadFilesFromStorage = async () => {
    try {
      const files = await MediaStorage.getAllFiles();
      setMediaLibrary({
        videos: files.videos || [],
        audios: files.audios || [],
        images: files.images || []
      });
    } catch (error) {
      console.error('Failed to load files from storage:', error);
      setError('Failed to load saved files');
    }
  };

  // Обновление информации о хранилище
  const updateStorageInfo = async () => {
    try {
      const info = await MediaStorage.getStorageInfo();
      setStorageInfo(info);
    } catch (error) {
      console.error('Failed to get storage info:', error);
    }
  };

  // Обработка загрузки видео
  const handleVideoUpload = async (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    setError(null);
    
    for (const file of files) {
      try {
        setIsLoading(true);
        
        // Проверяем, можно ли загрузить файл
        const canUpload = await MediaStorage.canUpload('videos');
        if (!canUpload) {
          const remaining = await MediaStorage.getRemainingSpace('videos');
          setError(`Storage limit reached. You can upload ${remaining} more videos.`);
          continue;
        }

        // Сохраняем файл в MediaStorage
        const savedFile = await MediaStorage.saveFile(file, 'videos');
        
        // Обновляем состояние компонента
        setMediaLibrary((prev) => ({
          ...prev,
          videos: [...prev.videos, savedFile],
        }));

      } catch (error) {
        console.error('Failed to save video:', error);
        setError(`Failed to save ${file.name}: ${error.message}`);
      }
    }
    
    setIsLoading(false);
    // Очищаем input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Обработка загрузки аудио
  const handleAudioUpload = async (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    setError(null);
    
    for (const file of files) {
      try {
        setIsLoading(true);
        
        const canUpload = await MediaStorage.canUpload('audios');
        if (!canUpload) {
          const remaining = await MediaStorage.getRemainingSpace('audios');
          setError(`Storage limit reached. You can upload ${remaining} more audio files.`);
          continue;
        }

        const savedFile = await MediaStorage.saveFile(file, 'audios');
        
        setMediaLibrary((prev) => ({
          ...prev,
          audios: [...prev.audios, savedFile],
        }));

      } catch (error) {
        console.error('Failed to save audio:', error);
        setError(`Failed to save ${file.name}: ${error.message}`);
      }
    }
    
    setIsLoading(false);
    if (audioInputRef.current) {
      audioInputRef.current.value = '';
    }
  };

  // Обработка загрузки изображений
  const handleImageUpload = async (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    setError(null);
    
    for (const file of files) {
      try {
        setIsLoading(true);
        
        const canUpload = await MediaStorage.canUpload('images');
        if (!canUpload) {
          const remaining = await MediaStorage.getRemainingSpace('images');
          setError(`Storage limit reached. You can upload ${remaining} more images.`);
          continue;
        }

        const savedFile = await MediaStorage.saveFile(file, 'images');
        
        setMediaLibrary((prev) => ({
          ...prev,
          images: [...prev.images, savedFile],
        }));

      } catch (error) {
        console.error('Failed to save image:', error);
        setError(`Failed to save ${file.name}: ${error.message}`);
      }
    }
    
    setIsLoading(false);
    if (imageInputRef.current) {
      imageInputRef.current.value = '';
    }
  };

  // Удаление из библиотеки
  const removeFromLibrary = async (itemId, type) => {
    try {
      setError(null);
      
      // Удаляем из MediaStorage
      await MediaStorage.deleteFile(itemId);
      
      // Обновляем состояние компонента
      setMediaLibrary((prev) => ({
        ...prev,
        [type]: prev[type].filter((item) => item.id !== itemId),
      }));
      
    } catch (error) {
      console.error('Failed to remove file:', error);
      setError('Failed to remove file');
    }
  };

  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setIsDragging(false);
  };

  // Очистка ошибки
  const clearError = () => {
    setError(null);
  };

  // Получить текущий тип файлов для отображения лимитов
  const getCurrentTypeKey = () => {
    return activeTab === 'videos' ? 'videos' : 
           activeTab === 'audios' ? 'audios' : 'images';
  };

  return (
    <div className="w-80 min-w-72 max-w-80 bg-white border-2 border-blue-200 flex flex-col rounded-2xl shadow-lg overflow-hidden flex-shrink-0">
      <div className="bg-blue-50 px-4 py-3 border-b border-blue-100">
        <h3 className="font-bold text-blue-800 flex items-center">
          <Upload size={18} className="mr-2" />
          Media Library
          {isLoading && <Loader2 size={16} className="ml-2 animate-spin" />}
        </h3>
        
        {/* Информация о хранилище */}
        {storageInfo && (
          <div className="text-xs text-blue-600 mt-1">
            {storageInfo.usage[getCurrentTypeKey()]}/{storageInfo.limits[getCurrentTypeKey()]} files
            {storageInfo.mode === 'fallback' && ' (fallback mode)'}
          </div>
        )}
      </div>

      {/* Отображение ошибок */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-3 mx-4 mt-2 rounded">
          <div className="flex items-center">
            <AlertTriangle size={16} className="text-red-400 mr-2" />
            <div className="flex-1">
              <p className="text-sm text-red-700">{error}</p>
            </div>
            <button
              onClick={clearError}
              className="text-red-400 hover:text-red-600"
            >
              ×
            </button>
          </div>
        </div>
      )}

      <div className="p-4 pb-0">
        <div className="flex bg-gray-100 rounded-xl p-1">
          {[
            { id: "videos", name: "Videos", icon: Video },
            { id: "audios", name: "Audio", icon: Music },
            { id: "images", name: "Images", icon: Image },
          ].map((tab) => {
            const IconComponent = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 p-3 text-sm font-medium transition-all duration-200 rounded-lg ${
                  activeTab === tab.id
                    ? "bg-white text-blue-600 shadow-sm"
                    : "text-gray-600 hover:text-gray-800"
                }`}
              >
                <IconComponent size={16} className="mr-2 inline" />
                {tab.name}
              </button>
            );
          })}
        </div>
      </div>

      <div className="p-4">
        <button
          onClick={() => {
            if (isLoading) return;
            if (activeTab === "videos") fileInputRef.current?.click();
            else if (activeTab === "audios") audioInputRef.current?.click();
            else imageInputRef.current?.click();
          }}
          disabled={isLoading}
          className={`w-full p-6 border-2 border-dashed border-gray-300 rounded-2xl hover:border-blue-400 hover:bg-blue-50/50 transition-all duration-200 text-center group ${
            isLoading ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          <div className="w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-3 group-hover:bg-blue-100 transition-colors">
            {isLoading ? (
              <Loader2 size={20} className="text-blue-500 animate-spin" />
            ) : (
              <Upload
                size={20}
                className="text-gray-500 group-hover:text-blue-500"
              />
            )}
          </div>
          <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">
            {isLoading ? 'Uploading...' : `Upload ${
              activeTab === "videos"
                ? "Video"
                : activeTab === "audios"
                ? "Audio"
                : "Images"
            }`}
          </span>
          <p className="text-xs text-gray-500 mt-1">
            {activeTab === "videos"
              ? "MP4, AVI, MOV up to 100MB"
              : activeTab === "audios"
              ? "MP3, WAV, M4A up to 100MB"
              : "JPG, PNG, GIF up to 100MB"}
          </p>
        </button>
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept="video/*"
        multiple
        onChange={handleVideoUpload}
        className="hidden"
        disabled={isLoading}
      />
      <input
        ref={audioInputRef}
        type="file"
        accept="audio/*"
        multiple
        onChange={handleAudioUpload}
        className="hidden"
        disabled={isLoading}
      />
      <input
        ref={imageInputRef}
        type="file"
        accept="image/*"
        multiple
        onChange={handleImageUpload}
        className="hidden"
        disabled={isLoading}
      />

      <div className="flex-1 overflow-y-auto px-4 pb-4">
        <div className="space-y-2">
          {mediaLibrary[activeTab].map((item) => (
            <div
              key={item.id}
              draggable="true"
              onDragStart={(e) => handleDragStart(e, item)}
              onDragEnd={handleDragEnd}
              className={`flex items-center p-3 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-sm transition-all duration-200 cursor-grab active:cursor-grabbing group ${
                draggedItem?.id === item.id ? "opacity-50" : ""
              }`}
              onClick={() => addToTimeline(item)}
            >
              <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center mr-3 shadow-sm group-hover:shadow">
                {activeTab === "videos" && (
                  <Video size={20} className="text-blue-500" />
                )}
                {activeTab === "audios" && (
                  <Music size={20} className="text-green-500" />
                )}
                {activeTab === "images" && (
                  <Image size={20} className="text-purple-500" />
                )}
              </div>
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-gray-900 truncate">
                  {item.name}
                </p>
                <p className="text-xs text-gray-500">
                  {activeTab === "images"
                    ? `${item.width || 0}×${item.height || 0}`
                    : formatTime(item.duration || 0)}
                </p>
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  removeFromLibrary(item.id, activeTab);
                }}
                className="opacity-0 group-hover:opacity-100 p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
              >
                <Trash2 size={14} />
              </button>
            </div>
          ))}
          {mediaLibrary[activeTab].length === 0 && !isLoading && (
            <div className="text-center py-8">
              <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-3">
                {activeTab === "videos" && (
                  <Video size={24} className="text-gray-400" />
                )}
                {activeTab === "audios" && (
                  <Music size={24} className="text-gray-400" />
                )}
                {activeTab === "images" && (
                  <Image size={24} className="text-gray-400" />
                )}
              </div>
              <p className="text-sm text-gray-500">
                No {activeTab} uploaded yet
              </p>
              <p className="text-xs text-gray-400 mt-1">
                Click upload button above
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
