import { useRef, useState, useEffect } from "react";


const SimpleTeleprompter = ({
  isOpen,
  onClose,
  scriptText = "",
  scriptTitle = "–°—Ü–µ–Ω–∞—Ä–∏–π",
}) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [speed, setSpeed] = useState(2);
  const [fontSize, setFontSize] = useState(24);
  const [position, setPosition] = useState(0);

  const textRef = useRef(null);
  const animationRef = useRef(null);
  const lastTimeRef = useRef(Date.now());

  // –ê–≤—Ç–æ–ø—Ä–æ–∫—Ä—É—Ç–∫–∞
  useEffect(() => {
    if (isPlaying && textRef.current) {
      const animate = () => {
        const now = Date.now();
        const deltaTime = now - lastTimeRef.current;
        lastTimeRef.current = now;

        setPosition((prev) => {
          const newPosition = prev + (speed * deltaTime) / 1000;
          const maxScroll =
            textRef.current.scrollHeight - textRef.current.clientHeight;

          if (newPosition >= maxScroll) {
            setIsPlaying(false);
            return maxScroll;
          }

          return Math.max(0, newPosition);
        });

        animationRef.current = requestAnimationFrame(animate);
      };

      animationRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isPlaying, speed]);

  // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∏
  useEffect(() => {
    if (textRef.current) {
      textRef.current.scrollTop = position;
    }
  }, [position]);

  // –°–±—Ä–æ—Å –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏
  useEffect(() => {
    if (isOpen) {
      setPosition(0);
      setIsPlaying(false);
      lastTimeRef.current = Date.now();
    }
  }, [isOpen]);

  if (!isOpen) return null;

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
    lastTimeRef.current = Date.now();
  };

  const handleReset = () => {
    setIsPlaying(false);
    setPosition(0);
  };

  return (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-hidden">
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800">
            üé¨ –°—É—Ñ–ª—ë—Ä - {scriptTitle}
          </h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 p-1"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        <div className="p-4 bg-gray-50 border-b">
          <div className="flex flex-wrap items-center gap-4 justify-between">
            {/* –û—Å–Ω–æ–≤–Ω—ã–µ –∫–Ω–æ–ø–∫–∏ */}
            <div className="flex gap-2">
              <button
                onClick={handlePlayPause}
                className={`px-4 py-2 rounded text-sm font-medium ${
                  isPlaying
                    ? "bg-gray-500 hover:bg-gray-600 text-white"
                    : "bg-blue-500 hover:bg-blue-600 text-white"
                }`}
              >
                {isPlaying ? "‚è∏Ô∏è –ü–∞—É–∑–∞" : "‚ñ∂Ô∏è –°—Ç–∞—Ä—Ç"}
              </button>
              <button
                onClick={handleReset}
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-gray-700 rounded text-sm font-medium"
              >
                üîÑ –°–Ω–∞—á–∞–ª–∞
              </button>
            </div>

            {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–∫–æ—Ä–æ—Å—Ç–∏ */}
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">–°–∫–æ—Ä–æ—Å—Ç—å:</span>
              {[0.5, 1, 2, 3, 4].map((speedValue) => (
                <button
                  key={speedValue}
                  onClick={() => setSpeed(speedValue)}
                  className={`px-2 py-1 text-xs rounded ${
                    speed === speedValue
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-700 hover:bg-gray-300"
                  }`}
                >
                  {speedValue}x
                </button>
              ))}
            </div>

            {/* –†–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞ */}
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">–†–∞–∑–º–µ—Ä:</span>
              {[20, 24, 28, 32, 36].map((size) => (
                <button
                  key={size}
                  onClick={() => setFontSize(size)}
                  className={`px-2 py-1 text-xs rounded ${
                    fontSize === size
                      ? "bg-blue-500 text-white"
                      : "bg-gray-200 text-gray-700 hover:bg-gray-300"
                  }`}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* –û–±–ª–∞—Å—Ç—å —á—Ç–µ–Ω–∏—è */}
        <div className="relative">
          <div
            ref={textRef}
            className="bg-black text-white p-6 overflow-y-auto"
            style={{
              height: "400px",
              fontSize: `${fontSize}px`,
              lineHeight: "1.8",
              fontFamily: "Arial, sans-serif",
            }}
          >
            <div className="whitespace-pre-wrap text-center">{scriptText}</div>
          </div>

          {/* –¶–µ–Ω—Ç—Ä–∞–ª—å–Ω–∞—è –ª–∏–Ω–∏—è */}
          <div className="absolute left-0 right-0 top-1/2 transform -translate-y-1/2 pointer-events-none">
            <div className="h-0.5 bg-red-500 opacity-40 mx-6"></div>
          </div>
        </div>

        {/* –ù–∏–∂–Ω—è—è –ø–∞–Ω–µ–ª—å */}
        <div className="p-4 bg-gray-50 border-t">
          <div className="flex justify-between text-sm text-gray-600">
            <span>–°–ª–æ–≤: {scriptText.trim().split(/\s+/).length}</span>
            <span>–°–∫–æ—Ä–æ—Å—Ç—å: {speed}x</span>
            <span>–†–∞–∑–º–µ—Ä: {fontSize}px</span>
          </div>
          <div className="mt-2 text-xs text-blue-600">
            üí° –†–∞—Å–ø–æ–ª–æ–∂–∏—Ç–µ —ç–∫—Ä–∞–Ω –∫–∞–∫ –º–æ–∂–Ω–æ –±–ª–∏–∂–µ –∫ –∫–∞–º–µ—Ä–µ –¥–ª—è –µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω–æ–≥–æ
            –≤–∑–≥–ª—è–¥–∞
          </div>
        </div>
      </div>
    </div>
  );
};
export default SimpleTeleprompter;
