// components/teleprompter/DraggableTeleprompterModal.jsx
import React, { useState, useRef, useEffect } from 'react';
import { Mic, Play, Pause, RotateCcw, Move, Maximize2, Minimize2, X, Volume2 } from 'lucide-react';

const DraggableTeleprompterModal = ({ 
  isOpen, 
  onClose, 
  script = "",
  scriptTitle = "Сценарий",
  onRecordingComplete
}) => {
  // Состояния суфлёра
  const [isPlaying, setIsPlaying] = useState(false);
  const [speed, setSpeed] = useState(5);
  const [fontSize, setFontSize] = useState(32);
  const [position, setPosition] = useState(0);
  
  // Состояния записи
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  
  // Состояния модального окна
  const [modalPosition, setModalPosition] = useState({ x: 100, y: 100 });
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  
  // Refs
  const textRef = useRef(null);
  const modalRef = useRef(null);
  const animationRef = useRef(null);
  const lastTimeRef = useRef(Date.now());
  const timerRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Автопрокрутка суфлёра
  useEffect(() => {
    if (isPlaying && textRef.current && isOpen) {
      const animate = () => {
        const now = Date.now();
        const deltaTime = now - lastTimeRef.current;
        lastTimeRef.current = now;

        setPosition(prev => {
          const newPosition = prev + (speed * deltaTime * 100);
          const maxScroll = textRef.current.scrollHeight - textRef.current.clientHeight;
          
          if (newPosition >= maxScroll) {
            setIsPlaying(false);
            return maxScroll;
          }
          
          return Math.max(0, newPosition);
        });

        animationRef.current = requestAnimationFrame(animate);
      };

      animationRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isPlaying, speed, isOpen]);

  // Синхронизация прокрутки
  useEffect(() => {
    if (textRef.current) {
      textRef.current.scrollTop = position;
    }
  }, [position]);

  // Таймер записи
  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } else {
      clearInterval(timerRef.current);
    }

    return () => clearInterval(timerRef.current);
  }, [isRecording]);

  // Сброс при открытии
  useEffect(() => {
    if (isOpen) {
      setPosition(0);
      setIsPlaying(false);
      setRecordingTime(0);
      lastTimeRef.current = Date.now();
    }
  }, [isOpen]);

  // Drag and Drop функциональность
  const handleMouseDown = (e) => {
    if (!isFullscreen && e.target.closest('.drag-handle')) {
      setIsDragging(true);
      const rect = modalRef.current.getBoundingClientRect();
      setDragOffset({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (isDragging && !isFullscreen) {
        const newX = e.clientX - dragOffset.x;
        const newY = e.clientY - dragOffset.y;
        
        // Ограничиваем положение в пределах экрана
        const maxX = window.innerWidth - 400;
        const maxY = window.innerHeight - 300;
        
        setModalPosition({
          x: Math.max(0, Math.min(newX, maxX)),
          y: Math.max(0, Math.min(newY, maxY))
        });
      }
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, dragOffset, isFullscreen]);

  // Запуск записи
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          sampleRate: 44100
        }
      });

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const audioUrl = URL.createObjectURL(audioBlob);
        setAudioBlob(audioBlob);
        setAudioUrl(audioUrl);
        
        if (onRecordingComplete) {
          onRecordingComplete({
            blob: audioBlob,
            url: audioUrl,
            duration: recordingTime,
            timestamp: new Date().toISOString(),
            scriptTitle: scriptTitle
          });
        }
        
        // Останавливаем поток
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start(100);
      setIsRecording(true);
      setRecordingTime(0);

    } catch (error) {
      console.error('Ошибка доступа к микрофону:', error);
      alert('Не удалось получить доступ к микрофону. Проверьте разрешения.');
    }
  };

  // Остановка записи
  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setIsPlaying(false);
    }
  };

  // Управление суфлёром
  const togglePlay = () => {
    setIsPlaying(!isPlaying);
    lastTimeRef.current = Date.now();
  };

  const resetTeleprompter = () => {
    setIsPlaying(false);
    setPosition(0);
  };

  // Старт записи с суфлёром
  const startRecordingWithTeleprompter = async () => {
    await startRecording();
    // Автоматически запускаем суфлёр через 1 секунду
    setTimeout(() => {
      setIsPlaying(true);
      lastTimeRef.current = Date.now();
    }, 1000);
  };

  // Форматирование времени
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const bloggerSpeeds = [3, 4, 5, 6, 7, 8, 10, 12];
  const fontSizes = [24, 28, 32, 36, 40, 44, 48];

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 pointer-events-none">
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/20 backdrop-blur-sm pointer-events-auto" />
      
      {/* Modal */}
      <div
        ref={modalRef}
        className="absolute pointer-events-auto"
        style={{
          left: isFullscreen ? 0 : modalPosition.x,
          top: isFullscreen ? 0 : modalPosition.y,
          width: isFullscreen ? '100%' : '500px',
          height: isFullscreen ? '100%' : 'auto',
          maxHeight: isFullscreen ? '100%' : '80vh'
        }}
        onMouseDown={handleMouseDown}
      >
        <div className="bg-white rounded-xl shadow-2xl border-2 border-slate-200 overflow-hidden h-full flex flex-col">
          {/* Header - Drag Handle */}
          <div className="drag-handle bg-gradient-to-r from-slate-700 to-blue-700 text-white p-4 cursor-move select-none">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Move size={18} />
                <h3 className="font-bold text-lg">🎬 {scriptTitle}</h3>
                {isRecording && (
                  <div className="flex items-center space-x-2 bg-red-500 px-3 py-1 rounded-full text-sm animate-pulse">
                    <div className="w-2 h-2 bg-white rounded-full"></div>
                    <span>REC {formatTime(recordingTime)}</span>
                  </div>
                )}
              </div>
              
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => setIsFullscreen(!isFullscreen)}
                  className="p-2 hover:bg-white/20 rounded-lg transition-colors"
                  title={isFullscreen ? "Свернуть" : "На весь экран"}
                >
                  {isFullscreen ? <Minimize2 size={16} /> : <Maximize2 size={16} />}
                </button>
                <button
                  onClick={onClose}
                  className="p-2 hover:bg-white/20 rounded-lg transition-colors"
                  title="Закрыть"
                >
                  <X size={16} />
                </button>
              </div>
            </div>
          </div>

          {/* Controls */}
          <div className="bg-gray-50 p-4 border-b border-gray-200">
            {/* Main Controls */}
            <div className="flex items-center justify-center space-x-3 mb-4">
              {!isRecording ? (
                <>
                  <button
                    onClick={startRecordingWithTeleprompter}
                    className="bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white font-bold py-2 px-4 rounded-lg transition-all duration-300 shadow-lg flex items-center space-x-2"
                  >
                    <Mic size={16} />
                    <span>🎬 Запись с суфлёром</span>
                  </button>
                  <button
                    onClick={startRecording}
                    className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded-lg transition-all duration-300 flex items-center space-x-2"
                  >
                    <Mic size={16} />
                    <span>Только запись</span>
                  </button>
                </>
              ) : (
                <button
                  onClick={stopRecording}
                  className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-6 rounded-lg transition-all duration-300"
                >
                  ⏹️ Остановить запись
                </button>
              )}
            </div>

            {/* Teleprompter Controls */}
            <div className="flex items-center justify-center space-x-2 mb-4">
              <button
                onClick={togglePlay}
                className={`p-2 rounded-lg transition-all duration-300 ${
                  isPlaying ? 'bg-yellow-500 text-white' : 'bg-green-500 text-white hover:bg-green-600'
                }`}
              >
                {isPlaying ? <Pause size={16} /> : <Play size={16} />}
              </button>
              <button
                onClick={resetTeleprompter}
                className="p-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-all duration-300"
              >
                <RotateCcw size={16} />
              </button>
            </div>

            {/* Speed Controls */}
            <div className="flex items-center justify-center space-x-2 mb-3">
              <span className="text-sm font-medium text-gray-600">Скорость:</span>
              {bloggerSpeeds.map(speedValue => (
                <button
                  key={speedValue}
                  onClick={() => setSpeed(speedValue)}
                  className={`px-2 py-1 text-sm rounded transition-all duration-200 ${
                    speed === speedValue 
                      ? 'bg-red-500 text-white' 
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {speedValue}x
                </button>
              ))}
            </div>

            {/* Font Size Controls */}
            <div className="flex items-center justify-center space-x-2">
              <span className="text-sm font-medium text-gray-600">Размер:</span>
              {fontSizes.map(size => (
                <button
                  key={size}
                  onClick={() => setFontSize(size)}
                  className={`px-2 py-1 text-sm rounded transition-all duration-200 ${
                    fontSize === size 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          {/* Teleprompter Text */}
          <div className="flex-1 relative overflow-hidden">
            <div
              ref={textRef}
              className="bg-black text-white p-6 h-full overflow-y-auto scrollbar-hide"
              style={{
                fontSize: `${fontSize}px`,
                lineHeight: '1.7',
                fontFamily: 'Arial, sans-serif',
                minHeight: isFullscreen ? 'calc(100vh - 200px)' : '300px'
              }}
            >
              <div className="whitespace-pre-wrap text-center">
                {script || "Добавьте текст сценария для использования суфлёра"}
              </div>
            </div>

            {/* Focus Line */}
            <div className="absolute left-0 right-0 top-1/2 transform -translate-y-1/2 pointer-events-none">
              <div className="h-1 bg-red-500 opacity-70 mx-6 rounded-full shadow-lg"></div>
            </div>

            {/* Progress Bar */}
            <div className="absolute bottom-0 left-0 right-0 h-2 bg-gray-800">
              <div 
                className="h-full bg-gradient-to-r from-blue-500 to-red-500 transition-all duration-100"
                style={{ 
                  width: textRef.current ? 
                    `${(position / Math.max(1, textRef.current.scrollHeight - textRef.current.clientHeight)) * 100}%` : 
                    '0%' 
                }}
              />
            </div>
          </div>

          {/* Audio Playback */}
          {audioUrl && (
            <div className="bg-green-50 p-4 border-t border-green-200">
              <div className="flex items-center space-x-3 mb-3">
                <Volume2 size={20} className="text-green-600" />
                <span className="font-semibold text-green-800">
                  Запись готова! ({formatTime(recordingTime)})
                </span>
              </div>
              <audio 
                controls 
                src={audioUrl}
                className="w-full mb-3"
              />
              <div className="flex space-x-2">
                <button
                  onClick={() => {
                    const a = document.createElement('a');
                    a.href = audioUrl;
                    a.download = `${scriptTitle}_${Date.now()}.webm`;
                    a.click();
                  }}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm transition-colors"
                >
                  📥 Скачать
                </button>
                <button
                  onClick={() => {
                    setAudioUrl(null);
                    setAudioBlob(null);
                    setRecordingTime(0);
                  }}
                  className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded text-sm transition-colors"
                >
                  🗑️ Удалить
                </button>
              </div>
            </div>
          )}

          {/* Tips */}
          <div className="bg-blue-50 p-3 border-t border-blue-200">
            <p className="text-sm text-blue-700 text-center">
              💡 <strong>Совет:</strong> Перетащите окно в удобное место. Скорость 5x-8x идеальна для живой речи
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DraggableTeleprompterModal;

// ============================================

// Обновленная Audio Creation Section для VideoEditorAppPage.jsx

{/* Audio Creation Section - ЗАМЕНИТЕ ВАШУ СЕКЦИЮ НА ЭТУ */}
{script && (
  <div className="space-y-6 border-t border-slate-200 pt-8">
    <div>
      <h3 className="text-xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
        🎙️ Студия записи голоса
      </h3>
      <p className="text-gray-600 font-medium">
        Записывайте профессиональный голос с суфлёром или без него
      </p>
    </div>

    {/* Recording Options */}
    <div className="grid grid-cols-1 gap-4">
      {/* Teleprompter Recording */}
      <div className="bg-gradient-to-r from-red-50 to-pink-50 p-6 rounded-xl border-2 border-red-200">
        <div className="flex items-center space-x-4">
          <div className="w-16 h-16 bg-gradient-to-br from-red-500 to-pink-500 rounded-xl flex items-center justify-center">
            <div className="text-2xl">🎬</div>
          </div>
          <div className="flex-1">
            <h4 className="font-bold text-red-800 text-lg mb-2">
              Запись с суфлёром (Рекомендуется)
            </h4>
            <p className="text-red-600 text-sm mb-3">
              Читайте сценарий с перетаскиваемого суфлёра и записывайте голос одновременно
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowAudioTeleprompter(true)}
                className="bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white font-bold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
              >
                🎬 Открыть суфлёр и записать
              </button>
              <div className="flex items-center space-x-2 text-sm text-red-600">
                <span>✨ Скорости 3x-12x для блогеров</span>
                <span>•</span>
                <span>📱 Перетаскиваемое окно</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Simple Recording */}
      <div className="bg-gradient-to-r from-gray-50 to-blue-50 p-6 rounded-xl border-2 border-gray-200">
        <div className="flex items-center space-x-4">
          <div className="w-16 h-16 bg-gradient-to-br from-gray-500 to-blue-500 rounded-xl flex items-center justify-center">
            <Mic size={24} className="text-white" />
          </div>
          <div className="flex-1">
            <h4 className="font-bold text-gray-800 text-lg mb-2">
              Обычная запись
            </h4>
            <p className="text-gray-600 text-sm mb-3">
              Простая запись голоса без суфлёра (для опытных ораторов)
            </p>
            <div className="flex space-x-3">
              <button
                onClick={startRecording}
                disabled={isRecording}
                className="bg-gradient-to-r from-gray-500 to-blue-500 hover:from-gray-600 hover:to-blue-600 disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed text-white font-bold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
              >
                {isRecording ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2 inline-block"></div>
                    Запись... {formatTime(recordingTime)}
                  </>
                ) : (
                  <>
                    <Mic className="mr-2 inline" size={18} />
                    Начать простую запись
                  </>
                )}
              </button>
              
              {isRecording && (
                <button
                  onClick={stopRecording}
                  className="bg-red-600 hover:bg-red-700 text-white font-bold py-3 px-6 rounded-xl transition-all duration-300"
                >
                  ⏹️ Остановить
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* AI Voice Option */}
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-xl border-2 border-blue-200">
        <div className="flex items-center space-x-4">
          <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-indigo-500 rounded-xl flex items-center justify-center">
            <Brain size={24} className="text-white" />
          </div>
          <div className="flex-1">
            <h4 className="font-bold text-blue-800 text-lg mb-2">
              ИИ Озвучка
            </h4>
            <p className="text-blue-600 text-sm mb-3">
              Автоматическая генерация профессионального голоса
            </p>
            <button
              onClick={() => setAudioMethod("ai")}
              className="bg-gradient-to-r from-blue-500 to-indigo-500 hover:from-blue-600 hover:to-indigo-600 text-white font-bold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
            >
              <Brain className="mr-2 inline" size={18} />
              Настроить ИИ голос
            </button>
          </div>
        </div>
      </div>
    </div>

    {/* Recording Result */}
    {(recordedAudio || audioPlaybackUrl) && (
      <div className="bg-gradient-to-r from-green-50 to-emerald-50 p-6 rounded-xl border-2 border-green-200">
        <div className="flex items-center space-x-4 mb-4">
          <div className="w-12 h-12 bg-green-500 rounded-xl flex items-center justify-center">
            <Volume2 size={24} className="text-white" />
          </div>
          <div className="flex-1">
            <h4 className="font-bold text-green-800 text-lg">
              ✅ Запись завершена успешно!
            </h4>
            <p className="text-green-600">
              Длительность: {formatTime(recordingTime)} • Качество: HD • Готово к использованию
            </p>
          </div>
        </div>

        {/* Audio Player */}
        {audioPlaybackUrl && (
          <div className="bg-white p-4 rounded-lg border border-green-200 mb-4">
            <audio 
              controls 
              src={audioPlaybackUrl}
              className="w-full"
              style={{ height: '50px' }}
            />
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-wrap gap-3">
          <button
            onClick={() => {
              console.log('Сохранение записи');
              alert('Запись сохранена в проект!');
              setCurrentStep(4);
            }}
            className="bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
          >
            💾 Использовать в проекте
          </button>
          
          <button
            onClick={() => {
              if (audioPlaybackUrl) {
                const a = document.createElement('a');
                a.href = audioPlaybackUrl;
                a.download = `${topic || 'recording'}_${Date.now()}.webm`;
                a.click();
              }
            }}
            className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
          >
            📥 Скачать файл
          </button>
          
          <button
            onClick={() => {
              setRecordedAudio(null);
              setAudioPlaybackUrl(null);
              setAudioBlob(null);
              setRecordingTime(0);
            }}
            className="bg-gray-500 hover:bg-gray-600 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300"
          >
            🗑️ Удалить запись
          </button>
        </div>
      </div>
    )}

    {/* Tips */}
    <div className="bg-gradient-to-r from-yellow-50 to-orange-50 p-4 rounded-xl border border-yellow-200">
      <div className="flex items-start space-x-3">
        <div className="text-2xl">💡</div>
        <div>
          <h4 className="font-semibold text-yellow-800 mb-2">
            Советы для качественной записи:
          </h4>
          <ul className="text-sm text-yellow-700 space-y-1">
            <li>• <strong>С суфлёром:</strong> Скорость 5x-7x оптимальна для естественной речи</li>
            <li>• <strong>Микрофон:</strong> Держите на расстоянии 15-20 см от рта</li>
            <li>• <strong>Окружение:</strong> Записывайте в тихом помещении без эха</li>
            <li>• <strong>Суфлёр:</strong> Перетащите окно ближе к камере для правильного взгляда</li>
          </ul>
        </div>
      </div>
    </div>

    {/* Draggable Teleprompter Modal */}
    <DraggableTeleprompterModal
      isOpen={showAudioTeleprompter}
      onClose={() => setShowAudioTeleprompter(false)}
      script={script}
      scriptTitle={topic || scriptTitle}
      onRecordingComplete={(recordingData) => {
        setAudioBlob(recordingData.blob);
        setAudioPlaybackUrl(recordingData.url);
        setRecordedAudio("recorded");
        setRecordingTime(recordingData.duration);
        console.log('Запись завершена:', recordingData);
      }}
    />
  </div>
)}

// ВАЖНО: Добавьте эти состояния в начало VideoEditorApp:
const [showAudioTeleprompter, setShowAudioTeleprompter] = useState(false);
const [audioPlaybackUrl, setAudioPlaybackUrl] = useState(null);
const [audioBlob, setAudioBlob] = useState(null);
