import Page from "../components/common/Page";
import Card from "../components/ui/Card";

const TermsOfUsePage = () => {
  const sections = [
    {
      title: "1. Acceptance of Terms",
      content:
        "By using our service, you agree to these terms of use...",
    },
    {
      title: "2. Service Description",
      content:
        "Our service provides tools for online video editing...",
    },
    {
      title: "3. User Rights",
      content:
        "You are entitled to use the service in accordance with these terms...",
    },
    {
      title: "4. Usage Restrictions",
      content:
        "It is prohibited to use the service for illegal purposes...",
    },
    {
      title: "5. Privacy",
      content:
        "We are committed to protecting your personal information...",
    },
  ];

  return (
    <Page
      title="Terms of Use - VideoEditor"
      description="Review the terms of use for our online video editor"
    >
      <div className="max-w-4xl mx-auto space-y-8">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">
            Terms of Use
          </h1>
          <p className="text-gray-600">
            Last updated: {new Date().toLocaleDateString("en-US")}
          </p>
        </div>

        <div className="space-y-6">
          {sections.map((section, index) => (
            <Card key={index} className="space-y-3">
              <h2 className="text-xl font-semibold text-gray-800">
                {section.title}
              </h2>
              <p className="text-gray-600 leading-relaxed">{section.content}</p>
            </Card>
          ))}
        </div>

        <Card className="bg-blue-50 border-blue-200">
          <div className="flex items-start space-x-3">
            <div className="text-blue-500 text-xl">ℹ️</div>
            <div>
              <h3 className="font-semibold text-blue-800 mb-2">
                Important Information
              </h3>
              <p className="text-blue-700 text-sm">
                If you have any questions regarding the terms of use, please contact us at support@videoeditor.com
              </p>
            </div>
          </div>
        </Card>
      </div>
    </Page>
  );
};

export default TermsOfUsePage;
