import { BrowserRouter as Router } from "react-router-dom";
import { AuthProvider } from "./contexts/AuthContext";
import MainLayout from "./components/layouts/MainLayout";
import AppRouter from "./components/routing/AppRouter";
import ErrorBoundary from "./components/common/ErrorBoundary";
import NotificationSystem from "./components/NotificationSystem";
function App() {
  useEffect(() => {
    checkPaymentSuccess();
  }, []);

  const checkPaymentSuccess = async () => {
    try {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ session_id –≤ URL
      const urlParams = new URLSearchParams(window.location.search);
      const sessionId = urlParams.get("session_id");
      const success = urlParams.get("success");

      if (sessionId && success === "true") {
        console.log("üîç Payment session detected, verifying...");

        const token = localStorage.getItem("token");
        if (!token) return;

        // –í—ã–∑—ã–≤–∞–µ–º endpoint –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
        const response = await fetch(
          `http://localhost:5000/api/payments/session/${sessionId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const data = await response.json();
        console.log("üí≥ Payment verification result:", data);

        if (data.success && data.data.user) {
          console.log("‚úÖ Payment successful! User data updated.");

          // –û–±–Ω–æ–≤–ª—è–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –∫–æ–Ω—Ç–µ–∫—Å—Ç–µ (–µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç–µ)
          // setUser(data.data.user);

          // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ
          showSuccessNotification(`Welcome to your ${data.data.planId} plan!`);
        }

        // –£–±–∏—Ä–∞–µ–º –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –∏–∑ URL
        window.history.replaceState(
          {},
          document.title,
          window.location.pathname
        );
      }
    } catch (error) {
      console.error("‚ùå Payment verification error:", error);
    }
  };
  return (
    <ErrorBoundary>
      <AuthProvider>
        <Router>
          <MainLayout>
            <AppRouter />
          </MainLayout>
          <NotificationSystem />
        </Router>
      </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;
