import { useState, useRef, useEffect, useCallback } from "react";
import {
  Video,
  Music,
  Copy,
  Trash2,
  Scissors,
  Film,
  Plus,
  Layers,
  ZoomIn,
  ZoomOut,
  RotateCcw,
  HelpCircle,
  Play,
  Pause,
  SkipBack,
  SkipForward,
  Volume2,
  Image,
  FileText,
} from "lucide-react";

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç VideoPlayer
const VideoPlayer = ({
  timelineItems,
  currentTime,
  videoDuration,
  isPlaying,
  setIsPlaying,
  seekTo,
  formatTime,
}) => {
  const videoRef = useRef(null);
  const [volume, setVolume] = useState(1);
  
  // –ü–æ–ª—É—á–∞–µ–º –∞–∫—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã –≤ —Ç–µ–∫—É—â–µ–º –≤—Ä–µ–º–µ–Ω–∏
  const getActiveItems = useCallback(() => {
    return timelineItems.filter(item => {
      const itemStart = item.startTime;
      const itemEnd = item.startTime + item.duration;
      return currentTime >= itemStart && currentTime < itemEnd;
    }).sort((a, b) => {
      // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Ç–∏–ø—É –¥–æ—Ä–æ–∂–∫–∏: main -> overlays -> audio
      const trackOrder = { main: 0, overlay: 1, audio: 2 };
      const aOrder = trackOrder[a.trackType] || 3;
      const bOrder = trackOrder[b.trackType] || 3;
      return aOrder - bOrder;
    });
  }, [timelineItems, currentTime]);

  const activeItems = getActiveItems();
  const mainVideoItem = activeItems.find(item => item.trackType === 'main');
  const overlayItems = activeItems.filter(item => item.trackType === 'overlay');
  const audioItems = activeItems.filter(item => item.trackType === 'audio');

  // –û–±–Ω–æ–≤–ª—è–µ–º –≤—Ä–µ–º—è –≤–∏–¥–µ–æ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ currentTime
  useEffect(() => {
    if (videoRef.current && mainVideoItem) {
      const videoElement = videoRef.current;
      const relativeTime = currentTime - mainVideoItem.startTime;
      
      if (Math.abs(videoElement.currentTime - relativeTime) > 0.1) {
        videoElement.currentTime = Math.max(0, relativeTime);
      }
    }
  }, [currentTime, mainVideoItem]);

  // –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–µ–º
  useEffect(() => {
    if (videoRef.current && mainVideoItem) {
      if (isPlaying) {
        videoRef.current.play().catch(console.error);
      } else {
        videoRef.current.pause();
      }
    }
  }, [isPlaying, mainVideoItem]);

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –≥—Ä–æ–º–∫–æ—Å—Ç–∏
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.volume = volume;
    }
  }, [volume]);

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const handleSeekBackward = () => {
    seekTo(Math.max(0, currentTime - 10));
  };

  const handleSeekForward = () => {
    seekTo(Math.min(videoDuration, currentTime + 10));
  };

  const renderVideoContent = () => {
    if (!mainVideoItem) {
      return (
        <div className="w-full h-full bg-gray-900 flex items-center justify-center text-white">
          <div className="text-center">
            <Video size={64} className="mx-auto mb-4 text-gray-500" />
            <p className="text-lg">No video content at current time</p>
            <p className="text-sm text-gray-400 mt-2">
              Add video to timeline or move playhead to video content
            </p>
          </div>
        </div>
      );
    }

    return (
      <div className="relative w-full h-full bg-black">
        <video
          ref={videoRef}
          className="w-full h-full object-contain"
          src={mainVideoItem.src}
          onTimeUpdate={(e) => {
            if (isPlaying) {
              const videoTime = e.target.currentTime;
              const timelineTime = mainVideoItem.startTime + videoTime;
              if (timelineTime <= mainVideoItem.startTime + mainVideoItem.duration) {
                seekTo(timelineTime);
              } else {
                setIsPlaying(false);
              }
            }
          }}
          onLoadedMetadata={() => {
            if (videoRef.current) {
              const relativeTime = currentTime - mainVideoItem.startTime;
              videoRef.current.currentTime = Math.max(0, relativeTime);
            }
          }}
        />
        
        {/* Overlays */}
        {overlayItems.map((overlay, index) => (
          <div
            key={overlay.id}
            className="absolute inset-0 flex items-center justify-center pointer-events-none"
            style={{ zIndex: 10 + index }}
          >
            {overlay.type === 'image' ? (
              <img
                src={overlay.src}
                alt={overlay.name}
                className="max-w-full max-h-full object-contain"
                style={{
                  opacity: overlay.opacity || 1,
                  transform: `scale(${overlay.scale || 1})`,
                }}
              />
            ) : overlay.type === 'text' ? (
              <div
                className="text-white text-center p-4 bg-black bg-opacity-50 rounded"
                style={{
                  fontSize: overlay.fontSize || '24px',
                  color: overlay.color || 'white',
                }}
              >
                {overlay.text || overlay.name}
              </div>
            ) : null}
          </div>
        ))}
        
        {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –∞–∫—Ç–∏–≤–Ω—ã—Ö overlay —ç–ª–µ–º–µ–Ω—Ç–æ–≤ */}
        {overlayItems.length > 0 && (
          <div className="absolute top-4 right-4 bg-black bg-opacity-50 text-white px-2 py-1 rounded text-sm">
            {overlayItems.length} overlay{overlayItems.length > 1 ? 's' : ''}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="bg-white rounded-2xl shadow-sm border-2 border-gray-200 overflow-hidden">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <div className="bg-gray-50 p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
            <Video size={18} />
            <span>Video Player</span>
            {mainVideoItem && (
              <span className="text-blue-600 text-sm">
                - Playing: "{mainVideoItem.name}"
              </span>
            )}
            {overlayItems.length > 0 && (
              <span className="text-purple-600 text-sm">
                + {overlayItems.length} overlay{overlayItems.length > 1 ? 's' : ''}
              </span>
            )}
            {audioItems.length > 0 && (
              <span className="text-green-600 text-sm">
                + {audioItems.length} audio track{audioItems.length > 1 ? 's' : ''}
              </span>
            )}
          </h4>
          
          <div className="text-sm text-gray-600">
            {formatTime(currentTime)} / {formatTime(videoDuration)}
          </div>
        </div>
      </div>

      {/* –í–∏–¥–µ–æ –æ–±–ª–∞—Å—Ç—å */}
      <div className="aspect-video bg-gray-900 relative">
        {renderVideoContent()}
        
        {/* –≠–ª–µ–º–µ–Ω—Ç—ã —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –ø–æ–≤–µ—Ä—Ö –≤–∏–¥–µ–æ */}
        <div className="absolute bottom-4 left-4 right-4 bg-black bg-opacity-50 rounded-lg p-3">
          <div className="flex items-center justify-between text-white">
            <div className="flex items-center space-x-3">
              <button
                onClick={handleSeekBackward}
                className="p-2 hover:bg-white hover:bg-opacity-20 rounded transition-colors"
                title="Rewind 10s"
              >
                <SkipBack size={20} />
              </button>
              
              <button
                onClick={handlePlayPause}
                className="p-3 bg-blue-600 hover:bg-blue-700 rounded-full transition-colors"
                title={isPlaying ? "Pause" : "Play"}
              >
                {isPlaying ? <Pause size={24} /> : <Play size={24} />}
              </button>
              
              <button
                onClick={handleSeekForward}
                className="p-2 hover:bg-white hover:bg-opacity-20 rounded transition-colors"
                title="Forward 10s"
              >
                <SkipForward size={20} />
              </button>
            </div>
            
            <div className="flex items-center space-x-2">
              <Volume2 size={16} />
              <input
                type="range"
                min="0"
                max="1"
                step="0.1"
                value={volume}
                onChange={(e) => setVolume(parseFloat(e.target.value))}
                className="w-20"
              />
            </div>
          </div>
        </div>
      </div>

      {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –∞–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–∞—Ö */}
      {activeItems.length > 0 && (
        <div className="p-3 bg-gray-50 border-t border-gray-200">
          <div className="text-sm text-gray-600">
            <strong>Active items:</strong>
            <div className="mt-1 space-y-1">
              {activeItems.map(item => (
                <div key={item.id} className="flex items-center space-x-2">
                  {item.type === 'video' && <Video size={14} className="text-blue-600" />}
                  {item.type === 'audio' && <Music size={14} className="text-green-600" />}
                  {item.type === 'image' && <Image size={14} className="text-purple-600" />}
                  {item.type === 'text' && <FileText size={14} className="text-orange-600" />}
                  <span>{item.name}</span>
                  <span className="text-gray-400">
                    ({formatTime(item.startTime)} - {formatTime(item.startTime + item.duration)})
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// –û–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç Timeline
const Timeline = ({
  timelineItems,
  setTimelineItems,
  currentTime,
  videoDuration,
  setVideoDuration,
  selectedTimelineItem,
  setSelectedTimelineItem,
  timelineZoom,
  setTimelineZoom,
  tracks,
  setTracks,
  draggedItem,
  draggedTimelineItem,
  setDraggedTimelineItem,
  dropPreview,
  setDropPreview,
  isDragging,
  isTimelineDragging,
  setIsTimelineDragging,
  isResizing,
  setIsResizing,
  copiedItem,
  formatTime,
  seekTo,
  splitTimelineItem,
  copyTimelineItem,
  pasteTimelineItem,
  deleteSelectedItem,
  addToTimeline,
  insertWithRipple,
  applyRippleEffect,
  removeFromTimeline,
}) => {
  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);
  const autoScrollIntervalRef = useRef(null);

  // Timeline-specific states
  const [dragOffset, setDragOffset] = useState(0);
  const [resizeHandle, setResizeHandle] = useState(null);
  const [resizeItem, setResizeItem] = useState(null);
  const [originalItemData, setOriginalItemData] = useState(null);

  // –ê–≤—Ç–æ–ø—Ä–æ–∫—Ä—É—Ç–∫–∞
  const stopAutoScroll = useCallback(() => {
    if (autoScrollIntervalRef.current) {
      clearInterval(autoScrollIntervalRef.current);
      autoScrollIntervalRef.current = null;
    }
  }, []);

  const startAutoScroll = useCallback(
    (direction, speed = 1) => {
      stopAutoScroll();

      autoScrollIntervalRef.current = setInterval(() => {
        if (timelineScrollRef.current) {
          const scrollAmount = 15 * speed;
          const currentScrollLeft = timelineScrollRef.current.scrollLeft;
          const maxScroll =
            timelineScrollRef.current.scrollWidth -
            timelineScrollRef.current.clientWidth;

          if (direction === "left" && currentScrollLeft > 0) {
            timelineScrollRef.current.scrollLeft = Math.max(
              0,
              currentScrollLeft - scrollAmount
            );
          } else if (direction === "right" && currentScrollLeft < maxScroll) {
            timelineScrollRef.current.scrollLeft = Math.min(
              maxScroll,
              currentScrollLeft + scrollAmount
            );
          }
        }
      }, 16);
    },
    [stopAutoScroll]
  );

  const checkAutoScroll = useCallback(
    (clientX) => {
      if (!timelineScrollRef.current) return;

      const rect = timelineScrollRef.current.getBoundingClientRect();
      const scrollZoneWidth = 120;
      const relativeX = clientX - rect.left;

      if (
        relativeX < scrollZoneWidth &&
        timelineScrollRef.current.scrollLeft > 0
      ) {
        const speed = ((scrollZoneWidth - relativeX) / scrollZoneWidth) * 3;
        startAutoScroll("left", speed);
      } else if (relativeX > rect.width - scrollZoneWidth) {
        const maxScroll =
          timelineScrollRef.current.scrollWidth -
          timelineScrollRef.current.clientWidth;
        if (timelineScrollRef.current.scrollLeft < maxScroll) {
          const speed =
            ((relativeX - (rect.width - scrollZoneWidth)) / scrollZoneWidth) *
            3;
          startAutoScroll("right", speed);
        }
      } else {
        stopAutoScroll();
      }
    },
    [startAutoScroll, stopAutoScroll]
  );

  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –¥–æ—Ä–æ–∂–∫–∏ –ø–æ —Ç–∏–ø—É –º–µ–¥–∏–∞
  const getTargetTrackForType = useCallback((mediaType) => {
    
    switch (mediaType) {
      case "video":
        const mainTrack = tracks.main?.[0];
        console.log(`Main –¥–æ—Ä–æ–∂–∫–∞ –¥–ª—è –≤–∏–¥–µ–æ:`, mainTrack);
        return mainTrack?.id;
      case "audio":
      case "music":
        const audioTrack = tracks.audio?.[0];
        console.log(`Audio –¥–æ—Ä–æ–∂–∫–∞ –¥–ª—è –∞—É–¥–∏–æ:`, audioTrack);
        return audioTrack?.id;
      case "image":
      case "text":
        const overlayTrack = tracks.overlays?.[0];
        console.log(`Overlay –¥–æ—Ä–æ–∂–∫–∞ –¥–ª—è –∫–∞—Ä—Ç–∏–Ω–∫–∏/—Ç–µ–∫—Å—Ç–∞:`, overlayTrack);
        return overlayTrack?.id;
      default:
        console.log(`–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —Ç–∏–ø ${mediaType}, –∏—Å–ø–æ–ª—å–∑—É–µ–º main –¥–æ—Ä–æ–∂–∫—É`);
        return tracks.main?.[0]?.id;
    }
  }, [tracks]);

  // –£–º–Ω–æ–µ —Ä–∞–∑–º–µ—â–µ–Ω–∏–µ —ç–ª–µ–º–µ–Ω—Ç–∞ –Ω–∞ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–π –¥–æ—Ä–æ–∂–∫–µ
  const addToTimelineAtCurrentTime = useCallback(() => {
    if (!draggedItem) return;
    
    const targetTrackId = getTargetTrackForType(draggedItem.type);
    if (!targetTrackId) return;
    
    // –ù–∞—Ö–æ–¥–∏–º –ª—É—á—à—É—é –ø–æ–∑–∏—Ü–∏—é –¥–ª—è —Ä–∞–∑–º–µ—â–µ–Ω–∏—è
    const bestPosition = findBestPositionForItem(draggedItem, targetTrackId);
    addToTimeline(draggedItem, bestPosition, targetTrackId);
  }, [draggedItem, getTargetTrackForType, addToTimeline]);

  // –ù–∞—Ö–æ–¥–∏–º –ª—É—á—à—É—é –ø–æ–∑–∏—Ü–∏—é –¥–ª—è —Ä–∞–∑–º–µ—â–µ–Ω–∏—è —ç–ª–µ–º–µ–Ω—Ç–∞
  const findBestPositionForItem = useCallback((item, targetTrackId) => {
    const duration = item.duration || (item.type === "image" ? 5 : 10);
    
    // –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ —ç–ª–µ–º–µ–Ω—Ç—ã –Ω–∞ —Ü–µ–ª–µ–≤–æ–π –¥–æ—Ä–æ–∂–∫–µ
    const itemsOnTrack = timelineItems
      .filter(timelineItem => timelineItem.trackId === targetTrackId)
      .sort((a, b) => a.startTime - b.startTime);
    
    // –ï—Å–ª–∏ –Ω–µ—Ç —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –Ω–∞ –¥–æ—Ä–æ–∂–∫–µ, —Ä–∞–∑–º–µ—â–∞–µ–º –≤ —Ç–µ–∫—É—â–µ–π –ø–æ–∑–∏—Ü–∏–∏
    if (itemsOnTrack.length === 0) {
      return currentTime;
    }
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –º–µ—Å—Ç–æ –≤ —Ç–µ–∫—É—â–µ–π –ø–æ–∑–∏—Ü–∏–∏
    const hasSpaceAtCurrentTime = !itemsOnTrack.some(existingItem => {
      const existingStart = existingItem.startTime;
      const existingEnd = existingItem.startTime + existingItem.duration;
      const newStart = currentTime;
      const newEnd = currentTime + duration;
      
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø–µ—Ä–µ—Å–µ—á–µ–Ω–∏–µ
      return !(newEnd <= existingStart || newStart >= existingEnd);
    });
    
    if (hasSpaceAtCurrentTime) {
      return currentTime;
    }
    
    // –ï—Å–ª–∏ –≤ —Ç–µ–∫—É—â–µ–π –ø–æ–∑–∏—Ü–∏–∏ –Ω–µ—Ç –º–µ—Å—Ç–∞, —Ä–∞–∑–º–µ—â–∞–µ–º –ø–æ—Å–ª–µ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
    const lastItem = itemsOnTrack[itemsOnTrack.length - 1];
    return lastItem.startTime + lastItem.duration;
  }, [timelineItems, currentTime]);

  // –ó—É–º –∏ –ø—Ä–æ–∫—Ä—É—Ç–∫–∞ –∫–æ–ª–µ—Å–æ–º –º—ã—à–∏ –∫–∞–∫ –≤ CapCut
  const handleWheel = useCallback((e) => {
    if (e.ctrlKey) {
      // Ctrl + –∫–æ–ª–µ—Å–æ = –∑—É–º —Å —Ñ–æ–∫—É—Å–æ–º –Ω–∞ –∫—É—Ä—Å–æ—Ä
      e.preventDefault();
      
      if (!timelineScrollRef.current) return;
      
      const rect = timelineScrollRef.current.getBoundingClientRect();
      const mouseX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
      const currentPixelsPerSecond = 50 * timelineZoom;
      const timeAtMouse = mouseX / currentPixelsPerSecond;
      
      const zoomDirection = e.deltaY < 0 ? 1 : -1;
      const zoomStep = 1.2;
      
      setTimelineZoom((prevZoom) => {
        let newZoom;
        if (zoomDirection > 0) {
          newZoom = Math.min(20, prevZoom * zoomStep);
        } else {
          newZoom = Math.max(0.1, prevZoom / zoomStep);
        }
        
        requestAnimationFrame(() => {
          if (timelineScrollRef.current) {
            const newPixelsPerSecond = 50 * newZoom;
            const newMouseX = timeAtMouse * newPixelsPerSecond;
            const targetScrollLeft = newMouseX - (e.clientX - rect.left);
            timelineScrollRef.current.scrollLeft = Math.max(0, targetScrollLeft);
          }
        });
        
        return newZoom;
      });
    } else if (e.shiftKey) {
      // Shift + –∫–æ–ª–µ—Å–æ = –≥–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω–∞—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∞
      e.preventDefault();
      
      if (timelineScrollRef.current) {
        const scrollAmount = e.deltaY * 2;
        timelineScrollRef.current.scrollLeft += scrollAmount;
      }
    }
  }, [timelineZoom, setTimelineZoom]);

  // –§—É–Ω–∫—Ü–∏–∏ –∑—É–º–∞
  const zoomIn = () => {
    setTimelineZoom((prev) => Math.min(20, prev * 1.5));
  };

  const zoomOut = () => {
    setTimelineZoom((prev) => Math.max(0.1, prev / 1.5));
  };

  const resetZoom = () => {
    setTimelineZoom(1);
  };

  // –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è resize
  const handleResizeStart = useCallback(
    (e, item, handle) => {
      e.preventDefault();
      e.stopPropagation();
      setIsResizing(true);
      setResizeHandle(handle);
      setResizeItem(item);
      setOriginalItemData({
        startTime: item.startTime,
        duration: item.duration,
      });

      setIsTimelineDragging(false);
      setDraggedTimelineItem(null);
    },
    [setIsResizing, setIsTimelineDragging, setDraggedTimelineItem]
  );

  const handleResizeEnd = useCallback(() => {
    setIsResizing(false);
    setResizeHandle(null);
    setResizeItem(null);
    setOriginalItemData(null);
    stopAutoScroll();
  }, [setIsResizing, stopAutoScroll]);

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Ç—Ä–µ–∫–æ–≤
  const addOverlayTrack = () => {
    const newTrackId = `overlay-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      overlays: [
        ...prev.overlays,
        {
          id: newTrackId,
          name: `Overlay ${prev.overlays.length + 1}`,
          type: "overlay",
        },
      ],
    }));
  };

  const addAudioTrack = () => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      audio: [
        ...prev.audio,
        {
          id: newTrackId,
          name: `Audio ${prev.audio.length + 1}`,
          type: "audio",
        },
      ],
    }));
  };

  // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è —Å–∫—Ä–æ–ª–ª–∞
  const handleTrackAreaScroll = (e) => {
    if (trackLabelsRef.current) {
      trackLabelsRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const handleTrackLabelsScroll = (e) => {
    if (timelineScrollRef.current) {
      timelineScrollRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const getTimelineWidth = () => {
    const baseWidth = videoDuration * 50 * timelineZoom;
    return Math.max(baseWidth, 2000);
  };

  const getPixelsPerSecond = () => {
    return 50 * timelineZoom;
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π
  const handleTimelineClick = (e) => {
    if (isDragging || isTimelineDragging || isResizing) return;

    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const pixelsPerSecond = getPixelsPerSecond();
    const clickTime = clickX / pixelsPerSecond;
    seekTo(clickTime);
  };

  const handleTimelineDoubleClick = useCallback((e) => {
    if (isDragging || isTimelineDragging || isResizing || !draggedItem) return;
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const pixelsPerSecond = getPixelsPerSecond();
    const clickTime = clickX / pixelsPerSecond;
    
    const targetTrackId = getTargetTrackForType(draggedItem.type);
    if (targetTrackId) {
      // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ç–æ—á–Ω—É—é –ø–æ–∑–∏—Ü–∏—é –∫–ª–∏–∫–∞ –¥–ª—è –¥–≤–æ–π–Ω–æ–≥–æ –∫–ª–∏–∫–∞
      addToTimeline(draggedItem, clickTime, targetTrackId);
    }
  }, [isDragging, isTimelineDragging, isResizing, draggedItem, getTargetTrackForType, addToTimeline]);

  const handleTimelineItemDragStart = (e, timelineItem) => {
    if (isResizing) {
      e.preventDefault();
      return;
    }

    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);

    const rect = timelineScrollRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(
      e.clientX - itemRect.left + timelineScrollRef.current.scrollLeft
    );

    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleTimelineItemDragEnd = () => {
    setDraggedTimelineItem(null);
    setDropPreview(null);
    setIsTimelineDragging(false);
    setDragOffset(0);
    stopAutoScroll();
  };

  const handleDragOver = (e) => {
    if (!draggedItem && !draggedTimelineItem) return;

    e.preventDefault();
    e.dataTransfer.dropEffect = draggedItem ? "copy" : "move";

    if (draggedTimelineItem || isResizing) {
      checkAutoScroll(e.clientX);
    }

    const timelineRect = timelineScrollRef.current.getBoundingClientRect();
    const dragX =
      e.clientX -
      timelineRect.left +
      timelineScrollRef.current.scrollLeft -
      (draggedTimelineItem ? dragOffset : 0);
    const pixelsPerSecond = getPixelsPerSecond();
    const dropTime = Math.max(0, dragX / pixelsPerSecond);

    const dragY =
      e.clientY - timelineRect.top + timelineScrollRef.current.scrollTop;
    const trackHeight = 60;
    const timeRulerHeight = 48;

    const relativeY = dragY - timeRulerHeight;
    const trackIndex = Math.floor(relativeY / trackHeight);

    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const targetTrack = allTracks[trackIndex];

    if (draggedItem && targetTrack) {
      const duration =
        draggedItem.duration || (draggedItem.type === "image" ? 5 : 10);
      const insertResult = insertWithRipple(targetTrack.id, dropTime, duration);

      setDropPreview({
        time: insertResult.time,
        duration: duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || [],
      });
    } else if (draggedTimelineItem && targetTrack) {
      const insertResult = insertWithRipple(
        targetTrack.id,
        dropTime,
        draggedTimelineItem.duration,
        draggedTimelineItem.id
      );

      setDropPreview({
        time: insertResult.time,
        duration: draggedTimelineItem.duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || [],
      });
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    stopAutoScroll();

    // –ï—Å–ª–∏ –Ω–µ—Ç –≤–∞–ª–∏–¥–Ω–æ–≥–æ preview, –Ω–æ –µ—Å—Ç—å draggedItem - –∏—Å–ø–æ–ª—å–∑—É–µ–º —É–º–Ω—É—é –ª–æ–≥–∏–∫—É —Ä–∞–∑–º–µ—â–µ–Ω–∏—è
    if (!dropPreview && draggedItem) {
      const targetTrackId = getTargetTrackForType(draggedItem.type);
      if (targetTrackId) {
        const bestPosition = findBestPositionForItem(draggedItem, targetTrackId);
        addToTimeline(draggedItem, bestPosition, targetTrackId);
      }
      setDropPreview(null);
      setIsTimelineDragging(false);
      return;
    }

    if ((!draggedItem && !draggedTimelineItem) || !dropPreview) return;

    if (dropPreview.isValid) {
      if (draggedItem) {
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          applyRippleEffect(dropPreview.affectedItems, dropPreview.duration);
        }
        addToTimeline(draggedItem, dropPreview.time, dropPreview.trackId);
      } else if (draggedTimelineItem) {
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          applyRippleEffect(
            dropPreview.affectedItems,
            draggedTimelineItem.duration
          );
        }

        setTimelineItems((prev) =>
          prev.map((item) =>
            item.id === draggedTimelineItem.id
              ? {
                  ...item,
                  startTime: dropPreview.time,
                  trackId: dropPreview.trackId,
                  trackType: dropPreview.trackType,
                }
              : item
          )
        );

        const newEndTime = dropPreview.time + draggedTimelineItem.duration;
        if (newEndTime > videoDuration) {
          setVideoDuration(newEndTime + 10);
        }
      }
    }

    setDropPreview(null);
    setIsTimelineDragging(false);
  };

  const handleDragLeave = (e) => {
    if (!timelineRef.current?.contains(e.relatedTarget)) {
      setDropPreview(null);
      stopAutoScroll();
    }
  };

  const getTimelineItemStyle = (item) => {
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(
      (track) => track.id === item.trackId
    );
    const pixelsPerSecond = getPixelsPerSecond();

    return {
      left: `${item.startTime * pixelsPerSecond}px`,
      width: `${item.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getDropPreviewStyle = () => {
    if (!dropPreview) return {};

    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(
      (track) => track.id === dropPreview.trackId
    );
    const pixelsPerSecond = getPixelsPerSecond();

    return {
      left: `${dropPreview.time * pixelsPerSecond}px`,
      width: `${dropPreview.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getTrackColor = (trackType) => {
    switch (trackType) {
      case "overlay":
        return "bg-purple-500 border-purple-400";
      case "main":
        return "bg-blue-500 border-blue-400";
      case "audio":
        return "bg-green-500 border-green-400";
      default:
        return "bg-gray-500 border-gray-400";
    }
  };

  // –≠—Ñ—Ñ–µ–∫—Ç—ã
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!isResizing || !resizeItem || !timelineScrollRef.current) return;

      checkAutoScroll(e.clientX);

      const rect = timelineScrollRef.current.getBoundingClientRect();
      const mouseX =
        e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
      const pixelsPerSecond = getPixelsPerSecond();
      const newTime = mouseX / pixelsPerSecond;

      setTimelineItems((prev) =>
        prev.map((item) => {
          if (item.id !== resizeItem.id) return item;

          const minDuration = 0.1;

          if (resizeHandle === "left") {
            const rightEdge =
              originalItemData.startTime + originalItemData.duration;
            const newStartTime = Math.max(
              0,
              Math.min(rightEdge - minDuration, newTime)
            );
            const newDuration = rightEdge - newStartTime;

            return {
              ...item,
              startTime: newStartTime,
              duration: newDuration,
            };
          } else if (resizeHandle === "right") {
            const leftEdge = originalItemData.startTime;
            const newEndTime = Math.max(leftEdge + minDuration, newTime);
            const newDuration = newEndTime - leftEdge;

            return {
              ...item,
              duration: newDuration,
            };
          }

          return item;
        })
      );
    };

    const handleMouseUp = () => {
      if (isResizing) {
        handleResizeEnd();
      }
    };

    if (isResizing) {
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    }

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [
    isResizing,
    resizeItem,
    resizeHandle,
    originalItemData,
    getPixelsPerSecond,
    checkAutoScroll,
    handleResizeEnd,
    setTimelineItems,
  ]);

  useEffect(() => {
    return () => {
      stopAutoScroll();
    };
  }, [stopAutoScroll]);

  return (
    <div
      className={`h-80 bg-white rounded-2xl shadow-sm border-2 transition-all duration-200 overflow-hidden flex flex-col flex-shrink-0 ${
        isDragging || isTimelineDragging || isResizing
          ? "border-blue-400 bg-blue-50/20"
          : "border-gray-200"
      }`}
      onWheel={handleWheel}
    >
      <div className="bg-gray-50 p-4 border-b border-gray-200 space-y-3">
        <div className="flex items-center justify-between">
          <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
            <Film size={18} />
            <span>Timeline Pro</span>
            {draggedItem && (
              <span className="text-blue-600 text-sm">
                - –ì–æ—Ç–æ–≤ –¥–æ–±–∞–≤–∏—Ç—å: "{draggedItem.name}" –Ω–∞ {
                  draggedItem.type === 'video' ? 'Main Video' :
                  draggedItem.type === 'audio' || draggedItem.type === 'music' ? 'Audio' :
                  'Overlay'
                } –¥–æ—Ä–æ–∂–∫—É
              </span>
            )}
            {isDragging && dropPreview && (
              <span className="text-green-600">
                - Adding "{draggedItem?.name}"{" "}
                {dropPreview.shouldRipple ? "‚ö° ripple" : "üìç exact"}
              </span>
            )}
            {isTimelineDragging && dropPreview && (
              <span className="text-blue-600">
                - Moving "{draggedTimelineItem?.name}"{" "}
                {dropPreview.shouldRipple ? "‚ö° push blocks" : "üìç exact"}
              </span>
            )}
            {isResizing && resizeItem && (
              <span className="text-orange-600">
                - Trimming "{resizeItem.name}" ({resizeHandle} edge)
              </span>
            )}
            {copiedItem && (
              <span className="text-blue-600 text-sm">
                - "{copiedItem.name}" copied (Ctrl+V to paste)
              </span>
            )}
          </h4>

          <div className="flex items-center space-x-3">
            <div className="flex items-center space-x-2 bg-white rounded-xl p-2 border border-gray-200">
              <button
                onClick={zoomOut}
                className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                title="Zoom Out (-)"
              >
                <ZoomOut size={16} />
              </button>
              <span className="text-xs text-gray-500 min-w-12 text-center">
                {(timelineZoom * 100).toFixed(0)}%
              </span>
              <button
                onClick={zoomIn}
                className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                title="Zoom In (+), or Ctrl+Wheel"
              >
                <ZoomIn size={16} />
              </button>
              <button
                onClick={resetZoom}
                className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                title="Reset Zoom (0)"
              >
                <RotateCcw size={16} />
              </button>
              <button
                className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                title="Timeline Controls Help"
              >
                <HelpCircle size={16} />
              </button>
            </div>
          </div>
        </div>

        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            <div className="flex items-center space-x-2">
              <button
                onClick={addOverlayTrack}
                className="p-2 text-purple-600 hover:text-purple-800 hover:bg-purple-50 rounded-xl transition-all duration-200"
                title="Add Overlay Track"
              >
                <Plus size={16} />
              </button>
              <button
                onClick={addAudioTrack}
                className="p-2 text-green-600 hover:text-green-800 hover:bg-green-50 rounded-xl transition-all duration-200"
                title="Add Audio Track"
              >
                <Music size={16} />
              </button>
              {draggedItem && (
                <button
                  onClick={addToTimelineAtCurrentTime}
                  className="p-2 text-blue-600 hover:text-blue-800 hover:bg-blue-50 rounded-xl transition-all duration-200 animate-pulse bg-blue-100"
                  title={`–î–æ–±–∞–≤–∏—Ç—å "${draggedItem.name}" –Ω–∞ ${
                    draggedItem.type === 'video' ? 'Main Video' :
                    draggedItem.type === 'audio' || draggedItem.type === 'music' ? 'Audio' :
                    'Overlay'
                  } –¥–æ—Ä–æ–∂–∫—É –≤ –æ–ø—Ç–∏–º–∞–ª—å–Ω—É—é –ø–æ–∑–∏—Ü–∏—é`}
                >
                  <Plus size={16} />
                </button>
              )}
              <button
                onClick={splitTimelineItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-red-600 hover:text-red-800 hover:bg-red-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Split Selected Item (Ctrl+B) - Cuts item at current time"
              >
                <Scissors size={16} />
              </button>
              <button
                onClick={copyTimelineItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-blue-600 hover:text-blue-800 hover:bg-blue-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Copy Selected Item (Ctrl+C) - Copy item to clipboard"
              >
                <Copy size={16} />
              </button>
              <button
                onClick={pasteTimelineItem}
                disabled={!copiedItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  copiedItem
                    ? "text-green-600 hover:text-green-800 hover:bg-green-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Paste Item (Ctrl+V) - Paste copied item"
              >
                <Copy size={16} className="rotate-180" />
              </button>
              <button
                onClick={deleteSelectedItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-red-600 hover:text-red-800 hover:bg-red-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Delete Selected Item (Del/Backspace) - Remove from timeline"
              >
                <Trash2 size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="flex-1 flex min-h-0">
        <div className="w-48 bg-gray-50 border-r border-gray-200 flex-shrink-0 flex flex-col">
          <div className="h-12 border-b border-gray-200 flex-shrink-0"></div>

          <div
            ref={trackLabelsRef}
            className="flex-1 overflow-y-auto overflow-x-hidden"
            onScroll={handleTrackLabelsScroll}
          >
            {tracks.overlays.map((track) => (
              <div
                key={track.id}
                className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-purple-700 bg-purple-50 flex-shrink-0"
                style={{ height: "60px" }}
              >
                <Layers size={16} className="mr-2" />
                {track.name}
              </div>
            ))}

            {tracks.main.map((track) => (
              <div
                key={track.id}
                className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-blue-700 bg-blue-50 flex-shrink-0"
                style={{ height: "60px" }}
              >
                <Video size={16} className="mr-2" />
                {track.name}
              </div>
            ))}

            {tracks.audio.map((track) => (
              <div
                key={track.id}
                className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-green-700 bg-green-50 flex-shrink-0"
                style={{ height: "60px" }}
              >
                <Music size={16} className="mr-2" />
                {track.name}
              </div>
            ))}
          </div>
        </div>

        <div
          ref={(el) => {
            timelineRef.current = el;
            timelineScrollRef.current = el;
          }}
          className="flex-1 relative overflow-auto bg-white"
          onScroll={handleTrackAreaScroll}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onDragLeave={handleDragLeave}
          style={{
            overflowX: "auto",
            overflowY: "auto",
            scrollbarWidth: "thin",
          }}
        >
          <div
            style={{
              width: `${getTimelineWidth()}px`,
              minHeight: "100%",
              position: "relative",
            }}
            onClick={handleTimelineClick}
            onDoubleClick={handleTimelineDoubleClick}
          >
            <div className="h-12 bg-gray-50 border-b border-gray-200 relative flex-shrink-0">
              {Array.from(
                { length: Math.ceil((videoDuration * timelineZoom) / 5) + 1 },
                (_, i) => {
                  const timeValue = (i * 5) / timelineZoom;
                  const pixelsPerSecond = getPixelsPerSecond();
                  return (
                    <div
                      key={i}
                      className="absolute top-0 h-full border-l border-gray-300"
                      style={{ left: `${timeValue * pixelsPerSecond}px` }}
                    >
                      <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-medium">
                        {formatTime(timeValue)}
                      </span>
                    </div>
                  );
                }
              )}

              {timelineZoom > 2 &&
                Array.from(
                  { length: Math.ceil(videoDuration * timelineZoom) + 1 },
                  (_, i) => {
                    const timeValue = i / timelineZoom;
                    const pixelsPerSecond = getPixelsPerSecond();
                    return (
                      <div
                        key={`sub-${i}`}
                        className="absolute top-8 w-0 h-4 border-l border-gray-200"
                        style={{ left: `${timeValue * pixelsPerSecond}px` }}
                      />
                    );
                  }
                )}

              <div
                className="absolute top-0 w-0.5 h-full bg-red-500 z-20 shadow-sm"
                style={{
                  left: `${currentTime * getPixelsPerSecond()}px`,
                }}
              />
            </div>

            <div
              className="relative flex-1"
              style={{
                height: `${
                  (tracks.overlays.length +
                    tracks.main.length +
                    tracks.audio.length) *
                  60
                }px`,
                minHeight: "100%",
              }}
            >
              {timelineItems.map((item) => (
                <div
                  key={item.id}
                  className={`absolute h-10 rounded-lg border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md group ${getTrackColor(
                    item.trackType
                  )} ${
                    selectedTimelineItem?.id === item.id
                      ? "ring-2 ring-blue-300"
                      : ""
                  } ${
                    draggedTimelineItem?.id === item.id ? "opacity-50 z-30" : ""
                  } ${
                    copiedItem?.id === item.id
                      ? "ring-2 ring-green-300 ring-dashed"
                      : ""
                  }`}
                  style={getTimelineItemStyle(item)}
                  onClick={(e) => {
                    e.stopPropagation();
                    setSelectedTimelineItem(item);
                  }}
                >
                  <div
                    className="absolute left-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-l-lg z-10 flex items-center justify-center"
                    onMouseDown={(e) => handleResizeStart(e, item, "left")}
                    title="Trim start - Drag to adjust start time"
                    style={{
                      background:
                        "linear-gradient(to right, rgba(255,255,255,0.8), transparent)",
                    }}
                  >
                    <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                  </div>

                  <div
                    draggable="true"
                    onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                    onDragEnd={handleTimelineItemDragEnd}
                    className="p-2 text-white text-xs truncate font-medium flex items-center h-full mx-3 cursor-grab active:cursor-grabbing"
                    title="Drag to move item on timeline - Ripple mode: pushes other blocks"
                  >
                    {item.name}
                    {copiedItem?.id === item.id && (
                      <span className="ml-2 text-green-200">üìã</span>
                    )}
                  </div>

                  <div
                    className="absolute right-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-r-lg z-10 flex items-center justify-center"
                    onMouseDown={(e) => handleResizeStart(e, item, "right")}
                    title="Trim end - Drag to adjust duration"
                    style={{
                      background:
                        "linear-gradient(to left, rgba(255,255,255,0.8), transparent)",
                    }}
                  >
                    <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                  </div>
                </div>
              ))}

              {dropPreview && (
                <div
                  className={`absolute h-10 rounded-lg border-2 transition-all ${
                    dropPreview.isValid
                      ? dropPreview.isExact
                        ? `${getTrackColor(
                            dropPreview.trackType
                          )} bg-opacity-60 border-solid`
                        : `${getTrackColor(
                            dropPreview.trackType
                          )} bg-opacity-40 border-dashed animate-pulse`
                      : "bg-red-300 border-red-500 border-dashed"
                  }`}
                  style={getDropPreviewStyle()}
                >
                  {dropPreview.isValid && !dropPreview.isExact && (
                    <div className="flex items-center justify-center h-full text-white text-xs font-bold">
                      üß≤
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// –î–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–æ–Ω–Ω–æ–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ
const VideoTimelineDemo = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è timeline
  const [timelineItems, setTimelineItems] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(60);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(null);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [isPlaying, setIsPlaying] = useState(false);
  
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è drag & drop
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedTimelineItem, setDraggedTimelineItem] = useState(null);
  const [dropPreview, setDropPreview] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isTimelineDragging, setIsTimelineDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [copiedItem, setCopiedItem] = useState(null);

  // –î–æ—Ä–æ–∂–∫–∏
  const [tracks, setTracks] = useState({
    overlays: [
      { id: "overlay-1", name: "Overlay 1", type: "overlay" },
    ],
    main: [
      { id: "main-1", name: "Main Video", type: "main" },
    ],
    audio: [
      { id: "audio-1", name: "Audio 1", type: "audio" },
    ],
  });

  // –¢–µ—Å—Ç–æ–≤—ã–µ –º–µ–¥–∏–∞ —Ñ–∞–π–ª—ã
  const [mediaLibrary] = useState([
    {
      id: "video-1",
      name: "Sample Video 1",
      type: "video",
      duration: 15,
      src: "https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4",
      thumbnail: "https://via.placeholder.com/200x112/3b82f6/ffffff?text=Video+1",
    },
    {
      id: "video-2", 
      name: "Sample Video 2",
      type: "video",
      duration: 12,
      src: "https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/ElephantsDream.mp4",
      thumbnail: "https://via.placeholder.com/200x112/3b82f6/ffffff?text=Video+2",
    },
    {
      id: "audio-1",
      name: "Background Music",
      type: "audio",
      duration: 30,
      src: "https://www.soundjay.com/misc/sounds/bell-ringing-05.wav",
      thumbnail: "https://via.placeholder.com/200x112/10b981/ffffff?text=Audio",
    },
    {
      id: "image-1",
      name: "Logo Overlay",
      type: "image",
      duration: 5,
      src: "https://via.placeholder.com/400x300/8b5cf6/ffffff?text=Logo",
      thumbnail: "https://via.placeholder.com/200x112/8b5cf6/ffffff?text=Image",
    },
    {
      id: "text-1",
      name: "Title Text",
      type: "text",
      duration: 3,
      text: "Welcome to Our Video",
      fontSize: "32px",
      color: "#ffffff",
      thumbnail: "https://via.placeholder.com/200x112/f59e0b/ffffff?text=Text",
    },
  ]);

  // –ê–≤—Ç–æ–æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –≤—Ä–µ–º–µ–Ω–∏ –ø—Ä–∏ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–∏
  useEffect(() => {
    let interval = null;
    if (isPlaying) {
      interval = setInterval(() => {
        setCurrentTime(prevTime => {
          const newTime = prevTime + 0.1;
          if (newTime >= videoDuration) {
            setIsPlaying(false);
            return videoDuration;
          }
          return newTime;
        });
      }, 100);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isPlaying, videoDuration]);

  // –£—Ç–∏–ª–∏—Ç–∞—Ä–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    const frames = Math.floor((seconds % 1) * 30);
    return `${mins.toString().padStart(2, "0")}:${secs
      .toString()
      .padStart(2, "0")}:${frames.toString().padStart(2, "0")}`;
  };

  const seekTo = (time) => {
    setCurrentTime(Math.max(0, Math.min(videoDuration, time)));
  };

  // –§—É–Ω–∫—Ü–∏–∏ timeline
  const addToTimeline = (item, startTime, trackId) => {
    const trackType = tracks.overlays.find(t => t.id === trackId) ? 'overlay' :
                     tracks.main.find(t => t.id === trackId) ? 'main' : 'audio';
    
    const newTimelineItem = {
      ...item,
      id: `timeline-${Date.now()}`,
      originalId: item.id,
      startTime,
      trackId,
      trackType,
    };

    setTimelineItems(prev => [...prev, newTimelineItem]);
    
    // –†–∞—Å—à–∏—Ä—è–µ–º duration –µ—Å–ª–∏ –Ω—É–∂–Ω–æ
    const newEndTime = startTime + item.duration;
    if (newEndTime > videoDuration) {
      setVideoDuration(newEndTime + 10);
    }
  };

  const insertWithRipple = (trackId, time, duration, excludeId = null) => {
    return {
      time,
      shouldRipple: false,
      affectedItems: [],
    };
  };

  const applyRippleEffect = (affectedItems, shiftDuration) => {
    // –õ–æ–≥–∏–∫–∞ ripple effect
  };

  const removeFromTimeline = (itemId) => {
    setTimelineItems(prev => prev.filter(item => item.id !== itemId));
  };

  const splitTimelineItem = () => {
    if (!selectedTimelineItem) return;
    
    const item = selectedTimelineItem;
    const splitTime = currentTime - item.startTime;
    
    if (splitTime <= 0 || splitTime >= item.duration) return;
    
    const firstPart = {
      ...item,
      id: `timeline-${Date.now()}-1`,
      duration: splitTime,
    };
    
    const secondPart = {
      ...item,
      id: `timeline-${Date.now()}-2`,
      startTime: item.startTime + splitTime,
      duration: item.duration - splitTime,
    };
    
    setTimelineItems(prev => 
      prev.map(timelineItem => 
        timelineItem.id === item.id ? firstPart : timelineItem
      ).concat(secondPart)
    );
    
    setSelectedTimelineItem(firstPart);
  };

  const copyTimelineItem = () => {
    if (selectedTimelineItem) {
      setCopiedItem(selectedTimelineItem);
    }
  };

  const pasteTimelineItem = () => {
    if (!copiedItem) return;
    
    const newItem = {
      ...copiedItem,
      id: `timeline-${Date.now()}`,
      startTime: currentTime,
    };
    
    setTimelineItems(prev => [...prev, newItem]);
  };

  const deleteSelectedItem = () => {
    if (selectedTimelineItem) {
      removeFromTimeline(selectedTimelineItem.id);
      setSelectedTimelineItem(null);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ drag & drop –¥–ª—è –º–µ–¥–∏–∞ –±–∏–±–ª–∏–æ—Ç–µ–∫–∏
  const handleMediaDragStart = (e, item) => {
    setDraggedItem(item);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
  };

  const handleMediaDragEnd = () => {
    setDraggedItem(null);
    setIsDragging(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Video Timeline System
          </h1>
          <p className="text-gray-600">
            –ü–µ—Ä–µ—Ç–∞—â–∏—Ç–µ –≤–∏–¥–µ–æ –≤ timeline –∏ –Ω–∞–∂–º–∏—Ç–µ Play –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
          </p>
        </div>

        {/* Video Player */}
        <VideoPlayer
          timelineItems={timelineItems}
          currentTime={currentTime}
          videoDuration={videoDuration}
          isPlaying={isPlaying}
          setIsPlaying={setIsPlaying}
          seekTo={seekTo}
          formatTime={formatTime}
        />

        {/* Media Library */}
        <div className="bg-white rounded-2xl shadow-sm border-2 border-gray-200 p-4">
          <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
            <Film size={20} className="mr-2" />
            Media Library
          </h3>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
            {mediaLibrary.map(item => (
              <div
                key={item.id}
                className={`bg-gray-50 rounded-xl p-3 cursor-grab active:cursor-grabbing border-2 transition-all hover:shadow-md ${
                  draggedItem?.id === item.id ? 'border-blue-400 bg-blue-50' : 'border-gray-200'
                }`}
                draggable="true"
                onDragStart={(e) => handleMediaDragStart(e, item)}
                onDragEnd={handleMediaDragEnd}
              >
                <div className="aspect-video bg-gray-200 rounded-lg mb-2 overflow-hidden">
                  <img 
                    src={item.thumbnail} 
                    alt={item.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="text-sm">
                  <div className="font-medium text-gray-900 truncate">
                    {item.name}
                  </div>
                  <div className="text-gray-500 text-xs mt-1 flex items-center justify-between">
                    <span className="capitalize">{item.type}</span>
                    <span>{formatTime(item.duration)}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Timeline */}
        <Timeline
          timelineItems={timelineItems}
          setTimelineItems={setTimelineItems}
          currentTime={currentTime}
          videoDuration={videoDuration}
          setVideoDuration={setVideoDuration}
          selectedTimelineItem={selectedTimelineItem}
          setSelectedTimelineItem={setSelectedTimelineItem}
          timelineZoom={timelineZoom}
          setTimelineZoom={setTimelineZoom}
          tracks={tracks}
          setTracks={setTracks}
          draggedItem={draggedItem}
          draggedTimelineItem={draggedTimelineItem}
          setDraggedTimelineItem={setDraggedTimelineItem}
          dropPreview={dropPreview}
          setDropPreview={setDropPreview}
          isDragging={isDragging}
          isTimelineDragging={isTimelineDragging}
          setIsTimelineDragging={setIsTimelineDragging}
          isResizing={isResizing}
          setIsResizing={setIsResizing}
          copiedItem={copiedItem}
          formatTime={formatTime}
          seekTo={seekTo}
          splitTimelineItem={splitTimelineItem}
          copyTimelineItem={copyTimelineItem}
          pasteTimelineItem={pasteTimelineItem}
          deleteSelectedItem={deleteSelectedItem}
          addToTimeline={addToTimeline}
          insertWithRipple={insertWithRipple}
          applyRippleEffect={applyRippleEffect}
          removeFromTimeline={removeFromTimeline}
        />

        {/* –ò–Ω—Å—Ç—Ä—É–∫—Ü–∏–∏ */}
        <div className="bg-blue-50 border border-blue-200 rounded-2xl p-4">
          <h4 className="font-semibold text-blue-900 mb-2">–ö–∞–∫ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å:</h4>
          <ul className="text-blue-800 text-sm space-y-1">
            <li>‚Ä¢ –ü–µ—Ä–µ—Ç–∞—â–∏—Ç–µ –≤–∏–¥–µ–æ –∏–∑ Media Library –Ω–∞ timeline</li>
            <li>‚Ä¢ –ù–∞–∂–º–∏—Ç–µ Play –≤ –≤–∏–¥–µ–æ–ø–ª–µ–µ—Ä–µ –¥–ª—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è</li>
            <li>‚Ä¢ –ö–ª–∏–∫–Ω–∏—Ç–µ –ø–æ timeline –¥–ª—è –ø–µ—Ä–µ–º–µ—â–µ–Ω–∏—è –ø–æ –≤—Ä–µ–º–µ–Ω–∏</li>
            <li>‚Ä¢ Overlay —ç–ª–µ–º–µ–Ω—Ç—ã –æ—Ç–æ–±—Ä–∞–∂–∞—é—Ç—Å—è –ø–æ–≤–µ—Ä—Ö –æ—Å–Ω–æ–≤–Ω–æ–≥–æ –≤–∏–¥–µ–æ</li>
            <li>‚Ä¢ Audio –¥–æ—Ä–æ–∂–∫–∏ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥—è—Ç—Å—è –≤–º–µ—Å—Ç–µ —Å –≤–∏–¥–µ–æ</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default VideoTimelineDemo;
