import React, { useState, useRef, useEffect } from "react";
import {
  Play,
  Pause,
  Download,
  Volume2,
  VolumeX,
  RotateCcw,
  Loader2,
  SkipForward,
  SkipBack,
  List,
  Plus,
  Check
} from "lucide-react";
import MediaStorage from "../utils/MediaStorage"; // ‚úÖ –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º MediaStorage

const AudioPlayer = ({ audioData, onClose, onUseAudio }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [currentChunk, setCurrentChunk] = useState(0);
  const [showPlaylist, setShowPlaylist] = useState(false);
  const [isSaving, setIsSaving] = useState(false); // ‚úÖ –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –∫–Ω–æ–ø–∫–∏ Use
  const [isSaved, setIsSaved] = useState(false); // ‚úÖ –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —á—Ç–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ
  const audioRef = useRef(null);

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º, —Ä–∞–±–æ—Ç–∞–µ–º –ª–∏ –º—ã —Å —á–∞—Å—Ç—è–º–∏
  const isChunkedAudio = audioData?.isChunked && audioData.audioChunks && audioData.audioChunks.length > 1;
  const currentAudioData = isChunkedAudio ? audioData.audioChunks[currentChunk] : audioData;
  const totalChunks = isChunkedAudio ? audioData.totalChunks : 1;

  console.log('üéµ AudioPlayer data:', {
    isChunked: audioData?.isChunked,
    totalChunks: audioData?.totalChunks,
    chunksCount: audioData?.audioChunks?.length,
    currentChunk,
    textLength: audioData?.textLength
  });

  useEffect(() => {
    if (currentAudioData?.audioUrl) {
      console.log(`üéµ Loading chunk ${currentChunk + 1}/${totalChunks}`);
      
      const audio = new Audio(currentAudioData.audioUrl);
      audioRef.current = audio;

      audio.addEventListener('loadedmetadata', () => {
        setDuration(audio.duration);
        setIsLoading(false);
        console.log(`‚úÖ Chunk ${currentChunk + 1} loaded, duration: ${audio.duration}s`);
      });

      audio.addEventListener('timeupdate', () => {
        setCurrentTime(audio.currentTime);
      });

      audio.addEventListener('ended', () => {
        console.log(`üèÅ Chunk ${currentChunk + 1} ended`);
        setIsPlaying(false);
        
        if (isChunkedAudio && currentChunk < audioData.audioChunks.length - 1) {
          // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –ø–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —Å–ª–µ–¥—É—é—â–µ–π —á–∞—Å—Ç–∏
          console.log(`‚è≠Ô∏è Auto-playing next chunk: ${currentChunk + 2}/${totalChunks}`);
          setTimeout(() => {
            setCurrentChunk(prev => prev + 1);
            setCurrentTime(0);
          }, 500); // –ù–µ–±–æ–ª—å—à–∞—è –ø–∞—É–∑–∞ –º–µ–∂–¥—É —á–∞—Å—Ç—è–º–∏
        } else {
          console.log('üéâ All audio chunks completed');
          setCurrentTime(0);
        }
      });

      audio.addEventListener('error', (e) => {
        console.error('‚ùå Audio error:', e);
        setIsLoading(false);
      });

      // –ï—Å–ª–∏ –∞—É–¥–∏–æ –±—ã–ª–æ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–æ, –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–∞–ø—É—Å–∫–∞–µ–º –Ω–æ–≤—É—é —á–∞—Å—Ç—å
      if (isPlaying && !isLoading) {
        audio.play().catch(console.error);
      }

      return () => {
        audio.pause();
        audio.src = '';
      };
    }
  }, [currentAudioData, currentChunk, isChunkedAudio, audioData]);

  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–∞–ø—É—Å–∫–∞–µ–º —Å–ª–µ–¥—É—é—â—É—é —á–∞—Å—Ç—å –ø—Ä–∏ —Å–º–µ–Ω–µ —á–∞–Ω–∫–∞ –≤–æ –≤—Ä–µ–º—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
  useEffect(() => {
    if (isPlaying && audioRef.current && !isLoading) {
      audioRef.current.play().catch(console.error);
    }
  }, [currentChunk, isPlaying, isLoading]);

  const togglePlay = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleSeek = (e) => {
    if (audioRef.current && duration) {
      const rect = e.currentTarget.getBoundingClientRect();
      const clickX = e.clientX - rect.left;
      const newTime = (clickX / rect.width) * duration;
      audioRef.current.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (audioRef.current) {
      audioRef.current.volume = newVolume;
    }
    setIsMuted(newVolume === 0);
  };

  const toggleMute = () => {
    if (audioRef.current) {
      if (isMuted) {
        audioRef.current.volume = volume;
        setIsMuted(false);
      } else {
        audioRef.current.volume = 0;
        setIsMuted(true);
      }
    }
  };

  const restart = () => {
    if (isChunkedAudio) {
      setCurrentChunk(0);
      setCurrentTime(0);
    } else if (audioRef.current) {
      audioRef.current.currentTime = 0;
      setCurrentTime(0);
    }
  };

  const nextChunk = () => {
    if (isChunkedAudio && currentChunk < audioData.audioChunks.length - 1) {
      console.log(`‚è≠Ô∏è Manual next chunk: ${currentChunk + 2}/${totalChunks}`);
      setCurrentChunk(prev => prev + 1);
      setCurrentTime(0);
    }
  };

  const prevChunk = () => {
    if (isChunkedAudio && currentChunk > 0) {
      console.log(`‚èÆÔ∏è Manual prev chunk: ${currentChunk}/${totalChunks}`);
      setCurrentChunk(prev => prev - 1);
      setCurrentTime(0);
    }
  };

  const playSpecificChunk = (chunkIndex) => {
    if (isChunkedAudio && chunkIndex >= 0 && chunkIndex < audioData.audioChunks.length) {
      console.log(`üéØ Playing specific chunk: ${chunkIndex + 1}/${totalChunks}`);
      setCurrentChunk(chunkIndex);
      setCurrentTime(0);
    }
  };

  // ‚úÖ –ù–æ–≤–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –∞—É–¥–∏–æ –≤ MediaLibrary
  const useAudio = async () => {
    if (isSaving || isSaved) return;
    
    setIsSaving(true);
    
    try {
      console.log('üíæ –°–æ—Ö—Ä–∞–Ω—è–µ–º –∞—É–¥–∏–æ –≤ MediaLibrary...');
      
      if (isChunkedAudio && audioData.audioChunks) {
        // –î–ª—è –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω–æ–≥–æ –∞—É–¥–∏–æ —Å–æ—Ö—Ä–∞–Ω—è–µ–º –≤—Å–µ —á–∞—Å—Ç–∏
        const savedFiles = [];
        
        for (let i = 0; i < audioData.audioChunks.length; i++) {
          const chunk = audioData.audioChunks[i];
          
          // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º base64 –≤ Blob
          const byteCharacters = atob(chunk.audioBase64);
          const byteNumbers = new Array(byteCharacters.length);
          for (let j = 0; j < byteCharacters.length; j++) {
            byteNumbers[j] = byteCharacters.charCodeAt(j);
          }
          const byteArray = new Uint8Array(byteNumbers);
          const blob = new Blob([byteArray], { type: 'audio/mpeg' });
          
          // –°–æ–∑–¥–∞–µ–º File –æ–±—ä–µ–∫—Ç
          const fileName = `AI_Audio_Part_${(i + 1).toString().padStart(2, '0')}_${Date.now()}.mp3`;
          const file = new File([blob], fileName, { type: 'audio/mpeg' });
          
          try {
            const savedFile = await MediaStorage.saveFile(file, 'audios');
            savedFiles.push(savedFile);
            console.log(`‚úÖ –°–æ—Ö—Ä–∞–Ω–µ–Ω–∞ —á–∞—Å—Ç—å ${i + 1}: ${savedFile.name}`);
          } catch (error) {
            console.error(`‚ùå –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —á–∞—Å—Ç–∏ ${i + 1}:`, error);
          }
        }
        
        console.log(`üéâ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ ${savedFiles.length} —á–∞—Å—Ç–µ–π –∞—É–¥–∏–æ –≤ MediaLibrary`);
        
        // –£–≤–µ–¥–æ–º–ª—è–µ–º —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–∏–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
        if (onUseAudio) {
          onUseAudio(savedFiles);
        }
        
        alert(`‚úÖ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ ${savedFiles.length} —á–∞—Å—Ç–µ–π –∞—É–¥–∏–æ –≤ Media Library!`);
        
      } else if (audioData?.audioBase64) {
        // –î–ª—è –æ–¥–∏–Ω–æ—á–Ω–æ–≥–æ –∞—É–¥–∏–æ
        const byteCharacters = atob(audioData.audioBase64);
        const byteNumbers = new Array(byteCharacters.length);
        for (let i = 0; i < byteCharacters.length; i++) {
          byteNumbers[i] = byteCharacters.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);
        const blob = new Blob([byteArray], { type: 'audio/mpeg' });
        
        const fileName = `AI_Audio_${Date.now()}.mp3`;
        const file = new File([blob], fileName, { type: 'audio/mpeg' });
        
        const savedFile = await MediaStorage.saveFile(file, 'audios');
        console.log(`‚úÖ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ –∞—É–¥–∏–æ: ${savedFile.name}`);
        
        // –£–≤–µ–¥–æ–º–ª—è–µ–º —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–∏–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
        if (onUseAudio) {
          onUseAudio([savedFile]);
        }
        
        alert(`‚úÖ –ê—É–¥–∏–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ –≤ Media Library!`);
      }
      
      setIsSaved(true);
      
      // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–Ω–æ–ø–∫—É –≤ –∏—Å—Ö–æ–¥–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã
      setTimeout(() => {
        setIsSaved(false);
      }, 3000);
      
    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –∞—É–¥–∏–æ:', error);
      
      if (error.message.includes('Storage limit reached')) {
        alert('‚ùå –î–æ—Å—Ç–∏–≥–Ω—É—Ç –ª–∏–º–∏—Ç —Ö—Ä–∞–Ω–∏–ª–∏—â–∞. –£–¥–∞–ª–∏—Ç–µ —Å—Ç–∞—Ä—ã–µ —Ñ–∞–π–ª—ã –∏–ª–∏ –æ–±–Ω–æ–≤–∏—Ç–µ –ø–ª–∞–Ω.');
      } else if (error.message.includes('File too large')) {
        alert('‚ùå –§–∞–π–ª —Å–ª–∏—à–∫–æ–º –±–æ–ª—å—à–æ–π –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è.');
      } else {
        alert('‚ùå –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –∞—É–¥–∏–æ. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
      }
    } finally {
      setIsSaving(false);
    }
  };

  const downloadAudio = () => {
    if (isChunkedAudio && audioData.audioChunks) {
      // –°–∫–∞—á–∏–≤–∞–µ–º –≤—Å–µ —á–∞—Å—Ç–∏ –¥–ª—è –ø–æ–ª–Ω–æ–≥–æ –∞—É–¥–∏–æ
      console.log(`üíæ Downloading ${audioData.audioChunks.length} audio parts`);
      
      audioData.audioChunks.forEach((chunk, index) => {
        const blob = new Blob(
          [
            new Uint8Array(
              atob(chunk.audioBase64)
                .split("")
                .map((char) => char.charCodeAt(0))
            ),
          ],
          { type: "audio/mpeg" }
        );

        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `full_audio_part_${(index + 1).toString().padStart(2, '0')}.mp3`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
        
        // –ù–µ–±–æ–ª—å—à–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ –º–µ–∂–¥—É —Å–∫–∞—á–∏–≤–∞–Ω–∏—è–º–∏
        if (index < audioData.audioChunks.length - 1) {
          setTimeout(() => {}, 100);
        }
      });
      
      alert(`‚úÖ –°–∫–∞—á–∞–Ω–æ ${audioData.audioChunks.length} —á–∞—Å—Ç–µ–π –∞—É–¥–∏–æ. –í–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ –∏—Ö –ø–æ –ø–æ—Ä—è–¥–∫—É –¥–ª—è –ø–æ–ª–Ω–æ–≥–æ –∞—É–¥–∏–æ.`);
    } else if (audioData?.audioBase64) {
      // –û–±—ã—á–Ω–æ–µ —Å–∫–∞—á–∏–≤–∞–Ω–∏–µ –æ–¥–Ω–æ–≥–æ —Ñ–∞–π–ª–∞
      const blob = new Blob(
        [
          new Uint8Array(
            atob(audioData.audioBase64)
              .split("")
              .map((char) => char.charCodeAt(0))
          ),
        ],
        { type: "audio/mpeg" }
      );

      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = audioData.filename || "generated_audio.mp3";
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  };

  const formatTime = (time) => {
    if (isNaN(time)) return "0:00";
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, "0")}`;
  };

  if (!audioData) return null;

  return (
    <div className="bg-white rounded-lg shadow-md border border-gray-200 p-3 space-y-3">
      {/* Compact Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <div className="w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center">
            <Volume2 className="text-blue-600" size={14} />
          </div>
          <div>
            <h4 className="text-xs font-semibold text-gray-800">
              {isChunkedAudio ? `Full Audio (${totalChunks} parts)` : 'AI Audio'}
            </h4>
            <p className="text-xs text-gray-500">
              {isChunkedAudio 
                ? `Part ${currentChunk + 1}/${totalChunks} ‚Ä¢ ${currentAudioData?.voiceId || audioData.voiceId}`
                : `${audioData.voiceId} ‚Ä¢ ${formatTime(duration)}`
              }
            </p>
          </div>
        </div>
        <div className="flex items-center space-x-1">
          {isChunkedAudio && (
            <button
              onClick={() => setShowPlaylist(!showPlaylist)}
              className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
              title="Show parts"
            >
              <List size={12} />
            </button>
          )}
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors p-1"
            title="Close"
          >
            <span className="text-sm">√ó</span>
          </button>
        </div>
      </div>

      {/* Playlist for chunked audio */}
      {isChunkedAudio && showPlaylist && (
        <div className="bg-gray-50 rounded-md p-2 max-h-24 overflow-y-auto">
          <h5 className="text-xs font-semibold text-gray-700 mb-1">Audio Parts:</h5>
          <div className="space-y-1">
            {audioData.audioChunks.map((chunk, index) => (
              <button
                key={index}
                onClick={() => playSpecificChunk(index)}
                className={`w-full text-left p-1 rounded text-xs transition-colors ${
                  currentChunk === index 
                    ? 'bg-blue-100 text-blue-800 border border-blue-300' 
                    : 'bg-white hover:bg-gray-100 border border-gray-200'
                }`}
              >
                <div className="flex justify-between items-center">
                  <span>Part {index + 1}</span>
                  <span className="text-xs text-gray-500">
                    {(chunk.size / 1024).toFixed(0)}KB
                  </span>
                </div>
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Compact Progress Bar */}
      <div className="space-y-1">
        <div
          className="w-full h-1.5 bg-gray-200 rounded-full cursor-pointer relative overflow-hidden"
          onClick={handleSeek}
        >
          <div
            className="h-full bg-blue-500 rounded-full transition-all duration-100"
            style={{
              width: duration ? `${(currentTime / duration) * 100}%` : "0%",
            }}
          />
          {isLoading && (
            <div className="absolute inset-0 bg-gray-300 animate-pulse rounded-full" />
          )}
        </div>
        <div className="flex justify-between text-xs text-gray-500">
          <span>{formatTime(currentTime)}</span>
          <span>{formatTime(duration)}</span>
        </div>
      </div>

      {/* Compact Controls */}
      <div className="flex items-center justify-between">
        {/* Play Controls */}
        <div className="flex items-center space-x-1">
          {isChunkedAudio && (
            <button
              onClick={prevChunk}
              disabled={currentChunk === 0}
              className="p-1 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-full transition-all disabled:opacity-30"
              title="Previous part"
            >
              <SkipBack size={10} />
            </button>
          )}

          <button
            onClick={restart}
            disabled={isLoading}
            className="p-1.5 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-full transition-all disabled:opacity-50"
            title="Restart"
          >
            <RotateCcw size={12} />
          </button>

          <button
            onClick={togglePlay}
            disabled={isLoading}
            className="p-2 bg-blue-500 hover:bg-blue-600 text-white rounded-full transition-all disabled:opacity-50 disabled:cursor-not-allowed"
            title={isPlaying ? "Pause" : "Play"}
          >
            {isLoading ? (
              <Loader2 className="animate-spin" size={14} />
            ) : isPlaying ? (
              <Pause size={14} />
            ) : (
              <Play size={14} />
            )}
          </button>

          {isChunkedAudio && (
            <button
              onClick={nextChunk}
              disabled={currentChunk === audioData.audioChunks.length - 1}
              className="p-1 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-full transition-all disabled:opacity-30"
              title="Next part"
            >
              <SkipForward size={10} />
            </button>
          )}
        </div>

        {/* Compact Volume */}
        <div className="flex items-center space-x-1">
          <button
            onClick={toggleMute}
            className="p-1 text-gray-600 hover:text-gray-800 transition-colors"
            title={isMuted ? "Unmute" : "Mute"}
          >
            {isMuted ? <VolumeX size={12} /> : <Volume2 size={12} />}
          </button>
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={isMuted ? 0 : volume}
            onChange={handleVolumeChange}
            className="w-12 h-1 bg-gray-200 rounded-lg appearance-none cursor-pointer"
          />
        </div>

        {/* Action Buttons */}
        <div className="flex items-center space-x-1">
          {/* ‚úÖ –ö–Ω–æ–ø–∫–∞ Use –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ MediaLibrary */}
          <button
            onClick={useAudio}
            disabled={isSaving || isSaved}
            className={`flex items-center space-x-1 px-2 py-1 rounded-md transition-all text-xs font-medium ${
              isSaved 
                ? 'bg-green-500 text-white' 
                : 'bg-blue-500 hover:bg-blue-600 text-white disabled:bg-gray-400'
            }`}
            title={isSaved ? "Saved to Media Library" : "Save to Media Library"}
          >
            {isSaving ? (
              <Loader2 className="animate-spin" size={12} />
            ) : isSaved ? (
              <Check size={12} />
            ) : (
              <Plus size={12} />
            )}
            <span className="hidden sm:inline">
              {isSaved ? 'Saved' : 'Use'}
            </span>
          </button>

          {/* –ö–Ω–æ–ø–∫–∞ Download */}
          <button
            onClick={downloadAudio}
            className="flex items-center space-x-1 px-2 py-1 bg-green-500 hover:bg-green-600 text-white rounded-md transition-all text-xs font-medium"
            title={isChunkedAudio ? `Download ${totalChunks} Parts` : "Download Audio"}
          >
            <Download size={12} />
            <span className="hidden sm:inline">
              {isChunkedAudio ? `${totalChunks}x` : 'Save'}
            </span>
          </button>
        </div>
      </div>

      {/* Compact Info Bar */}
      <div className="bg-gray-50 rounded-md p-2">
        <div className="flex justify-between items-center text-xs text-gray-600">
          <span>{audioData.textLength} chars</span>
          <span>{(audioData.size / 1024 / 1024).toFixed(1)}MB</span>
          {isChunkedAudio ? (
            <span className="text-blue-600">üìö {totalChunks} parts</span>
          ) : (
            <span className="text-green-600">‚úì Ready</span>
          )}
        </div>
        
        {isChunkedAudio && (
          <div className="mt-1 text-xs text-blue-600">
            üéµ Full audio: {audioData.textLength} chars total, auto-play sequence
          </div>
        )}
        
        {audioData.wasTruncated && (
          <div className="mt-1 text-xs text-orange-600">
            ‚ö† Text was shortened for processing
          </div>
        )}
      </div>
    </div>
  );
};

export default AudioPlayer;
