import { useState, useRef, useEffect, useCallback } from "react";
import {
  Upload,
  Play,
  Pause,
  Volume2,
  Video,
  Music,
  SkipBack,
  SkipForward,
  VolumeX,
  Copy,
  Trash2,
  Scissors,
  Image,
  Film,
  Plus,
  Layers,
  HelpCircle
} from "lucide-react";

const VideoEditor = () => {
  const [mediaLibrary, setMediaLibrary] = useState({
    videos: [],
    audios: [],
    images: [],
  });
  const [timelineItems, setTimelineItems] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(60);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(null);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [activeTab, setActiveTab] = useState("videos");
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentVideoSource, setCurrentVideoSource] = useState(null);
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedTimelineItem, setDraggedTimelineItem] = useState(null);
  const [dropPreview, setDropPreview] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isTimelineDragging, setIsTimelineDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [copiedItem, setCopiedItem] = useState(null);
  const [showShortcutsTooltip, setShowShortcutsTooltip] = useState(false);
  
  // Треки как в CapCut
  const [tracks, setTracks] = useState({
    overlays: [
      { id: 'overlay-1', name: 'Overlay 1', type: 'overlay' },
      { id: 'overlay-2', name: 'Overlay 2', type: 'overlay' }
    ],
    main: [
      { id: 'main-video', name: 'Main Video', type: 'main' }
    ],
    audio: [
      { id: 'audio-1', name: 'Audio 1', type: 'audio' },
      { id: 'audio-2', name: 'Audio 2', type: 'audio' }
    ]
  });

  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);
  const intervalRef = useRef(null);
  const audioElementsRef = useRef(new Map());
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  const videoRef = useRef(null);
  const autoScrollIntervalRef = useRef(null);

  // Базовые функции
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const seekTo = (time) => {
    const newTime = Math.max(0, Math.min(videoDuration, time));
    setCurrentTime(newTime);
    
    const content = getCurrentTimelineContent();
    content.audio.forEach(audioItem => {
      const audioElement = audioElementsRef.current.get(audioItem.id);
      if (audioElement) {
        const relativeTime = newTime - audioItem.startTime;
        audioElement.currentTime = Math.max(0, relativeTime);
      }
    });
  };

  const getCurrentTimelineContent = useCallback(() => {
    const currentItems = timelineItems.filter(
      (item) =>
        currentTime >= item.startTime &&
        currentTime < item.startTime + item.duration
    );

    return {
      mainVideo: currentItems.find(item => item.trackType === 'main' && item.type === 'video'),
      overlays: currentItems.filter(item => item.trackType === 'overlay'),
      audio: currentItems.filter(item => item.trackType === 'audio'),
    };
  }, [currentTime, timelineItems]);

  const createAudioElement = useCallback((audioItem) => {
    if (!audioElementsRef.current.has(audioItem.id)) {
      const audio = document.createElement('audio');
      audio.src = audioItem.url;
      audio.volume = (audioItem.volume || 1) * volume;
      audio.muted = isMuted;
      audio.preload = "metadata";
      audioElementsRef.current.set(audioItem.id, audio);
    }
    return audioElementsRef.current.get(audioItem.id);
  }, [volume, isMuted]);

  const syncAudioTime = useCallback((audioItem, relativeTime) => {
    const audioElement = audioElementsRef.current.get(audioItem.id);
    if (audioElement) {
      const timeDiff = Math.abs(audioElement.currentTime - relativeTime);
      if (timeDiff > 0.2) {
        audioElement.currentTime = Math.max(0, relativeTime);
      }
    }
  }, []);

  const checkCollision = useCallback((newItem, excludeId = null) => {
    return timelineItems.some(item => {
      if (item.id === excludeId) return false;
      if (item.trackId !== newItem.trackId) return false;
      
      const itemStart = item.startTime;
      const itemEnd = item.startTime + item.duration;
      const newStart = newItem.startTime;
      const newEnd = newItem.startTime + newItem.duration;
      
      return (newStart < itemEnd && newEnd > itemStart);
    });
  }, [timelineItems]);

  const findNextAvailablePosition = useCallback((trackId, duration, preferredStart = 0) => {
    const trackItems = timelineItems
      .filter(item => item.trackId === trackId)
      .sort((a, b) => a.startTime - b.startTime);
    
    if (preferredStart >= 0) {
      const testItem = { trackId, startTime: preferredStart, duration };
      if (!checkCollision(testItem)) {
        return preferredStart;
      }
    }
    
    for (let i = 0; i < trackItems.length; i++) {
      const currentItem = trackItems[i];
      const nextItem = trackItems[i + 1];
      
      if (i === 0 && currentItem.startTime >= duration) {
        return 0;
      }
      
      if (nextItem) {
        const gapStart = currentItem.startTime + currentItem.duration;
        const gapEnd = nextItem.startTime;
        const gapSize = gapEnd - gapStart;
        
        if (gapSize >= duration) {
          return gapStart;
        }
      }
    }
    
    if (trackItems.length === 0) return 0;
    const lastItem = trackItems[trackItems.length - 1];
    return lastItem.startTime + lastItem.duration;
  }, [timelineItems, checkCollision]);

  const removeFromTimeline = (itemId) => {
    const audioElement = audioElementsRef.current.get(itemId);
    if (audioElement) {
      audioElement.pause();
      audioElement.src = '';
      audioElementsRef.current.delete(itemId);
    }
    
    setTimelineItems((prev) => prev.filter((item) => item.id !== itemId));
    setSelectedTimelineItem(null);
  };

  // Функции для работы с элементами таймлайна
  const splitTimelineItem = useCallback(() => {
    if (!selectedTimelineItem) return;
    
    const item = selectedTimelineItem;
    const splitTime = currentTime;
    
    if (splitTime <= item.startTime || splitTime >= item.startTime + item.duration) {
      return;
    }
    
    const firstPartDuration = splitTime - item.startTime;
    const secondPartStart = splitTime;
    const secondPartDuration = item.duration - firstPartDuration;
    
    const secondPart = {
      ...item,
      id: Date.now() + Math.random(),
      startTime: secondPartStart,
      duration: secondPartDuration,
    };
    
    const updatedFirstPart = {
      ...item,
      duration: firstPartDuration,
    };
    
    setTimelineItems(prev => prev.map(timelineItem => 
      timelineItem.id === item.id ? updatedFirstPart : timelineItem
    ).concat(secondPart));
    
    setSelectedTimelineItem(null);
  }, [selectedTimelineItem, currentTime]);

  const copyTimelineItem = useCallback(() => {
    if (!selectedTimelineItem) return;
    setCopiedItem(selectedTimelineItem);
  }, [selectedTimelineItem]);

  const pasteTimelineItem = useCallback(() => {
    if (!copiedItem) return;
    
    const targetTrackId = copiedItem.trackId;
    const pasteTime = findNextAvailablePosition(targetTrackId, copiedItem.duration, currentTime);
    
    const newItem = {
      ...copiedItem,
      id: Date.now() + Math.random(),
      startTime: pasteTime,
    };

    if (!checkCollision(newItem)) {
      setTimelineItems(prev => [...prev, newItem]);

      const newEndTime = newItem.startTime + newItem.duration;
      if (newEndTime > videoDuration) {
        setVideoDuration(newEndTime);
      }
      
      setSelectedTimelineItem(newItem);
    }
  }, [copiedItem, currentTime, findNextAvailablePosition, checkCollision, videoDuration]);

  const deleteSelectedItem = useCallback(() => {
    if (!selectedTimelineItem) return;
    removeFromTimeline(selectedTimelineItem.id);
  }, [selectedTimelineItem]);

  // Остановка авто-прокрутки
  const stopAutoScroll = useCallback(() => {
    if (autoScrollIntervalRef.current) {
      clearInterval(autoScrollIntervalRef.current);
      autoScrollIntervalRef.current = null;
    }
  }, []);

  // Автопрокрутка таймлайна
  const startAutoScroll = useCallback((direction, speed = 1) => {
    stopAutoScroll(); // Останавливаем предыдущую автопрокрутку
    
    autoScrollIntervalRef.current = setInterval(() => {
      if (timelineScrollRef.current) {
        const scrollAmount = 10 * speed; // Базовая скорость прокрутки
        const currentScrollLeft = timelineScrollRef.current.scrollLeft;
        const maxScroll = timelineScrollRef.current.scrollWidth - timelineScrollRef.current.clientWidth;
        
        if (direction === 'left' && currentScrollLeft > 0) {
          timelineScrollRef.current.scrollLeft = Math.max(0, currentScrollLeft - scrollAmount);
        } else if (direction === 'right' && currentScrollLeft < maxScroll) {
          timelineScrollRef.current.scrollLeft = Math.min(maxScroll, currentScrollLeft + scrollAmount);
        }
      }
    }, 50); // Обновляем каждые 50мс для плавности
  }, [stopAutoScroll]);

  // Проверка необходимости автопрокрутки
  const checkAutoScroll = useCallback((clientX) => {
    if (!timelineScrollRef.current) return;
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const scrollZoneWidth = 100; // Ширина зоны автопрокрутки в пикселях
    const relativeX = clientX - rect.left;
    
    // Левая зона автопрокрутки
    if (relativeX < scrollZoneWidth && timelineScrollRef.current.scrollLeft > 0) {
      const speed = (scrollZoneWidth - relativeX) / scrollZoneWidth * 2; // Ускорение ближе к краю
      startAutoScroll('left', speed);
    }
    // Правая зона автопрокрутки
    else if (relativeX > rect.width - scrollZoneWidth) {
      const maxScroll = timelineScrollRef.current.scrollWidth - timelineScrollRef.current.clientWidth;
      if (timelineScrollRef.current.scrollLeft < maxScroll) {
        const speed = (relativeX - (rect.width - scrollZoneWidth)) / scrollZoneWidth * 2;
        startAutoScroll('right', speed);
      }
    }
    // Средняя зона - остановить автопрокрутку
    else {
      stopAutoScroll();
    }
  }, [startAutoScroll, stopAutoScroll]);

  // Синхронизация скролла
  const handleTrackAreaScroll = (e) => {
    if (trackLabelsRef.current) {
      trackLabelsRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const handleTrackLabelsScroll = (e) => {
    if (timelineScrollRef.current) {
      timelineScrollRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const getTimelineWidth = () => {
    // Увеличиваем минимальную ширину и учитываем zoom правильно
    const baseWidth = videoDuration * 20 * timelineZoom;
    return Math.max(baseWidth, 1200); // Минимум 1200px для гарантированной прокрутки
  };

  // Основные эффекты
  useEffect(() => {
    const content = getCurrentTimelineContent();

    if (content.mainVideo && content.mainVideo.url !== currentVideoSource) {
      setCurrentVideoSource(content.mainVideo.url);
      if (videoRef.current) {
        const relativeTime = currentTime - content.mainVideo.startTime;
        videoRef.current.currentTime = Math.max(0, relativeTime);
      }
    } else if (!content.mainVideo && content.overlays.find(o => o.type === 'image')) {
      const firstImage = content.overlays.find(o => o.type === 'image');
      setCurrentVideoSource(firstImage.url);
    } else if (!content.mainVideo && !content.overlays.find(o => o.type === 'image')) {
      setCurrentVideoSource(null);
    }

    const currentAudioIds = new Set(content.audio.map(item => item.id));
    
    audioElementsRef.current.forEach((audioElement, id) => {
      if (!currentAudioIds.has(id)) {
        if (!audioElement.paused) {
          audioElement.pause();
        }
      }
    });

    content.audio.forEach(audioItem => {
      const audioElement = createAudioElement(audioItem);
      const relativeTime = currentTime - audioItem.startTime;
      
      audioElement.volume = (audioItem.volume || 1) * volume;
      audioElement.muted = isMuted;
      
      if (isPlaying && relativeTime >= 0) {
        syncAudioTime(audioItem, relativeTime);
        if (audioElement.paused) {
          audioElement.play().catch(console.error);
        }
      } else {
        if (!audioElement.paused) {
          audioElement.pause();
        }
      }
    });

  }, [currentTime, timelineItems, getCurrentTimelineContent, isPlaying, volume, isMuted, createAudioElement, syncAudioTime, currentVideoSource]);

  useEffect(() => {
    if (isPlaying) {
      intervalRef.current = setInterval(() => {
        setCurrentTime((prev) => {
          const newTime = prev + 0.1;
          if (newTime >= videoDuration) {
            setIsPlaying(false);
            return videoDuration;
          }
          return newTime;
        });
      }, 100);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isPlaying, videoDuration]);

  useEffect(() => {
    if (videoRef.current && currentVideoSource) {
      const content = getCurrentTimelineContent();
      if (content.mainVideo) {
        const relativeTime = currentTime - content.mainVideo.startTime;
        const timeDiff = Math.abs(videoRef.current.currentTime - relativeTime);

        if (timeDiff > 0.5) {
          videoRef.current.currentTime = Math.max(0, relativeTime);
        }

        if (isPlaying && videoRef.current.paused) {
          videoRef.current.play().catch(console.error);
        } else if (!isPlaying && !videoRef.current.paused) {
          videoRef.current.pause();
        }
      }
    }
  }, [currentTime, currentVideoSource, getCurrentTimelineContent, isPlaying]);

  useEffect(() => {
    const currentAudioIds = new Set(
      timelineItems
        .filter(item => item.type === "audio")
        .map(item => item.id)
    );

    audioElementsRef.current.forEach((audioElement, id) => {
      if (!currentAudioIds.has(id)) {
        audioElement.pause();
        audioElement.src = '';
        audioElementsRef.current.delete(id);
      }
    });
  }, [timelineItems]);

  // Очистка при размонтировании
  useEffect(() => {
    return () => {
      stopAutoScroll();
    };
  }, [stopAutoScroll]);

  // Обработка клавиатурных шорткатов
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
        return;
      }

      if (e.key === 'Delete' || e.key === 'Backspace') {
        e.preventDefault();
        deleteSelectedItem();
      }

      if (e.ctrlKey && e.key === 'b') {
        e.preventDefault();
        splitTimelineItem();
      }

      if (e.ctrlKey && e.key === 'c') {
        e.preventDefault();
        copyTimelineItem();
      }

      if (e.ctrlKey && e.key === 'v') {
        e.preventDefault();
        pasteTimelineItem();
      }

      if (e.key === ' ' || e.code === 'Space') {
        e.preventDefault();
        setIsPlaying(prev => !prev);
      }

      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        seekTo(Math.max(0, currentTime - (e.shiftKey ? 1 : 0.1)));
      }
      if (e.key === 'ArrowRight') {
        e.preventDefault();
        seekTo(Math.min(videoDuration, currentTime + (e.shiftKey ? 1 : 0.1)));
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedTimelineItem, copiedItem, currentTime, videoDuration, deleteSelectedItem, splitTimelineItem, copyTimelineItem, pasteTimelineItem, seekTo]);

  const handleVideoUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newVideo = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 10,
        type: "video",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        videos: [...prev.videos, newVideo],
      }));
    });
  };

  const handleAudioUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newAudio = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 10,
        type: "audio",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        audios: [...prev.audios, newAudio],
      }));
    });
  };

  const handleImageUpload = (event) => {
    const files = event.target.files ? Array.from(event.target.files) : [];
    files.forEach((file) => {
      const url = URL.createObjectURL(file);
      const newImage = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        type: "image",
      };

      setMediaLibrary((prev) => ({
        ...prev,
        images: [...prev.images, newImage],
      }));
    });
  };

  const addToTimeline = (mediaItem, startTime = null, targetTrackId = null) => {
    let trackType, trackId;
    
    if (mediaItem.type === "video") {
      trackType = targetTrackId && targetTrackId.startsWith('overlay') ? 'overlay' : 'main';
      trackId = targetTrackId || 'main-video';
    } else if (mediaItem.type === "image") {
      trackType = 'overlay';
      trackId = targetTrackId || 'overlay-1';
    } else if (mediaItem.type === "audio") {
      trackType = 'audio';
      trackId = targetTrackId || 'audio-1';
    }
    
    const duration = mediaItem.duration || (mediaItem.type === "image" ? 5 : 10);
    const finalStartTime = startTime !== null ? startTime : 
      findNextAvailablePosition(trackId, duration);

    const newTimelineItem = {
      id: Date.now() + Math.random(),
      mediaId: mediaItem.id,
      type: mediaItem.type,
      name: mediaItem.name,
      url: mediaItem.url,
      startTime: finalStartTime,
      duration: duration,
      trackId: trackId,
      trackType: trackType,
      volume: 1,
      opacity: 1,
    };

    if (!checkCollision(newTimelineItem)) {
      setTimelineItems((prev) => [...prev, newTimelineItem]);

      const newEndTime = newTimelineItem.startTime + newTimelineItem.duration;
      if (newEndTime > videoDuration) {
        setVideoDuration(newEndTime);
      }
    }
  };

  const addOverlayTrack = () => {
    const newTrackId = `overlay-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      overlays: [...prev.overlays, {
        id: newTrackId,
        name: `Overlay ${prev.overlays.length + 1}`,
        type: 'overlay'
      }]
    }));
  };

  const addAudioTrack = () => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      audio: [...prev.audio, {
        id: newTrackId,
        name: `Audio ${prev.audio.length + 1}`,
        type: 'audio'
      }]
    }));
  };

  const removeFromLibrary = (itemId, type) => {
    setMediaLibrary((prev) => ({
      ...prev,
      [type]: prev[type].filter((item) => item.id !== itemId),
    }));
  };

  const handleTimelineClick = (e) => {
    if (isDragging || isTimelineDragging) return;
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const timePerPixel = videoDuration / (videoDuration * 20 * timelineZoom);
    const clickTime = clickX * timePerPixel;
    seekTo(clickTime);
  };

  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setDropPreview(null);
    setIsDragging(false);
    // Не останавливаем автоскролл для элементов медиа библиотеки
  };

  const handleTimelineItemDragStart = (e, timelineItem) => {
    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(e.clientX - itemRect.left + timelineScrollRef.current.scrollLeft);
    
    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleTimelineItemDragEnd = () => {
    setDraggedTimelineItem(null);
    setDropPreview(null);
    setIsTimelineDragging(false);
    setDragOffset(0);
    stopAutoScroll(); // Останавливаем авто-прокрутку
  };

  const handleDragOver = (e) => {
    if (!draggedItem && !draggedTimelineItem) return;
    
    e.preventDefault();
    e.dataTransfer.dropEffect = draggedItem ? "copy" : "move";
    
    // Проверяем необходимость автопрокрутки только при перетаскивании элементов таймлайна
    if (draggedTimelineItem) {
      checkAutoScroll(e.clientX);
    }
    
    const timelineRect = timelineScrollRef.current.getBoundingClientRect();
    const dragX = e.clientX - timelineRect.left + timelineScrollRef.current.scrollLeft - (draggedTimelineItem ? dragOffset : 0);
    const timePerPixel = videoDuration / (videoDuration * 20 * timelineZoom);
    const dropTime = Math.max(0, dragX * timePerPixel);
    
    const dragY = e.clientY - timelineRect.top + timelineScrollRef.current.scrollTop;
    const trackHeight = 60;
    const timeRulerHeight = 48;
    
    const relativeY = dragY - timeRulerHeight;
    const trackIndex = Math.floor(relativeY / trackHeight);
    
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const targetTrack = allTracks[trackIndex];
    
    if (draggedItem && targetTrack) {
      const duration = draggedItem.duration || (draggedItem.type === "image" ? 5 : 10);
      const testItem = { trackId: targetTrack.id, startTime: dropTime, duration };
      
      setDropPreview({
        time: dropTime,
        duration: duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: !checkCollision(testItem)
      });
    } else if (draggedTimelineItem && targetTrack) {
      const testItem = { 
        ...draggedTimelineItem, 
        startTime: dropTime,
        trackId: targetTrack.id
      };
      
      setDropPreview({
        time: dropTime,
        duration: draggedTimelineItem.duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: !checkCollision(testItem, draggedTimelineItem.id)
      });
    }
  };

  const handleDrop = (e) => {
    if ((!draggedItem && !draggedTimelineItem) || !dropPreview) return;
    
    e.preventDefault();
    stopAutoScroll(); // Останавливаем автопрокрутку при завершении перетаскивания
    
    if (dropPreview.isValid) {
      if (draggedItem) {
        addToTimeline(draggedItem, dropPreview.time, dropPreview.trackId);
      } else if (draggedTimelineItem) {
        setTimelineItems(prev => prev.map(item => 
          item.id === draggedTimelineItem.id 
            ? { ...item, startTime: dropPreview.time, trackId: dropPreview.trackId, trackType: dropPreview.trackType }
            : item
        ));
      }
    }
    
    setDropPreview(null);
    setDraggedItem(null);
    setDraggedTimelineItem(null);
    setIsDragging(false);
    setIsTimelineDragging(false);
  };

  const handleDragLeave = (e) => {
    if (!timelineRef.current?.contains(e.relatedTarget)) {
      setDropPreview(null);
      stopAutoScroll(); // Останавливаем автопрокрутку при покидании таймлайна
    }
  };

  const getTimelineItemStyle = (item) => {
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(track => track.id === item.trackId);
    
    return {
      left: `${(item.startTime / videoDuration) * videoDuration * 20 * timelineZoom}px`,
      width: `${(item.duration / videoDuration) * videoDuration * 20 * timelineZoom}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getDropPreviewStyle = () => {
    if (!dropPreview) return {};
    
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(track => track.id === dropPreview.trackId);
    
    return {
      left: `${(dropPreview.time / videoDuration) * videoDuration * 20 * timelineZoom}px`,
      width: `${(dropPreview.duration / videoDuration) * videoDuration * 20 * timelineZoom}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getTrackColor = (trackType) => {
    switch (trackType) {
      case 'overlay': return 'bg-purple-500 border-purple-400';
      case 'main': return 'bg-blue-500 border-blue-400';
      case 'audio': return 'bg-green-500 border-green-400';
      default: return 'bg-gray-500 border-gray-400';
    }
  };

  return (
    <div className="flex h-screen w-full gap-4 p-4 bg-gray-100 overflow-hidden">
      {/* 📁 MEDIA LIBRARY - ЛЕВОЕ МЕНЮ - АДАПТИВНАЯ ШИРИНА */}
      <div className="w-80 min-w-72 max-w-80 bg-white border-2 border-blue-200 flex flex-col rounded-2xl shadow-lg overflow-hidden flex-shrink-0">
        {/* Заголовок медиа библиотеки */}
        <div className="bg-blue-50 px-4 py-3 border-b border-blue-100">
          <h3 className="font-bold text-blue-800 flex items-center">
            <Upload size={18} className="mr-2" />
            Media Library
          </h3>
        </div>
        
        {/* Вкладки для типов файлов */}
        <div className="p-4 pb-0">
          <div className="flex bg-gray-100 rounded-xl p-1">
            {[
              { id: "videos", name: "Videos", icon: Video },
              { id: "audios", name: "Audio", icon: Music },
              { id: "images", name: "Images", icon: Image },
            ].map((tab) => {
              const IconComponent = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex-1 p-3 text-sm font-medium transition-all duration-200 rounded-lg ${
                    activeTab === tab.id
                      ? "bg-white text-blue-600 shadow-sm"
                      : "text-gray-600 hover:text-gray-800"
                  }`}
                >
                  <IconComponent size={16} className="mr-2 inline" />
                  {tab.name}
                </button>
              );
            })}
          </div>
        </div>

        {/* Upload зона */}
        <div className="p-4">
          <button
            onClick={() => {
              if (activeTab === "videos") fileInputRef.current?.click();
              else if (activeTab === "audios") audioInputRef.current?.click();
              else imageInputRef.current?.click();
            }}
            className="w-full p-6 border-2 border-dashed border-gray-300 rounded-2xl hover:border-blue-400 hover:bg-blue-50/50 transition-all duration-200 text-center group"
          >
            <div className="w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-3 group-hover:bg-blue-100 transition-colors">
              <Upload
                size={20}
                className="text-gray-500 group-hover:text-blue-500"
              />
            </div>
            <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">
              Upload{" "}
              {activeTab === "videos"
                ? "Video"
                : activeTab === "audios"
                ? "Audio"
                : "Images"}
            </span>
            <p className="text-xs text-gray-500 mt-1">
              {activeTab === "videos"
                ? "MP4, AVI, MOV up to 2GB"
                : activeTab === "audios"
                ? "MP3, WAV, M4A up to 100MB"
                : "JPG, PNG, GIF up to 10MB"}
            </p>
          </button>
        </div>

        {/* Скрытые input элементы для загрузки */}
        <input
          ref={fileInputRef}
          type="file"
          accept="video/*"
          multiple
          onChange={handleVideoUpload}
          className="hidden"
        />
        <input
          ref={audioInputRef}
          type="file"
          accept="audio/*"
          multiple
          onChange={handleAudioUpload}
          className="hidden"
        />
        <input
          ref={imageInputRef}
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          className="hidden"
        />

        {/* Список загруженных файлов */}
        <div className="flex-1 overflow-y-auto px-4 pb-4">
          <div className="space-y-2">
            {mediaLibrary[activeTab].map((item) => (
              <div
                key={item.id}
                draggable="true"
                onDragStart={(e) => handleDragStart(e, item)}
                onDragEnd={handleDragEnd}
                className={`flex items-center p-3 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-sm transition-all duration-200 cursor-grab active:cursor-grabbing group ${
                  draggedItem?.id === item.id ? "opacity-50" : ""
                }`}
                onClick={() => addToTimeline(item)}
              >
                <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center mr-3 shadow-sm group-hover:shadow">
                  {activeTab === "videos" && (
                    <Video size={20} className="text-blue-500" />
                  )}
                  {activeTab === "audios" && (
                    <Music size={20} className="text-green-500" />
                  )}
                  {activeTab === "images" && (
                    <Image size={20} className="text-purple-500" />
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 truncate">
                    {item.name}
                  </p>
                  <p className="text-xs text-gray-500">
                    {activeTab === "images"
                      ? "Image"
                      : formatTime(item.duration || 0)}
                  </p>
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromLibrary(item.id, activeTab);
                  }}
                  className="opacity-0 group-hover:opacity-100 p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
                >
                  <Trash2 size={14} />
                </button>
              </div>
            ))}
            {mediaLibrary[activeTab].length === 0 && (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-3">
                  {activeTab === "videos" && <Video size={24} className="text-gray-400" />}
                  {activeTab === "audios" && <Music size={24} className="text-gray-400" />}
                  {activeTab === "images" && <Image size={24} className="text-gray-400" />}
                </div>
                <p className="text-sm text-gray-500">No {activeTab} uploaded yet</p>
                <p className="text-xs text-gray-400 mt-1">Click upload button above</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Main Content Area - АДАПТИВНАЯ ШИРИНА */}
      <div className="flex-1 flex flex-col gap-4 min-w-0 overflow-hidden">
        {/* Video Preview - АДАПТИВНАЯ ВЫСОТА */}
        <div className="flex-1 bg-black rounded-2xl relative overflow-hidden shadow-sm min-h-0">
          {currentVideoSource ? (
            <div className="relative w-full h-full">
              {getCurrentTimelineContent().mainVideo ? (
                <video
                  ref={videoRef}
                  src={currentVideoSource}
                  className="w-full h-full object-contain rounded-2xl"
                  muted={isMuted}
                />
              ) : (
                <img
                  src={currentVideoSource}
                  alt="Timeline content"
                  className="w-full h-full object-contain rounded-2xl"
                />
              )}
              
              {/* Overlays */}
              {getCurrentTimelineContent().overlays.map((item, index) => (
                <div
                  key={item.id}
                  className="absolute top-4 right-4 w-32 h-24 border-2 border-white rounded-lg overflow-hidden shadow-lg"
                  style={{ 
                    top: `${4 + index * 100}px`,
                    opacity: item.opacity || 1,
                  }}
                >
                  {item.type === 'video' ? (
                    <video
                      src={item.url}
                      className="w-full h-full object-cover"
                      muted
                    />
                  ) : (
                    <img
                      src={item.url}
                      alt={item.name}
                      className="w-full h-full object-cover"
                    />
                  )}
                </div>
              ))}
            </div>
          ) : (
            <div className="w-full h-full flex items-center justify-center text-white">
              <div className="text-center">
                <div className="w-20 h-20 bg-gray-800 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <Film size={40} className="opacity-50" />
                </div>
                <p className="text-xl opacity-75 font-medium">
                  No content at current time
                </p>
                <p className="text-sm opacity-50">
                  Drag media to timeline tracks
                </p>
              </div>
            </div>
          )}

          {/* Player Controls */}
          <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 bg-black/60 backdrop-blur-md rounded-2xl p-4">
            <div className="flex items-center space-x-6">
              <button
                onClick={() => seekTo(Math.max(0, currentTime - 10))}
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
              >
                <SkipBack size={20} />
              </button>
              <button
                onClick={handlePlayPause}
                className="w-12 h-12 bg-white/20 hover:bg-white/30 rounded-2xl flex items-center justify-center text-white transition-all hover:scale-105"
              >
                {isPlaying ? <Pause size={24} /> : <Play size={24} />}
              </button>
              <button
                onClick={() =>
                  seekTo(Math.min(videoDuration, currentTime + 10))
                }
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
              >
                <SkipForward size={20} />
              </button>
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setIsMuted(!isMuted)}
                  className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
                >
                  {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
                </button>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={volume}
                  onChange={(e) => setVolume(parseFloat(e.target.value))}
                  className="w-24 accent-blue-500"
                />
              </div>
              <span className="text-white text-sm font-medium min-w-0">
                {formatTime(currentTime)} / {formatTime(videoDuration)}
              </span>
            </div>
          </div>
        </div>

        {/* Timeline - ФИКСИРОВАННАЯ ВЫСОТА */}
        <div 
          className={`h-80 bg-white rounded-2xl shadow-sm border-2 transition-all duration-200 overflow-hidden flex flex-col flex-shrink-0 ${
            isDragging || isTimelineDragging 
              ? "border-blue-400 bg-blue-50/20" 
              : "border-gray-200"
          }`}
        >
          {/* Timeline Header */}
          <div className="bg-gray-50 p-4 border-b border-gray-200 space-y-3">
            <div className="flex items-center justify-between">
              <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
                <Film size={18} />
                <span>Timeline</span>
                {(isDragging || isTimelineDragging) && dropPreview && (
                  <span className={dropPreview.isValid ? "text-green-600" : "text-red-600"}>
                    - {dropPreview.isValid ? "Drop to place" : "Position blocked"}
                  </span>
                )}
                {copiedItem && (
                  <span className="text-blue-600 text-sm">
                    - "{copiedItem.name}" copied (Ctrl+V to paste)
                  </span>
                )}
              </h4>
              
              <div className="flex items-center space-x-3">
                <span className="text-gray-600 text-sm font-medium">Zoom:</span>
                <div className="bg-white rounded-xl p-2 border border-gray-200">
                  <input
                    type="range"
                    min="0.5"
                    max="3"
                    step="0.1"
                    value={timelineZoom}
                    onChange={(e) => setTimelineZoom(parseFloat(e.target.value))}
                    className="w-24 accent-blue-500"
                  />
                </div>
                <span className="text-xs text-gray-500">
                  {(timelineZoom * 100).toFixed(0)}%
                </span>
              </div>
            </div>
            
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-6">
                <div className="flex items-center space-x-2">
                  <button
                    onClick={addOverlayTrack}
                    className="p-2 text-purple-600 hover:text-purple-800 hover:bg-purple-50 rounded-xl transition-all duration-200"
                    title="Add Overlay Track"
                  >
                    <Plus size={16} />
                  </button>
                  <button
                    onClick={addAudioTrack}
                    className="p-2 text-green-600 hover:text-green-800 hover:bg-green-50 rounded-xl transition-all duration-200"
                    title="Add Audio Track"
                  >
                    <Music size={16} />
                  </button>
                  <button
                    onClick={splitTimelineItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-red-600 hover:text-red-800 hover:bg-red-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Split Selected Item (Ctrl+B)"
                  >
                    <Scissors size={16} />
                  </button>
                  <button 
                    onClick={copyTimelineItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-blue-600 hover:text-blue-800 hover:bg-blue-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Copy Selected Item (Ctrl+C)"
                  >
                    <Copy size={16} />
                  </button>
                  <button 
                    onClick={deleteSelectedItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-red-600 hover:text-red-800 hover:bg-red-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Delete Selected Item (Del)"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
                
                {/* Панель с шорткатами */}
                <div className="text-xs text-gray-500 bg-gray-50 px-3 py-1 rounded-lg">
                  <span className="font-medium">Shortcuts:</span>
                  <span className="ml-2">Space: Play/Pause</span>
                  <span className="ml-2">Del: Delete</span>
                  <span className="ml-2">Ctrl+B: Split</span>
                  <span className="ml-2">Ctrl+C: Copy</span>
                  <span className="ml-2">Ctrl+V: Paste</span>
                  <span className="ml-2">←→: Navigate</span>
                </div>
              </div>
            </div>
          </div>

          {/* Timeline Content */}
          <div className="flex-1 flex min-h-0">
            {/* Track Labels - Fixed Left Side */}
            <div className="w-48 bg-gray-50 border-r border-gray-200 flex-shrink-0 flex flex-col">
              {/* Empty space above track labels to align with time ruler */}
              <div className="h-12 border-b border-gray-200 flex-shrink-0"></div>
              
              {/* Track Labels - Scrollable */}
              <div 
                ref={trackLabelsRef}
                className="flex-1 overflow-y-auto overflow-x-hidden"
                onScroll={handleTrackLabelsScroll}
              >
                {/* Overlay Tracks */}
                {tracks.overlays.map((track, index) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-purple-700 bg-purple-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Layers size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
                
                {/* Main Video Track */}
                {tracks.main.map((track, index) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-blue-700 bg-blue-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Video size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
                
                {/* Audio Tracks */}
                {tracks.audio.map((track, index) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-green-700 bg-green-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Music size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
              </div>
            </div>

            {/* Timeline Area - Right Side */}
            <div 
              ref={(el) => {
                timelineRef.current = el;
                timelineScrollRef.current = el;
              }}
              className="flex-1 relative overflow-auto bg-white"
              onScroll={handleTrackAreaScroll}
              onDragOver={handleDragOver}
              onDrop={handleDrop}
              onDragLeave={handleDragLeave}
              style={{ 
                overflowX: 'auto', 
                overflowY: 'auto',
                scrollbarWidth: 'thin' // Для Firefox
              }}
            >
              {/* Container with dynamic width for horizontal scrolling */}
              <div 
                style={{ 
                  width: `${getTimelineWidth()}px`, 
                  minHeight: '100%',
                  position: 'relative'
                }}
                onClick={handleTimelineClick}
              >
                {/* Time Ruler */}
                <div className="h-12 bg-gray-50 border-b border-gray-200 relative flex-shrink-0">
                  {Array.from({ length: Math.ceil(videoDuration / 5) + 1 }, (_, i) => (
                    <div
                      key={i}
                      className="absolute top-0 h-full border-l border-gray-300"
                      style={{ left: `${i * 100 * timelineZoom}px` }}
                    >
                      <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-medium">
                        {formatTime(i * 5)}
                      </span>
                    </div>
                  ))}
                  {/* Playhead */}
                  <div
                    className="absolute top-0 w-0.5 h-full bg-red-500 z-20 shadow-sm"
                    style={{
                      left: `${(currentTime / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                    }}
                  />
                </div>

                {/* Track Area */}
                <div 
                  className="relative flex-1" 
                  style={{ 
                    height: `${(tracks.overlays.length + tracks.main.length + tracks.audio.length) * 60}px`,
                    minHeight: '100%'
                  }}
                >
                  {/* Timeline Items */}
                  {timelineItems.map((item) => (
                    <div
                      key={item.id}
                      draggable="true"
                      onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                      onDragEnd={handleTimelineItemDragEnd}
                      className={`absolute h-10 rounded-lg border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md ${
                        getTrackColor(item.trackType)
                      } ${
                        selectedTimelineItem?.id === item.id ? "ring-2 ring-blue-300" : ""
                      } ${
                        draggedTimelineItem?.id === item.id ? "opacity-50 z-30" : ""
                      } ${
                        copiedItem?.id === item.id ? "ring-2 ring-green-300 ring-dashed" : ""
                      }`}
                      style={getTimelineItemStyle(item)}
                      onClick={(e) => {
                        e.stopPropagation();
                        setSelectedTimelineItem(item);
                      }}
                    >
                      <div className="p-2 text-white text-xs truncate font-medium flex items-center">
                        {item.name}
                        {copiedItem?.id === item.id && (
                          <span className="ml-2 text-green-200">📋</span>
                        )}
                      </div>
                    </div>
                  ))}
                  
                  {/* Drop Preview */}
                  {dropPreview && (
                    <div
                      className={`absolute h-10 rounded-lg border-2 border-dashed opacity-75 ${
                        dropPreview.isValid 
                          ? `${getTrackColor(dropPreview.trackType)} bg-opacity-50` 
                          : "bg-red-300 border-red-500"
                      }`}
                      style={getDropPreviewStyle()}
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoEditor;
