import { useState, useRef, useEffect } from "react";

export const TeleprompterModal = ({
  isOpen = true,
  onClose = () => {},
  script = "Welcome to the teleprompter!...",
  onRecordingComplete = () => {},
  setCurrentStep,
  onSaveToLibrary = null,
}) => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [recordingType, setRecordingType] = useState("audio");
  const [speed, setSpeed] = useState(50);
  const [fontSize, setFontSize] = useState(24);
  const [isScrolling, setIsScrolling] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [popupWindow, setPopupWindow] = useState(null);

  const modalRef = useRef(null);
  const scrollContainerRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const scrollIntervalRef = useRef(null);
  const timerRef = useRef(null);
  const streamRef = useRef(null);
  const videoPreviewRef = useRef(null);

  // –ü–æ–ª–Ω–æ—ç–∫—Ä–∞–Ω–Ω—ã–π —Ä–µ–∂–∏–º
  const toggleFullscreen = async () => {
    try {
      if (!isFullscreen) {
        if (modalRef.current?.requestFullscreen) {
          await modalRef.current.requestFullscreen();
        } else if (modalRef.current?.webkitRequestFullscreen) {
          await modalRef.current.webkitRequestFullscreen();
        } else if (modalRef.current?.msRequestFullscreen) {
          await modalRef.current.msRequestFullscreen();
        }
        setIsFullscreen(true);
      } else {
        if (document.exitFullscreen) {
          await document.exitFullscreen();
        } else if (document.webkitExitFullscreen) {
          await document.webkitExitFullscreen();
        } else if (document.msExitFullscreen) {
          await document.msExitFullscreen();
        }
        setIsFullscreen(false);
      }
    } catch (error) {
      console.error("Fullscreen error:", error);
    }
  };

  // –û—Ç–∫—Ä—ã—Ç—å –≤ –æ—Ç–¥–µ–ª—å–Ω–æ–º –æ–∫–Ω–µ
  const openInNewWindow = () => {
    const width = 1200;
    const height = 800;
    const left = window.screen.availWidth - width;
    const top = (window.screen.availHeight - height) / 2;

    const newWindow = window.open(
      "",
      "teleprompter",
      `width=${width},height=${height},left=${left},top=${top},resizable=yes,scrollbars=no,status=no,menubar=no,toolbar=no,location=no`
    );

    if (newWindow) {
      setPopupWindow(newWindow);
      
      // –°–æ–∑–¥–∞–µ–º HTML –¥–ª—è –Ω–æ–≤–æ–≥–æ –æ–∫–Ω–∞
      const htmlContent = `
        <!DOCTYPE html>
        <html>
        <head>
          <title>Teleprompter - External Window</title>
          <style>
            * {
              margin: 0;
              padding: 0;
              box-sizing: border-box;
            }
            body {
              font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
              background: #000;
              overflow: hidden;
              height: 100vh;
            }
            .teleprompter-container {
              height: 100vh;
              overflow-y: auto;
              padding: 80px 48px;
              scrollbar-width: none;
              -ms-overflow-style: none;
            }
            .teleprompter-container::-webkit-scrollbar {
              display: none;
            }
            .teleprompter-text {
              color: white;
              line-height: 1.8;
              max-width: 1000px;
              margin: 0 auto;
              text-align: center;
              font-weight: 500;
              text-shadow: 0 2px 4px rgba(0,0,0,0.5);
            }
            .controls {
              position: fixed;
              bottom: 20px;
              left: 20px;
              right: 20px;
              background: rgba(0, 0, 0, 0.8);
              backdrop-filter: blur(10px);
              border-radius: 12px;
              padding: 16px;
              display: flex;
              justify-content: center;
              gap: 12px;
              z-index: 1000;
            }
            .control-btn {
              background: #3b82f6;
              color: white;
              border: none;
              padding: 12px 20px;
              border-radius: 8px;
              font-weight: 600;
              cursor: pointer;
              transition: background-color 0.2s;
            }
            .control-btn:hover {
              background: #2563eb;
            }
            .control-btn.recording {
              background: #ef4444;
            }
            .control-btn.recording:hover {
              background: #dc2626;
            }
            .timer {
              position: fixed;
              top: 20px;
              left: 20px;
              background: rgba(239, 68, 68, 0.9);
              color: white;
              padding: 12px 20px;
              border-radius: 12px;
              font-family: monospace;
              font-weight: bold;
              font-size: 18px;
              backdrop-filter: blur(10px);
              display: none;
            }
            .timer.visible {
              display: block;
            }
          </style>
        </head>
        <body>
          <div class="timer" id="timer">00:00</div>
          <div class="teleprompter-container" id="container">
            <div class="teleprompter-text" id="text" style="font-size: ${fontSize}px;">
              ${script}
            </div>
            <div style="height: 100vh;"></div>
          </div>
          <div class="controls">
            <button class="control-btn" id="scrollBtn">‚ñ∂Ô∏è Start Scroll</button>
            <button class="control-btn" id="recordBtn">üé§ Record</button>
            <button class="control-btn" id="resetBtn">üîÑ Reset</button>
            <button class="control-btn" id="closeBtn">‚úï Close</button>
          </div>

          <script>
            let isScrolling = false;
            let isRecording = false;
            let scrollInterval = null;
            let recordingTimer = null;
            let recordingTime = 0;
            let mediaRecorder = null;
            let stream = null;

            const container = document.getElementById('container');
            const text = document.getElementById('text');
            const scrollBtn = document.getElementById('scrollBtn');
            const recordBtn = document.getElementById('recordBtn');
            const resetBtn = document.getElementById('resetBtn');
            const closeBtn = document.getElementById('closeBtn');
            const timer = document.getElementById('timer');

            // Scroll control
            scrollBtn.addEventListener('click', () => {
              if (isScrolling) {
                stopScrolling();
              } else {
                startScrolling();
              }
            });

            function startScrolling() {
              isScrolling = true;
              scrollBtn.textContent = '‚èπÔ∏è Stop Scroll';
              scrollInterval = setInterval(() => {
                const scrollStep = (${speed} / 50) * 2;
                container.scrollTop += scrollStep;
                if (container.scrollTop >= container.scrollHeight - container.clientHeight) {
                  stopScrolling();
                }
              }, 50);
            }

            function stopScrolling() {
              isScrolling = false;
              scrollBtn.textContent = '‚ñ∂Ô∏è Start Scroll';
              if (scrollInterval) {
                clearInterval(scrollInterval);
                scrollInterval = null;
              }
            }

            // Recording control
            recordBtn.addEventListener('click', () => {
              if (isRecording) {
                stopRecording();
              } else {
                startRecording();
              }
            });

            async function startRecording() {
              try {
                stream = await navigator.mediaDevices.getUserMedia({
                  audio: { echoCancellation: true, noiseSuppression: true },
                  video: ${recordingType === 'video' ? 'true' : 'false'}
                });

                mediaRecorder = new MediaRecorder(stream);
                const chunks = [];

                mediaRecorder.ondataavailable = (event) => {
                  if (event.data.size > 0) {
                    chunks.push(event.data);
                  }
                };

                mediaRecorder.onstop = () => {
                  const blob = new Blob(chunks, { type: mediaRecorder.mimeType });
                  const url = URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url;
                  a.download = \`recording-\${Date.now()}.webm\`;
                  a.click();
                  URL.revokeObjectURL(url);
                  
                  stream.getTracks().forEach(track => track.stop());
                };

                mediaRecorder.start();
                isRecording = true;
                recordBtn.textContent = '‚èπÔ∏è Stop Record';
                recordBtn.classList.add('recording');
                timer.classList.add('visible');

                recordingTimer = setInterval(() => {
                  recordingTime++;
                  const mins = Math.floor(recordingTime / 60);
                  const secs = recordingTime % 60;
                  timer.textContent = \`\${mins.toString().padStart(2, '0')}:\${secs.toString().padStart(2, '0')}\`;
                }, 1000);

                if (!isScrolling) {
                  startScrolling();
                }
              } catch (error) {
                alert('Failed to access microphone/camera: ' + error.message);
              }
            }

            function stopRecording() {
              if (mediaRecorder && isRecording) {
                mediaRecorder.stop();
                isRecording = false;
                recordBtn.textContent = 'üé§ Record';
                recordBtn.classList.remove('recording');
                timer.classList.remove('visible');
                
                if (recordingTimer) {
                  clearInterval(recordingTimer);
                  recordingTimer = null;
                }
                recordingTime = 0;
              }
            }

            // Reset
            resetBtn.addEventListener('click', () => {
              stopScrolling();
              stopRecording();
              container.scrollTop = 0;
            });

            // Close
            closeBtn.addEventListener('click', () => {
              stopScrolling();
              stopRecording();
              window.close();
            });

            // Keyboard shortcuts
            document.addEventListener('keydown', (e) => {
              if (e.code === 'Space') {
                e.preventDefault();
                if (isScrolling) {
                  stopScrolling();
                } else {
                  startScrolling();
                }
              } else if (e.code === 'KeyR') {
                e.preventDefault();
                if (isRecording) {
                  stopRecording();
                } else {
                  startRecording();
                }
              } else if (e.code === 'Escape') {
                resetBtn.click();
              }
            });

            // Handle window close
            window.addEventListener('beforeunload', () => {
              stopScrolling();
              stopRecording();
            });
          </script>
        </body>
        </html>
      `;

      newWindow.document.write(htmlContent);
      newWindow.document.close();

      // –ó–∞–∫—Ä—ã—Ç–∏–µ –æ—Å–Ω–æ–≤–Ω–æ–≥–æ –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏ –≤ –Ω–æ–≤–æ–º –æ–∫–Ω–µ
      handleClose();

      // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –∑–∞–∫—Ä—ã—Ç–∏—è –≤–Ω–µ—à–Ω–µ–≥–æ –æ–∫–Ω–∞
      const checkClosed = setInterval(() => {
        if (newWindow.closed) {
          setPopupWindow(null);
          clearInterval(checkClosed);
        }
      }, 1000);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã—Ö–æ–¥–∞ –∏–∑ –ø–æ–ª–Ω–æ—ç–∫—Ä–∞–Ω–Ω–æ–≥–æ —Ä–µ–∂–∏–º–∞
  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    document.addEventListener('webkitfullscreenchange', handleFullscreenChange);
    document.addEventListener('msfullscreenchange', handleFullscreenChange);

    return () => {
      document.removeEventListener('fullscreenchange', handleFullscreenChange);
      document.removeEventListener('webkitfullscreenchange', handleFullscreenChange);
      document.removeEventListener('msfullscreenchange', handleFullscreenChange);
    };
  }, []);

  // –§—É–Ω–∫—Ü–∏—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ –º–µ–¥–∏–∞–±–∏–±–ª–∏–æ—Ç–µ–∫—É
  const saveToMediaLibrary = async () => {
    if (!recordedBlob || !onSaveToLibrary) return;
    setCurrentStep(4);
    setSaveSuccess(false);

    try {
      const recordingData = {
        blob: recordedBlob,
        type: recordingType,
        duration: recordingTime,
        timestamp: new Date().toISOString(),
      };

      const success = await onSaveToLibrary(recordingData);

      if (success) {
        setSaveSuccess(true);
        setTimeout(() => setSaveSuccess(false), 3000);
      }
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ –±–∏–±–ª–∏–æ—Ç–µ–∫—É:", error);
    } finally {
      setIsSaving(false);
    }
  };

  const startScrolling = () => {
    if (scrollIntervalRef.current) return;

    setIsScrolling(true);
    scrollIntervalRef.current = setInterval(() => {
      if (scrollContainerRef.current && isScrolling && !isPaused) {
        const container = scrollContainerRef.current;
        const scrollStep = (speed / 50) * 2;
        container.scrollTop += scrollStep;

        if (
          container.scrollTop >=
          container.scrollHeight - container.clientHeight
        ) {
          stopScrolling();
        }
      }
    }, 50);
  };

  const stopScrolling = () => {
    if (scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = null;
    }
    setIsScrolling(false);
  };

  const resetScroll = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollTop = 0;
    }
    stopScrolling();
  };

  const startTimer = () => {
    timerRef.current = setInterval(() => {
      setRecordingTime((prev) => prev + 1);
    }, 1000);
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const stopRecordingAndResetTimer = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }

    stopTimer();
    setRecordingTime(0);

    stopScrolling();
    setIsPaused(false);

    if (videoPreviewRef.current) {
      videoPreviewRef.current.srcObject = null;
      videoPreviewRef.current.pause();
      videoPreviewRef.current.load();
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => {
        track.stop();
      });
      streamRef.current = null;
    }

    setRecordedBlob(null);
  };

  const resetAllStates = () => {
    stopRecordingAndResetTimer();
    stopScrolling();
    setIsPaused(false);
    resetScroll();
    setShowSettings(false);
    setIsSaving(false);
    setSaveSuccess(false);
  };

  const startRecording = async () => {
    try {
      const constraints =
        recordingType === "video"
          ? {
              video: {
                width: { ideal: 1280 },
                height: { ideal: 720 },
                frameRate: { ideal: 30 },
              },
              audio: {
                echoCancellation: true,
                noiseSuppression: true,
                sampleRate: 44100,
              },
            }
          : {
              audio: {
                echoCancellation: true,
                noiseSuppression: true,
                sampleRate: 44100,
              },
            };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      let mediaRecorder;

      if (recordingType === "video") {
        const videoCodecs = [
          "video/webm;codecs=vp9,opus",
          "video/webm;codecs=vp8,opus",
          "video/webm;codecs=h264,opus",
          "video/webm",
          "video/mp4",
        ];

        let selectedCodec = null;
        for (const codec of videoCodecs) {
          if (MediaRecorder.isTypeSupported(codec)) {
            selectedCodec = codec;
            console.log("‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–æ–¥–µ–∫ –¥–ª—è –≤–∏–¥–µ–æ:", codec);
            break;
          }
        }

        mediaRecorder = new MediaRecorder(stream, {
          mimeType: selectedCodec,
          videoBitsPerSecond: 2500000,
          audioBitsPerSecond: 128000,
        });
      } else {
        const audioCodecs = [
          "audio/webm;codecs=opus",
          "audio/webm",
          "audio/mp4",
        ];

        let selectedCodec = null;
        for (const codec of audioCodecs) {
          if (MediaRecorder.isTypeSupported(codec)) {
            selectedCodec = codec;
            console.log("‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–æ–¥–µ–∫ –¥–ª—è –∞—É–¥–∏–æ:", codec);
            break;
          }
        }

        mediaRecorder = new MediaRecorder(stream, {
          mimeType: selectedCodec,
          audioBitsPerSecond: 128000,
        });
      }

      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
          console.log("üì¶ –ü–æ–ª—É—á–µ–Ω chunk:", event.data.size, "bytes");
        }
      };

      mediaRecorder.onstop = () => {
        console.log("üî¥ –ó–∞–ø–∏—Å—å –æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞, chunks:", chunks.length);

        const mimeType = mediaRecorder.mimeType;
        const blob = new Blob(chunks, { type: mimeType });

        console.log("üìπ –°–æ–∑–¥–∞–Ω blob:", {
          size: blob.size,
          type: blob.type,
          recordingType: recordingType,
        });

        setRecordedBlob(blob);

        if (videoPreviewRef.current) {
          videoPreviewRef.current.srcObject = null;
        }

        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start(100);
      setIsRecording(true);
      startTimer();

      if (!isScrolling) {
        startScrolling();
      }
    } catch (error) {
      console.error("‚ùå –û—à–∏–±–∫–∞ –∑–∞–ø–∏—Å–∏:", error);
      alert("Failed to access microphone/camera: " + error.message);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      stopTimer();
      stopScrolling();
      setIsPaused(false);

      if (videoPreviewRef.current) {
        videoPreviewRef.current.srcObject = null;
        videoPreviewRef.current.pause();
      }

      if (streamRef.current) {
        streamRef.current.getTracks().forEach((track) => {
          track.stop();
        });
        streamRef.current = null;
      }
    }
  };

  const downloadRecording = () => {
    if (recordedBlob) {
      const url = URL.createObjectURL(recordedBlob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `recording-${Date.now()}.${
        recordingType === "video" ? "webm" : "webm"
      }`;
      a.click();
      URL.revokeObjectURL(url);
    }
  };

  const useRecording = () => {
    if (recordedBlob && onRecordingComplete) {
      onRecordingComplete(recordedBlob, recordingType);
    }
    handleClose();
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, "0")}:${secs
      .toString()
      .padStart(2, "0")}`;
  };

  const handleClose = () => {
    resetAllStates();
    if (isFullscreen) {
      document.exitFullscreen?.();
    }
    if (popupWindow) {
      popupWindow.close();
    }
    onClose();
  };

  useEffect(() => {
    if (
      isRecording &&
      recordingType === "video" &&
      streamRef.current &&
      videoPreviewRef.current
    ) {
      const videoElement = videoPreviewRef.current;
      if (!videoElement.srcObject) {
        console.log("Additional video setup through useEffect");
        videoElement.srcObject = streamRef.current;
        videoElement
          .play()
          .catch((e) => console.log("UseEffect play error:", e));
      }
    }
  }, [isRecording, recordingType, streamRef.current]);

  useEffect(() => {
    if (isScrolling && !isPaused && scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = setInterval(() => {
        if (scrollContainerRef.current && isScrolling && !isPaused) {
          const container = scrollContainerRef.current;
          const scrollStep = (speed / 50) * 2;
          container.scrollTop += scrollStep;

          if (
            container.scrollTop >=
            container.scrollHeight - container.clientHeight
          ) {
            stopScrolling();
          }
        }
      }, 50);
    }
  }, [speed, isScrolling, isPaused]);

  useEffect(() => {
    // –ó–∞–∫—Ä—ã—Ç–∏–µ –≤–Ω–µ—à–Ω–µ–≥–æ –æ–∫–Ω–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    return () => {
      console.log("Component unmounting, cleaning up resources");

      if (scrollIntervalRef.current) {
        clearInterval(scrollIntervalRef.current);
        scrollIntervalRef.current = null;
      }

      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }

      if (
        mediaRecorderRef.current &&
        mediaRecorderRef.current.state !== "inactive"
      ) {
        mediaRecorderRef.current.stop();
      }

      if (streamRef.current) {
        streamRef.current.getTracks().forEach((track) => {
          track.stop();
        });
      }

      if (videoPreviewRef.current) {
        videoPreviewRef.current.srcObject = null;
        videoPreviewRef.current.pause();
      }

      if (popupWindow) {
        popupWindow.close();
      }
    };
  }, [popupWindow]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-md flex items-center justify-center z-[9999] p-4">
      <div
        ref={modalRef}
        className={`bg-white/95 backdrop-blur-xl rounded-2xl w-full max-w-7xl flex flex-col overflow-hidden border border-slate-200/50 shadow-2xl transition-all duration-300 ${
          isFullscreen ? 'h-screen max-w-none rounded-none' : 'h-[90vh]'
        }`}
      >
        {/* Modal header */}
        <div className="flex items-center justify-between p-6 border-b border-slate-200/50 bg-gradient-to-r from-slate-50 to-blue-50">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-gradient-to-r from-slate-600 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg">
              <span className="text-white text-2xl">üëÅÔ∏è</span>
            </div>
            <div>
              <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                Teleprompter
              </h2>
              <p className="text-sm text-gray-600 font-medium">
                Professional recording with a prompter
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowSettings(!showSettings)}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
              title="Settings"
            >
              ‚öôÔ∏è
            </button>
            <button
              onClick={toggleFullscreen}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
              title={isFullscreen ? "Exit fullscreen" : "Enter fullscreen"}
            >
              {isFullscreen ? 'üóó' : '‚õ∂'}
            </button>
            <button
              onClick={openInNewWindow}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
              title="Open in new window"
            >
              ü™ü
            </button>
            <button
              onClick={handleClose}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
              title="Close"
            >
              ‚úï
            </button>
          </div>
        </div>

        {/* Settings */}
        {showSettings && (
          <div className="bg-gradient-to-r from-slate-50/90 to-blue-50/90 backdrop-blur-xl p-6 border-b border-slate-200/50">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-3">
                  Scroll speed: {speed}%
                </label>
                <input
                  type="range"
                  min="10"
                  max="200"
                  value={speed}
                  onChange={(e) => setSpeed(parseInt(e.target.value))}
                  className="w-full h-2 bg-slate-200 rounded-lg appearance-none cursor-pointer"
                />
                <div className="flex justify-between text-xs text-slate-500 mt-1">
                  <span>Slow</span>
                  <span>Fast</span>
                </div>
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-3">
                  Font size: {fontSize}px
                </label>
                <input
                  type="range"
                  min="16"
                  max="48"
                  value={fontSize}
                  onChange={(e) => setFontSize(parseInt(e.target.value))}
                  className="w-full h-2 bg-slate-200 rounded-lg appearance-none cursor-pointer"
                />
                <div className="flex justify-between text-xs text-slate-500 mt-1">
                  <span>Small</span>
                  <span>Large</span>
                </div>
              </div>
              <div>
                <label className="block text-sm font-semibold text-slate-700 mb-3">
                  Recording type
                </label>
                <select
                  value={recordingType}
                  onChange={(e) => setRecordingType(e.target.value)}
                  className="w-full p-3 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-slate-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg font-medium"
                >
                  <option value="audio">Audio only</option>
                  <option value="video">Video + audio</option>
                </select>
              </div>
            </div>
          </div>
        )}

        <div className="flex flex-1 overflow-hidden">
          {/* Main area with teleprompter */}
          <div className="flex-1 flex flex-col">
            {/* Teleprompter area */}
            <div className="flex-1 relative bg-black overflow-hidden">
              <div
                ref={scrollContainerRef}
                className="h-full overflow-y-scroll px-12 py-20"
                style={{
                  scrollbarWidth: "none",
                  msOverflowStyle: "none",
                }}
              >
                <style>{`
                  .scrollbar-hide::-webkit-scrollbar {
                    display: none;
                  }
                `}</style>
                <div
                  className="text-white leading-relaxed max-w-4xl mx-auto text-center font-medium"
                  style={{
                    fontSize: `${fontSize}px`,
                    lineHeight: "1.8",
                    textShadow: "0 2px 4px rgba(0,0,0,0.5)",
                  }}
                >
                  {script}
                </div>
                <div className="h-screen"></div>
              </div>

              {/* Recording indicator */}
              {isRecording && (
                <div className="absolute top-6 left-6 flex items-center space-x-3 bg-gradient-to-r from-red-500 to-pink-500 text-white px-6 py-3 rounded-2xl shadow-2xl backdrop-blur-xl border border-red-400/30">
                  <div className="w-4 h-4 bg-white rounded-full animate-pulse"></div>
                  <span className="font-mono font-bold text-lg">
                    {formatTime(recordingTime)}
                  </span>
                  <span className="text-sm opacity-90">REC</span>
                </div>
              )}

              {/* Scroll indicator */}
              {isScrolling && (
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 bg-blue-500/80 text-white px-4 py-2 rounded-xl backdrop-blur-xl border border-blue-400/30">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <span className="text-sm font-medium">
                      {isPaused ? "Paused" : "Scrolling active"}
                    </span>
                  </div>
                </div>
              )}

              {/* Fullscreen controls overlay */}
              {isFullscreen && (
                <div className="absolute bottom-6 right-6 flex space-x-3">
                  <button
                    onClick={() => {
                      if (isScrolling) {
                        stopScrolling();
                        setIsPaused(false);
                      } else {
                        startScrolling();
                      }
                    }}
                    className="bg-blue-500/80 hover:bg-blue-600/80 text-white p-4 rounded-xl backdrop-blur-xl border border-blue-400/30 transition-all"
                  >
                    {isScrolling ? '‚èπÔ∏è' : '‚ñ∂Ô∏è'}
                  </button>
                  {!isRecording ? (
                    <button
                      onClick={startRecording}
                      className="bg-red-500/80 hover:bg-red-600/80 text-white p-4 rounded-xl backdrop-blur-xl border border-red-400/30 transition-all"
                    >
                      üé§
                    </button>
                  ) : (
                    <button
                      onClick={stopRecording}
                      className="bg-slate-500/80 hover:bg-slate-600/80 text-white p-4 rounded-xl backdrop-blur-xl border border-slate-400/30 transition-all"
                    >
                      ‚èπÔ∏è
                    </button>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Control panel - —Å–∫—Ä—ã—Ç –≤ –ø–æ–ª–Ω–æ—ç–∫—Ä–∞–Ω–Ω–æ–º —Ä–µ–∂–∏–º–µ */}
          {!isFullscreen && (
            <div className="w-96 bg-gradient-to-br from-white/90 to-slate-50/90 backdrop-blur-xl border-l border-slate-200/50 p-6 flex flex-col overflow-y-auto max-h-full">
              {/* Video preview */}
              {recordingType === "video" && (
                <div className="mb-6">
                  <h3 className="font-bold text-slate-800 mb-4 flex items-center text-lg">
                    <span className="mr-2">üìπ</span>
                    Video preview
                  </h3>
                  <div className="bg-gradient-to-r from-slate-50 to-blue-50 border-2 border-slate-200 rounded-xl p-4 shadow-lg">
                    <div className="relative">
                      <video
                        ref={videoPreviewRef}
                        className="w-full h-48 rounded-xl object-cover bg-gray-900 border border-gray-600"
                        autoPlay
                        muted
                        playsInline
                        style={{
                          display: "block",
                          backgroundColor: "#1f2937",
                        }}
                      />
                      {isRecording && (
                        <div className="absolute top-3 left-3 bg-red-500 text-white text-xs px-3 py-1 rounded-full font-bold animate-pulse">
                          LIVE
                        </div>
                      )}
                      {!isRecording && (
                        <div className="absolute inset-0 flex items-center justify-center text-white text-sm bg-black/50 rounded-xl">
                          <div className="text-center">
                            <div className="text-2xl mb-2">üìπ</div>
                            <span>Ready to record</span>
                          </div>
                        </div>
                      )}
                    </div>
                    <p className="text-slate-700 text-sm text-center mt-3 font-medium">
                      {isRecording ? "Recording video" : "Video mode active"}
                    </p>
                  </div>
                </div>
              )}

              {/* Scroll control */}
              <div className="mb-8">
                <h3 className="font-bold text-slate-800 mb-4 flex items-center text-lg">
                  <span className="mr-2">üìÑ</span>
                  Text management
                </h3>
                <div className="flex space-x-3">
                  <button
                    onClick={() => {
                      if (isScrolling) {
                        stopScrolling();
                        setIsPaused(false);
                      } else {
                        startScrolling();
                      }
                    }}
                    className="flex-1 bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white py-4 px-4 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl flex items-center justify-center"
                  >
                    {isScrolling ? (
                      <>
                        <span className="mr-2">‚èπÔ∏è</span> Stop
                      </>
                    ) : (
                      <>
                        <span className="mr-2">‚ñ∂Ô∏è</span> Start
                      </>
                    )}
                  </button>
                  <button
                    onClick={resetScroll}
                    className="bg-slate-500 hover:bg-slate-600 text-white py-4 px-4 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                  >
                    üîÑ
                  </button>
                </div>

                <div className="mt-3 text-center">
                  <span
                    className={`text-sm font-medium ${
                      isScrolling ? "text-green-600" : "text-slate-500"
                    }`}
                  >
                    {isScrolling ? "Scrolling active" : "Scrolling stopped"}
                  </span>
                </div>
              </div>

              {/* Recording control */}
              <div className="mb-8">
                <h3 className="font-bold text-slate-800 mb-4 flex items-center text-lg">
                  <span className="mr-2">üé§</span>
                  Recording
                </h3>

                {!isRecording ? (
                  <button
                    onClick={startRecording}
                    className="w-full bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white py-4 px-6 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl flex items-center justify-center group"
                  >
                    {recordingType === "video" ? (
                      <>
                        <span className="mr-3 text-xl group-hover:scale-110 transition-transform">
                          üìπ
                        </span>
                        Record video
                      </>
                    ) : (
                      <>
                        <span className="mr-3 text-xl group-hover:scale-110 transition-transform">
                          üé§
                        </span>
                        Record audio
                      </>
                    )}
                  </button>
                ) : (
                  <div className="space-y-3">
                    <button
                      onClick={stopRecording}
                      className="w-full bg-gradient-to-r from-slate-600 to-slate-700 hover:from-slate-700 hover:to-slate-800 text-white py-4 px-6 rounded-xl font-semibold transition-all duration-300 shadow-lg flex items-center justify-center"
                    >
                      <span className="mr-3">‚èπÔ∏è</span>
                      Stop recording
                    </button>

                    <button
                      onClick={stopRecordingAndResetTimer}
                      className="w-full bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white py-3 px-6 rounded-xl font-semibold transition-all duration-300 shadow-lg flex items-center justify-center text-sm"
                    >
                      <span className="mr-2">üõë</span>
                      Abort and reset
                    </button>
                  </div>
                )}

                {recordingTime > 0 && (
                  <div className="mt-4 text-center bg-slate-50 p-4 rounded-xl border border-slate-200">
                    <div className="text-3xl font-mono font-bold text-slate-800 mb-2">
                      {formatTime(recordingTime)}
                    </div>
                    {!isRecording && (
                      <button
                        onClick={() => setRecordingTime(0)}
                        className="text-sm text-slate-600 hover:text-slate-800 underline font-medium"
                      >
                        Reset timer
                      </button>
                    )}
                  </div>
                )}
              </div>

              {/* Recording result */}
              {recordedBlob && (
                <div className="bg-gradient-to-r from-green-50 to-emerald-50 border-2 border-green-200 rounded-xl p-6 mb-6 shadow-lg">
                  <div className="flex items-center space-x-3 mb-4">
                    <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center shadow-lg">
                      <span className="text-white text-xl">üîä</span>
                    </div>
                    <div>
                      <h4 className="font-bold text-green-800 text-lg">Ready!</h4>
                      <p className="text-sm text-green-600 font-medium">
                        {recordingType === "video" ? "Video" : "Audio"}{" "}
                        successfully recorded
                      </p>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <div className="flex space-x-3">
                      <button
                        onClick={downloadRecording}
                        className="flex-1 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white py-3 px-4 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl flex items-center justify-center"
                      >
                        <span className="mr-2">üì•</span>
                        Download
                      </button>
                      <button
                        onClick={useRecording}
                        className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white py-3 px-4 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl"
                      >
                        Use
                      </button>
                    </div>

                    {onSaveToLibrary && (
                      <button
                        onClick={saveToMediaLibrary}
                        disabled={isSaving || saveSuccess}
                        className={`w-full py-3 px-4 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl flex items-center justify-center ${
                          saveSuccess
                            ? "bg-gradient-to-r from-purple-500 to-purple-600 text-white cursor-default"
                            : isSaving
                            ? "bg-gradient-to-r from-gray-400 to-gray-500 text-white cursor-not-allowed"
                            : "bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 text-white"
                        }`}
                      >
                        {isSaving ? (
                          <>
                            <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                            Saving....
                          </>
                        ) : saveSuccess ? (
                          <>
                            <span className="mr-2">‚úÖ</span>
                            Save to library
                          </>
                        ) : (
                          <>
                            <span className="mr-2">üíæ</span>
                            Save to media
                          </>
                        )}
                      </button>
                    )}
                  </div>
                </div>
              )}

              {(isRecording ||
                recordingTime > 0 ||
                recordedBlob ||
                isScrolling) && (
                <div className="mb-6">
                  <button
                    onClick={resetAllStates}
                    className="w-full bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 text-white py-3 px-6 rounded-xl font-semibold transition-all duration-300 shadow-lg hover:shadow-xl flex items-center justify-center"
                  >
                    <span className="mr-2">üîÑ</span>
                    Reset everything
                  </button>
                </div>
              )}

              <div className="mt-auto">
                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border-2 border-blue-200 rounded-xl p-6 shadow-lg">
                  <h4 className="font-bold text-blue-800 mb-3 flex items-center">
                    <span className="mr-2">‚ú®</span>
                    Recording Tips
                  </h4>
                  <ul className="text-sm text-blue-700 space-y-2 font-medium">
                    <li className="flex items-start">
                      <span className="text-blue-500 mr-2">‚Ä¢</span>
                      Press Space to start/stop scrolling
                    </li>
                    <li className="flex items-start">
                      <span className="text-blue-500 mr-2">‚Ä¢</span>
                      Use fullscreen mode for better focus
                    </li>
                    <li className="flex items-start">
                      <span className="text-blue-500 mr-2">‚Ä¢</span>
                      Open in new window for dual monitor setups
                    </li>
                    <li className="flex items-start">
                      <span className="text-blue-500 mr-2">‚Ä¢</span>
                      Adjust speed to match your reading pace
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TeleprompterModal;
