import { useRef, useState, useEffect } from "react";

import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  ChevronLeft,
  ChevronRight,
  Brain,
  Mic,
  Users,
  Eye,
  Clock,
  TrendingUp,
  BookOpen,
  Heart,
  Search,
  Lightbulb,
  Music,
  RotateCcw,
  Target,
  AlertCircle,
  CheckCircle,
  Award,
} from "lucide-react";

const ContentStudio = ({
  isLeftPanelCollapsed,
  setIsLeftPanelCollapsed,
  contentType,
  setContentType,
  topic,
  setTopic,
  keyPoints,
  duration,
  setDuration,
  script,
  setScript,
  isGeneratingKeyPoints,
  isGeneratingScript,
  isExtendingScript,
  isAuthenticated,
  audioMethod,
  setAudioMethod,
  recordedAudio,
  setShowTeleprompter,
  setShowAudioEditor,
  audioUrl,
  assessment,
  assessmentLoading,
  handleTextSelection,
  generateKeyPoints,
  generateScript,
  extendScript,
  generateAudio,
  addKeyPoint,
  updateKeyPoint,
  removeKeyPoint,
  contentTypes,
}) => {
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false);
  const [isContentTypeDropdownOpen, setIsContentTypeDropdownOpen] =
    useState(false);

  const languages = [
    { id: "english", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
    { id: "russian", name: "Ð ÑƒÑÑÐºÐ¸Ð¹", flag: "ðŸ‡·ðŸ‡º" },
    { id: "spanish", name: "EspaÃ±ol", flag: "ðŸ‡ªðŸ‡¸" },
    { id: "french", name: "FranÃ§ais", flag: "ðŸ‡«ðŸ‡·" },
    { id: "german", name: "Deutsch", flag: "ðŸ‡©ðŸ‡ª" },
    { id: "chinese", name: "ä¸­æ–‡", flag: "ðŸ‡¨ðŸ‡³" },
  ];

  const [selectedLanguage, setSelectedLanguage] = useState("english");

  const scriptRef = useRef(null);

  // Script loading skeleton component
  const ScriptEditorSkeleton = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="h-6 bg-gray-200 rounded w-48 animate-pulse"></div>
        <div className="h-8 bg-gray-200 rounded w-32 animate-pulse"></div>
      </div>
      <div className="space-y-3">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="h-4 bg-gray-200 rounded animate-pulse"
            style={{ width: `${85 + Math.random() * 15}%` }}
          ></div>
        ))}
      </div>
    </div>
  );
  const DetailedAnalytics = ({ assessmentLoading, assessment }) => {
    // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¾Ð¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð¸Ñ Ñ†Ð²ÐµÑ‚Ð° Ð¸ Ð³Ñ€Ð°Ð´Ð¸ÐµÐ½Ñ‚Ð° Ð½Ð° Ð¾ÑÐ½Ð¾Ð²Ðµ Ð±Ð°Ð»Ð»Ð°
    const getScoreStyle = (score) => {
      if (score >= 8)
        return {
          text: "text-emerald-50",
          bg: "bg-gradient-to-br from-emerald-500 via-emerald-600 to-emerald-700",
          border: "border-emerald-300/50",
          glow: "shadow-emerald-500/30",
        };
      if (score >= 6)
        return {
          text: "text-amber-50",
          bg: "bg-gradient-to-br from-amber-500 via-amber-600 to-amber-700",
          border: "border-amber-300/50",
          glow: "shadow-amber-500/30",
        };
      return {
        text: "text-rose-50",
        bg: "bg-gradient-to-br from-rose-500 via-rose-600 to-rose-700",
        border: "border-rose-300/50",
        glow: "shadow-rose-500/30",
      };
    };

    // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¾Ð¿Ñ€ÐµÐ´ÐµÐ»ÐµÐ½Ð¸Ñ Ñ†Ð²ÐµÑ‚Ð° Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ-Ð±Ð°Ñ€Ð°
    const getProgressStyle = (score) => {
      if (score >= 8) return "bg-gradient-to-r from-emerald-400 to-emerald-600";
      if (score >= 6) return "bg-gradient-to-r from-amber-400 to-amber-600";
      return "bg-gradient-to-r from-rose-400 to-rose-600";
    };

    // Ð¡Ñ‚Ð¸Ð»ÑŒÐ½Ð°Ñ ÐºÐ¾Ð¼Ð¿Ð°ÐºÑ‚Ð½Ð°Ñ Ð¼ÐµÑ‚Ñ€Ð¸ÐºÐ°
    const StylishMetric = ({ icon: Icon, label, score }) => {
      const style = getScoreStyle(score);

      return (
        <div className="group cursor-pointer">
          <div className="bg-white/80 backdrop-blur-xl rounded-2xl p-3 border border-white/30 hover:border-white/50 transition-all duration-300 hover:shadow-xl hover:shadow-purple-500/10 hover:-translate-y-1">
            <div className="flex items-center justify-between mb-2">
              <div className="flex items-center space-x-2">
                <div className="p-2 rounded-xl bg-gradient-to-br from-indigo-500 to-purple-600 shadow-lg group-hover:shadow-xl group-hover:scale-110 transition-all duration-300">
                  <Icon size={12} className="text-white" />
                </div>
                <span className="text-xs font-bold text-gray-800 group-hover:text-gray-900 transition-colors">
                  {label}
                </span>
              </div>
              <div
                className={`px-2 py-1 rounded-lg text-xs font-bold border ${style.bg} ${style.text} ${style.border} shadow-lg ${style.glow} group-hover:scale-105 transition-all duration-300`}
              >
                {score}
              </div>
            </div>

            <div className="w-full bg-gray-200/50 rounded-full h-1.5 overflow-hidden">
              <div
                className={`h-1.5 rounded-full ${getProgressStyle(
                  score
                )} transition-all duration-700 ease-out shadow-sm`}
                style={{ width: `${Math.min(score * 10, 100)}%` }}
              />
            </div>
          </div>
        </div>
      );
    };

    // ÐšÑ€Ð°ÑÐ¸Ð²Ñ‹Ð¹ ÐºÑ€ÑƒÐ³Ð¾Ð²Ð¾Ð¹ Ð³Ñ€Ð°Ñ„Ð¸Ðº
    const BeautifulCircularScore = ({ score }) => {
      const percentage = Math.round(score * 10);
      const circumference = 2 * Math.PI * 22;
      const strokeDashoffset =
        circumference - (percentage / 100) * circumference;

      const getGradeStyle = (score) => {
        if (score >= 8)
          return {
            gradient: "from-emerald-400 via-emerald-500 to-emerald-600",
            glow: "shadow-emerald-500/50",
            grade: "A",
          };
        if (score >= 6)
          return {
            gradient: "from-amber-400 via-amber-500 to-amber-600",
            glow: "shadow-amber-500/50",
            grade: "B",
          };
        return {
          gradient: "from-rose-400 via-rose-500 to-rose-600",
          glow: "shadow-rose-500/50",
          grade: "C",
        };
      };

      const gradeStyle = getGradeStyle(score);

      return (
        <div
          className={`bg-gradient-to-br ${gradeStyle.gradient} rounded-2xl p-4 text-white shadow-2xl ${gradeStyle.glow} relative overflow-hidden`}
        >
          {/* Ð”ÐµÐºÐ¾Ñ€Ð°Ñ‚Ð¸Ð²Ð½Ñ‹Ðµ ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ñ‹ */}
          <div className="absolute top-0 right-0 w-20 h-20 bg-white/10 rounded-full -mr-10 -mt-10"></div>
          <div className="absolute bottom-0 left-0 w-16 h-16 bg-white/5 rounded-full -ml-8 -mb-8"></div>
          <div className="absolute top-1/2 left-1/2 w-12 h-12 bg-white/5 rounded-full -ml-6 -mt-6"></div>

          <div className="flex items-center justify-between relative z-10">
            <div>
              <div className="flex items-center space-x-2 mb-1">
                <div className="p-1 rounded-lg bg-white/20 backdrop-blur-sm">
                  <Award size={14} className="text-white" />
                </div>
                <span className="text-sm font-bold">Quality Score</span>
              </div>
              <p className="text-xs text-white/90 font-medium">
                Overall Rating
              </p>
            </div>

            <div className="relative">
              <svg
                className="w-12 h-12 transform -rotate-90 drop-shadow-lg"
                viewBox="0 0 48 48"
              >
                <circle
                  cx="24"
                  cy="24"
                  r="22"
                  stroke="rgba(255, 255, 255, 0.2)"
                  strokeWidth="3"
                  fill="none"
                />
                <circle
                  cx="24"
                  cy="24"
                  r="22"
                  stroke="white"
                  strokeWidth="3"
                  fill="none"
                  strokeLinecap="round"
                  strokeDasharray={circumference}
                  strokeDashoffset={strokeDashoffset}
                  className="transition-all duration-1000 ease-out drop-shadow-sm"
                />
              </svg>
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                <span className="text-lg font-bold drop-shadow-sm">
                  {score}
                </span>
                <span className="text-xs font-bold opacity-90">
                  {gradeStyle.grade}
                </span>
              </div>
            </div>
          </div>
        </div>
      );
    };

    // Ð¡Ñ‚Ð¸Ð»ÑŒÐ½Ñ‹Ðµ Ñ€ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸
    const StylishRecommendations = ({ recommendations }) => (
      <div className="bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 backdrop-blur-xl border border-blue-200/50 rounded-2xl p-4 shadow-lg">
        <div className="flex items-center space-x-2 mb-3">
          <div className="p-2 rounded-xl bg-gradient-to-br from-blue-500 to-indigo-600 shadow-lg">
            <Lightbulb size={14} className="text-white" />
          </div>
          <span className="font-bold text-gray-800 text-sm">Smart Tips</span>
          <Sparkles size={12} className="text-indigo-500" />
        </div>

        <div className="space-y-3">
          {recommendations?.slice(0, 2).map((rec, index) => (
            <div key={index} className="group">
              <div className="flex items-start space-x-3 bg-white/80 backdrop-blur-sm rounded-xl p-3 border border-white/50 hover:border-indigo-200 transition-all duration-300 hover:shadow-lg">
                <div className="flex-shrink-0 w-6 h-6 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-full flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300">
                  <span className="text-xs font-bold text-white">
                    {index + 1}
                  </span>
                </div>
                <p className="text-xs text-gray-700 leading-relaxed font-medium">
                  {rec}
                </p>
              </div>
            </div>
          ))}

          {recommendations && recommendations.length > 2 && (
            <div className="text-center">
              <div className="inline-flex items-center space-x-1 bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-3 py-1 rounded-full text-xs font-bold shadow-lg">
                <Zap size={10} />
                <span>+{recommendations.length - 2} more insights</span>
              </div>
            </div>
          )}
        </div>
      </div>
    );

    if (assessmentLoading) {
      return (
        <div className="bg-gradient-to-br from-slate-100 via-indigo-50 to-purple-50 border border-indigo-200/50 p-4 rounded-2xl backdrop-blur-xl shadow-xl">
          <div className="flex items-center space-x-3 mb-4">
            <div className="animate-spin rounded-full h-6 w-6 border-3 border-gradient-to-r from-indigo-500 to-purple-600 border-t-transparent shadow-lg"></div>
            <span className="text-sm font-bold text-gray-800">
              Analyzing your content...
            </span>
            <Sparkles size={14} className="text-indigo-500 animate-pulse" />
          </div>

          <div className="grid grid-cols-3 gap-3 mb-4">
            {[...Array(9)].map((_, index) => (
              <div
                key={index}
                className="bg-white/60 backdrop-blur-sm rounded-xl p-3 border border-white/30 animate-pulse"
              >
                <div className="h-3 bg-gradient-to-r from-gray-300 to-gray-400 rounded mb-2"></div>
                <div className="h-1.5 bg-gray-300 rounded"></div>
              </div>
            ))}
          </div>

          <div className="h-16 bg-gradient-to-r from-gray-300 via-gray-400 to-gray-300 rounded-2xl animate-pulse"></div>
        </div>
      );
    }

    if (!assessment) {
      return (
        <div className="bg-gradient-to-br from-gray-100 to-gray-200 border border-gray-300 p-6 rounded-2xl text-center backdrop-blur-xl shadow-lg">
          <AlertCircle size={20} className="text-gray-400 mx-auto mb-2" />
          <p className="text-sm text-gray-600 font-medium">
            No analysis data available
          </p>
        </div>
      );
    }

    const metrics = [
      { icon: Target, label: "Structure", score: assessment.structure || 0 },
      { icon: Clock, label: "Duration", score: assessment.video_duration || 0 },
      { icon: TrendingUp, label: "Engage", score: assessment.engagement || 0 },
      { icon: BookOpen, label: "Read", score: assessment.readability || 0 },
      { icon: Heart, label: "Emotion", score: assessment.emotional_tone || 0 },
      { icon: Eye, label: "Clarity", score: assessment.clarity || 0 },
      { icon: Search, label: "SEO", score: assessment.seo_optimization || 0 },
      { icon: Music, label: "Rhythm", score: assessment.rhythm || 0 },
      { icon: RotateCcw, label: "Repeat", score: assessment.repetition || 0 },
    ];

    const excellentCount = metrics.filter((m) => m.score >= 8).length;
    const goodCount = metrics.filter((m) => m.score >= 6 && m.score < 8).length;
    const needsWorkCount = metrics.filter((m) => m.score < 6).length;

    return (
      <div className="bg-gradient-to-br from-slate-50 via-indigo-50 to-purple-50 border border-indigo-200/50 p-4 rounded-2xl backdrop-blur-xl shadow-xl">
        {/* Ð¡Ñ‚Ð¸Ð»ÑŒÐ½Ñ‹Ð¹ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²Ð¾Ðº */}
        <div className="flex items-center space-x-3 mb-4">
          <div className="w-8 h-8 bg-gradient-to-br from-indigo-500 via-purple-600 to-pink-600 rounded-xl flex items-center justify-center shadow-lg">
            <Award className="text-white" size={16} />
          </div>
          <div>
            <h3 className="text-sm font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
              Content Analysis
            </h3>
            <p className="text-xs text-gray-600 font-medium">
              AI-powered insights
            </p>
          </div>
          <Sparkles size={14} className="text-purple-500 animate-pulse" />
        </div>

        {/* ÐšÑ€Ð°ÑÐ¸Ð²Ñ‹Ð¹ Ð¾Ð±Ñ‰Ð¸Ð¹ Ð±Ð°Ð»Ð» */}
        <div className="mb-4">
          <BeautifulCircularScore score={assessment.total_score || 0} />
        </div>

        {/* Ð¡Ñ‚Ð¸Ð»ÑŒÐ½Ð°Ñ ÑÐµÑ‚ÐºÐ° Ð¼ÐµÑ‚Ñ€Ð¸Ðº */}
        <div className="grid grid-cols-3 gap-3 mb-4">
          {metrics.map((metric, index) => (
            <StylishMetric
              key={index}
              icon={metric.icon}
              label={metric.label}
              score={metric.score}
            />
          ))}
        </div>

        {/* ÐšÑ€Ð°ÑÐ¸Ð²Ð°Ñ ÑÐ²Ð¾Ð´ÐºÐ° */}
        <div className="bg-white/80 backdrop-blur-xl rounded-2xl p-3 border border-white/50 mb-4 shadow-lg">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <div className="w-5 h-5 bg-gradient-to-br from-green-500 to-emerald-600 rounded-lg flex items-center justify-center">
                <Zap size={10} className="text-white" />
              </div>
              <span className="text-sm font-bold text-gray-800">
                Performance
              </span>
            </div>
            <div className="flex items-center space-x-1">
              <Sparkles size={10} className="text-indigo-500" />
              <span className="text-xs text-gray-600 font-bold">
                {excellentCount + goodCount}/9 Strong
              </span>
            </div>
          </div>

          <div className="grid grid-cols-3 gap-3 text-center">
            <div className="bg-gradient-to-br from-emerald-50 to-emerald-100 rounded-xl p-2 border border-emerald-200/50">
              <div className="text-lg font-bold bg-gradient-to-r from-emerald-600 to-emerald-700 bg-clip-text text-transparent">
                {excellentCount}
              </div>
              <div className="text-xs text-emerald-700 font-bold">
                Excellent
              </div>
            </div>
            <div className="bg-gradient-to-br from-amber-50 to-amber-100 rounded-xl p-2 border border-amber-200/50">
              <div className="text-lg font-bold bg-gradient-to-r from-amber-600 to-amber-700 bg-clip-text text-transparent">
                {goodCount}
              </div>
              <div className="text-xs text-amber-700 font-bold">Good</div>
            </div>
            <div className="bg-gradient-to-br from-rose-50 to-rose-100 rounded-xl p-2 border border-rose-200/50">
              <div className="text-lg font-bold bg-gradient-to-r from-rose-600 to-rose-700 bg-clip-text text-transparent">
                {needsWorkCount}
              </div>
              <div className="text-xs text-rose-700 font-bold">Improve</div>
            </div>
          </div>
        </div>

        {/* Ð¡Ñ‚Ð¸Ð»ÑŒÐ½Ñ‹Ðµ Ñ€ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸ */}
        {assessment.recommendations &&
          assessment.recommendations.length > 0 && (
            <StylishRecommendations
              recommendations={assessment.recommendations}
            />
          )}
      </div>
    );
  };

  return (
    <div
      className={`${
        isLeftPanelCollapsed ? "w-24" : "w-[450px]"
      } h-[106vh] overflow-hidden bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10 flex-shrink-0`}
    >
      <style>
        {`
          .custom-scrollbar::-webkit-scrollbar {
            width: 8px;
          }
          
          .custom-scrollbar::-webkit-scrollbar-track {
            background: #f1f5f9;
            border-radius: 10px;
            margin: 8px 0;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb {
            background: #cbd5e1;
            border-radius: 10px;
            transition: all 0.2s ease;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb:hover {
            background: #94a3b8;
          }
          
          .custom-scrollbar {
            scrollbar-width: thin;
            scrollbar-color: #cbd5e1 #f1f5f9;
          }
        `}
      </style>

      <div className="flex items-center justify-between p-6 border-b border-slate-200/50 from-slate-50 to-blue-50 flex-shrink-0">
        {!isLeftPanelCollapsed && (
          <div className="flex flex-col items-center space-y-4 h-full justify-start pt-4">
            <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
              Content Studio
            </h2>
            <p className="text-sm text-gray-600 font-medium">
              Professional content creation tools
            </p>
          </div>
        )}
        <button
          onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
          className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300 group"
        >
          {isLeftPanelCollapsed ? (
            <ChevronRight
              size={20}
              className="text-gray-600 group-hover:text-slate-700"
            />
          ) : (
            <ChevronLeft
              size={20}
              className="text-gray-600 group-hover:text-slate-700"
            />
          )}
        </button>
      </div>

      {isLeftPanelCollapsed ? (
        <div className="p-4 space-y-4">
          <div className="flex flex-col items-center space-y-4">
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-4 bg-gradient-to-br from-slate-100 to-blue-100 hover:from-slate-200 hover:to-blue-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Create script"
            >
              <FileText
                size={20}
                className="text-slate-600 group-hover:text-blue-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-4 bg-gradient-to-br from-blue-100 to-indigo-100 hover:from-blue-200 hover:to-indigo-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Upload video"
            >
              <Upload
                size={20}
                className="text-blue-600 group-hover:text-indigo-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-4 bg-gradient-to-br from-indigo-100 to-purple-100 hover:from-indigo-200 hover:to-purple-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
              title="AI voiceover"
            >
              <Mic
                size={20}
                className="text-indigo-600 group-hover:text-purple-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-4 bg-gradient-to-br from-purple-100 to-pink-100 hover:from-purple-200 hover:to-pink-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Editing"
            >
              <Scissors
                size={20}
                className="text-purple-600 group-hover:text-pink-600"
              />
            </button>
          </div>
        </div>
      ) : (
        <div className="flex-1 overflow-y-auto custom-scrollbar">
          <div className="p-6 space-y-6">
            <div className="bg-gradient-to-r from-slate-600 to-blue-600 text-white p-4 rounded-xl">
              <div className="flex items-center space-x-3 mb-2">
                <Brain size={20} />
                <span className="font-semibold text-lg">AI-Powered</span>
              </div>
              <p className="text-sm opacity-90">
                Create professional content using artificial intelligence
              </p>
            </div>
            <div className="mb-6">
              <label className="block text-sm font-semibold text-gray-800 mb-3">
                Content Language
              </label>
              <div className="relative">
                <button
                  onClick={() =>
                    setIsLanguageDropdownOpen(!isLanguageDropdownOpen)
                  }
                  className="w-full bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl p-4 text-left transition-all duration-300 hover:border-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 shadow-lg"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2 justify-center leading-tight">
                      <span className="text-base leading-none align-middle">
                        {
                          languages.find((lang) => lang.id === selectedLanguage)
                            ?.flag
                        }
                      </span>
                      <span className="text-base font-medium align-middle text-gray-900">
                        {
                          languages.find((lang) => lang.id === selectedLanguage)
                            ?.name
                        }
                      </span>
                    </div>

                    <div
                      className={`transform transition-transform duration-200 ${
                        isLanguageDropdownOpen ? "rotate-180" : ""
                      }`}
                    >
                      <svg
                        className="w-5 h-5 text-gray-500"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M19 9l-7 7-7-7"
                        />
                      </svg>
                    </div>
                  </div>
                </button>

                {isLanguageDropdownOpen && (
                  <div className="absolute z-50 w-full mt-2 bg-white/95 backdrop-blur-xl border-2 border-slate-200 rounded-xl shadow-2xl overflow-hidden">
                    {languages.map((lang) => (
                      <button
                        key={lang.id}
                        onClick={() => {
                          setSelectedLanguage(lang.id);
                          setIsLanguageDropdownOpen(false);
                        }}
                        className={`w-full p-4 text-left transition-all duration-200 flex items-center space-x-3 hover:bg-blue-50 ${
                          selectedLanguage === lang.id
                            ? "bg-blue-50 text-blue-700 border-l-4 border-blue-500"
                            : "text-gray-700 hover:text-blue-600"
                        }`}
                      >
                        <div className="flex items-center gap-2 justify-center leading-tight">
                          <span className="text-sm font-medium align-middle">
                            {lang.flag}
                          </span>
                          <span className="text-sm font-medium align-middle">
                            {lang.name}
                          </span>
                        </div>
                        {selectedLanguage === lang.id && (
                          <div className="ml-auto">
                            <svg
                              className="w-5 h-5 text-blue-500"
                              fill="currentColor"
                              viewBox="0 0 24 24"
                            >
                              <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                          </div>
                        )}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
            Content Type Selection
            <div className="mb-6">
              <label className="block text-sm font-semibold text-gray-800 mb-3">
                Content Type
              </label>
              <div className="relative">
                <button
                  onClick={() =>
                    setIsContentTypeDropdownOpen(!isContentTypeDropdownOpen)
                  }
                  className="w-full bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl p-4 text-left transition-all duration-300 hover:border-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 shadow-lg"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div
                        className={`w-10 h-10 rounded-xl flex items-center justify-center ${
                          contentTypes.find((type) => type.id === contentType)
                            ?.color === "slate"
                            ? "bg-slate-100"
                            : contentTypes.find(
                                (type) => type.id === contentType
                              )?.color === "blue"
                            ? "bg-blue-100"
                            : "bg-indigo-100"
                        }`}
                      >
                        <span className="text-lg">
                          {
                            contentTypes.find((type) => type.id === contentType)
                              ?.emoji
                          }
                        </span>
                      </div>
                      <div>
                        <span className="font-medium text-gray-900 block">
                          {
                            contentTypes.find((type) => type.id === contentType)
                              ?.name
                          }
                        </span>
                        <span className="text-sm text-gray-500">
                          Content category
                        </span>
                      </div>
                    </div>
                    <div
                      className={`transform transition-transform duration-200 ${
                        isContentTypeDropdownOpen ? "rotate-180" : ""
                      }`}
                    >
                      <svg
                        className="w-5 h-5 text-gray-500"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M19 9l-7 7-7-7"
                        />
                      </svg>
                    </div>
                  </div>
                </button>

                {isContentTypeDropdownOpen && (
                  <div className="absolute z-50 w-full mt-2 bg-white/95 backdrop-blur-xl border-2 border-slate-200 rounded-xl shadow-2xl overflow-hidden">
                    {contentTypes.map((type) => {
                      const IconComponent = type.icon;
                      return (
                        <button
                          key={type.id}
                          onClick={() => {
                            setContentType(type.id);
                            setIsContentTypeDropdownOpen(false);
                          }}
                          className={`w-full p-4 text-left transition-all duration-200 flex items-center space-x-4 hover:bg-gray-50 ${
                            contentType === type.id
                              ? `${
                                  type.color === "slate"
                                    ? "bg-slate-50 border-l-4 border-slate-500"
                                    : type.color === "blue"
                                    ? "bg-blue-50 border-l-4 border-blue-500"
                                    : "bg-indigo-50 border-l-4 border-indigo-500"
                                }`
                              : "hover:bg-gray-50"
                          }`}
                        >
                          <div
                            className={`w-12 h-12 rounded-xl flex items-center justify-center transition-all duration-300 ${
                              contentType === type.id
                                ? type.color === "slate"
                                  ? "bg-slate-100 shadow-lg shadow-slate-500/20"
                                  : type.color === "blue"
                                  ? "bg-blue-100 shadow-lg shadow-blue-500/20"
                                  : "bg-indigo-100 shadow-lg shadow-indigo-500/20"
                                : "bg-gray-50"
                            }`}
                          >
                            <span className="text-xl">{type.emoji}</span>
                          </div>

                          <div className="flex-1">
                            <div
                              className={`font-semibold transition-colors ${
                                contentType === type.id
                                  ? type.color === "slate"
                                    ? "text-slate-700"
                                    : type.color === "blue"
                                    ? "text-blue-700"
                                    : "text-indigo-700"
                                  : "text-gray-700"
                              }`}
                            >
                              {type.name}
                            </div>
                            <div className="text-sm text-gray-500">
                              {type.id === "lifestyle" &&
                                "Personal growth & wellness"}
                              {type.id === "gaming" &&
                                "Gaming content & reviews"}
                              {type.id === "tech" && "Technology & innovations"}
                              {type.id === "education" &&
                                "Learning & tutorials"}
                              {type.id === "entertainment" &&
                                "Fun & entertainment"}
                            </div>
                          </div>

                          {contentType === type.id && (
                            <div className="ml-auto">
                              <svg
                                className={`w-6 h-6 ${
                                  type.color === "slate"
                                    ? "text-slate-500"
                                    : type.color === "blue"
                                    ? "text-blue-500"
                                    : "text-indigo-500"
                                }`}
                                fill="currentColor"
                                viewBox="0 0 24 24"
                              >
                                <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                              </svg>
                            </div>
                          )}
                        </button>
                      );
                    })}
                  </div>
                )}
              </div>
            </div>
            {/* Topic Input */}
            <div className="space-y-3">
              <label className="block text-gray-700 font-semibold">
                What is the video about?
              </label>
              <div className="relative group">
                <input
                  type="text"
                  value={topic}
                  onChange={(e) => setTopic(e.target.value)}
                  placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                  className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
                />
                <div className="absolute right-4 top-4">
                  <Brain size={20} className="text-slate-400" />
                </div>
              </div>
            </div>
            {/* Key Points */}
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <label className="block text-gray-700 font-semibold text-lg">
                    Key points
                  </label>
                  <p className="text-sm text-gray-500 font-medium">
                    What will engage viewers?
                  </p>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={generateKeyPoints}
                    disabled={
                      !topic.trim() ||
                      isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript ||
                      !isAuthenticated
                    }
                    className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                  >
                    {isGeneratingKeyPoints ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        Generating...
                      </>
                    ) : (
                      <>
                        <Brain size={16} className="mr-2" />
                        AI generation
                      </>
                    )}
                  </button>
                  <button
                    onClick={addKeyPoint}
                    disabled={
                      keyPoints.length >= 10 ||
                      isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript
                    }
                    className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-slate-500 to-blue-500 hover:from-slate-600 hover:to-blue-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                  >
                    <Plus size={16} className="mr-2" />
                    Add
                  </button>
                </div>
              </div>
              <div className="space-y-3 max-h-80 overflow-y-auto custom-scrollbar">
                {keyPoints.map((point, index) => (
                  <div
                    key={index}
                    className="flex items-center space-x-3 group"
                  >
                    <div className="w-8 h-8 bg-gradient-to-br from-slate-400 to-blue-400 rounded-lg flex items-center justify-center text-white font-semibold text-sm">
                      {index + 1}
                    </div>
                    <input
                      type="text"
                      value={point}
                      onChange={(e) => updateKeyPoint(index, e.target.value)}
                      placeholder="Enter key point..."
                      className="flex-1 p-3 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 hover:shadow-md font-medium"
                    />
                    {keyPoints.length > 1 && (
                      <button
                        onClick={() => removeKeyPoint(index)}
                        className="p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-xl transition-all duration-300 opacity-0 group-hover:opacity-100"
                      >
                        <X size={16} />
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>
            {/* Duration */}
            <div className="space-y-3">
              <label className="block text-gray-700 font-semibold">
                Video duration
              </label>
              <select
                value={duration}
                onChange={(e) => setDuration(e.target.value)}
                className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
              >
                <option>~5 minutes (500 words)</option>
                <option>~10 minutes (1000 words)</option>
                <option>~15 minutes (1500 words)</option>
                <option>~20 minutes (2000 words)</option>
              </select>
            </div>
            {/* Generate Button */}
            <button
              onClick={generateScript}
              disabled={
                !topic ||
                isGeneratingScript ||
                isGeneratingKeyPoints ||
                isExtendingScript ||
                !isAuthenticated
              }
              className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center shadow-lg hover:shadow-xl hover:scale-105 group overflow-hidden relative"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-slate-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              <span className="relative flex items-center">
                {isGeneratingScript ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    Generating script...
                  </>
                ) : isGeneratingKeyPoints ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    Waiting for key points...
                  </>
                ) : isExtendingScript ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    Expanding script...
                  </>
                ) : (
                  <>
                    <Wand2 className="mr-3" size={20} />
                    Generate Script
                  </>
                )}
              </span>
            </button>
            {/* Tips */}
            {!script && (
              <div className="space-y-4">
                <div className="bg-gradient-to-r from-blue-100 to-indigo-100 border border-blue-300 p-4 rounded-xl">
                  <div className="flex items-start space-x-3">
                    <div className="text-xl">ðŸ’¡</div>
                    <div>
                      <h4 className="font-semibold text-gray-800 mb-2">
                        Tip from Scriptify:
                      </h4>
                      <p className="text-sm text-gray-700">
                        Structure content logically: introduction, main section
                        with key points, conclusion. This will help viewers
                        better absorb information.
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {/* Script Editor */}
            {script && !isGeneratingScript ? (
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <label className="block text-gray-700 font-semibold text-lg">
                      Your script is ready âœ¨
                    </label>
                    <p className="text-sm text-gray-500">
                      Professional content for your video
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="flex items-center text-xs text-blue-600 bg-blue-50 px-3 py-2 rounded-xl border border-blue-200">
                      <Edit3 size={12} className="mr-2" />
                      Select text for AI improvement
                    </div>
                    <button
                      onClick={extendScript}
                      disabled={
                        isExtendingScript ||
                        isGeneratingKeyPoints ||
                        !isAuthenticated
                      }
                      className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                      title="Add more content to script"
                    >
                      {isExtendingScript ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          Expanding...
                        </>
                      ) : (
                        <>
                          <Plus size={16} className="mr-2" />
                          Write more
                        </>
                      )}
                    </button>
                  </div>
                </div>
                <textarea
                  ref={scriptRef}
                  value={script}
                  onChange={(e) => setScript(e.target.value)}
                  onMouseUp={handleTextSelection}
                  rows={12}
                  className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 leading-relaxed transition-all duration-300 shadow-lg hover:shadow-xl resize-none font-medium custom-scrollbar"
                  style={{ userSelect: "text" }}
                />
                {/* Script Stats */}
                <div className="flex items-center justify-between text-sm bg-gradient-to-r from-blue-50 to-indigo-50 p-3 rounded-xl border border-blue-200">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                      {assessmentLoading ? (
                        <div
                          className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                          style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                        />
                      ) : (
                        <Eye size={20} className="text-blue-600" />
                      )}
                      <span className="font-medium text-blue-700">
                        {!assessmentLoading &&
                          `Quality: ${assessment?.total_score || 8}/10`}
                      </span>
                    </div>
                    <div className="flex items-center space-x-1">
                      {assessmentLoading ? (
                        <div
                          className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                          style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                        />
                      ) : (
                        <Clock size={20} className="text-slate-600" />
                      )}
                      <span className="font-medium text-blue-700">
                        {!assessmentLoading &&
                          `~${
                            assessment?.video_duration ||
                            Math.ceil(script.length / 150)
                          } min`}
                      </span>
                    </div>
                    <div className="flex items-center space-x-1">
                      {assessmentLoading ? (
                        <div
                          className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                          style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                        />
                      ) : (
                        <Users size={20} className="text-indigo-600" />
                      )}
                      <span className="font-medium text-blue-700">
                        {!assessmentLoading && ` ${script.length} characters`}
                      </span>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500">
                    âœ¨ Ready for recording
                  </div>
                </div>
                <DetailedAnalytics
                  assessmentLoading={assessmentLoading}
                  assessment={assessment}
                />
              </div>
            ) : script.length && isGeneratingScript ? (
              <ScriptEditorSkeleton />
            ) : null}
            {/* Audio Creation Section */}
            {script && (
              <div className="space-y-6 border-t border-slate-200 pt-8">
                <div>
                  <h3 className="text-xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
                    Voiceover creation
                  </h3>
                  <p className="text-gray-600 font-medium">
                    Choose a method to create audio track
                  </p>
                </div>

                {/* Audio Method Selection */}
                <div className="grid grid-cols-2 gap-4">
                  <button
                    onClick={() => setAudioMethod("ai")}
                    className={`p-6 rounded-xl border-2 transition-all duration-300 ${
                      audioMethod === "ai"
                        ? "border-blue-500 bg-blue-50 shadow-lg shadow-blue-500/30"
                        : "border-gray-200 bg-white hover:border-gray-300"
                    }`}
                  >
                    <Brain
                      size={32}
                      className={`mx-auto mb-3 ${
                        audioMethod === "ai" ? "text-blue-600" : "text-gray-400"
                      }`}
                    />
                    <h4
                      className={`font-semibold mb-2 ${
                        audioMethod === "ai" ? "text-blue-700" : "text-gray-600"
                      }`}
                    >
                      AI Voiceover
                    </h4>
                    <p className="text-sm text-gray-500">
                      Generate voice using artificial intelligence
                    </p>
                  </button>
                  <button
                    onClick={() => setAudioMethod("record")}
                    className={`p-6 rounded-xl border-2 transition-all duration-300 ${
                      audioMethod === "record"
                        ? "border-slate-500 bg-slate-50 shadow-lg shadow-slate-500/30"
                        : "border-gray-200 bg-white hover:border-gray-300"
                    }`}
                  >
                    <Mic
                      size={32}
                      className={`mx-auto mb-3 ${
                        audioMethod === "record"
                          ? "text-slate-600"
                          : "text-gray-400"
                      }`}
                    />
                    <h4
                      className={`font-semibold mb-2 ${
                        audioMethod === "record"
                          ? "text-slate-700"
                          : "text-gray-600"
                      }`}
                    >
                      Record yourself
                    </h4>
                    <p className="text-sm text-gray-500">
                      Use teleprompter for professional recording
                    </p>
                  </button>
                </div>

                {/* AI Voice Options */}
                {audioMethod === "ai" && (
                  <div className="space-y-4">
                    <div>
                      <label className="block text-gray-700 font-semibold mb-3">
                        Select voice
                      </label>
                      <select className="w-full p-4 bg-white border-2 border-slate-200 rounded-xl text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium">
                        <option>Alexei - Male, professional</option>
                        <option>Anya - Female, friendly</option>
                        <option>Dmitry - Young male, energetic</option>
                        <option>Elena - Female, strict</option>
                        <option>Maxim - Male, charismatic</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-gray-700 font-semibold mb-3">
                        Speech speed
                      </label>
                      <div className="flex items-center space-x-4">
                        <span className="text-sm text-gray-500">Slow</span>
                        <input
                          type="range"
                          min="0.5"
                          max="2"
                          step="0.1"
                          defaultValue="1"
                          className="flex-1"
                        />
                        <span className="text-sm text-gray-500">Fast</span>
                      </div>
                    </div>
                    <button
                      onClick={generateAudio}
                      disabled={!isAuthenticated}
                      className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105"
                    >
                      <Volume2 className="mr-3 inline" size={20} />
                      Generate AI voiceover
                    </button>
                  </div>
                )}

                {/* Recording Options */}
                {audioMethod === "record" && (
                  <div className="space-y-4">
                    <div className="bg-gradient-to-r from-slate-50 to-blue-50 p-6 rounded-xl border-2 border-slate-200 shadow-lg">
                      <div className="text-center">
                        {!recordedAudio && (
                          <>
                            <div className="w-16 h-16 bg-gradient-to-r from-slate-600 to-blue-600 rounded-2xl mx-auto mb-4 flex items-center justify-center shadow-lg">
                              <Eye size={32} className="text-white" />
                            </div>
                            <h4 className="font-bold text-slate-800 mb-2 text-lg">
                              Professional recording
                            </h4>
                            <p className="text-sm text-slate-600 mb-6 font-medium">
                              Use a teleprompter for quality recording
                            </p>
                            <button
                              onClick={() => setShowTeleprompter(true)}
                              className="bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 flex items-center mx-auto"
                            >
                              <Eye className="mr-3" size={20} />
                              Open teleprompter
                            </button>
                          </>
                        )}
                        {recordedAudio && (
                          <>
                            <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-600 rounded-2xl mx-auto mb-4 flex items-center justify-center shadow-lg">
                              <Volume2 size={32} className="text-white" />
                            </div>
                            <h4 className="font-bold text-green-800 mb-2 text-lg">
                              Recording completed!
                            </h4>
                            <p className="text-sm text-green-600 mb-6 font-medium">
                              {recordedAudio.type === "video"
                                ? "Video"
                                : "Audio"}{" "}
                              successfully recorded
                            </p>
                            <div className="flex space-x-3 justify-center">
                              <button
                                onClick={() => setShowAudioEditor(true)}
                                className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                              >
                                <Edit3 className="mr-2 inline" size={16} />
                                Edit
                              </button>
                              <button
                                onClick={() => setShowTeleprompter(true)}
                                className="bg-gradient-to-r from-slate-600 to-slate-700 hover:from-slate-700 hover:to-slate-800 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                              >
                                Record again
                              </button>
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                )}

                {/* Generated Audio Preview */}
                {audioUrl && audioMethod === "ai" && (
                  <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-xl border-2 border-blue-200 shadow-lg">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg">
                        <Volume2 size={20} className="text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="font-bold text-blue-800 text-lg">
                          AI voiceover created!
                        </h4>
                        <p className="text-sm text-blue-600 font-medium">
                          Professional voiceover ready
                        </p>
                      </div>
                      <button
                        onClick={() => setShowAudioEditor(true)}
                        className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-3 px-4 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                      >
                        <Edit3 className="mr-2 inline" size={14} />
                        Edit
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ContentStudio;
