import React, { useState, useEffect } from "react";
import { X, Sparkles, Image, ArrowRight, Upload, Type, Palette, Wand2, Plus, Trash2 } from "lucide-react";

const ThumbnailCreator = ({ isOpen, onClose, exportSettings }) => {
  const [activeTab, setActiveTab] = useState("styles"); // "styles" –∏–ª–∏ "custom"
  const [selectedStyle, setSelectedStyle] = useState(null);
  const [customData, setCustomData] = useState({
    text: "",
    description: "",
    referenceImage: null,
    objectImages: [] // –î–æ 3 –æ–±—ä–µ–∫—Ç–æ–≤
  });
  
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedThumbnails, setGeneratedThumbnails] = useState([]);
  const [selectedThumbnail, setSelectedThumbnail] = useState(null);
  const [stylePreviewImages, setStylePreviewImages] = useState({});

  // –ì–æ—Ç–æ–≤—ã–µ —Å—Ç–∏–ª–∏ –æ–±–ª–æ–∂–µ–∫ —Å –ø—Ä–µ–≤—å—é
  const thumbnailStyles = [
    {
      id: "youtube-gaming",
      name: "Gaming YouTube",
      colors: ["#FF0080", "#7928CA", "#FF4081"],
      prompt: "Epic gaming thumbnail with vibrant neon colors, explosive effects, dramatic lighting, and gaming elements"
    },
    {
      id: "youtube-tech",
      name: "Tech Review",
      colors: ["#00D4FF", "#5B73DE", "#9D50BB"],
      prompt: "Clean tech review thumbnail with modern gadgets, blue and white color scheme, minimalist design"
    },
    {
      id: "youtube-tutorial",
      name: "Tutorial",
      colors: ["#FF6B35", "#F7931E", "#FFD23F"],
      prompt: "Educational tutorial thumbnail with clear text, arrows, step-by-step layout, and professional design"
    },
    {
      id: "youtube-vlog",
      name: "Vlog Style",
      colors: ["#FF9A9E", "#FECFEF", "#FECFEF"],
      prompt: "Personal vlog thumbnail with warm pastel colors, friendly atmosphere, and lifestyle vibes"
    },
    {
      id: "youtube-music",
      name: "Music Video",
      colors: ["#A8EDEA", "#FED6E3", "#D299C2"],
      prompt: "Music video thumbnail with vibrant artistic colors, musical elements, and creative design"
    },
    {
      id: "youtube-fitness",
      name: "Fitness",
      colors: ["#FF416C", "#FF4B2B", "#FF8E53"],
      prompt: "Fitness thumbnail with energetic red-orange colors, dynamic poses, and motivational design"
    },
    {
      id: "youtube-food",
      name: "Food & Cooking",
      colors: ["#FDBB2D", "#22C1C3", "#FF9472"],
      prompt: "Food thumbnail with appetizing warm colors, delicious presentation, and cozy kitchen vibes"
    },
    {
      id: "youtube-business",
      name: "Business",
      colors: ["#667eea", "#764ba2", "#f093fb"],
      prompt: "Professional business thumbnail with sophisticated colors, charts, corporate style, and success vibes"
    }
  ];

  // –°–æ–∑–¥–∞–Ω–∏–µ –ø—Ä–µ–≤—å—é –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –¥–ª—è —Å—Ç–∏–ª–µ–π
  useEffect(() => {
    const createStylePreviews = async () => {
      const previews = {};
      
      for (const style of thumbnailStyles) {
        const canvas = document.createElement('canvas');
        canvas.width = 320;
        canvas.height = 180;
        const ctx = canvas.getContext('2d');
        
        // –°–æ–∑–¥–∞–µ–º –≥—Ä–∞–¥–∏–µ–Ω—Ç –¥–ª—è —Å—Ç–∏–ª—è
        const gradient = ctx.createLinearGradient(0, 0, 320, 180);
        gradient.addColorStop(0, style.colors[0]);
        gradient.addColorStop(0.5, style.colors[1]);
        gradient.addColorStop(1, style.colors[2]);
        
        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 320, 180);
        
        // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—Å—Ç—É—Ä—É/–ø–∞—Ç—Ç–µ—Ä–Ω –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Å—Ç–∏–ª—è
        if (style.id.includes('gaming')) {
          // –î–æ–±–∞–≤–ª—è–µ–º "–≤–∑—Ä—ã–≤–Ω—ã–µ" —ç–ª–µ–º–µ–Ω—Ç—ã
          ctx.fillStyle = 'rgba(255, 255, 255, 0.3)';
          for (let i = 0; i < 15; i++) {
            const x = Math.random() * 320;
            const y = Math.random() * 180;
            const size = Math.random() * 8 + 3;
            ctx.beginPath();
            ctx.arc(x, y, size, 0, Math.PI * 2);
            ctx.fill();
          }
        } else if (style.id.includes('tech')) {
          // –î–æ–±–∞–≤–ª—è–µ–º –≥–µ–æ–º–µ—Ç—Ä–∏—á–µ—Å–∫–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã
          ctx.strokeStyle = 'rgba(255, 255, 255, 0.4)';
          ctx.lineWidth = 2;
          for (let i = 0; i < 5; i++) {
            const x = Math.random() * 280 + 20;
            const y = Math.random() * 140 + 20;
            const size = Math.random() * 30 + 20;
            ctx.strokeRect(x, y, size, size);
          }
        } else if (style.id.includes('food')) {
          // –î–æ–±–∞–≤–ª—è–µ–º –∫—Ä—É–≥–ª—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã (–∫–∞–∫ –ø—É–∑—ã—Ä—å–∫–∏)
          ctx.fillStyle = 'rgba(255, 255, 255, 0.2)';
          for (let i = 0; i < 8; i++) {
            const x = Math.random() * 320;
            const y = Math.random() * 180;
            const size = Math.random() * 25 + 15;
            ctx.beginPath();
            ctx.arc(x, y, size, 0, Math.PI * 2);
            ctx.fill();
          }
        }
        
        // –î–æ–±–∞–≤–ª—è–µ–º –Ω–∞–∑–≤–∞–Ω–∏–µ —Å—Ç–∏–ª—è
        ctx.fillStyle = '#FFFFFF';
        ctx.font = 'bold 16px Arial';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.strokeStyle = '#000000';
        ctx.lineWidth = 3;
        ctx.strokeText(style.name, 160, 90);
        ctx.fillText(style.name, 160, 90);
        
        const blob = await new Promise(resolve => canvas.toBlob(resolve, 'image/png'));
        previews[style.id] = URL.createObjectURL(blob);
      }
      
      setStylePreviewImages(previews);
    };

    if (isOpen) {
      createStylePreviews();
    }
  }, [isOpen]);

  // –§—É–Ω–∫—Ü–∏—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —á–µ—Ä–µ–∑ DALL-E
  const generateWithDALLE = async () => {
    setIsGenerating(true);
    setGeneratedThumbnails([]);

    try {
      // –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è API
      const requestData = {
        mode: activeTab,
        text: customData.text,
        objectCount: customData.objectImages.length
      };

      if (activeTab === "styles" && selectedStyle) {
        requestData.style = selectedStyle;
      } else if (activeTab === "custom") {
        requestData.description = customData.description;
        
        // –ï—Å–ª–∏ –µ—Å—Ç—å —Ä–µ—Ñ–µ—Ä–µ–Ω—Å–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ, –∞–Ω–∞–ª–∏–∑–∏—Ä—É–µ–º –µ–≥–æ
        if (customData.referenceImage) {
          console.log('üîç Analyzing reference image...');
          
          // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º base64 –≤ File object –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏
          const base64Response = await fetch(customData.referenceImage);
          const blob = await base64Response.blob();
          const formData = new FormData();
          formData.append('image', blob, 'reference.jpg');

          const analysisResponse = await fetch('http://localhost:5000/api/thumbnails/analyze-image', {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('authToken')}`
            },
            body: formData
          });

          if (analysisResponse.ok) {
            const analysisData = await analysisResponse.json();
            requestData.referenceImageAnalysis = analysisData.data.analysis;
            console.log('‚úÖ Image analysis completed');
          }
        }
      }

      console.log('üé® Sending generation request:', requestData);

      // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ –≥–µ–Ω–µ—Ä–∞—Ü–∏—é
      const response = await fetch('http://localhost:5000/api/thumbnails/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to generate thumbnails');
      }

      if (data.success && data.data.thumbnails) {
        console.log(`‚úÖ Generated ${data.data.thumbnails.length} thumbnails`);
        setGeneratedThumbnails(data.data.thumbnails);
      } else {
        throw new Error('No thumbnails received from server');
      }
      
    } catch (error) {
      console.error('‚ùå Generation error:', error);
      
      // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é –ø–æ–Ω—è—Ç–Ω–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
      let errorMessage = 'Failed to generate thumbnails. Please try again.';
      
      if (error.message.includes('Rate limit') || error.message.includes('rate limit')) {
        errorMessage = 'AI service is currently busy. Please try again in a few minutes.';
      } else if (error.message.includes('limit reached')) {
        errorMessage = 'You have reached your monthly thumbnail generation limit. Upgrade your subscription for more.';
      } else if (error.message.includes('inappropriate content')) {
        errorMessage = 'Please try a different description. Some content may not be suitable for generation.';
      } else if (error.message.includes('Invalid')) {
        errorMessage = 'Please check your inputs and try again.';
      }
      
      // –ú–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å state –¥–ª—è –ø–æ–∫–∞–∑–∞ –æ—à–∏–±–æ–∫ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é
      alert(errorMessage); // –í—Ä–µ–º–µ–Ω–Ω–æ –∏—Å–ø–æ–ª—å–∑—É–µ–º alert, –≤ –ø—Ä–æ–¥–∞–∫—à–µ–Ω–µ –ª—É—á—à–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å toast –∏–ª–∏ modal
      
    } finally {
      setIsGenerating(false);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –æ–±—ä–µ–∫—Ç–∞
  const addObjectImage = (event) => {
    const file = event.target.files[0];
    if (file && customData.objectImages.length < 3) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCustomData(prev => ({ 
          ...prev, 
          objectImages: [...prev.objectImages, { 
            id: Date.now(), 
            url: e.target.result,
            name: file.name 
          }]
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è —É–¥–∞–ª–µ–Ω–∏—è –æ–±—ä–µ–∫—Ç–∞
  const removeObjectImage = (id) => {
    setCustomData(prev => ({
      ...prev,
      objectImages: prev.objectImages.filter(img => img.id !== id)
    }));
  };

  // –§—É–Ω–∫—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–µ—Ñ–µ—Ä–µ–Ω—Å–Ω–æ–≥–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
  const handleReferenceUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setCustomData(prev => ({ ...prev, referenceImage: e.target.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ thumbnail
  const downloadThumbnail = () => {
    if (selectedThumbnail) {
      const a = document.createElement('a');
      a.href = selectedThumbnail.url;
      a.download = `${exportSettings?.filename || 'video'}_thumbnail_v${selectedThumbnail.id}.png`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  const navigateToApp = () => {
    window.location.href = 'http://localhost:3000/app';
  };

  const canGenerate = (activeTab === "styles" && selectedStyle) || 
                     (activeTab === "custom" && (customData.description || customData.referenceImage));

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999] p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-7xl w-full max-h-[95vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Wand2 size={24} className="mr-3 text-purple-600" />
            AI Thumbnail Generator
          </h2>
          <button
            onClick={() => { onClose(); navigateToApp(); }}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          {/* Tabs */}
          <div className="flex bg-gray-100 p-1 rounded-xl">
            <button
              onClick={() => setActiveTab("styles")}
              className={`flex-1 flex items-center justify-center py-3 px-4 rounded-lg font-medium transition-all ${
                activeTab === "styles" 
                  ? "bg-white text-purple-600 shadow-sm" 
                  : "text-gray-600 hover:text-gray-800"
              }`}
            >
              <Palette size={18} className="mr-2" />
              Style Gallery
            </button>
            <button
              onClick={() => setActiveTab("custom")}
              className={`flex-1 flex items-center justify-center py-3 px-4 rounded-lg font-medium transition-all ${
                activeTab === "custom" 
                  ? "bg-white text-purple-600 shadow-sm" 
                  : "text-gray-600 hover:text-gray-800"
              }`}
            >
              <Sparkles size={18} className="mr-2" />
              Custom Design
            </button>
          </div>

          {/* Content */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Left Panel - Controls */}
            <div className="space-y-6">
              {activeTab === "styles" ? (
                <>
                  {/* Style Selection */}
                  <div className="bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl p-6">
                    <h3 className="text-lg font-semibold text-gray-800 mb-4">Choose Your Style</h3>
                    
                    <div className="grid grid-cols-2 gap-4 mb-6">
                      {thumbnailStyles.map((style) => (
                        <button
                          key={style.id}
                          onClick={() => setSelectedStyle(style.id)}
                          className={`relative rounded-lg overflow-hidden border-3 transition-all ${
                            selectedStyle === style.id
                              ? "border-purple-500 ring-2 ring-purple-200"
                              : "border-gray-200 hover:border-purple-300"
                          }`}
                        >
                          {stylePreviewImages[style.id] ? (
                            <img 
                              src={stylePreviewImages[style.id]} 
                              alt={style.name}
                              className="w-full h-24 object-cover"
                            />
                          ) : (
                            <div className="w-full h-24 bg-gradient-to-r from-gray-200 to-gray-300 flex items-center justify-center">
                              <div className="text-gray-500">Loading...</div>
                            </div>
                          )}
                          
                          {selectedStyle === style.id && (
                            <div className="absolute inset-0 bg-purple-500 bg-opacity-20 flex items-center justify-center">
                              <div className="bg-white rounded-full p-2">
                                <ArrowRight size={16} className="text-purple-600" />
                              </div>
                            </div>
                          )}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Objects Section - Always Visible in Style Gallery */}
                  <div className="bg-white border border-gray-200 rounded-xl p-6">
                    <h4 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                      <Image size={20} className="mr-2 text-blue-600" />
                      Add Objects (up to 3)
                    </h4>
                    
                    <div className="grid grid-cols-3 gap-4 mb-4">
                      {[...Array(3)].map((_, index) => {
                        const existingObject = customData.objectImages[index];
                        return (
                          <div key={index} className="relative">
                            {existingObject ? (
                              <div className="relative group">
                                <img 
                                  src={existingObject.url} 
                                  alt={`Object ${index + 1}`}
                                  className="w-full h-24 object-cover rounded-lg border-2 border-purple-200"
                                />
                                <button
                                  onClick={() => removeObjectImage(existingObject.id)}
                                  className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 transition-colors opacity-0 group-hover:opacity-100"
                                >
                                  <Trash2 size={14} />
                                </button>
                                <div className="absolute bottom-1 left-1 bg-black bg-opacity-70 text-white text-xs px-2 py-1 rounded">
                                  Object {index + 1}
                                </div>
                              </div>
                            ) : (
                              <label className="cursor-pointer bg-gradient-to-br from-gray-50 to-gray-100 border-2 border-dashed border-gray-300 hover:border-purple-400 hover:from-purple-50 hover:to-blue-50 rounded-lg h-24 flex flex-col items-center justify-center transition-all duration-300">
                                <Plus size={24} className="text-gray-400 mb-1" />
                                <span className="text-xs text-gray-500">Add Object {index + 1}</span>
                                <input
                                  type="file"
                                  accept="image/*"
                                  onChange={addObjectImage}
                                  className="hidden"
                                />
                              </label>
                            )}
                          </div>
                        );
                      })}
                    </div>
                    
                    <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                      <p className="text-sm text-blue-800 font-medium mb-1">üí° Object Ideas:</p>
                      <p className="text-xs text-blue-700">
                        Products ‚Ä¢ Faces/People ‚Ä¢ Logos ‚Ä¢ Food Items ‚Ä¢ Gadgets ‚Ä¢ Before/After Images ‚Ä¢ Arrows ‚Ä¢ Icons
                      </p>
                    </div>
                  </div>
                </>
              ) : (
                <div className="bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl p-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Custom Thumbnail Design</h3>
                  
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Type size={16} className="inline mr-1" />
                        Thumbnail Text
                      </label>
                      <input
                        type="text"
                        value={customData.text}
                        onChange={(e) => setCustomData(prev => ({ ...prev, text: e.target.value }))}
                        placeholder="Enter text for your thumbnail..."
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Sparkles size={16} className="inline mr-1" />
                        Describe Your Thumbnail
                      </label>
                      <textarea
                        value={customData.description}
                        onChange={(e) => setCustomData(prev => ({ ...prev, description: e.target.value }))}
                        placeholder="Describe the style, colors, mood, and elements you want (e.g., 'bright cartoon style with explosion effects')"
                        rows={3}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent resize-none"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Image size={16} className="inline mr-1" />
                        Reference Style Image
                      </label>
                      <label className="cursor-pointer bg-white border-2 border-dashed border-gray-300 hover:border-purple-400 rounded-lg p-4 flex items-center justify-center transition-colors">
                        <Upload size={20} className="mr-2 text-gray-400" />
                        <span className="text-sm text-gray-600">Upload style reference</span>
                        <input
                          type="file"
                          accept="image/*"
                          onChange={handleReferenceUpload}
                          className="hidden"
                        />
                      </label>
                      {customData.referenceImage && (
                        <div className="mt-2">
                          <img 
                            src={customData.referenceImage} 
                            alt="Reference" 
                            className="w-full h-32 object-cover rounded-lg"
                          />
                        </div>
                      )}
                    </div>

                    {/* Objects Section for Custom */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Image size={16} className="inline mr-1" />
                        Add Objects (up to 3)
                      </label>
                      
                      <div className="grid grid-cols-3 gap-3 mb-3">
                        {[...Array(3)].map((_, index) => {
                          const existingObject = customData.objectImages[index];
                          return (
                            <div key={index} className="relative">
                              {existingObject ? (
                                <div className="relative">
                                  <img 
                                    src={existingObject.url} 
                                    alt={`Object ${index + 1}`}
                                    className="w-full h-20 object-cover rounded-lg border-2 border-purple-200"
                                  />
                                  <button
                                    onClick={() => removeObjectImage(existingObject.id)}
                                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600 transition-colors"
                                  >
                                    <X size={12} />
                                  </button>
                                </div>
                              ) : (
                                <label className="cursor-pointer bg-gray-50 border-2 border-dashed border-gray-300 hover:border-purple-400 rounded-lg h-20 flex items-center justify-center transition-colors">
                                  <Plus size={20} className="text-gray-400" />
                                  <input
                                    type="file"
                                    accept="image/*"
                                    onChange={addObjectImage}
                                    className="hidden"
                                  />
                                </label>
                              )}
                            </div>
                          );
                        })}
                      </div>
                      
                      <p className="text-xs text-gray-500">
                        Add specific objects you want in your thumbnail (products, people, items, etc.)
                      </p>
                    </div>
                  </div>
                </div>
              )}

              {/* Generate Button */}
              <button
                onClick={generateWithDALLE}
                disabled={isGenerating || !canGenerate}
                className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl disabled:cursor-not-allowed flex items-center justify-center"
              >
                {isGenerating ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    Generating with DALL-E...
                  </>
                ) : (
                  <>
                    <Wand2 size={20} className="mr-3" />
                    Generate 5 AI Thumbnails
                  </>
                )}
              </button>
            </div>

            {/* Right Panel - Preview/Results */}
            <div className="space-y-6">
              <div className="bg-gray-50 rounded-xl p-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                  <Image size={20} className="mr-2 text-blue-600" />
                  Generated Thumbnails
                </h3>
                
                {generatedThumbnails.length === 0 ? (
                  <div className="bg-white rounded-lg p-8 text-center border-2 border-dashed border-gray-300">
                    <div className="text-4xl mb-4">üé®</div>
                    <div className="text-lg text-gray-600 mb-2">Ready to create amazing thumbnails</div>
                    <div className="text-sm text-gray-400">
                      {activeTab === "styles" 
                        ? "Choose a style, add text and objects, then generate!" 
                        : "Describe your vision, upload images, then generate 5 AI variants!"}
                    </div>
                    
                    {/* Preview of current settings */}
                    {(customData.text || customData.objectImages.length > 0) && (
                      <div className="mt-4 p-4 bg-purple-50 rounded-lg">
                        <div className="text-xs text-purple-700 mb-2">Preview of your settings:</div>
                        {customData.text && (
                          <div className="text-sm text-purple-800 font-medium">Text: "{customData.text}"</div>
                        )}
                        {customData.objectImages.length > 0 && (
                          <div className="text-sm text-purple-800">Objects: {customData.objectImages.length} added</div>
                        )}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="grid grid-cols-1 gap-4">
                    {generatedThumbnails.map((thumbnail) => (
                      <div 
                        key={thumbnail.id}
                        className={`relative cursor-pointer rounded-lg overflow-hidden border-3 transition-all ${
                          selectedThumbnail?.id === thumbnail.id 
                            ? "border-purple-500 ring-2 ring-purple-200" 
                            : "border-gray-200 hover:border-purple-300"
                        }`}
                        onClick={() => setSelectedThumbnail(thumbnail)}
                      >
                        <img 
                          src={thumbnail.url} 
                          alt={`Thumbnail ${thumbnail.id}`}
                          className="w-full h-32 object-cover"
                        />
                        <div className="absolute top-2 left-2 bg-black bg-opacity-70 text-white text-xs px-2 py-1 rounded">
                          –í–∞—Ä–∏–∞–Ω—Ç {thumbnail.id}
                        </div>
                        {selectedThumbnail?.id === thumbnail.id && (
                          <div className="absolute inset-0 bg-purple-500 bg-opacity-20 flex items-center justify-center">
                            <div className="bg-white rounded-full p-2">
                              <ArrowRight size={16} className="text-purple-600" />
                            </div>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Quick Tips */}
              <div className="bg-blue-50 border border-blue-200 rounded-xl p-6">
                <h4 className="font-semibold text-blue-800 mb-3 flex items-center">
                  <Sparkles size={18} className="mr-2" />
                  Pro Tips
                </h4>
                <ul className="text-sm text-blue-700 space-y-2">
                  <li>‚Ä¢ <strong>Text:</strong> Use action words like "AMAZING", "SHOCKING", "MUST SEE"</li>
                  <li>‚Ä¢ <strong>Objects:</strong> Add faces, products, or key items from your video</li>
                  <li>‚Ä¢ <strong>Style:</strong> Gaming = bright/neon, Tech = clean/minimal, Food = warm colors</li>
                  <li>‚Ä¢ <strong>Emotions:</strong> Include "excited", "surprised", or "pointing" for engagement</li>
                  <li>‚Ä¢ <strong>Composition:</strong> Think about where text and objects will be placed</li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-sm text-gray-500">
            {selectedThumbnail 
              ? `Thumbnail #${selectedThumbnail.id} selected and ready to download` 
              : generatedThumbnails.length > 0
                ? 'Choose a thumbnail to download'
                : 'Select style and customize to generate thumbnails'
            }
          </div>
          <div className="flex space-x-3">
            <button
              onClick={() => { onClose(); navigateToApp(); }}
              className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              Skip & Continue
            </button>
            {selectedThumbnail && (
              <button
                onClick={() => {
                  downloadThumbnail();
                  onClose();
                  navigateToApp();
                }}
                className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 font-medium transition-colors flex items-center"
              >
                Download & Continue
                <ArrowRight size={16} className="ml-2" />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThumbnailCreator;
