import Page from "../components/common/Page";
import Card from "../components/ui/Card";

const TermsOfUsePage = () => {
  const sections = [
    {
      title: "1. Принятие условий",
      content:
        "Используя наш сервис, вы соглашаетесь с данными условиями использования...",
    },
    {
      title: "2. Описание сервиса",
      content:
        "Наш сервис предоставляет инструменты для редактирования видео онлайн...",
    },
    {
      title: "3. Права пользователя",
      content:
        "Вы имеете право использовать сервис в соответствии с данными условиями...",
    },
    {
      title: "4. Ограничения использования",
      content: "Запрещается использовать сервис для незаконных целей...",
    },
    {
      title: "5. Конфиденциальность",
      content: "Мы обязуемся защищать вашу личную информацию...",
    },
  ];

  return (
    <Page
      title="Условия использования - VideoEditor"
      description="Ознакомьтесь с условиями использования нашего онлайн видеоредактора"
    >
      <div className="max-w-4xl mx-auto space-y-8">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">
            Условия использования
          </h1>
          <p className="text-gray-600">
            Последнее обновление: {new Date().toLocaleDateString("ru-RU")}
          </p>
        </div>

        <div className="space-y-6">
          {sections.map((section, index) => (
            <Card key={index} className="space-y-3">
              <h2 className="text-xl font-semibold text-gray-800">
                {section.title}
              </h2>
              <p className="text-gray-600 leading-relaxed">{section.content}</p>
            </Card>
          ))}
        </div>

        <Card className="bg-blue-50 border-blue-200">
          <div className="flex items-start space-x-3">
            <div className="text-blue-500 text-xl">ℹ️</div>
            <div>
              <h3 className="font-semibold text-blue-800 mb-2">
                Важная информация
              </h3>
              <p className="text-blue-700 text-sm">
                Если у вас есть вопросы по условиям использования, свяжитесь с
                нами по адресу support@videoeditor.com
              </p>
            </div>
          </div>
        </Card>
      </div>
    </Page>
  );
};

export default TermsOfUsePage;
