import React, { useState, useRef, useEffect } from "react";
import { X, Download, Check, AlertCircle, Settings, Play, Film } from "lucide-react";

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  tracks,
  // –î–æ–±–∞–≤–ª—è–µ–º –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–µ –∏—Å—Ç–æ—á–Ω–∏–∫–∏ –¥–∞–Ω–Ω—ã—Ö
  timelineData,
  elements,
  clips,
  mediaItems,
}) => {
  // –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ø—ã—Ç–∞–µ–º—Å—è –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ —Ä–∞–∑–Ω—ã—Ö –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤
  const actualTimelineItems = React.useMemo(() => {
    // –ü—Ä–æ–±—É–µ–º —Ä–∞–∑–Ω—ã–µ –≤–∞—Ä–∏–∞–Ω—Ç—ã –Ω–∞–∑–≤–∞–Ω–∏–π –ø—Ä–æ–ø—Å–æ–≤
    const items = timelineItems || timelineData || elements || clips || mediaItems || [];
    
    console.log('üîÑ –ü–æ–ª—É—á–∏–ª–∏ –¥–∞–Ω–Ω—ã–µ timeline:', {
