import { useRef, useState } from "react";

import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  ChevronLeft,
  ChevronRight,
  Brain,
  Mic,
  Users,
  Eye,
  Clock,
  TrendingUp,
  BookOpen,
  Heart,
  Search,
  Lightbulb,
  Music,
  Target,
  AlertCircle,
  Award,
} from "lucide-react";

const ContentStudio = ({
  isLeftPanelCollapsed,
  setIsLeftPanelCollapsed,
  contentType,
  setContentType,
  topic,
  setTopic,
  keyPoints,
  duration,
  setDuration,
  script,
  setScript,
  isGeneratingKeyPoints,
  isGeneratingScript,
  isExtendingScript,
  isAuthenticated,
  audioMethod,
  setAudioMethod,
  recordedAudio,
  setShowTeleprompter,
  setShowAudioEditor,
  audioUrl,
  assessment,
  assessmentLoading,
  handleTextSelection,
  generateKeyPoints,
  generateScript,
  extendScript,
  generateAudio,
  addKeyPoint,
  updateKeyPoint,
  removeKeyPoint,
  contentTypes,
}) => {
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false);
  const [isContentTypeDropdownOpen, setIsContentTypeDropdownOpen] =
    useState(false);

  const languages = [
    { id: "english", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
    { id: "russian", name: "Ð ÑƒÑÑÐºÐ¸Ð¹", flag: "ðŸ‡·ðŸ‡º" },
    { id: "spanish", name: "EspaÃ±ol", flag: "ðŸ‡ªðŸ‡¸" },
    { id: "french", name: "FranÃ§ais", flag: "ðŸ‡«ðŸ‡·" },
    { id: "german", name: "Deutsch", flag: "ðŸ‡©ðŸ‡ª" },
    { id: "chinese", name: "ä¸­æ–‡", flag: "ðŸ‡¨ðŸ‡³" },
  ];

  const [selectedLanguage, setSelectedLanguage] = useState("english");

  const scriptRef = useRef(null);

  const ScriptEditorSkeleton = () => (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="h-6 bg-gray-200 rounded w-48 animate-pulse"></div>
        <div className="h-8 bg-gray-200 rounded w-32 animate-pulse"></div>
      </div>
      <div className="space-y-3">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="h-4 bg-gray-200 rounded animate-pulse"
            style={{ width: `${85 + Math.random() * 15}%` }}
          ></div>
        ))}
      </div>
    </div>
  );

  const DetailedAnalytics = ({ assessmentLoading, assessment }) => {
    const getScoreStyle = (score) => {
      if (score >= 8)
        return {
          text: "text-white",
          bg: "bg-gradient-to-br from-blue-500 to-indigo-600",
          border: "border-blue-300/50",
        };
      if (score >= 6)
        return {
          text: "text-white",
          bg: "bg-gradient-to-br from-indigo-500 to-purple-600",
          border: "border-indigo-300/50",
        };
      return {
        text: "text-white",
        bg: "bg-gradient-to-br from-slate-500 to-gray-600",
        border: "border-slate-300/50",
      };
    };

    const getProgressStyle = (score) => {
      if (score >= 8) return "bg-gradient-to-r from-blue-400 to-blue-600";
      if (score >= 6) return "bg-gradient-to-r from-indigo-400 to-indigo-600";
      return "bg-gradient-to-r from-slate-400 to-slate-600";
    };

    const CompactMetric = ({ icon: Icon, label, score }) => {
      const style = getScoreStyle(score);
      return (
        <div className="bg-white/80 rounded-md p-1.5 border border-white/30 hover:border-white/50 transition-all">
          <div className="flex items-center justify-between mb-0.5">
            <div className="flex items-center space-x-1">
              <div className="p-0.5 rounded bg-gradient-to-br from-indigo-500 to-purple-600">
                <Icon size={6} className="text-white" />
              </div>
              <span className="text-xs font-medium text-gray-800 truncate">
                {label}
              </span>
            </div>
            <div
              className={`px-1 py-0.5 rounded text-xs font-bold ${style.bg} ${style.text}`}
            >
              {score}
            </div>
          </div>
          <div className="w-full bg-gray-200/50 rounded-full h-0.5">
            <div
              className={`h-0.5 rounded-full ${getProgressStyle(
                score
              )} transition-all duration-500`}
              style={{ width: `${Math.min(score * 10, 100)}%` }}
            />
          </div>
        </div>
      );
    };

    const CompactCircularScore = ({ score }) => {
      const percentage = Math.round(score * 10);
      const circumference = 2 * Math.PI * 16;
      const strokeDashoffset =
        circumference - (percentage / 100) * circumference;

      const getGradeStyle = (score) => {
        if (score >= 8)
          return { gradient: "from-blue-500 to-indigo-600", grade: "A" };
        if (score >= 6)
          return { gradient: "from-indigo-500 to-purple-600", grade: "B" };
        return { gradient: "from-slate-500 to-gray-600", grade: "C" };
      };

      const gradeStyle = getGradeStyle(score);

      return (
        <div
          className={`bg-gradient-to-br ${gradeStyle.gradient} rounded-lg p-2 text-white shadow-lg`}
        >
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center space-x-1 mb-0.5">
                <Award size={10} className="text-white" />
                <span className="text-xs font-bold">Score</span>
              </div>
              <p className="text-xs text-white/90">Overall</p>
            </div>
            <div className="relative">
              <svg className="w-8 h-8 transform -rotate-90" viewBox="0 0 36 36">
                <circle
                  cx="18"
                  cy="18"
                  r="16"
                  stroke="rgba(255, 255, 255, 0.2)"
                  strokeWidth="2"
                  fill="none"
                />
                <circle
                  cx="18"
                  cy="18"
                  r="16"
                  stroke="white"
                  strokeWidth="2"
                  fill="none"
                  strokeLinecap="round"
                  strokeDasharray={circumference}
                  strokeDashoffset={strokeDashoffset}
                  className="transition-all duration-700"
                />
              </svg>
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                <span className="text-sm font-bold">{score}</span>
                <span className="text-xs opacity-90">{gradeStyle.grade}</span>
              </div>
            </div>
          </div>
        </div>
      );
    };

    const CompactRecommendations = ({ recommendations }) => {
      const [showAll, setShowAll] = useState(false);

      return (
        <div className="bg-gradient-to-br from-blue-50/80 to-purple-50/80 rounded-lg p-2 border border-blue-200/50">
          <div className="flex items-center space-x-1 mb-2">
            <Lightbulb size={10} className="text-indigo-600" />
            <span className="font-bold text-gray-800 text-xs">Tips</span>
          </div>
          <div className="space-y-1">
            {recommendations
              ?.slice(0, showAll ? recommendations.length : 1)
              .map((rec, index) => (
                <div key={index} className="bg-white/80 rounded-md p-2">
                  <p className="text-xs text-gray-700 leading-tight">{rec}</p>
                </div>
              ))}
            {recommendations && recommendations.length > 1 && (
              <div className="text-center">
                <button
                  onClick={() => setShowAll(!showAll)}
                  className="inline-block bg-indigo-500 hover:bg-indigo-600 text-white px-2 py-0.5 rounded-full text-xs font-bold transition-colors cursor-pointer"
                >
                  {showAll
                    ? "Show less"
                    : `+${recommendations.length - 1} more`}
                </button>
              </div>
            )}
          </div>
        </div>
      );
    };

    if (assessmentLoading) {
      return (
        <div className="bg-gradient-to-br from-slate-100 to-purple-50 border border-indigo-200/50 p-3 rounded-lg shadow-lg">
          <div className="flex items-center space-x-2 mb-3">
            <div className="animate-spin rounded-full h-4 w-4 border-2 border-indigo-500 border-t-transparent"></div>
            <span className="text-xs font-bold text-gray-800">
              Analyzing...
            </span>
          </div>
          <div className="grid grid-cols-4 gap-2 mb-3">
            {[...Array(8)].map((_, index) => (
              <div
                key={index}
                className="bg-white/60 rounded-md p-2 animate-pulse"
              >
                <div className="h-2 bg-gray-300 rounded mb-1"></div>
                <div className="h-1 bg-gray-300 rounded"></div>
              </div>
            ))}
          </div>
          <div className="h-10 bg-gray-300 rounded-lg animate-pulse"></div>
        </div>
      );
    }

    if (!assessment) {
      return (
        <div className="bg-gray-100 border border-gray-300 p-4 rounded-lg text-center">
          <AlertCircle size={16} className="text-gray-400 mx-auto mb-1" />
          <p className="text-xs text-gray-600">No analysis available</p>
        </div>
      );
    }

    assessment = JSON.parse(assessment);

    const metrics = [
      { icon: Target, label: "Structure", score: assessment["structure"] || 0 },
      { icon: Clock, label: "Duration", score: assessment.video_duration || 0 },
      { icon: TrendingUp, label: "Engage", score: assessment.engagement || 0 },
      { icon: BookOpen, label: "Read", score: assessment.readability || 0 },
      { icon: Heart, label: "Emotion", score: assessment.emotional_tone || 0 },
      { icon: Eye, label: "Clarity", score: assessment.clarity || 0 },
      { icon: Search, label: "SEO", score: assessment.seo_optimization || 0 },
      { icon: Music, label: "Rhythm", score: assessment.rhythm || 0 },
    ];

    return (
      <div className="bg-gradient-to-br from-slate-50 to-purple-50 border border-indigo-200/50 p-3 rounded-lg shadow-lg">
        {/* ÐšÐ¾Ð¼Ð¿Ð°ÐºÑ‚Ð½Ñ‹Ð¹ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²Ð¾Ðº */}
        <div className="flex items-center space-x-2 mb-3">
          <div className="w-6 h-6 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-lg flex items-center justify-center">
            <Award className="text-white" size={12} />
          </div>
          <div>
            <h3 className="text-xs font-bold text-indigo-600">Analysis</h3>
            <p className="text-xs text-gray-600">AI insights</p>
          </div>
        </div>

        {/* ÐšÐ¾Ð¼Ð¿Ð°ÐºÑ‚Ð½Ñ‹Ð¹ Ð¾Ð±Ñ‰Ð¸Ð¹ Ð±Ð°Ð»Ð» */}
        <div className="mb-3">
          <CompactCircularScore score={assessment.total_score || 0} />
        </div>

        {/* ÐšÐ¾Ð¼Ð¿Ð°ÐºÑ‚Ð½Ð°Ñ ÑÐµÑ‚ÐºÐ° Ð¼ÐµÑ‚Ñ€Ð¸Ðº */}
        <div className="grid grid-cols-2 gap-1.5 mb-3">
          {metrics.map((metric, index) => (
            <CompactMetric
              key={index}
              icon={metric.icon}
              label={metric.label}
              score={metric.score}
            />
          ))}
        </div>

        {/* ÐšÐ¾Ð¼Ð¿Ð°ÐºÑ‚Ð½Ñ‹Ðµ Ñ€ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸ */}
        {assessment.recommendations &&
          assessment.recommendations.length > 0 && (
            <CompactRecommendations
              recommendations={assessment.recommendations}
            />
          )}
      </div>
    );
  };

  return (
    <div
      className={`${
        isLeftPanelCollapsed ? "w-24" : "w-[450px]"
      } h-[106vh] overflow-hidden bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10 flex-shrink-0`}
    >
      <style>
        {`
          .custom-scrollbar::-webkit-scrollbar {
            width: 8px;
          }
          
          .custom-scrollbar::-webkit-scrollbar-track {
            background: #f1f5f9;
            border-radius: 10px;
            margin: 8px 0;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb {
            background: #cbd5e1;
            border-radius: 10px;
            transition: all 0.2s ease;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb:hover {
            background: #94a3b8;
          }
          
          .custom-scrollbar {
            scrollbar-width: thin;
            scrollbar-color: #cbd5e1 #f1f5f9;
          }
        `}
      </style>

      <div className="flex items-center justify-between p-6 border-b border-slate-200/50 from-slate-50 to-blue-50 flex-shrink-0">
        {!isLeftPanelCollapsed && (
          <div className="flex flex-col items-center space-y-4 h-full justify-start pt-4">
            <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
              Content Studio
            </h2>
            <p className="text-sm text-gray-600 font-medium">
              Professional content creation tools
            </p>
          </div>
        )}
        <button
          onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
          className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300 group"
        >
          {isLeftPanelCollapsed ? (
            <ChevronRight
              size={20}
              className="text-gray-600 group-hover:text-slate-700"
            />
          ) : (
            <ChevronLeft
              size={20}
              className="text-gray-600 group-hover:text-slate-700"
            />
          )}
        </button>
      </div>

      {isLeftPanelCollapsed ? (
        <div className="p-4 space-y-4">
          <div className="flex flex-col items-center space-y-4">
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-4 bg-gradient-to-br from-slate-100 to-blue-100 hover:from-slate-200 hover:to-blue-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Create script"
            >
              <FileText
                size={20}
                className="text-slate-600 group-hover:text-blue-600"
              />
            </button>
            <button
