        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          console.log(`‚úÖ –í–∏–¥–µ–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ: ${item.name}, —Ä–∞–∑–º–µ—Ä: ${video.videoWidth}x${video.videoHeight}`);
          clearTimeout(timeout);
          resolve(video);
