import { useState, useRef, useEffect, useCallback } from "react";
import {
  Upload,
  Play,
  Pause,
  Volume2,
  Video,
  Music,
  SkipBack,
  SkipForward,
  VolumeX,
  Copy,
  Trash2,
  Scissors,
  Image,
  Film,
  Plus,
  Layers,
  ZoomIn,
  ZoomOut,
  RotateCcw,
  HelpCircle,
  X,
  AlertCircle,
  CheckCircle,
  Loader
} from "lucide-react";
import MediaStorage from '../utils/MediaStorage'; // Импортируем MediaStorage

const VideoEditor = () => {
  const [mediaLibrary, setMediaLibrary] = useState({
    videos: [],
    audios: [],
    images: [],
  });
  
  // Новые состояния для работы с MediaStorage
  const [storageInfo, setStorageInfo] = useState(null);
  const [isStorageReady, setIsStorageReady] = useState(false);
  const [uploadingFiles, setUploadingFiles] = useState(new Set());
  const [storageError, setStorageError] = useState(null);
  
  const [timelineItems, setTimelineItems] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(60);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(null);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [activeTab, setActiveTab] = useState("videos");
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentVideoSource, setCurrentVideoSource] = useState(null);
  const [draggedItem, setDraggedItem] = useState(null);
  const [draggedTimelineItem, setDraggedTimelineItem] = useState(null);
  const [dropPreview, setDropPreview] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isTimelineDragging, setIsTimelineDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState(0);
  const [copiedItem, setCopiedItem] = useState(null);
  
  // Новые состояния для resize
  const [isResizing, setIsResizing] = useState(false);
  const [resizeHandle, setResizeHandle] = useState(null); // 'left' | 'right'
  const [resizeItem, setResizeItem] = useState(null);
  const [originalItemData, setOriginalItemData] = useState(null);
  
  // Состояния для управления overlays на видео
  const [selectedOverlay, setSelectedOverlay] = useState(null);
  const [overlayTransforms, setOverlayTransforms] = useState({}); // { overlayId: { x, y, scale, opacity } }
  const [isDraggingOverlay, setIsDraggingOverlay] = useState(false);
  const [isResizingOverlay, setIsResizingOverlay] = useState(false);
  const [overlayDragStart, setOverlayDragStart] = useState({ x: 0, y: 0 });
  const [resizeOverlayHandle, setResizeOverlayHandle] = useState(null);
  const [initialOverlayTransform, setInitialOverlayTransform] = useState(null);
  
  // Модальное окно помощи
  const [showHelp, setShowHelp] = useState(false);
  
  // Треки как в CapCut
  const [tracks, setTracks] = useState({
    overlays: [
      { id: 'overlay-1', name: 'Overlay 1', type: 'overlay' },
      { id: 'overlay-2', name: 'Overlay 2', type: 'overlay' }
    ],
    main: [
      { id: 'main-video', name: 'Main Video', type: 'main' }
    ],
    audio: [
      { id: 'audio-1', name: 'Audio 1', type: 'audio' },
      { id: 'audio-2', name: 'Audio 2', type: 'audio' }
    ]
  });

  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);
  const intervalRef = useRef(null);
  const audioElementsRef = useRef(new Map());
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  const videoRef = useRef(null);
  const autoScrollIntervalRef = useRef(null);
  const videoContainerRef = useRef(null);

  // Инициализация MediaStorage при загрузке компонента
  useEffect(() => {
    const initStorage = async () => {
      try {
        setStorageError(null);
        await MediaStorage.init();
        setIsStorageReady(true);
        
        // Загружаем существующие файлы
        await loadExistingFiles();
        
        // Получаем информацию о хранилище
        const info = await MediaStorage.getStorageInfo();
        setStorageInfo(info);
        
        // Очищаем истекшие файлы
        await MediaStorage.cleanupExpiredFiles();
        
        console.log('MediaStorage initialized successfully');
      } catch (error) {
        console.error('Failed to initialize MediaStorage:', error);
        setStorageError(error.message);
        setIsStorageReady(false);
      }
    };

    initStorage();

    // Слушаем события обновления хранилища
    const handleStorageUpdate = (event) => {
      setStorageInfo(event.detail);
    };

    window.addEventListener('storageUpdated', handleStorageUpdate);
    
    return () => {
      window.removeEventListener('storageUpdated', handleStorageUpdate);
    };
  }, []);

  // Загрузка существующих файлов из MediaStorage
  const loadExistingFiles = async () => {
    try {
      const files = await MediaStorage.getAllFiles();
      setMediaLibrary(files);
      console.log('Loaded existing files from storage:', files);
    } catch (error) {
      console.error('Failed to load existing files:', error);
    }
  };

  // Базовые функции
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    const ms = Math.floor((seconds % 1) * 100);
    return timelineZoom > 5 ? `${mins}:${secs.toString().padStart(2, "0")}.${ms.toString().padStart(2, "0")}` : `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const seekTo = (time) => {
    const newTime = Math.max(0, Math.min(videoDuration, time));
    setCurrentTime(newTime);
    
    const content = getCurrentTimelineContent();
    content.audio.forEach(audioItem => {
      const audioElement = audioElementsRef.current.get(audioItem.id);
      if (audioElement) {
        const relativeTime = newTime - audioItem.startTime;
        audioElement.currentTime = Math.max(0, relativeTime);
      }
    });
  };

  const getCurrentTimelineContent = useCallback(() => {
    const currentItems = timelineItems.filter(
      (item) =>
        currentTime >= item.startTime &&
        currentTime < item.startTime + item.duration
    );

    return {
      mainVideo: currentItems.find(item => item.trackType === 'main' && item.type === 'video'),
      overlays: currentItems.filter(item => item.trackType === 'overlay'),
      audio: currentItems.filter(item => item.trackType === 'audio'),
    };
  }, [currentTime, timelineItems]);

  const createAudioElement = useCallback((audioItem) => {
    if (!audioElementsRef.current.has(audioItem.id)) {
      const audio = document.createElement('audio');
      audio.src = audioItem.url;
      audio.volume = (audioItem.volume || 1) * volume;
      audio.muted = isMuted;
      audio.preload = "metadata";
      audioElementsRef.current.set(audioItem.id, audio);
    }
    return audioElementsRef.current.get(audioItem.id);
  }, [volume, isMuted]);

  const syncAudioTime = useCallback((audioItem, relativeTime) => {
    const audioElement = audioElementsRef.current.get(audioItem.id);
    if (audioElement) {
      const timeDiff = Math.abs(audioElement.currentTime - relativeTime);
      if (timeDiff > 0.2) {
        audioElement.currentTime = Math.max(0, relativeTime);
      }
    }
  }, []);

  const checkCollision = useCallback((newItem, excludeId = null) => {
    return timelineItems.some(item => {
      if (item.id === excludeId) return false;
      if (item.trackId !== newItem.trackId) return false;
      
      const itemStart = item.startTime;
      const itemEnd = item.startTime + item.duration;
      const newStart = newItem.startTime;
      const newEnd = newItem.startTime + newItem.duration;
      
      return (newStart < itemEnd && newEnd > itemStart);
    });
  }, [timelineItems]);

  // Функция для вставки с автоматическим сдвигом блоков (как в CapCut)
  const insertWithRipple = useCallback((trackId, insertTime, duration, excludeId = null) => {
    const trackItems = timelineItems
      .filter(item => item.trackId === trackId && item.id !== excludeId)
      .sort((a, b) => a.startTime - b.startTime);

    // Если трек пустой
    if (trackItems.length === 0) {
      return { time: Math.max(0, insertTime), shouldRipple: false, affectedItems: [] };
    }

    // Ищем где вставить
    let insertPosition = insertTime;
    let shouldRipple = false;
    let affectedItems = [];

    // Проверяем есть ли точно в этом месте блок
    const blockAtPosition = trackItems.find(item => 
      insertTime >= item.startTime && insertTime < item.startTime + item.duration
    );

    if (blockAtPosition) {
      // Вставляем в начало этого блока и сдвигаем все что справа
      insertPosition = blockAtPosition.startTime;
      shouldRipple = true;
      affectedItems = trackItems.filter(item => item.startTime >= insertPosition);
    } else {
      // Ищем блоки которые нужно сдвинуть
      const blocksToShift = trackItems.filter(item => item.startTime >= insertTime);
      
      if (blocksToShift.length > 0) {
        // Проверяем, есть ли место без сдвига
        const nextBlock = blocksToShift[0];
        const availableSpace = nextBlock.startTime - insertTime;
        
        if (availableSpace >= duration) {
          // Помещается без сдвига
          insertPosition = insertTime;
          shouldRipple = false;
        } else {
          // Нужно сдвинуть
          insertPosition = insertTime;
          shouldRipple = true;
          affectedItems = blocksToShift;
        }
      } else {
        // Вставляем в конец
        insertPosition = insertTime;
        shouldRipple = false;
      }
    }

    return {
      time: Math.max(0, insertPosition),
      shouldRipple,
      affectedItems,
      shiftAmount: shouldRipple ? duration : 0
    };
  }, [timelineItems]);

  // Функция для поиска ближайшего свободного места (обратная совместимость)
  const findNearestValidPosition = useCallback((trackId, dropTime, duration, excludeId = null) => {
    const result = insertWithRipple(trackId, dropTime, duration, excludeId);
    return {
      time: result.time,
      isExact: !result.shouldRipple
    };
  }, [insertWithRipple]);

  // Функция для применения ripple effect
  const applyRippleEffect = useCallback((affectedItems, shiftAmount) => {
    if (!affectedItems.length || shiftAmount <= 0) return;

    console.log('Applying ripple effect:', { 
      affectedCount: affectedItems.length, 
      shiftAmount,
      affectedItems: affectedItems.map(item => ({ name: item.name, oldStart: item.startTime }))
    });

    setTimelineItems(prev => prev.map(item => {
      const affectedItem = affectedItems.find(affected => affected.id === item.id);
      if (affectedItem) {
        console.log(`Shifting ${item.name} from ${item.startTime} to ${item.startTime + shiftAmount}`);
        return {
          ...item,
          startTime: item.startTime + shiftAmount
        };
      }
      return item;
    }));

    // Обновляем общую длительность если нужно
    if (affectedItems.length > 0) {
      const maxEndTime = Math.max(...affectedItems.map(item => item.startTime + item.duration + shiftAmount));
      if (maxEndTime > videoDuration) {
        setVideoDuration(maxEndTime + 10);
      }
    }
  }, [videoDuration]);

  const findNextAvailablePosition = useCallback((trackId, duration, preferredStart = 0) => {
    const trackItems = timelineItems
      .filter(item => item.trackId === trackId)
      .sort((a, b) => a.startTime - b.startTime);
    
    if (preferredStart >= 0) {
      const testItem = { trackId, startTime: preferredStart, duration };
      if (!checkCollision(testItem)) {
        return preferredStart;
      }
    }
    
    for (let i = 0; i < trackItems.length; i++) {
      const currentItem = trackItems[i];
      const nextItem = trackItems[i + 1];
      
      if (i === 0 && currentItem.startTime >= duration) {
        return 0;
      }
      
      if (nextItem) {
        const gapStart = currentItem.startTime + currentItem.duration;
        const gapEnd = nextItem.startTime;
        const gapSize = gapEnd - gapStart;
        
        if (gapSize >= duration) {
          return gapStart;
        }
      }
    }
    
    if (trackItems.length === 0) return 0;
    const lastItem = trackItems[trackItems.length - 1];
    return lastItem.startTime + lastItem.duration;
  }, [timelineItems, checkCollision]);

  const removeFromTimeline = (itemId) => {
    const audioElement = audioElementsRef.current.get(itemId);
    if (audioElement) {
      audioElement.pause();
      audioElement.src = '';
      audioElementsRef.current.delete(itemId);
    }
    
    setTimelineItems((prev) => prev.filter((item) => item.id !== itemId));
    setSelectedTimelineItem(null);
  };

  // Функции для управления overlays на видео
  const getOverlayTransform = useCallback((overlayId) => {
    return overlayTransforms[overlayId] || { x: 0, y: 0, scale: 1, opacity: 1 };
  }, [overlayTransforms]);

  const updateOverlayTransform = useCallback((overlayId, updates) => {
    setOverlayTransforms(prev => ({
      ...prev,
      [overlayId]: { ...getOverlayTransform(overlayId), ...updates }
    }));
  }, [getOverlayTransform]);

  const handleOverlayMouseDown = useCallback((e, overlay) => {
