    e.preventDefault();
    e.stopPropagation();
    
    setSelectedOverlay(overlay);
    setIsDraggingOverlay(true);
    
    const container = videoContainerRef.current;
    if (!container) return;
    
    const containerRect = container.getBoundingClientRect();
    const overlayElement = e.currentTarget;
    const overlayRect = overlayElement.getBoundingClientRect();
    const transform = getOverlayTransform(overlay.id);
    
    // Запоминаем точный offset от позиции мыши до текущей позиции элемента
    setOverlayDragStart({
      x: e.clientX - overlayRect.left,
      y: e.clientY - overlayRect.top,
      containerX: containerRect.left,
      containerY: containerRect.top
    });
  }, [getOverlayTransform]);

  const handleOverlayResizeStart = useCallback((e, overlay, handle) => {
    e.preventDefault();
    e.stopPropagation();
    
    console.log('Starting resize:', handle); // Для отладки
    
    setSelectedOverlay(overlay);
    setIsResizingOverlay(true);
    setResizeOverlayHandle(handle);
    
    const currentTransform = getOverlayTransform(overlay.id);
    setInitialOverlayTransform(currentTransform);
    
