  const handleOverlayMouseMove = useCallback((e) => {
    if (!selectedOverlay) return;
    
    if (isDraggingOverlay) {
      // Перетаскивание с максимальной плавностью
      const container = videoContainerRef.current;
      if (!container) return;
      
      const rect = container.getBoundingClientRect();
      const overlaySize = 128 * getOverlayTransform(selectedOverlay.id).scale;
      
      // Вычисляем новую позицию с учетом начального offset
      const newX = e.clientX - overlayDragStart.containerX - overlayDragStart.x;
      const newY = e.clientY - overlayDragStart.containerY - overlayDragStart.y;
      
      // Ограничиваем движение границами контейнера с учетом размера overlay
      const padding = 5; // Минимальный отступ
      const limitedX = Math.max(padding, Math.min(rect.width - overlaySize - padding, newX));
      const limitedY = Math.max(padding, Math.min(rect.height - overlaySize - padding, newY));
      
      // Обновляем позицию относительно базовой позиции
      const baseX = 200;
      const baseY = 50 + selectedOverlay.index * 120;
      
      // Мгновенное обновление без debounce для плавности
