      let scaleFactor = 1;
      const sensitivity = 0.005; // Базовая чувствительность
      
      switch (resizeOverlayHandle) {
        case 'nw': // Северо-запад: тянем влево-вверх = уменьшаем
          scaleFactor = 1 - (deltaX + deltaY) * sensitivity;
          break;
        case 'ne': // Северо-восток: тянем вправо-вверх = увеличиваем вправо, уменьшаем вверх  
          scaleFactor = 1 + (deltaX - deltaY) * sensitivity;
          break;
        case 'sw': // Юго-запад: тянем влево-вниз = уменьшаем влево, увеличиваем вниз
          scaleFactor = 1 + (-deltaX + deltaY) * sensitivity;
          break;
        case 'se': // Юго-восток: тянем вправо-вниз = увеличиваем
          scaleFactor = 1 + (deltaX + deltaY) * sensitivity;
          break;
      }
      
      // Применяем новый масштаб
      const newScale = Math.max(0.2, Math.min(4, overlayDragStart.startScale * scaleFactor));
      
      // Простое обновление без сложной компенсации позиции
      updateOverlayTransform(selectedOverlay.id, { 
