  }, [selectedOverlay, isDraggingOverlay, isResizingOverlay, overlayDragStart, initialOverlayTransform, updateOverlayTransform, getOverlayTransform, resizeOverlayHandle]);

  const handleOverlayMouseUp = useCallback(() => {
    console.log('Mouse up - stopping drag/resize'); // Для отладки
    setIsDraggingOverlay(false);
    setIsResizingOverlay(false);
