  };

  const handleTrackLabelsScroll = (e) => {
    if (timelineScrollRef.current) {
      timelineScrollRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const getTimelineWidth = () => {
    const baseWidth = videoDuration * 50 * timelineZoom;
    return Math.max(baseWidth, 2000);
  };

  const getPixelsPerSecond = () => {
    return 50 * timelineZoom;
  };

  // НОВЫЕ ФУНКЦИИ ДЛЯ РАБОТЫ С MEDIASTORAGE

  // Обновленная функция загрузки видео с MediaStorage
  const handleVideoUpload = async (event) => {
    if (!isStorageReady) {
      console.error('Storage not ready');
      return;
    }

    const files = event.target.files ? Array.from(event.target.files) : [];
    
    for (const file of files) {
      const fileId = Date.now() + Math.random();
      setUploadingFiles(prev => new Set([...prev, fileId]));
      
      try {
        // Проверяем, можем ли загрузить
        const canUpload = await MediaStorage.canUpload('videos');
        if (!canUpload) {
          const remaining = await MediaStorage.getRemainingSpace('videos');
          throw new Error(`Video storage limit reached. ${remaining} slots remaining.`);
        }

        // Сохраняем файл в MediaStorage
        const savedFile = await MediaStorage.saveFile(file, 'videos');
        console.log('Video saved to MediaStorage:', savedFile);

        // Добавляем в медиабиблиотеку
        setMediaLibrary((prev) => ({
          ...prev,
          videos: [...prev.videos, savedFile],
        }));

      } catch (error) {
        console.error('Failed to upload video:', error);
        setStorageError(error.message);
      } finally {
        setUploadingFiles(prev => {
          const newSet = new Set(prev);
          newSet.delete(fileId);
          return newSet;
        });
      }
    }
    
    // Очищаем input
    if (event.target) {
      event.target.value = '';
    }
  };

  // Обновленная функция загрузки аудио с MediaStorage
  const handleAudioUpload = async (event) => {
    if (!isStorageReady) {
      console.error('Storage not ready');
      return;
    }

    const files = event.target.files ? Array.from(event.target.files) : [];
    
