      setUploadingFiles(prev => new Set([...prev, fileId]));
      
      try {
        // Проверяем, можем ли загрузить
        const canUpload = await MediaStorage.canUpload('images');
        if (!canUpload) {
          const remaining = await MediaStorage.getRemainingSpace('images');
          throw new Error(`Image storage limit reached. ${remaining} slots remaining.`);
        }

        // Сохраняем файл в MediaStorage
        const savedFile = await MediaStorage.saveFile(file, 'images');
        console.log('Image saved to MediaStorage:', savedFile);

        // Добавляем в медиабиблиотеку
        setMediaLibrary((prev) => ({
