        }));

      } catch (error) {
        console.error('Failed to upload image:', error);
        setStorageError(error.message);
      } finally {
        setUploadingFiles(prev => {
          const newSet = new Set(prev);
          newSet.delete(fileId);
          return newSet;
        });
      }
    }
    
    // Очищаем input
    if (event.target) {
      event.target.value = '';
    }
  };

  // Обновленная функция удаления из библиотеки с MediaStorage
  const removeFromLibrary = async (itemId, type) => {
    try {
      // Удаляем из MediaStorage
      await MediaStorage.deleteFile(itemId);
      console.log('File deleted from MediaStorage:', itemId);

      // Удаляем из медиабиблиотеки
      setMediaLibrary((prev) => ({
        ...prev,
        [type]: prev[type].filter((item) => item.id !== itemId),
