    return () => {
      stopAutoScroll();
    };
  }, [stopAutoScroll]);

  // Обработка клавиатурных шорткатов
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
        return;
      }

      if (e.key === 'Delete' || e.key === 'Backspace') {
        e.preventDefault();
        deleteSelectedItem();
      }

      if (e.ctrlKey && e.key === 'b') {
        e.preventDefault();
        splitTimelineItem();
      }

      if (e.ctrlKey && e.key === 'c') {
        e.preventDefault();
        copyTimelineItem();
      }

      if (e.ctrlKey && e.key === 'v') {
        e.preventDefault();
        pasteTimelineItem();
      }

      if (e.key === ' ' || e.code === 'Space') {
        e.preventDefault();
        setIsPlaying(prev => !prev);
      }

      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        const step = e.ctrlKey ? 5 : e.shiftKey ? 1 : 0.1;
        seekTo(Math.max(0, currentTime - step));
      }
      if (e.key === 'ArrowRight') {
        e.preventDefault();
        const step = e.ctrlKey ? 5 : e.shiftKey ? 1 : 0.1;
        seekTo(Math.min(videoDuration, currentTime + step));
      }

      if (e.key === '=' || e.key === '+') {
        e.preventDefault();
        zoomIn();
      }
      if (e.key === '-') {
        e.preventDefault();
        zoomOut();
      }
      if (e.key === '0') {
        e.preventDefault();
        resetZoom();
      }
      
      if (e.key === '?' || (e.shiftKey && e.key === '/')) {
        e.preventDefault();
        setShowHelp(true);
      }
      
      if (e.key === 'Escape') {
        e.preventDefault();
        if (showHelp) {
          setShowHelp(false);
        } else if (selectedOverlay) {
          setSelectedOverlay(null);
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [selectedTimelineItem, copiedItem, currentTime, videoDuration, showHelp, selectedOverlay, deleteSelectedItem, splitTimelineItem, copyTimelineItem, pasteTimelineItem]);

  // Обработка колесика мыши для зума
  useEffect(() => {
    const handleWheel = (e) => {
      if (e.ctrlKey && timelineRef.current?.contains(e.target)) {
        e.preventDefault();
        if (e.deltaY < 0) {
          zoomIn();
        } else {
          zoomOut();
        }
      }
    };

    document.addEventListener('wheel', handleWheel, { passive: false });
    return () => document.removeEventListener('wheel', handleWheel);
  }, []);

  const addToTimeline = (mediaItem, startTime = null, targetTrackId = null) => {
    let trackType, trackId;
    
    if (mediaItem.type === "video") {
      trackType = targetTrackId && targetTrackId.startsWith('overlay') ? 'overlay' : 'main';
      trackId = targetTrackId || 'main-video';
    } else if (mediaItem.type === "image") {
      trackType = 'overlay';
      trackId = targetTrackId || 'overlay-1';
    } else if (mediaItem.type === "audio") {
      trackType = 'audio';
      trackId = targetTrackId || 'audio-1';
    }
    
    const duration = mediaItem.duration || (mediaItem.type === "image" ? 5 : 10);
    
    const finalStartTime = startTime !== null ? 
      startTime : 
      findNextAvailablePosition(trackId, duration);

    const newTimelineItem = {
      id: Date.now() + Math.random(),
      mediaId: mediaItem.id,
      type: mediaItem.type,
      name: mediaItem.name,
      url: mediaItem.url,
      startTime: finalStartTime,
      duration: duration,
      trackId: trackId,
      trackType: trackType,
      volume: 1,
      opacity: 1,
    };

    setTimelineItems((prev) => [...prev, newTimelineItem]);

    const newEndTime = newTimelineItem.startTime + newTimelineItem.duration;
    if (newEndTime > videoDuration) {
      setVideoDuration(newEndTime + 10);
    }
  };

  const addOverlayTrack = () => {
    const newTrackId = `overlay-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      overlays: [...prev.overlays, {
        id: newTrackId,
        name: `Overlay ${prev.overlays.length + 1}`,
        type: 'overlay'
      }]
    }));
  };

  const addAudioTrack = () => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      audio: [...prev.audio, {
        id: newTrackId,
        name: `Audio ${prev.audio.length + 1}`,
        type: 'audio'
      }]
    }));
  };

  const handleTimelineClick = (e) => {
    if (isDragging || isTimelineDragging || isResizing) return;
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const pixelsPerSecond = getPixelsPerSecond();
    const clickTime = clickX / pixelsPerSecond;
    seekTo(clickTime);
  };

  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setDropPreview(null);
    setIsDragging(false);
  };

  const handleTimelineItemDragStart = (e, timelineItem) => {
    if (isResizing) {
      e.preventDefault();
      return;
    }
    
    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);
    
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(e.clientX - itemRect.left + timelineScrollRef.current.scrollLeft);
    
    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleTimelineItemDragEnd = () => {
    setDraggedTimelineItem(null);
    setDropPreview(null);
    setIsTimelineDragging(false);
    setDragOffset(0);
    stopAutoScroll();
  };

  const handleDragOver = (e) => {
    if (!draggedItem && !draggedTimelineItem) return;
    
    e.preventDefault();
    e.dataTransfer.dropEffect = draggedItem ? "copy" : "move";
    
    if (draggedTimelineItem || isResizing) {
      checkAutoScroll(e.clientX);
    }
    
    const timelineRect = timelineScrollRef.current.getBoundingClientRect();
    const dragX = e.clientX - timelineRect.left + timelineScrollRef.current.scrollLeft - (draggedTimelineItem ? dragOffset : 0);
    const pixelsPerSecond = getPixelsPerSecond();
    const dropTime = Math.max(0, dragX / pixelsPerSecond);
    
    const dragY = e.clientY - timelineRect.top + timelineScrollRef.current.scrollTop;
    const trackHeight = 60;
    const timeRulerHeight = 48;
    
    const relativeY = dragY - timeRulerHeight;
    const trackIndex = Math.floor(relativeY / trackHeight);
    
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const targetTrack = allTracks[trackIndex];
    
    if (draggedItem && targetTrack) {
      // Новый элемент из библиотеки
      const duration = draggedItem.duration || (draggedItem.type === "image" ? 5 : 10);
      const insertResult = insertWithRipple(targetTrack.id, dropTime, duration);
      
      setDropPreview({
        time: insertResult.time,
        duration: duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || []
      });
    } else if (draggedTimelineItem && targetTrack) {
      // Существующий элемент
      const insertResult = insertWithRipple(
        targetTrack.id, 
        dropTime, 
        draggedTimelineItem.duration, 
        draggedTimelineItem.id
      );
      
      setDropPreview({
        time: insertResult.time,
        duration: draggedTimelineItem.duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || []
      });
    }
  };

  const handleDrop = (e) => {
    if ((!draggedItem && !draggedTimelineItem) || !dropPreview) return;
    
    e.preventDefault();
    stopAutoScroll();
    
    console.log('Dropping with ripple:', { 
      dropPreview,
      shouldRipple: dropPreview.shouldRipple,
      affectedItems: dropPreview.affectedItems?.length || 0
    });
    
    if (dropPreview.isValid) {
      if (draggedItem) {
        // Новый элемент из библиотеки
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          // Сначала сдвигаем существующие блоки
          applyRippleEffect(dropPreview.affectedItems, dropPreview.duration);
        }
        
        // Добавляем новый элемент
        addToTimeline(draggedItem, dropPreview.time, dropPreview.trackId);
        
        console.log('Added with ripple effect:', dropPreview.shouldRipple);
        
      } else if (draggedTimelineItem) {
        // Перемещение существующего элемента
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          // Сначала сдвигаем блоки, которые нужно освободить место
          applyRippleEffect(dropPreview.affectedItems, draggedTimelineItem.duration);
        }
        
        // Перемещаем элемент
        setTimelineItems(prev => prev.map(item => 
          item.id === draggedTimelineItem.id 
            ? { 
                ...item, 
                startTime: dropPreview.time, 
                trackId: dropPreview.trackId, 
                trackType: dropPreview.trackType 
              }
            : item
        ));
        
        console.log('Moved with ripple effect:', dropPreview.shouldRipple);
        
        const newEndTime = dropPreview.time + draggedTimelineItem.duration;
        if (newEndTime > videoDuration) {
          setVideoDuration(newEndTime + 10);
        }
      }
    }
    
    setDropPreview(null);
    setDraggedItem(null);
    setDraggedTimelineItem(null);
    setIsDragging(false);
    setIsTimelineDragging(false);
  };

  const handleDragLeave = (e) => {
    if (!timelineRef.current?.contains(e.relatedTarget)) {
      setDropPreview(null);
      stopAutoScroll();
    }
  };

  const getTimelineItemStyle = (item) => {
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(track => track.id === item.trackId);
    const pixelsPerSecond = getPixelsPerSecond();
    
    return {
      left: `${item.startTime * pixelsPerSecond}px`,
      width: `${item.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getDropPreviewStyle = () => {
    if (!dropPreview) return {};
    
    const allTracks = [...tracks.overlays, ...tracks.main, ...tracks.audio];
    const trackIndex = allTracks.findIndex(track => track.id === dropPreview.trackId);
    const pixelsPerSecond = getPixelsPerSecond();
    
    return {
      left: `${dropPreview.time * pixelsPerSecond}px`,
      width: `${dropPreview.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getTrackColor = (trackType) => {
    switch (trackType) {
      case 'overlay': return 'bg-purple-500 border-purple-400';
      case 'main': return 'bg-blue-500 border-blue-400';
      case 'audio': return 'bg-green-500 border-green-400';
      default: return 'bg-gray-500 border-gray-400';
    }
  };

  return (
    <div className="flex h-screen w-full gap-4 p-4 bg-gray-100 overflow-hidden">
      {/* Media Library */}
      <div className="w-80 min-w-72 max-w-80 bg-white border-2 border-blue-200 flex flex-col rounded-2xl shadow-lg overflow-hidden flex-shrink-0">
        <div className="bg-blue-50 px-4 py-3 border-b border-blue-100">
          <h3 className="font-bold text-blue-800 flex items-center">
            <Upload size={18} className="mr-2" />
            Media Library
            {!isStorageReady && (
              <Loader size={16} className="ml-2 animate-spin text-orange-500" />
            )}
          </h3>
          
          {/* Storage Info */}
          {storageInfo && (
            <div className="text-xs text-blue-600 mt-1 flex items-center justify-between">
              <span>
                {storageInfo.usage.totalSizeMB}MB used • {storageInfo.mode}
              </span>
              {storageInfo.mode === 'fallback' && (
                <span className="text-orange-600 font-medium">Fallback Mode</span>
              )}
            </div>
          )}
        </div>
        
        {/* Storage Error */}
        {storageError && (
          <div className="p-3 bg-red-50 border-b border-red-200 flex items-center">
            <AlertCircle size={16} className="text-red-500 mr-2 flex-shrink-0" />
            <span className="text-sm text-red-700">{storageError}</span>
            <button
              onClick={() => setStorageError(null)}
              className="ml-auto text-red-500 hover:text-red-700"
            >
              <X size={14} />
            </button>
          </div>
        )}
        
        <div className="p-4 pb-0">
          <div className="flex bg-gray-100 rounded-xl p-1">
            {[
              { id: "videos", name: "Videos", icon: Video },
              { id: "audios", name: "Audio", icon: Music },
              { id: "images", name: "Images", icon: Image },
            ].map((tab) => {
              const IconComponent = tab.icon;
              const count = mediaLibrary[tab.id]?.length || 0;
              const limit = storageInfo?.limits?.[tab.id] || 0;
              
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex-1 p-3 text-sm font-medium transition-all duration-200 rounded-lg ${
                    activeTab === tab.id
                      ? "bg-white text-blue-600 shadow-sm"
                      : "text-gray-600 hover:text-gray-800"
                  }`}
                >
                  <IconComponent size={16} className="mr-2 inline" />
                  {tab.name}
                  {storageInfo && (
                    <span className={`ml-1 text-xs ${count >= limit ? 'text-red-500' : 'text-gray-400'}`}>
                      ({count}/{limit})
                    </span>
                  )}
                </button>
              );
            })}
          </div>
        </div>

        <div className="p-4">
          <button
            onClick={() => {
              if (!isStorageReady) return;
              if (activeTab === "videos") fileInputRef.current?.click();
              else if (activeTab === "audios") audioInputRef.current?.click();
              else imageInputRef.current?.click();
            }}
            disabled={!isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])}
            className={`w-full p-6 border-2 border-dashed rounded-2xl transition-all duration-200 text-center group ${
              !isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])
                ? "border-gray-200 bg-gray-50 cursor-not-allowed"
                : "border-gray-300 hover:border-blue-400 hover:bg-blue-50/50"
            }`}
          >
            <div className={`w-12 h-12 rounded-xl flex items-center justify-center mx-auto mb-3 transition-colors ${
              !isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])
                ? "bg-gray-100"
                : "bg-gray-100 group-hover:bg-blue-100"
            }`}>
              {uploadingFiles.size > 0 ? (
                <Loader size={20} className="text-blue-500 animate-spin" />
              ) : (
                <Upload
                  size={20}
                  className={`${
                    !isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])
                      ? "text-gray-400"
                      : "text-gray-500 group-hover:text-blue-500"
                  }`}
                />
              )}
            </div>
            <span className={`text-sm font-medium ${
              !isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])
                ? "text-gray-400"
                : "text-gray-700 group-hover:text-blue-600"
            }`}>
              {!isStorageReady ? "Initializing..." : 
               uploadingFiles.size > 0 ? "Uploading..." :
               storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab] ? "Storage Full" :
               `Upload ${activeTab === "videos" ? "Video" : activeTab === "audios" ? "Audio" : "Images"}`}
            </span>
            <p className={`text-xs mt-1 ${
              !isStorageReady || (storageInfo && mediaLibrary[activeTab]?.length >= storageInfo.limits[activeTab])
                ? "text-gray-400"
                : "text-gray-500"
            }`}>
              {activeTab === "videos"
                ? "MP4, AVI, MOV up to 100MB"
                : activeTab === "audios"
                ? "MP3, WAV, M4A up to 100MB"
                : "JPG, PNG, GIF up to 10MB"}
            </p>
          </button>
        </div>

        <input
          ref={fileInputRef}
          type="file"
          accept="video/*"
          multiple
          onChange={handleVideoUpload}
          className="hidden"
        />
        <input
          ref={audioInputRef}
          type="file"
          accept="audio/*"
          multiple
          onChange={handleAudioUpload}
          className="hidden"
        />
        <input
          ref={imageInputRef}
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          className="hidden"
        />

        <div className="flex-1 overflow-y-auto px-4 pb-4">
          <div className="space-y-2">
            {mediaLibrary[activeTab]?.map((item) => (
              <div
                key={item.id}
                draggable="true"
                onDragStart={(e) => handleDragStart(e, item)}
                onDragEnd={handleDragEnd}
                className={`flex items-center p-3 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-sm transition-all duration-200 cursor-grab active:cursor-grabbing group ${
                  draggedItem?.id === item.id ? "opacity-50" : ""
                }`}
                onClick={() => addToTimeline(item)}
              >
                <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center mr-3 shadow-sm group-hover:shadow">
                  {activeTab === "videos" && (
                    <Video size={20} className="text-blue-500" />
                  )}
                  {activeTab === "audios" && (
                    <Music size={20} className="text-green-500" />
                  )}
                  {activeTab === "images" && (
                    <Image size={20} className="text-purple-500" />
                  )}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-gray-900 truncate">
                    {item.name}
                  </p>
                  <div className="flex items-center justify-between text-xs text-gray-500">
                    <span>
                      {activeTab === "images"
                        ? item.width && item.height 
                          ? `${item.width}×${item.height}px`
                          : "Image"
                        : formatTime(item.duration || 0)}
                    </span>
                    {item.size && (
                      <span>{formatFileSize(item.size)}</span>
                    )}
                  </div>
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromLibrary(item.id, activeTab);
                  }}
                  className="opacity-0 group-hover:opacity-100 p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
                >
                  <Trash2 size={14} />
                </button>
              </div>
            )) || []}
            
            {mediaLibrary[activeTab]?.length === 0 && isStorageReady && (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-3">
                  {activeTab === "videos" && <Video size={24} className="text-gray-400" />}
                  {activeTab === "audios" && <Music size={24} className="text-gray-400" />}
                  {activeTab === "images" && <Image size={24} className="text-gray-400" />}
                </div>
                <p className="text-sm text-gray-500">No {activeTab} uploaded yet</p>
                <p className="text-xs text-gray-400 mt-1">Click upload button above</p>
              </div>
            )}
            
            {!isStorageReady && (
              <div className="text-center py-8">
                <Loader size={32} className="text-blue-500 animate-spin mx-auto mb-3" />
                <p className="text-sm text-gray-500">Initializing storage...</p>
              </div>
            )}
          </div>
        </div>
        
        {/* Storage Debug Controls (можно убрать в продакшене) */}
        {process.env.NODE_ENV === 'development' && storageInfo && (
          <div className="p-4 border-t border-gray-200 bg-gray-50">
            <div className="text-xs text-gray-600 space-y-1">
              <div>Mode: {storageInfo.mode}</div>
              <div>Total: {storageInfo.usage.totalSizeMB}MB</div>
              <button
                onClick={clearStorage}
                className="text-red-600 hover:text-red-800 underline"
              >
                Clear Storage (Debug)
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col gap-4 min-w-0 overflow-hidden">
        {/* Video Preview */}
        <div 
          ref={videoContainerRef}
          className="flex-1 bg-black rounded-2xl relative overflow-hidden shadow-sm min-h-0" 
          title="Video Preview - Click overlays to select, drag to move, Ctrl+scroll to resize, use opacity slider"
        >
          {currentVideoSource ? (
            <div 
              className="relative w-full h-full"
              onClick={(e) => {
                // Снимаем выделение overlay при клике на пустое место
                if (e.target === e.currentTarget) {
                  setSelectedOverlay(null);
                }
              }}
            >
              {getCurrentTimelineContent().mainVideo ? (
                <video
                  ref={videoRef}
                  src={currentVideoSource}
                  className="w-full h-full object-contain rounded-2xl"
                  muted={isMuted}
                />
              ) : (
                <img
                  src={currentVideoSource}
                  alt="Timeline content"
                  className="w-full h-full object-contain rounded-2xl"
                />
              )}
              
              {getCurrentTimelineContent().overlays.map((item, index) => {
                const transform = getOverlayTransform(item.id);
                const isSelected = selectedOverlay?.id === item.id;
                const isDraggingThis = isDraggingOverlay && isSelected;
                const isResizingThis = isResizingOverlay && isSelected;
                
                return (
                  <div
                    key={item.id}
                    className={`absolute select-none ${
                      isSelected ? 'ring-2 ring-blue-400 ring-opacity-75 shadow-xl' : 'hover:ring-2 hover:ring-blue-300 hover:ring-opacity-50'
                    } ${isDraggingThis ? 'cursor-grabbing z-50' : isResizingThis ? 'cursor-nw-resize' : 'cursor-grab hover:shadow-lg'}`}
                    style={{ 
                      left: `${200 + transform.x}px`,
                      top: `${50 + index * 120 + transform.y}px`,
                      width: `${128 * transform.scale}px`,
                      height: `${96 * transform.scale}px`,
                      opacity: transform.opacity,
                      zIndex: isSelected ? 1000 : 10 + index,
                      transform: isDraggingThis ? 'scale(1.02)' : 'scale(1)',
                      transition: isDraggingThis || isResizingThis ? 'none' : 'transform 0.2s ease, box-shadow 0.2s ease',
                    }}
                    onMouseDown={(e) => handleOverlayMouseDown(e, {...item, index})}
                    onWheel={(e) => handleOverlayWheel(e, item.id)}
                  >
                    <div className={`relative w-full h-full border-2 rounded-lg overflow-hidden shadow-lg group ${
                      isSelected ? 'border-blue-400' : 'border-white hover:border-blue-300'
                    }`}
                    style={{
                      transition: isDraggingThis || isResizingThis ? 'none' : 'border-color 0.2s ease',
                    }}>
                      {item.type === 'video' ? (
                        <video
                          src={item.url}
                          className="w-full h-full object-cover pointer-events-none"
                          muted
                        />
                      ) : (
                        <img
                          src={item.url}
                          alt={item.name}
                          className="w-full h-full object-cover pointer-events-none"
                          draggable={false}
                        />
                      )}
                      
                      {/* Overlay hover effect */}
                      <div className={`absolute inset-0 bg-blue-500 bg-opacity-0 hover:bg-opacity-10 ${
                        isSelected ? 'bg-opacity-10' : ''
                      }`}
                      style={{
                        transition: isDraggingThis || isResizingThis ? 'none' : 'background-color 0.2s ease',
                      }}></div>
                      
                      {isSelected && (
                        <>
                          {/* Resize handles - с визуальной обратной связью */}
                          <div 
                            className={`resize-handle absolute -top-2 -left-2 w-4 h-4 bg-blue-500 rounded-full cursor-nw-resize opacity-90 hover:opacity-100 hover:scale-125 shadow-md border-2 border-white ${
                              isResizingThis && resizeOverlayHandle === 'nw' ? 'scale-150 bg-red-500' : ''
                            }`}
                            onMouseDown={(e) => handleOverlayResizeStart(e, item, 'nw')}
                            style={{
                              transition: isDraggingThis || isResizingThis ? 'none' : 'all 0.2s ease',
                            }}
                          ></div>
                          <div 
                            className={`resize-handle absolute -top-2 -right-2 w-4 h-4 bg-blue-500 rounded-full cursor-ne-resize opacity-90 hover:opacity-100 hover:scale-125 shadow-md border-2 border-white ${
                              isResizingThis && resizeOverlayHandle === 'ne' ? 'scale-150 bg-red-500' : ''
                            }`}
                            onMouseDown={(e) => handleOverlayResizeStart(e, item, 'ne')}
                            style={{
                              transition: isDraggingThis || isResizingThis ? 'none' : 'all 0.2s ease',
                            }}
                          ></div>
                          <div 
                            className={`resize-handle absolute -bottom-2 -left-2 w-4 h-4 bg-blue-500 rounded-full cursor-sw-resize opacity-90 hover:opacity-100 hover:scale-125 shadow-md border-2 border-white ${
                              isResizingThis && resizeOverlayHandle === 'sw' ? 'scale-150 bg-red-500' : ''
                            }`}
                            onMouseDown={(e) => handleOverlayResizeStart(e, item, 'sw')}
                            style={{
                              transition: isDraggingThis || isResizingThis ? 'none' : 'all 0.2s ease',
                            }}
                          ></div>
                          <div 
                            className={`resize-handle absolute -bottom-2 -right-2 w-4 h-4 bg-blue-500 rounded-full cursor-se-resize opacity-90 hover:opacity-100 hover:scale-125 shadow-md border-2 border-white ${
                              isResizingThis && resizeOverlayHandle === 'se' ? 'scale-150 bg-red-500' : ''
                            }`}
                            onMouseDown={(e) => handleOverlayResizeStart(e, item, 'se')}
                            style={{
                              transition: isDraggingThis || isResizingThis ? 'none' : 'all 0.2s ease',
                            }}
                          ></div>
                          
                          {/* Центральная точка для перетаскивания */}
                          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-2 h-2 bg-blue-500 rounded-full opacity-60 pointer-events-none"></div>
                          
                          {/* Opacity slider - улучшенный */}
                          <div className="absolute -bottom-12 left-0 right-0 bg-black/80 backdrop-blur-sm rounded-lg px-3 py-2 border border-white/20 shadow-lg">
                            <div className="flex items-center space-x-2">
                              <span className="text-white text-xs font-medium min-w-fit">Opacity:</span>
                              <input
                                type="range"
                                min="0.1"
                                max="1"
                                step="0.05"
                                value={transform.opacity}
                                onChange={(e) => updateOverlayTransform(item.id, { opacity: parseFloat(e.target.value) })}
                                className="flex-1 h-2 accent-blue-500 cursor-pointer"
                                onMouseDown={(e) => e.stopPropagation()}
                              />
                              <span className="text-white text-xs min-w-8 text-right font-mono">
                                {Math.round(transform.opacity * 100)}%
                              </span>
                            </div>
                          </div>
                        </>
                      )}
                      
                      {/* Layer info - улучшенная */}
                      <div className={`absolute top-1 left-1 bg-black/70 text-white text-xs px-2 py-1 rounded pointer-events-none ${
                        isSelected ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'
                      }`}
                      style={{
                        transition: isDraggingThis || isResizingThis ? 'none' : 'opacity 0.2s ease',
                      }}>
                        {item.name} • {Math.round(transform.scale * 100)}% • {Math.round(transform.opacity * 100)}%
                      </div>
                      
                      {/* Индикатор выделения */}
                      {isSelected && (
                        <div className="absolute -top-0.5 -left-0.5 -right-0.5 -bottom-0.5 border-2 border-blue-400 rounded-lg pointer-events-none animate-pulse"></div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="w-full h-full flex items-center justify-center text-white">
              <div className="text-center">
                <div className="w-20 h-20 bg-gray-800 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <Film size={40} className="opacity-50" />
                </div>
                <p className="text-xl opacity-75 font-medium">
                  No content at current time
                </p>
                <p className="text-sm opacity-50 mb-4">
                  Drag media to timeline tracks to start editing
                </p>
                <div className="text-xs opacity-40 space-y-1">
                  <p>💡 Overlays are interactive • Ripple mode enabled</p>
                  <p>📁 Files stored in {storageInfo?.mode || 'storage'} mode</p>
                  <p>Press <kbd className="px-1 py-0.5 bg-gray-700 rounded text-xs">?</kbd> for help & shortcuts</p>
                </div>
              </div>
            </div>
          )}

          <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 bg-black/60 backdrop-blur-md rounded-2xl p-4">
            <div className="flex items-center space-x-6">
              <button
                onClick={() => seekTo(Math.max(0, currentTime - 10))}
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
                title="Skip back 10 seconds"
              >
                <SkipBack size={20} />
              </button>
              <button
                onClick={handlePlayPause}
                className="w-12 h-12 bg-white/20 hover:bg-white/30 rounded-2xl flex items-center justify-center text-white transition-all hover:scale-105"
                title="Play/Pause (Space)"
              >
                {isPlaying ? <Pause size={24} /> : <Play size={24} />}
              </button>
              <button
                onClick={() =>
                  seekTo(Math.min(videoDuration, currentTime + 10))
                }
                className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
                title="Skip forward 10 seconds"
              >
                <SkipForward size={20} />
              </button>
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setIsMuted(!isMuted)}
                  className="text-white hover:text-blue-400 transition-colors p-1 rounded-lg hover:bg-white/10"
                  title="Toggle mute"
                >
                  {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
                </button>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={volume}
                  onChange={(e) => setVolume(parseFloat(e.target.value))}
                  className="w-24 accent-blue-500"
                  title="Volume control"
                />
              </div>
              <span className="text-white text-sm font-medium min-w-0" title="Current time / Total duration">
                {formatTime(currentTime)} / {formatTime(videoDuration)}
              </span>
            </div>
          </div>
        </div>

        {/* Timeline */}
        <div 
          className={`h-80 bg-white rounded-2xl shadow-sm border-2 transition-all duration-200 overflow-hidden flex flex-col flex-shrink-0 ${
            isDragging || isTimelineDragging || isResizing
              ? "border-blue-400 bg-blue-50/20" 
              : "border-gray-200"
          }`}
        >
          <div className="bg-gray-50 p-4 border-b border-gray-200 space-y-3">
            <div className="flex items-center justify-between">
              <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
                <Film size={18} />
                <span>Timeline Pro</span>
                {storageInfo && (
                  <span className={`text-xs px-2 py-1 rounded-full ${
                    storageInfo.mode === 'indexeddb' 
                      ? 'bg-green-100 text-green-700' 
                      : 'bg-orange-100 text-orange-700'
                  }`}>
                    {storageInfo.mode === 'indexeddb' ? '💾 IndexedDB' : '🔄 Fallback'}
                  </span>
                )}
                {isDragging && dropPreview && (
                  <span className="text-green-600">
                    - Adding "{draggedItem?.name}" {dropPreview.shouldRipple ? '⚡ ripple' : '📍 exact'}
                  </span>
                )}
                {isTimelineDragging && dropPreview && (
                  <span className="text-blue-600">
                    - Moving "{draggedTimelineItem?.name}" {dropPreview.shouldRipple ? '⚡ push blocks' : '📍 exact'}
                  </span>
                )}
                {isResizing && resizeItem && (
                  <span className="text-orange-600">
                    - Trimming "{resizeItem.name}" ({resizeHandle} edge)
                  </span>
                )}
                {copiedItem && (
                  <span className="text-blue-600 text-sm">
                    - "{copiedItem.name}" copied (Ctrl+V to paste)
                  </span>
                )}
              </h4>
              
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setShowHelp(true)}
                  className="p-2 text-blue-600 hover:text-blue-800 hover:bg-blue-50 rounded-xl transition-all duration-200"
                  title="📖 Help & Shortcuts Guide - Learn all controls and hotkeys (Press ? key)"
                >
                  <HelpCircle size={18} />
                </button>
                
                <div className="flex items-center space-x-2 bg-white rounded-xl p-2 border border-gray-200">
                  <button
                    onClick={zoomOut}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                    title="Zoom Out (-)"
                  >
                    <ZoomOut size={16} />
                  </button>
                  <span className="text-xs text-gray-500 min-w-12 text-center">
                    {(timelineZoom * 100).toFixed(0)}%
                  </span>
                  <button
                    onClick={zoomIn}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                    title="Zoom In (+), or Ctrl+Wheel"
                  >
                    <ZoomIn size={16} />
                  </button>
                  <button
                    onClick={resetZoom}
                    className="p-1 text-gray-600 hover:text-blue-600 transition-colors"
                    title="Reset Zoom (0)"
                  >
                    <RotateCcw size={16} />
                  </button>
                </div>
              </div>
            </div>
            
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-6">
                <div className="flex items-center space-x-2">
                  <button
                    onClick={addOverlayTrack}
                    className="p-2 text-purple-600 hover:text-purple-800 hover:bg-purple-50 rounded-xl transition-all duration-200"
                    title="Add Overlay Track"
                  >
                    <Plus size={16} />
                  </button>
                  <button
                    onClick={addAudioTrack}
                    className="p-2 text-green-600 hover:text-green-800 hover:bg-green-50 rounded-xl transition-all duration-200"
                    title="Add Audio Track"
                  >
                    <Music size={16} />
                  </button>
                  <button
                    onClick={splitTimelineItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-red-600 hover:text-red-800 hover:bg-red-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Split Selected Item (Ctrl+B) - Cuts item at current time"
                  >
                    <Scissors size={16} />
                  </button>
                  <button 
                    onClick={copyTimelineItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-blue-600 hover:text-blue-800 hover:bg-blue-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Copy Selected Item (Ctrl+C) - Copy item to clipboard"
                  >
                    <Copy size={16} />
                  </button>
                  <button 
                    onClick={deleteSelectedItem}
                    disabled={!selectedTimelineItem}
                    className={`p-2 rounded-xl transition-all duration-200 ${
                      selectedTimelineItem 
                        ? "text-red-600 hover:text-red-800 hover:bg-red-50" 
                        : "text-gray-400 cursor-not-allowed"
                    }`}
                    title="Delete Selected Item (Del/Backspace) - Remove from timeline"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div className="flex-1 flex min-h-0">
            <div className="w-48 bg-gray-50 border-r border-gray-200 flex-shrink-0 flex flex-col">
              <div className="h-12 border-b border-gray-200 flex-shrink-0"></div>
              
              <div 
                ref={trackLabelsRef}
                className="flex-1 overflow-y-auto overflow-x-hidden"
                onScroll={handleTrackLabelsScroll}
              >
                {tracks.overlays.map((track) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-purple-700 bg-purple-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Layers size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
                
                {tracks.main.map((track) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-blue-700 bg-blue-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Video size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
                
                {tracks.audio.map((track) => (
                  <div
                    key={track.id}
                    className="h-15 flex items-center px-4 border-b border-gray-100 text-sm font-medium text-green-700 bg-green-50 flex-shrink-0"
                    style={{ height: '60px' }}
                  >
                    <Music size={16} className="mr-2" />
                    {track.name}
                  </div>
                ))}
              </div>
            </div>

            <div 
              ref={(el) => {
                timelineRef.current = el;
                timelineScrollRef.current = el;
              }}
              className="flex-1 relative overflow-auto bg-white"
              onScroll={handleTrackAreaScroll}
              onDragOver={handleDragOver}
              onDrop={handleDrop}
              onDragLeave={handleDragLeave}
              style={{ 
                overflowX: 'auto', 
                overflowY: 'auto',
                scrollbarWidth: 'thin'
              }}
            >
              <div 
                style={{ 
                  width: `${getTimelineWidth()}px`, 
                  minHeight: '100%',
                  position: 'relative'
                }}
                onClick={handleTimelineClick}
              >
                <div className="h-12 bg-gray-50 border-b border-gray-200 relative flex-shrink-0">
                  {Array.from({ length: Math.ceil(videoDuration * timelineZoom / 5) + 1 }, (_, i) => {
                    const timeValue = i * 5 / timelineZoom;
                    const pixelsPerSecond = getPixelsPerSecond();
                    return (
                      <div
                        key={i}
                        className="absolute top-0 h-full border-l border-gray-300"
                        style={{ left: `${timeValue * pixelsPerSecond}px` }}
                      >
                        <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-medium">
                          {formatTime(timeValue)}
                        </span>
                      </div>
                    );
                  })}
                  
                  {timelineZoom > 2 && Array.from({ length: Math.ceil(videoDuration * timelineZoom) + 1 }, (_, i) => {
                    const timeValue = i / timelineZoom;
                    const pixelsPerSecond = getPixelsPerSecond();
                    return (
                      <div
                        key={`sub-${i}`}
                        className="absolute top-8 w-0 h-4 border-l border-gray-200"
                        style={{ left: `${timeValue * pixelsPerSecond}px` }}
                      />
                    );
                  })}
                  
                  <div
                    className="absolute top-0 w-0.5 h-full bg-red-500 z-20 shadow-sm"
                    style={{
                      left: `${currentTime * getPixelsPerSecond()}px`,
                    }}
                  />
                </div>

                <div 
                  className="relative flex-1" 
                  style={{ 
                    height: `${(tracks.overlays.length + tracks.main.length + tracks.audio.length) * 60}px`,
                    minHeight: '100%'
                  }}
                >
                  {timelineItems.map((item) => (
                    <div
                      key={item.id}
                      className={`absolute h-10 rounded-lg border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md group ${
                        getTrackColor(item.trackType)
                      } ${
                        selectedTimelineItem?.id === item.id ? "ring-2 ring-blue-300" : ""
                      } ${
                        draggedTimelineItem?.id === item.id ? "opacity-50 z-30" : ""
                      } ${
                        copiedItem?.id === item.id ? "ring-2 ring-green-300 ring-dashed" : ""
                      }`}
                      style={getTimelineItemStyle(item)}
                      onClick={(e) => {
                        e.stopPropagation();
                        setSelectedTimelineItem(item);
                      }}
                    >
                      <div
                        className="absolute left-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-l-lg z-10 flex items-center justify-center"
                        onMouseDown={(e) => handleResizeStart(e, item, 'left')}
                        title="Trim start - Drag to adjust start time"
                        style={{ background: 'linear-gradient(to right, rgba(255,255,255,0.8), transparent)' }}
                      >
                        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                      </div>
                      
                      <div
                        draggable="true"
                        onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                        onDragEnd={handleTimelineItemDragEnd}
                        className="p-2 text-white text-xs truncate font-medium flex items-center h-full mx-3 cursor-grab active:cursor-grabbing"
                        title="Drag to move item on timeline - Ripple mode: pushes other blocks"
                      >
                        {item.name}
                        {copiedItem?.id === item.id && (
                          <span className="ml-2 text-green-200">📋</span>
                        )}
                      </div>
                      
                      <div
                        className="absolute right-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-r-lg z-10 flex items-center justify-center"
                        onMouseDown={(e) => handleResizeStart(e, item, 'right')}
                        title="Trim end - Drag to adjust duration"
                        style={{ background: 'linear-gradient(to left, rgba(255,255,255,0.8), transparent)' }}
                      >
                        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                      </div>
                    </div>
                  ))}
                  
                  {dropPreview && (
                    <div
                      className={`absolute h-10 rounded-lg border-2 transition-all ${
                        dropPreview.isValid 
                          ? dropPreview.isExact 
                            ? `${getTrackColor(dropPreview.trackType)} bg-opacity-60 border-solid` 
                            : `${getTrackColor(dropPreview.trackType)} bg-opacity-40 border-dashed animate-pulse`
                          : "bg-red-300 border-red-500 border-dashed"
                      }`}
                      style={getDropPreviewStyle()}
                    >
                      {dropPreview.isValid && !dropPreview.isExact && (
                        <div className="flex items-center justify-center h-full text-white text-xs font-bold">
                          🧲
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Help Modal */}
      {showHelp && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[80vh] overflow-y-auto">
            <div className="p-6 border-b border-gray-200 flex items-center justify-between">
              <h3 className="text-xl font-bold text-gray-800 flex items-center">
                <HelpCircle size={24} className="mr-3 text-blue-600" />
                CapCut Pro - Help & Shortcuts
              </h3>
              <button
                onClick={() => setShowHelp(false)}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-xl transition-all"
              >
                <X size={20} />
              </button>
            </div>
            
            <div className="p-6 space-y-6">
              {/* MediaStorage Info */}
              <div>
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  💾 Smart Storage System
                </h4>
                <div className="grid grid-cols-1 gap-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Current mode:</span>
                    <span className="font-medium">{storageInfo?.mode || 'Loading...'}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Storage limits:</span>
                    <span className="font-medium">5 videos, 10 images, 5 audios</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Auto cleanup:</span>
                    <span className="font-medium">Files expire after 7 days</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Fallback mode:</span>
                    <span className="font-medium">If IndexedDB unavailable</span>
                  </div>
                </div>
              </div>

              {/* Timeline Controls */}
              <div>
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  <Film size={18} className="mr-2 text-blue-600" />
                  Timeline Controls
                </h4>
                <div className="grid grid-cols-2 gap-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Drag center:</span>
                    <span className="font-medium">Move item</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Drag edges:</span>
                    <span className="font-medium">Trim duration</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Auto ripple:</span>
                    <span className="font-medium">Push other blocks</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Ctrl + Wheel:</span>
                    <span className="font-medium">Zoom timeline</span>
                  </div>
                </div>
              </div>

              {/* Video Overlays */}
              <div>
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  <Layers size={18} className="mr-2 text-purple-600" />
                  Video Overlays (CapCut Style)
                </h4>
                <div className="grid grid-cols-2 gap-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Click overlay:</span>
                    <span className="font-medium">Select & show controls</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Drag anywhere:</span>
                    <span className="font-medium">Move position</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Drag corner dots:</span>
                    <span className="font-medium">Resize overlay</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Ctrl + Scroll:</span>
                    <span className="font-medium">Smooth zoom</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Opacity slider:</span>
                    <span className="font-medium">0-100% transparency</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Auto bounds:</span>
                    <span className="font-medium">Stays in view area</span>
                  </div>
                </div>
              </div>

              {/* Keyboard Shortcuts */}
              <div>
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  ⌨️ Keyboard Shortcuts
                </h4>
                <div className="grid grid-cols-2 gap-3 text-sm">
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Space</kbd>
                    <span className="font-medium">Play / Pause</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">← →</kbd>
                    <span className="font-medium">Seek (0.1s)</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Shift + ← →</kbd>
                    <span className="font-medium">Seek (1s)</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Ctrl + ← →</kbd>
                    <span className="font-medium">Seek (5s)</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Ctrl + B</kbd>
                    <span className="font-medium">Split item</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Ctrl + C</kbd>
                    <span className="font-medium">Copy item</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Ctrl + V</kbd>
                    <span className="font-medium">Paste item</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">Del</kbd>
                    <span className="font-medium">Delete item</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">+ -</kbd>
                    <span className="font-medium">Zoom in/out</span>
                  </div>
                  <div className="flex justify-between">
                    <kbd className="px-2 py-1 bg-gray-100 rounded text-xs font-mono">0</kbd>
                    <span className="font-medium">Reset zoom</span>
                  </div>
                </div>
              </div>

              {/* Tips & Tricks */}
              <div>
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  💡 Pro Tips
                </h4>
                <ul className="space-y-2 text-sm text-gray-600">
                  <li className="flex items-start">
                    <span className="inline-block w-2 h-2 bg-blue-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                    <span>Files are automatically saved to smart storage with 7-day retention</span>
                  </li>
                  <li className="flex items-start">
                    <span className="inline-block w-2 h-2 bg-purple-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                    <span>Storage automatically switches to fallback mode if IndexedDB unavailable</span>
                  </li>
                  <li className="flex items-start">
                    <span className="inline-block w-2 h-2 bg-orange-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                    <span>Upload limits prevent storage overflow (5 videos, 10 images, 5 audios)</span>
                  </li>
                  <li className="flex items-start">
                    <span className="inline-block w-2 h-2 bg-green-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                    <span>Ripple mode automatically pushes blocks when inserting new content</span>
                  </li>
                  <li className="flex items-start">
                    <span className="inline-block w-2 h-2 bg-red-500 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                    <span>Use Ctrl+scroll over overlays for precise size control like in CapCut</span>
                  </li>
                </ul>
              </div>
            </div>
            
            <div className="p-6 border-t border-gray-200 bg-gray-50 rounded-b-2xl">
              <p className="text-center text-sm text-gray-500">
                🎬 Made with CapCut Pro styling • 💾 Smart MediaStorage • Press <kbd className="px-1 py-0.5 bg-white rounded text-xs font-mono border">?</kbd> anytime
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoEditor;
