import { useRef, useState } from "react";

import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  ChevronLeft,
  ChevronRight,
  Brain,
  Mic,
  Users,
  Eye,
  Clock,
  TrendingUp,
  BookOpen,
  Heart,
  Search,
  Lightbulb,
  Music,
  Target,
  AlertCircle,
  Award,
} from "lucide-react";

const ContentStudio = () => {
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [contentType, setContentType] = useState("lifestyle");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [duration, setDuration] = useState("~5 minutes (500 words)");
  const [script, setScript] = useState("");
  const [isGeneratingKeyPoints, setIsGeneratingKeyPoints] = useState(false);
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [isExtendingScript, setIsExtendingScript] = useState(false);
  const [isAuthenticated] = useState(true);
  const [audioMethod, setAudioMethod] = useState("ai");
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [audioUrl, setAudioUrl] = useState("");
  const [assessment] = useState('{"total_score": 8.5, "structure": 9, "video_duration": 5, "engagement": 8, "readability": 9, "emotional_tone": 7, "clarity": 8, "seo_optimization": 7, "rhythm": 8, "recommendations": ["Add more engaging hooks in the introduction", "Consider adding call-to-action at the end"]}');
  const [assessmentLoading] = useState(false);
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false);
  const [isContentTypeDropdownOpen, setIsContentTypeDropdownOpen] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState("english");

  const scriptRef = useRef(null);

  const languages = [
    { id: "english", name: "English", flag: "🇺🇸" },
    { id: "russian", name: "Русский", flag: "🇷🇺" },
    { id: "spanish", name: "Español", flag: "🇪🇸" },
    { id: "french", name: "Français", flag: "🇫🇷" },
    { id: "german", name: "Deutsch", flag: "🇩🇪" },
    { id: "chinese", name: "中文", flag: "🇨🇳" },
  ];

  const contentTypes = [
    { id: "lifestyle", name: "Lifestyle", emoji: "🌟", color: "slate" },
    { id: "gaming", name: "Gaming", emoji: "🎮", color: "blue" },
    { id: "tech", name: "Tech", emoji: "💻", color: "indigo" },
    { id: "education", name: "Education", emoji: "📚", color: "slate" },
    { id: "entertainment", name: "Entertainment", emoji: "🎭", color: "blue" },
  ];

  const generateKeyPoints = () => {
    setIsGeneratingKeyPoints(true);
    setTimeout(() => {
      setKeyPoints([
        "Game atmosphere and world building",
        "Combat system and mechanics",
        "Performance and technical issues",
        "Comparison with previous games"
      ]);
      setIsGeneratingKeyPoints(false);
    }, 2000);
  };

  const generateScript = () => {
    setIsGeneratingScript(true);
    setTimeout(() => {
      setScript(`Welcome to my review of S.T.A.L.K.E.R. 2! Today I'll share my honest thoughts after spending 20+ hours in the Zone.

First, let's talk about the atmosphere. The developers have truly captured the essence of the Zone - every location feels alive yet dangerous. The weather effects and lighting create an immersive experience that keeps you on edge.

The combat system has been significantly improved. Weapon handling feels more realistic, and the AI behavior is much more challenging. However, I did encounter some technical issues that affected the overall experience.

Compared to the original trilogy, S.T.A.L.K.E.R. 2 brings modern graphics while maintaining the core gameplay elements that made the series special.

Overall, despite some technical hiccups, this is a worthy successor that fans have been waiting for. What do you think? Let me know in the comments!`);
      setIsGeneratingScript(false);
    }, 3000);
  };

  const extendScript = () => {
    setIsExtendingScript(true);
    setTimeout(() => {
      setScript(prev => prev + "\n\nLet me dive deeper into the technical aspects and share some gameplay tips that will help you survive in the Zone...");
      setIsExtendingScript(false);
    }, 2000);
  };

  const generateAudio = () => {
    setTimeout(() => {
      setAudioUrl("generated-audio.mp3");
    }, 2000);
  };

  const addKeyPoint = () => {
    if (keyPoints.length < 10) {
      setKeyPoints([...keyPoints, ""]);
    }
  };

  const updateKeyPoint = (index, value) => {
    const newKeyPoints = [...keyPoints];
    newKeyPoints[index] = value;
    setKeyPoints(newKeyPoints);
  };

  const removeKeyPoint = (index) => {
    if (keyPoints.length > 1) {
      setKeyPoints(keyPoints.filter((_, i) => i !== index));
    }
  };

  const handleTextSelection = () => {
    // Handle text selection for AI improvement
  };

  const DetailedAnalytics = ({ assessmentLoading, assessment }) => {
    const getScoreStyle = (score) => {
      if (score >= 8)
        return {
          text: "text-white",
          bg: "bg-gradient-to-br from-blue-500 to-indigo-600",
          border: "border-blue-300/50",
        };
      if (score >= 6)
        return {
          text: "text-white",
          bg: "bg-gradient-to-br from-indigo-500 to-purple-600",
          border: "border-indigo-300/50",
        };
      return {
        text: "text-white",
        bg: "bg-gradient-to-br from-slate-500 to-gray-600",
        border: "border-slate-300/50",
      };
    };

    const getProgressStyle = (score) => {
      if (score >= 8) return "bg-gradient-to-r from-blue-400 to-blue-600";
      if (score >= 6) return "bg-gradient-to-r from-indigo-400 to-indigo-600";
      return "bg-gradient-to-r from-slate-400 to-slate-600";
    };

    const CompactMetric = ({ icon: Icon, label, score }) => {
      const style = getScoreStyle(score);
      return (
        <div className="bg-white/80 rounded-md p-1 border border-white/30 hover:border-white/50 transition-all">
          <div className="flex items-center justify-between mb-0.5">
            <div className="flex items-center space-x-1">
              <div className="p-0.5 rounded bg-gradient-to-br from-indigo-500 to-purple-600">
                <Icon size={8} className="text-white" />
              </div>
              <span className="text-xs font-medium text-gray-800 truncate">
                {label}
              </span>
            </div>
            <div
              className={`px-1 py-0.5 rounded text-xs font-bold ${style.bg} ${style.text}`}
            >
              {score}
            </div>
          </div>
          <div className="w-full bg-gray-200/50 rounded-full h-0.5">
            <div
              className={`h-0.5 rounded-full ${getProgressStyle(
                score
              )} transition-all duration-500`}
              style={{ width: `${Math.min(score * 10, 100)}%` }}
            />
          </div>
        </div>
      );
    };

    const CompactCircularScore = ({ score }) => {
      const percentage = Math.round(score * 10);
      const circumference = 2 * Math.PI * 12;
      const strokeDashoffset =
        circumference - (percentage / 100) * circumference;

      const getGradeStyle = (score) => {
        if (score >= 8)
          return { gradient: "from-blue-500 to-indigo-600", grade: "A" };
        if (score >= 6)
          return { gradient: "from-indigo-500 to-purple-600", grade: "B" };
        return { gradient: "from-slate-500 to-gray-600", grade: "C" };
      };

      const gradeStyle = getGradeStyle(score);

      return (
        <div
          className={`bg-gradient-to-br ${gradeStyle.gradient} rounded-lg p-2 text-white shadow-lg`}
        >
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center space-x-1 mb-0.5">
                <Award size={8} className="text-white" />
                <span className="text-xs font-bold">Score</span>
              </div>
              <p className="text-xs text-white/90">Overall</p>
            </div>
            <div className="relative">
              <svg className="w-6 h-6 transform -rotate-90" viewBox="0 0 24 24">
                <circle
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="rgba(255, 255, 255, 0.2)"
                  strokeWidth="2"
                  fill="none"
                />
                <circle
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="white"
                  strokeWidth="2"
                  fill="none"
                  strokeLinecap="round"
                  strokeDasharray={circumference}
                  strokeDashoffset={strokeDashoffset}
                  className="transition-all duration-700"
                />
              </svg>
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                <span className="text-xs font-bold">{score}</span>
                <span className="text-xs opacity-90">{gradeStyle.grade}</span>
              </div>
            </div>
          </div>
        </div>
      );
    };

    if (assessmentLoading) {
      return (
        <div className="bg-gradient-to-br from-slate-100 to-purple-50 border border-indigo-200/50 p-2 rounded-lg shadow-lg">
          <div className="flex items-center space-x-2 mb-2">
            <div className="animate-spin rounded-full h-3 w-3 border-2 border-indigo-500 border-t-transparent"></div>
            <span className="text-xs font-bold text-gray-800">
              Analyzing...
            </span>
          </div>
        </div>
      );
    }

    if (!assessment) {
      return (
        <div className="bg-gray-100 border border-gray-300 p-2 rounded-lg text-center">
          <AlertCircle size={12} className="text-gray-400 mx-auto mb-1" />
          <p className="text-xs text-gray-600">No analysis available</p>
        </div>
      );
    }

    const parsedAssessment = JSON.parse(assessment);

    const metrics = [
      { icon: Target, label: "Structure", score: parsedAssessment.structure || 0 },
      { icon: Clock, label: "Duration", score: parsedAssessment.video_duration || 0 },
      { icon: TrendingUp, label: "Engage", score: parsedAssessment.engagement || 0 },
      { icon: BookOpen, label: "Read", score: parsedAssessment.readability || 0 },
      { icon: Heart, label: "Emotion", score: parsedAssessment.emotional_tone || 0 },
      { icon: Eye, label: "Clarity", score: parsedAssessment.clarity || 0 },
      { icon: Search, label: "SEO", score: parsedAssessment.seo_optimization || 0 },
      { icon: Music, label: "Rhythm", score: parsedAssessment.rhythm || 0 },
    ];

    return (
      <div className="bg-gradient-to-br from-slate-50 to-purple-50 border border-indigo-200/50 p-2 rounded-lg shadow-lg">
        <div className="flex items-center space-x-2 mb-2">
          <div className="w-4 h-4 bg-gradient-to-br from-indigo-500 to-purple-600 rounded flex items-center justify-center">
            <Award className="text-white" size={8} />
          </div>
          <div>
            <h3 className="text-xs font-bold text-indigo-600">Analysis</h3>
          </div>
        </div>

        <div className="mb-2">
          <CompactCircularScore score={parsedAssessment.total_score || 0} />
        </div>

        <div className="grid grid-cols-2 gap-1 mb-2">
          {metrics.map((metric, index) => (
            <CompactMetric
              key={index}
              icon={metric.icon}
              label={metric.label}
              score={metric.score}
            />
          ))}
        </div>
        
        {parsedAssessment.recommendations &&
          parsedAssessment.recommendations.length > 0 && (
            <div className="bg-gradient-to-br from-blue-50/80 to-purple-50/80 rounded p-1 border border-blue-200/50">
              <div className="flex items-center space-x-1 mb-1">
                <Lightbulb size={8} className="text-indigo-600" />
                <span className="font-bold text-gray-800 text-xs">Tips</span>
              </div>
              <div className="bg-white/80 rounded p-1">
                <p className="text-xs text-gray-700 leading-tight">{parsedAssessment.recommendations[0]}</p>
              </div>
            </div>
          )}
      </div>
    );
  };

  return (
    <div
      className={`${
        isLeftPanelCollapsed ? "w-16" : "w-80"
      } h-screen overflow-hidden bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10 flex-shrink-0`}
    >
      <style>
        {`
          .custom-scrollbar::-webkit-scrollbar {
            width: 6px;
          }
          
          .custom-scrollbar::-webkit-scrollbar-track {
            background: #f1f5f9;
            border-radius: 10px;
            margin: 4px 0;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb {
            background: #cbd5e1;
            border-radius: 10px;
            transition: all 0.2s ease;
          }
          
          .custom-scrollbar::-webkit-scrollbar-thumb:hover {
            background: #94a3b8;
          }
        `}
      </style>

      <div className="flex items-center justify-between p-3 border-b border-slate-200/50 flex-shrink-0">
        {!isLeftPanelCollapsed && (
          <div className="flex flex-col space-y-1">
            <h2 className="text-lg font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
              Content Studio
            </h2>
            <p className="text-xs text-gray-600 font-medium">
              Professional content creation
            </p>
          </div>
        )}
        <button
          onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
          className="p-2 hover:bg-slate-100 rounded-lg transition-all duration-300"
        >
          {isLeftPanelCollapsed ? (
            <ChevronRight
              size={16}
              className="text-gray-600"
            />
          ) : (
            <ChevronLeft
              size={16}
              className="text-gray-600"
            />
          )}
        </button>
      </div>

      {isLeftPanelCollapsed ? (
        <div className="p-2 space-y-2">
          <div className="flex flex-col items-center space-y-2">
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-2 bg-gradient-to-br from-slate-100 to-blue-100 hover:from-slate-200 hover:to-blue-200 rounded-lg transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Create script"
            >
              <FileText
                size={16}
                className="text-slate-600 group-hover:text-blue-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-2 bg-gradient-to-br from-blue-100 to-indigo-100 hover:from-blue-200 hover:to-indigo-200 rounded-lg transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Upload video"
            >
              <Upload
                size={16}
                className="text-blue-600 group-hover:text-indigo-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-2 bg-gradient-to-br from-indigo-100 to-purple-100 hover:from-indigo-200 hover:to-purple-200 rounded-lg transition-all duration-300 group hover:scale-110 shadow-lg"
              title="AI voiceover"
            >
              <Mic
                size={16}
                className="text-indigo-600 group-hover:text-purple-600"
              />
            </button>
            <button
              onClick={() => setIsLeftPanelCollapsed(false)}
              className="p-2 bg-gradient-to-br from-purple-100 to-pink-100 hover:from-purple-200 hover:to-pink-200 rounded-lg transition-all duration-300 group hover:scale-110 shadow-lg"
              title="Editing"
            >
              <Scissors
                size={16}
                className="text-purple-600 group-hover:text-pink-600"
              />
            </button>
          </div>
        </div>
      ) : (
        <div className="flex-1 overflow-y-auto custom-scrollbar">
          <div className="p-3 space-y-3">
            <div className="bg-gradient-to-r from-slate-600 to-blue-600 text-white p-3 rounded-lg">
              <div className="flex items-center space-x-2 mb-1">
                <Brain size={16} />
                <span className="font-semibold">AI-Powered</span>
              </div>
              <p className="text-xs opacity-90">
                Create professional content using AI
              </p>
            </div>

            {/* Language */}
            <div className="mb-3">
              <label className="block text-xs font-semibold text-gray-800 mb-2">
                Content Language
              </label>
              <div className="relative">
                <button
                  onClick={() =>
                    setIsLanguageDropdownOpen(!isLanguageDropdownOpen)
                  }
                  className="w-full bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg p-2 text-left transition-all duration-300 hover:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 shadow"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-1">
                      <span className="text-sm">
                        {
                          languages.find((lang) => lang.id === selectedLanguage)
                            ?.flag
                        }
                      </span>
                      <span className="text-sm font-medium text-gray-900">
                        {
                          languages.find((lang) => lang.id === selectedLanguage)
                            ?.name
                        }
                      </span>
                    </div>
                    <svg
                      className={`w-4 h-4 text-gray-500 transform transition-transform ${
                        isLanguageDropdownOpen ? "rotate-180" : ""
                      }`}
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M19 9l-7 7-7-7"
                      />
                    </svg>
                  </div>
                </button>

                {isLanguageDropdownOpen && (
                  <div className="absolute z-50 w-full mt-1 bg-white/95 backdrop-blur-xl border border-slate-200 rounded-lg shadow-xl overflow-hidden">
                    {languages.map((lang) => (
                      <button
                        key={lang.id}
                        onClick={() => {
                          setSelectedLanguage(lang.id);
                          setIsLanguageDropdownOpen(false);
                        }}
                        className={`w-full p-2 text-left transition-all duration-200 flex items-center space-x-2 hover:bg-blue-50 ${
                          selectedLanguage === lang.id
                            ? "bg-blue-50 text-blue-700 border-l-2 border-blue-500"
                            : "text-gray-700 hover:text-blue-600"
                        }`}
                      >
                        <span className="text-sm">{lang.flag}</span>
                        <span className="text-sm font-medium">{lang.name}</span>
                        {selectedLanguage === lang.id && (
                          <div className="ml-auto">
                            <svg
                              className="w-4 h-4 text-blue-500"
                              fill="currentColor"
                              viewBox="0 0 24 24"
                            >
                              <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                          </div>
                        )}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Content Type */}
            <div className="mb-3">
              <label className="block text-xs font-semibold text-gray-800 mb-2">
                Content Type
              </label>
              <div className="relative">
                <button
                  onClick={() =>
                    setIsContentTypeDropdownOpen(!isContentTypeDropdownOpen)
                  }
                  className="w-full bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg p-2 text-left transition-all duration-300 hover:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 shadow"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <div
                        className={`w-6 h-6 rounded-lg flex items-center justify-center ${
                          contentTypes.find((type) => type.id === contentType)
                            ?.color === "slate"
                            ? "bg-slate-100"
                            : contentTypes.find(
                                (type) => type.id === contentType
                              )?.color === "blue"
                            ? "bg-blue-100"
                            : "bg-indigo-100"
                        }`}
                      >
                        <span className="text-sm">
                          {
                            contentTypes.find((type) => type.id === contentType)
                              ?.emoji
                          }
                        </span>
                      </div>
                      <span className="font-medium text-gray-900 text-sm">
                        {
                          contentTypes.find((type) => type.id === contentType)
                            ?.name
                        }
                      </span>
                    </div>
                    <svg
                      className={`w-4 h-4 text-gray-500 transform transition-transform ${
                        isContentTypeDropdownOpen ? "rotate-180" : ""
                      }`}
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M19 9l-7 7-7-7"
                      />
                    </svg>
                  </div>
                </button>

                {isContentTypeDropdownOpen && (
                  <div className="absolute z-50 w-full mt-1 bg-white/95 backdrop-blur-xl border border-slate-200 rounded-lg shadow-xl overflow-hidden">
                    {contentTypes.map((type) => (
                      <button
                        key={type.id}
                        onClick={() => {
                          setContentType(type.id);
                          setIsContentTypeDropdownOpen(false);
                        }}
                        className={`w-full p-2 text-left transition-all duration-200 flex items-center space-x-2 hover:bg-gray-50 ${
                          contentType === type.id
                            ? `${
                                type.color === "slate"
                                  ? "bg-slate-50 border-l-2 border-slate-500"
                                  : type.color === "blue"
                                  ? "bg-blue-50 border-l-2 border-blue-500"
                                  : "bg-indigo-50 border-l-2 border-indigo-500"
                              }`
                            : "hover:bg-gray-50"
                        }`}
                      >
                        <div
                          className={`w-6 h-6 rounded-lg flex items-center justify-center ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "bg-slate-100"
                                : type.color === "blue"
                                ? "bg-blue-100"
                                : "bg-indigo-100"
                              : "bg-gray-50"
                          }`}
                        >
                          <span className="text-sm">{type.emoji}</span>
                        </div>
                        <span
                          className={`font-semibold text-sm ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "text-slate-700"
                                : type.color === "blue"
                                ? "text-blue-700"
                                : "text-indigo-700"
                              : "text-gray-700"
                          }`}
                        >
                          {type.name}
                        </span>
                        {contentType === type.id && (
                          <div className="ml-auto">
                            <svg
                              className={`w-4 h-4 ${
                                type.color === "slate"
                                  ? "text-slate-500"
                                  : type.color === "blue"
                                  ? "text-blue-500"
                                  : "text-indigo-500"
                              }`}
                              fill="currentColor"
                              viewBox="0 0 24 24"
                            >
                              <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                          </div>
                        )}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Topic Input */}
            <div className="space-y-2">
              <label className="block text-gray-700 font-semibold text-sm">
                What is the video about?
              </label>
              <div className="relative">
                <input
                  type="text"
                  value={topic}
                  onChange={(e) => setTopic(e.target.value)}
                  placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                  className="w-full p-2 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow text-sm"
                />
                <div className="absolute right-2 top-2">
                  <Brain size={14} className="text-slate-400" />
                </div>
              </div>
            </div>

            {/* Key Points */}
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <div>
                  <label className="block text-gray-700 font-semibold text-sm">
                    Key points
                  </label>
                  <p className="text-xs text-gray-500">
                    What will engage viewers?
                  </p>
                </div>
                <div className="flex space-x-1">
                  <button
                    onClick={generateKeyPoints}
                    disabled={
                      !topic.trim() ||
                      isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript ||
                      !isAuthenticated
                    }
                    className="flex items-center px-2 py-1 text-xs bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 text-white rounded-lg disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow"
                  >
                    {isGeneratingKeyPoints ? (
                      <>
                        <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-1"></div>
                        AI
                      </>
                    ) : (
                      <>
                        <Brain size={12} className="mr-1" />
                        AI
                      </>
                    )}
                  </button>
                  <button
                    onClick={addKeyPoint}
                    disabled={
                      keyPoints.length >= 10 ||
                      isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript
                    }
                    className="flex items-center px-2 py-1 text-xs bg-gradient-to-r from-slate-500 to-blue-500 hover:from-slate-600 hover:to-blue-600 text-white rounded-lg disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow"
                  >
                    <Plus size={12} className="mr-1" />
                    Add
                  </button>
                </div>
              </div>
              <div className="space-y-2 max-h-40 overflow-y-auto custom-scrollbar">
                {keyPoints.map((point, index) => (
                  <div
                    key={index}
                    className="flex items-center space-x-2 group"
                  >
                    <div className="w-5 h-5 bg-gradient-to-br from-slate-400 to-blue-400 rounded flex items-center justify-center text-white font-semibold text-xs">
                      {index + 1}
                    </div>
                    <input
                      type="text"
                      value={point}
                      onChange={(e) => updateKeyPoint(index, e.target.value)}
                      placeholder="Enter key point..."
                      className="flex-1 p-2 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 text-sm"
                    />
                    {keyPoints.length > 1 && (
                      <button
                        onClick={() => removeKeyPoint(index)}
                        className="p-1 text-red-400 hover:text-red-600 hover:bg-red-50 rounded transition-all duration-300 opacity-0 group-hover:opacity-100"
                      >
                        <X size={12} />
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {/* Duration */}
            <div className="space-y-2">
              <label className="block text-gray-700 font-semibold text-sm">
                Video duration
              </label>
              <select
                value={duration}
                onChange={(e) => setDuration(e.target.value)}
                className="w-full p-2 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg text-gray-900 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow text-sm"
              >
                <option>~5 minutes (500 words)</option>
                <option>~10 minutes (1000 words)</option>
                <option>~15 minutes (1500 words)</option>
                <option>~20 minutes (2000 words)</option>
              </select>
            </div>

            {/* Generate Button */}
            <button
              onClick={generateScript}
              disabled={
                !topic ||
                isGeneratingScript ||
                isGeneratingKeyPoints ||
                isExtendingScript ||
                !isAuthenticated
              }
              className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white font-semibold py-3 px-4 rounded-lg transition-all duration-300 flex items-center justify-center shadow hover:shadow-lg hover:scale-105"
            >
              <span className="flex items-center">
                {isGeneratingScript ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Generating script...
                  </>
                ) : isGeneratingKeyPoints ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Waiting...
                  </>
                ) : isExtendingScript ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Expanding...
                  </>
                ) : (
                  <>
                    <Wand2 className="mr-2" size={16} />
                    Generate Script
                  </>
                )}
              </span>
            </button>

            {/* Tips */}
            {!script && (
              <div className="bg-gradient-to-r from-blue-100 to-indigo-100 border border-blue-300 p-3 rounded-lg">
                <div className="flex items-start space-x-2">
                  <div className="text-lg">💡</div>
                  <div>
                    <h4 className="font-semibold text-gray-800 mb-1 text-sm">
                      Tip from Scriptify:
                    </h4>
                    <p className="text-xs text-gray-700">
                      Structure content logically: introduction, main section
                      with key points, conclusion.
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Script Editor */}
            {script && !isGeneratingScript && (
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <div>
                    <label className="block text-gray-700 font-semibold text-sm">
                      Your script is ready ✨
                    </label>
                    <p className="text-xs text-gray-500">
                      Professional content for your video
                    </p>
                  </div>
                  <div className="flex items-center space-x-1">
                    <button
                      onClick={extendScript}
                      disabled={
                        isExtendingScript ||
                        isGeneratingKeyPoints ||
                        !isAuthenticated
                      }
                      className="flex items-center px-2 py-1 text-xs bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed text-white rounded-lg transition-all duration-300 shadow"
                      title="Add more content to script"
                    >
                      {isExtendingScript ? (
                        <>
                          <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-1"></div>
                          Expanding...
                        </>
                      ) : (
                        <>
                          <Plus size={12} className="mr-1" />
                          More
                        </>
                      )}
                    </button>
                  </div>
                </div>
                <textarea
                  ref={scriptRef}
                  value={script}
                  onChange={(e) => setScript(e.target.value)}
                  onMouseUp={handleTextSelection}
                  rows={8}
                  className="w-full p-3 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-lg text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 leading-relaxed transition-all duration-300 shadow resize-none text-sm custom-scrollbar"
                  style={{ userSelect: "text" }}
                />
                <div className="flex items-center justify-between text-xs bg-gradient-to-r from-blue-50 to-indigo-50 p-2 rounded-lg border border-blue-200">
                  <div className="flex items-center space-x-3">
                    <div className="flex items-center space-x-1">
                      <Eye size={12} className="text-blue-600" />
                      <span className="font-medium text-blue-700">
                        Quality: 8.5/10
                      </span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Clock size={12} className="text-slate-600" />
                      <span className="font-medium text-blue-700">
                        ~5 min
                      </span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Users size={12} className="text-indigo-600" />
                      <span className="font-medium text-blue-700">
                        {script.length} chars
                      </span>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500">
                    ✨ Ready
                  </div>
                </div>
                <DetailedAnalytics
                  assessmentLoading={assessmentLoading}
                  assessment={assessment}
                />
              </div>
            )}

            {script && (
              <div className="space-y-3 border-t border-slate-200 pt-4">
                <div>
                  <h3 className="text-sm font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-1">
                    Voiceover creation
                  </h3>
                  <p className="text-gray-600 text-xs">
                    Choose a method to create audio track
                  </p>
                </div>

                <div className="grid grid-cols-2 gap-2">
                  <button
                    onClick={() => setAudioMethod("ai")}
                    className={`p-3 rounded-lg border-2 transition-all duration-300 ${
                      audioMethod === "ai"
                        ? "border-blue-500 bg-blue-50 shadow-lg shadow-blue-500/30"
                        : "border-gray-200 bg-white hover:border-gray-300"
                    }`}
                  >
                    <Brain
                      size={20}
                      className={`mx-auto mb-2 ${
                        audioMethod === "ai" ? "text-blue-600" : "text-gray-400"
                      }`}
                    />
                    <h4
                      className={`font-semibold mb-1 text-xs ${
                        audioMethod === "ai" ? "text-blue-700" : "text-gray-600"
                      }`}
                    >
                      AI Voiceover
                    </h4>
                    <p className="text-xs text-gray-500">
                      Generate voice using AI
                    </p>
                  </button>
                  <button
                    onClick={() => setAudioMethod("record")}
                    className={`p-3 rounded-lg border-2 transition-all duration-300 ${
                      audioMethod === "record"
                        ? "border-slate-500 bg-slate-50 shadow-lg shadow-slate-500/30"
                        : "border-gray-200 bg-white hover:border-gray-300"
                    }`}
                  >
                    <Mic
                      size={20}
                      className={`mx-auto mb-2 ${
                        audioMethod === "record"
                          ? "text-slate-600"
                          : "text-gray-400"
                      }`}
                    />
                    <h4
                      className={`font-semibold mb-1 text-xs ${
                        audioMethod === "record"
                          ? "text-slate-700"
                          : "text-gray-600"
                      }`}
                    >
                      Record yourself
                    </h4>
                    <p className="text-xs text-gray-500">
                      Use teleprompter
                    </p>
                  </button>
                </div>

                {audioMethod === "ai" && (
                  <div className="space-y-2">
                    <div>
                      <label className="block text-gray-700 font-semibold mb-2 text-sm">
                        Select voice
                      </label>
                      <select className="w-full p-2 bg-white border border-slate-200 rounded-lg text-gray-900 focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow text-sm">
                        <option>Alexei - Male, professional</option>
                        <option>Anya - Female, friendly</option>
                        <option>Dmitry - Young male, energetic</option>
                        <option>Elena - Female, strict</option>
                        <option>Maxim - Male, charismatic</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-gray-700 font-semibold mb-2 text-sm">
                        Speech speed
                      </label>
                      <div className="flex items-center space-x-2">
                        <span className="text-xs text-gray-500">Slow</span>
                        <input
                          type="range"
                          min="0.5"
                          max="2"
                          step="0.1"
                          defaultValue="1"
                          className="flex-1"
                        />
                        <span className="text-xs text-gray-500">Fast</span>
                      </div>
                    </div>
                    <button
                      onClick={generateAudio}
                      disabled={!isAuthenticated}
                      className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-300 shadow hover:shadow-lg hover:scale-105 text-sm"
                    >
                      <Volume2 className="mr-2 inline" size={14} />
                      Generate AI voiceover
                    </button>
                  </div>
                )}

                {audioMethod === "record" && (
                  <div className="space-y-2">
                    <div className="bg-gradient-to-r from-slate-50 to-blue-50 p-3 rounded-lg border border-slate-200 shadow">
                      <div className="text-center">
                        {!recordedAudio && (
                          <>
                            <div className="w-10 h-10 bg-gradient-to-r from-slate-600 to-blue-600 rounded-lg mx-auto mb-2 flex items-center justify-center shadow">
                              <Eye size={20} className="text-white" />
                            </div>
                            <h4 className="font-bold text-slate-800 mb-1 text-sm">
                              Professional recording
                            </h4>
                            <p className="text-xs text-slate-600 mb-3">
                              Use a teleprompter for quality recording
                            </p>
                            <button
                              onClick={() => alert("Opening teleprompter...")}
                              className="bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition-all duration-300 shadow hover:shadow-lg hover:scale-105 flex items-center mx-auto text-sm"
                            >
                              <Eye className="mr-2" size={14} />
                              Open teleprompter
                            </button>
                          </>
                        )}
                        {recordedAudio && (
                          <>
                            <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg mx-auto mb-2 flex items-center justify-center shadow">
                              <Volume2 size={20} className="text-white" />
                            </div>
                            <h4 className="font-bold text-green-800 mb-1 text-sm">
                              Recording completed!
                            </h4>
                            <p className="text-xs text-green-600 mb-3">
                              {recordedAudio.type === "video"
                                ? "Video"
                                : "Audio"}{" "}
                              successfully recorded
                            </p>
                            <div className="flex space-x-2 justify-center">
                              <button
                                onClick={() => alert("Opening audio editor...")}
                                className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-2 px-3 rounded-lg transition-all duration-300 shadow text-xs"
                              >
                                <Edit3 className="mr-1 inline" size={12} />
                                Edit
                              </button>
                              <button
                                onClick={() => alert("Recording again...")}
                                className="bg-gradient-to-r from-slate-600 to-slate-700 hover:from-slate-700 hover:to-slate-800 text-white font-semibold py-2 px-3 rounded-lg transition-all duration-300 shadow text-xs"
                              >
                                Record again
                              </button>
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                )}

                {audioUrl && audioMethod === "ai" && (
                  <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-3 rounded-lg border border-blue-200 shadow">
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-lg flex items-center justify-center shadow">
                        <Volume2 size={14} className="text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="font-bold text-blue-800 text-sm">
                          AI voiceover created!
                        </h4>
                        <p className="text-xs text-blue-600">
                          Professional voiceover ready
                        </p>
                      </div>
                      <button
                        onClick={() => alert("Opening audio editor...")}
                        className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-1 px-2 rounded-lg transition-all duration-300 shadow text-xs"
                      >
                        <Edit3 className="mr-1 inline" size={12} />
                        Edit
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ContentStudio;
