import { useState, useRef, useEffect } from "react";
import {
  Upload,
  Play,
  Pause,
  Scissors,

  FileText,
  Wand2,
  Plus,
  X,

  Zap,
  Brain,

  Heart,


  Star,
  Target,
  Download,
  Trash2,

  ZoomIn,
  ZoomOut,
  SkipBack,
  SkipForward,

  Film,
  Music,

} from "lucide-react";

const AdvancedVideoEditor = () => {
  // Existing states
  const [script, setScript] = useState("");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [duration, setDuration] = useState("~10 минут (1000 слов)");
  const [currentStep, setCurrentStep] = useState(1);
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [contentType, setContentType] = useState("lifestyle");
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);

  // New states for advanced editor
  const [mediaLibrary, setMediaLibrary] = useState([]);
  const [timeline, setTimeline] = useState({
    videoTracks: [[]],
    audioTracks: [[]],
    duration: 0,
    currentTime: 0,
    zoom: 1,
  });
  const [selectedTrackItem, setSelectedTrackItem] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [canvasPreview, setCanvasPreview] = useState(null);
  const [draggedItem, setDraggedItem] = useState(null);
  const [showExportModal, setShowExportModal] = useState(false);

  const fileInputRef = useRef(null);
  const canvasRef = useRef(null);
  const timelineRef = useRef(null);

  // Media Library Functions
  const handleMediaUpload = (event) => {
    const files = Array.from(event.target.files);

    files.forEach((file) => {
      const mediaItem = {
        id: Date.now() + Math.random(),
        name: file.name,
        file: file,
        type: file.type.startsWith("video/") ? "video" : "audio",
        url: URL.createObjectURL(file),
        duration: 0, // Will be set when metadata loads
        size: file.size,
        thumbnail: null,
      };

      // Generate thumbnail for videos
      if (mediaItem.type === "video") {
        const video = document.createElement("video");
        video.src = mediaItem.url;
        video.addEventListener("loadedmetadata", () => {
          mediaItem.duration = video.duration;
          const canvas = document.createElement("canvas");
          canvas.width = 160;
          canvas.height = 90;
          const ctx = canvas.getContext("2d");
          video.currentTime = video.duration * 0.25; // 25% into video
          video.addEventListener(
            "seeked",
            () => {
              ctx.drawImage(video, 0, 0, 160, 90);
              mediaItem.thumbnail = canvas.toDataURL();
              setMediaLibrary((prev) => [...prev, mediaItem]);
            },
            { once: true }
          );
        });
      } else {
        setMediaLibrary((prev) => [...prev, mediaItem]);
      }
    });
  };

  const removeMediaItem = (id) => {
    setMediaLibrary((prev) => {
      const item = prev.find((item) => item.id === id);
      if (item) URL.revokeObjectURL(item.url);
      return prev.filter((item) => item.id !== id);
    });
  };

  // Timeline Functions
  const addToTimeline = (mediaItem, trackType, trackIndex = 0) => {
    const timelineItem = {
      id: Date.now() + Math.random(),
      mediaId: mediaItem.id,
      name: mediaItem.name,
      type: mediaItem.type,
      url: mediaItem.url,
      startTime: timeline.currentTime,
      duration: mediaItem.duration || 5,
      trimStart: 0,
      trimEnd: mediaItem.duration || 5,
      volume: 1,
      effects: [],
    };

    setTimeline((prev) => {
      const newTimeline = { ...prev };
      if (trackType === "video") {
        if (!newTimeline.videoTracks[trackIndex]) {
          newTimeline.videoTracks[trackIndex] = [];
        }
        newTimeline.videoTracks[trackIndex].push(timelineItem);
      } else {
        if (!newTimeline.audioTracks[trackIndex]) {
          newTimeline.audioTracks[trackIndex] = [];
        }
        newTimeline.audioTracks[trackIndex].push(timelineItem);
      }
      return newTimeline;
    });
  };

  const removeFromTimeline = (itemId, trackType, trackIndex) => {
    setTimeline((prev) => {
      const newTimeline = { ...prev };
      if (trackType === "video") {
        newTimeline.videoTracks[trackIndex] = newTimeline.videoTracks[
          trackIndex
        ].filter((item) => item.id !== itemId);
      } else {
        newTimeline.audioTracks[trackIndex] = newTimeline.audioTracks[
          trackIndex
        ].filter((item) => item.id !== itemId);
      }
      return newTimeline;
    });
  };

  // Drag and Drop
  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    e.dataTransfer.effectAllowed = "copy";
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "copy";
  };

  const handleDrop = (e, trackType, trackIndex) => {
    e.preventDefault();
    if (draggedItem) {
      addToTimeline(draggedItem, trackType, trackIndex);
      setDraggedItem(null);
    }
  };

  // Playback Controls
  const togglePlayback = () => {
    setIsPlaying(!isPlaying);
  };

  const seekToTime = (time) => {
    setTimeline((prev) => ({ ...prev, currentTime: time }));
  };

  // Format time display
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  // Content Types
  const contentTypes = [
    { id: "lifestyle", name: "Lifestyle", icon: Heart, color: "slate" },
    { id: "gaming", name: "Gaming", icon: Target, color: "blue" },
    { id: "tech", name: "Technology", icon: Zap, color: "indigo" },
    { id: "education", name: "Education", icon: Brain, color: "blue" },
    { id: "entertainment", name: "Entertainment", icon: Star, color: "slate" },
  ];

  const steps = [
    {
      num: 1,
      title: "Create Script",
      active: currentStep >= 1,
      completed: currentStep > 1,
      icon: FileText,
      desc: "AI will create professional content",
    },
    {
      num: 2,
      title: "Media Library",
      active: currentStep >= 2,
      completed: currentStep > 2,
      icon: Upload,
      desc: "Upload and organize media",
    },
    {
      num: 3,
      title: "Timeline Editing",
