import { useState, useRef, useEffect } from "react";

export const TeleprompterModal = ({
  isOpen = true,
  onClose = () => {},
  script = "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ —Ç–µ–ª–µ–ø—Ä–æ–º–ø—Ç–µ—Ä! –≠—Ç–æ –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏—è —Ä–∞–±–æ—Ç—ã —Å—É—Ñ–ª–µ—Ä–∞ —Å –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å—é –∑–∞–ø–∏—Å–∏. –ó–¥–µ—Å—å –≤—ã –º–æ–∂–µ—Ç–µ –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å —Å–∫–æ—Ä–æ—Å—Ç—å –ø—Ä–æ–∫—Ä—É—Ç–∫–∏, —Ä–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞ –∏ —Ç–∏–ø –∑–∞–ø–∏—Å–∏. –ü—Ä–æ–∫—Ä—É—Ç–∫–∞ –±—É–¥–µ—Ç –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–∞–ø—É—â–µ–Ω–∞ –ø—Ä–∏ –Ω–∞—á–∞–ª–µ –∑–∞–ø–∏—Å–∏. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –¥–ª—è –ø–∞—É–∑—ã, –ø—Ä–æ–¥–æ–ª–∂–µ–Ω–∏—è –∏–ª–∏ —Å–±—Ä–æ—Å–∞ —Ç–µ–∫—Å—Ç–∞. –ü—Ä–∏ –∑–∞–ø–∏—Å–∏ –≤–∏–¥–µ–æ –≤—ã —É–≤–∏–¥–∏—Ç–µ –æ–∫–Ω–æ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –≤ –ø—Ä–∞–≤–æ–º –≤–µ—Ä—Ö–Ω–µ–º —É–≥–ª—É. –≠—Ç–æ –ø–æ–º–æ–∂–µ—Ç –≤–∞–º –≤–∏–¥–µ—Ç—å, —á—Ç–æ –∏–º–µ–Ω–Ω–æ –∑–∞–ø–∏—Å—ã–≤–∞–µ—Ç—Å—è. –¢–µ–ª–µ–ø—Ä–æ–º–ø—Ç–µ—Ä –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç –∫–∞–∫ –∞—É–¥–∏–æ, —Ç–∞–∫ –∏ –≤–∏–¥–µ–æ –∑–∞–ø–∏—Å—å. –ù–∞—Å—Ç—Ä–æ–π—Ç–µ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–∏ —Å –≤–∞—à–∏–º–∏ –ø–æ—Ç—Ä–µ–±–Ω–æ—Å—Ç—è–º–∏ –∏ –Ω–∞—á–∏–Ω–∞–π—Ç–µ –∑–∞–ø–∏—Å—å!",
  onRecordingComplete = () => {},
}) => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [recordingType, setRecordingType] = useState("audio");
  const [speed, setSpeed] = useState(50);
  const [fontSize, setFontSize] = useState(24);
  const [isScrolling, setIsScrolling] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);

  const scrollContainerRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const scrollIntervalRef = useRef(null);
  const timerRef = useRef(null);
  const streamRef = useRef(null);
  const videoPreviewRef = useRef(null);

  const startScrolling = () => {
    if (scrollIntervalRef.current) return;
    
    setIsScrolling(true);
    scrollIntervalRef.current = setInterval(() => {
      if (scrollContainerRef.current && isScrolling && !isPaused) {
        const container = scrollContainerRef.current;
        const scrollStep = (speed / 50) * 2;
        container.scrollTop += scrollStep;

        if (
          container.scrollTop >=
          container.scrollHeight - container.clientHeight
        ) {
          stopScrolling();
        }
      }
    }, 50);
  };

  const stopScrolling = () => {
    if (scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = null;
    }
    setIsScrolling(false);
  };

  const resetScroll = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollTop = 0;
    }
    stopScrolling();
  };

  const startTimer = () => {
    timerRef.current = setInterval(() => {
      setRecordingTime((prev) => prev + 1);
    }, 1000);
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª–Ω–æ–π –æ—Å—Ç–∞–Ω–æ–≤–∫–∏ –∑–∞–ø–∏—Å–∏ –∏ —Å–±—Ä–æ—Å–∞ —Ç–∞–π–º–µ—Ä–∞
  const stopRecordingAndResetTimer = () => {
    // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∑–∞–ø–∏—Å—å –µ—Å–ª–∏ –æ–Ω–∞ –∏–¥–µ—Ç
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }

    // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∏ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º —Ç–∞–π–º–µ—Ä
    stopTimer();
    setRecordingTime(0);

    // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω—ã–π –ø—Ä–æ—Å–º–æ—Ç—Ä
    if (videoPreviewRef.current) {
      videoPreviewRef.current.srcObject = null;
    }

    // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –º–µ–¥–∏–∞-–ø–æ—Ç–æ–∫
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
      streamRef.current = null;
    }

    // –°–±—Ä–∞—Å—ã–≤–∞–µ–º blob –∑–∞–ø–∏—Å–∏
    setRecordedBlob(null);
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª–Ω–æ–≥–æ —Å–±—Ä–æ—Å–∞ –≤—Å–µ—Ö —Å–æ—Å—Ç–æ—è–Ω–∏–π
  const resetAllStates = () => {
    // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∑–∞–ø–∏—Å—å
    stopRecordingAndResetTimer();
    
    // –°–±—Ä–∞—Å—ã–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∏
    stopScrolling();
    setIsPaused(false);
    resetScroll();
    
    // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –Ω–∞—Å—Ç—Ä–æ–π–∫–∏
    setShowSettings(false);
    
    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –∫ –Ω–∞—á–∞–ª—å–Ω—ã–º –∑–Ω–∞—á–µ–Ω–∏—è–º (–ø–æ –∂–µ–ª–∞–Ω–∏—é)
    // setSpeed(50);
    // setFontSize(24);
    // setRecordingType("audio");
  };

  const startRecording = async () => {
    try {
      const constraints =
        recordingType === "video"
          ? { video: true, audio: true }
          : { audio: true };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      // –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –≤–∏–¥–µ–æ —Å –ø—Ä–æ–≤–µ—Ä–∫–æ–π –∏ –æ–∂–∏–¥–∞–Ω–∏–µ–º
      if (recordingType === "video") {
        const setupVideoPreview = () => {
          if (videoPreviewRef.current) {
            const videoElement = videoPreviewRef.current;
            videoElement.srcObject = stream;
            
            videoElement.onloadedmetadata = () => {
              console.log("–í–∏–¥–µ–æ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã");
              videoElement.play().catch(e => console.log("–û—à–∏–±–∫–∞ play:", e));
            };
            
            videoElement.oncanplay = () => {
              console.log("–í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ –∫ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—é");
            };
            
            videoElement.onerror = (e) => {
              console.log("–û—à–∏–±–∫–∞ –≤–∏–¥–µ–æ:", e);
            };
            
            // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –ø—ã—Ç–∞–µ–º—Å—è –∑–∞–ø—É—Å—Ç–∏—Ç—å
            setTimeout(() => {
              if (videoElement.srcObject) {
                videoElement.play().catch(e => console.log("–û—Ç–ª–æ–∂–µ–Ω–Ω—ã–π play error:", e));
              }
            }, 100);
            
            return true;
          }
          return false;
        };

        // –ü—ã—Ç–∞–µ–º—Å—è –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å –≤–∏–¥–µ–æ —Å—Ä–∞–∑—É
        if (!setupVideoPreview()) {
          // –ï—Å–ª–∏ ref –µ—â–µ null, –∂–¥–µ–º –Ω–µ–º–Ω–æ–≥–æ –∏ –ø—Ä–æ–±—É–µ–º —Å–Ω–æ–≤–∞
          console.log("–í–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç –Ω–µ –≥–æ—Ç–æ–≤, –æ–∂–∏–¥–∞–µ–º...");
          const maxAttempts = 10;
          let attempts = 0;
          
          const waitForVideoElement = () => {
            attempts++;
            if (setupVideoPreview()) {
              console.log("–í–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç –Ω–∞—Å—Ç—Ä–æ–µ–Ω!");
              return;
            }
            
            if (attempts < maxAttempts) {
              setTimeout(waitForVideoElement, 100);
            } else {
              console.log("–ù–µ —É–¥–∞–ª–æ—Å—å –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç –ø–æ—Å–ª–µ", maxAttempts, "–ø–æ–ø—ã—Ç–æ–∫");
            }
          };
          
          setTimeout(waitForVideoElement, 50);
        }
      }

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, {
          type: recordingType === "video" ? "video/webm" : "audio/webm",
        });
        setRecordedBlob(blob);

        if (onRecordingComplete) {
          onRecordingComplete(blob, recordingType);
        }

        // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞
        if (videoPreviewRef.current) {
          videoPreviewRef.current.srcObject = null;
        }

        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      startTimer();
      
      // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –Ω–∞—á–∏–Ω–∞–µ–º –ø—Ä–æ–∫—Ä—É—Ç–∫—É –ø—Ä–∏ –∑–∞–ø–∏—Å–∏
      if (!isScrolling) {
        startScrolling();
      }
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞ –∫ –º–µ–¥–∏–∞:", error);
      alert("–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–æ—Å—Ç—É–ø –∫ –º–∏–∫—Ä–æ—Ñ–æ–Ω—É/–∫–∞–º–µ—Ä–µ");
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      stopTimer();

      // –û—Å—Ç–∞–Ω–æ–≤–∫–∞ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞
      if (videoPreviewRef.current) {
        videoPreviewRef.current.srcObject = null;
      }

      if (streamRef.current) {
        streamRef.current.getTracks().forEach((track) => track.stop());
      }
    }
  };

  const downloadRecording = () => {
    if (recordedBlob) {
      const url = URL.createObjectURL(recordedBlob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `recording-${Date.now()}.${
        recordingType === "video" ? "webm" : "webm"
      }`;
      a.click();
      URL.revokeObjectURL(url);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, "0")}:${secs
      .toString()
      .padStart(2, "0")}`;
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∑–∞–∫—Ä—ã—Ç–∏—è –º–æ–¥–∞–ª–∫–∏ —Å –ø–æ–ª–Ω—ã–º —Å–±—Ä–æ—Å–æ–º
  const handleClose = () => {
    resetAllStates();
    onClose();
  };

  // useEffect –¥–ª—è –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–π –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –≤–∏–¥–µ–æ
  useEffect(() => {
    if (isRecording && recordingType === "video" && streamRef.current && videoPreviewRef.current) {
      const videoElement = videoPreviewRef.current;
      if (!videoElement.srcObject) {
        console.log("–î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –Ω–∞—Å—Ç—Ä–æ–π–∫–∞ –≤–∏–¥–µ–æ —á–µ—Ä–µ–∑ useEffect");
        videoElement.srcObject = streamRef.current;
        videoElement.play().catch(e => console.log("UseEffect play error:", e));
      }
    }
  }, [isRecording, recordingType, streamRef.current]);

  // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–π useEffect –¥–ª—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–≥–æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∏
  useEffect(() => {
    if (isScrolling && !isPaused && scrollIntervalRef.current) {
      // –ü–µ—Ä–µ–∑–∞–ø—É—Å–∫–∞–µ–º –∏–Ω—Ç–µ—Ä–≤–∞–ª –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ —Å–∫–æ—Ä–æ—Å—Ç–∏
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = setInterval(() => {
        if (scrollContainerRef.current && isScrolling && !isPaused) {
          const container = scrollContainerRef.current;
          const scrollStep = (speed / 50) * 2;
          container.scrollTop += scrollStep;

          if (
            container.scrollTop >=
            container.scrollHeight - container.clientHeight
          ) {
            stopScrolling();
          }
        }
